  public function destroy($server_id)
  {
    
    $linkuser = $this->servermodel->find($server_id);

    $db1 = \Config\Database::connect();

        $builder = $db1->table('linked_erp_user');

        $builder->delete([
            'SERVER_ID' => $linkuser['SERVER_ID']
