  public function update($SERVER_ID)
  {
    //set rules validation form
    $rules = [
      'database' => 'required|min_length[1]|max_length[30]|is_unique[server.DATABASE_NAME,SERVER_ID,' . $SERVER_ID . ']',
      'port' => 'required[server.PORT,' . $SERVER_ID . ']',
      'max' => 'required[server.MAX_USER,' . $SERVER_ID . ']'
    ];

    if ($this->validate($rules)) {
      $database = new ServerModel();
      $data = [
        'SERVER_ID' => $SERVER_ID,
        'DATABASE_NAME' => $this->request->getVar('database'),
        'LAST_UPDATE_DATE' => $this->request->getVar('last_update_date'),
        'NAMA_PERUSAHAAN' => $this->request->getVar('perusahaan'),
        'ALAMAT_PERUSAHAAN' => $this->request->getVar('alamat'),
        'JENIS_USAHA' => $this->request->getVar('jenis'),
        'ACTIVE_DATE' => $this->request->getVar('tanggal'),
        'HOSTNAME' => $this->request->getVar('hostname'),
        'PORT' => $this->request->getVar('port'),
        'MAX_USER' => $this->request->getVar('max')
      ];
      $database->save($data);
      session()->setFlashdata('pesan', 'Data berhasil diubah');
      return redirect()->to('/server');
    } else {
      session()->setFlashdata('error', $this->validator->listErrors());
      return redirect()->back()->withInput();
    }
  }

