  public function store()
  {
    //set rules validation form
    $rules = [
      'database' => 'required|min_length[1]|max_length[30]|is_unique[server.DATABASE_NAME]|alpha_dash',
      'port' => 'required[server.PORT]',
      'max' => 'required[server.MAX_USER]'
    ];

    if ($this->validate($rules)) {
      $database = new ServerModel();
      $dbname = $this->request->getVar('database');

      $data = [
        'DATABASE_NAME' => $dbname,
        'CREATED_DATE' => date("Y-m-d H:i:s", strtotime($this->request->getVar('created_date'))),
        'LAST_UPDATE_DATE' => date("Y-m-d H:i:s", strtotime($this->request->getVar('last_update_date'))),
