(431, 'Jual_So_Retur', 'Retur Jual', NULL, 400, 431, 'N', '2013-07-26 14:52:42', 1, 4, '2009-01-04 23:23:31', 'RJ-', 'NOTA_PENJUALAN', 'FORM\\F_SLS_INVRJX', 5, NULL, 'DOCUMENT_DATE', 'SELECT @abc2:=0,NO FROM ERP_NO_FORM WHERE\r\nNO <= (SELECT IF(count() mod @Baris =0, (COUNT())  DIV @Baris ,  (COUNT())  DIV @Baris +1 ) FROM NOTA_PENJUALAN_DETAIL\r\nWHERE NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID )', 'SELECT * FROM\r\nERP_NO_FORM\r\nLEFT JOIN\r\n(\r\nselect  @abc2 :=0,@abc3 :=0,@abc4:=0, YY. from\r\n(\r\nselect (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as x ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.*, i.ITEM_CODE\r\nFROM\r\nNOTA_PENJUALAN_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.NOTA_PENJUALAN_id=  :NOTA_PENJUALAN_ID\r\norder by  a.NOTA_PENJUALAN_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X = :NO\r\n)AS ZZ ON (ERP_NO_FORM.NO = ZZ.X2)\r\nWHERE\r\nERP_NO_FORM.NO <= @Baris', NULL, NULL, NULL, 15, 'INV RETUR JUAL', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, 'Y', 'N', NULL, NULL),
(488, 'Efaktur', 'Lapor Efaktur PPN keluaran', NULL, 400, 488, 'Y', '2017-09-12 09:49:07', 0, 0, '2017-08-15 13:12:13', NULL, NULL, 'SALES\\t_ppn_keluaran2', NULL, NULL, 'DOCUMENT_DATE', 'SELECT \r\na.NOTA_PENJUALAN_ID,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\nconcat(a.document_no,\' Rp. \',format(a.total_net,2))NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\nd.DOCUMENT_REFF_NO,\r\na.PERIOD_NAME,\r\nfloor(sum(b.subtotal-b.DISCOUNT_PRICE1))TOTAL_AMOUNT,\r\na.TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\na.PPN_PERCEN,\r\na.PPN_AMOUNT,\r\na.PRINT_COUNT,\r\na.KARYAWAN_ID,\r\na.PPN_CODE,\r\na.JTEMPO,\r\nc.PERSON_NAME,\r\nSITE_NAME, \r\nv.DISPLAY_NAME as STATUS_DOC,\r\nw.WAREHOUSE_NAME,\r\na.TOTAL_NET,\r\ncs.ADDRESS1,\r\ncs.COUNTRY,\r\nconcat(e,f,g,h,i,j) as npwp_no,\r\nconcat(b,c,d) as no,\r\nfloor(sum((b.subtotal-b.DISCOUNT_PRICE1)* 0.1)) as PPN\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nINNER JOIN (\r\nSELECT NOTA_PENJUALAN_ID,\r\nsubstring_index(a.DOCUMENT_REFF_NO,\'.\',-1) as d,\r\nsubstring_index(substring_index(a.DOCUMENT_REFF_NO,\'-\',-1),\'.\',1) as c,\r\nsubstring_index(substring_index(a.DOCUMENT_REFF_NO,\'-\',1),\'.\',-1) as b,\r\nsubstring_index(a.DOCUMENT_REFF_NO,\'.\',1) as a\r\nfrom nota_penjualan a\r\nwhere true \r\n&kondisi) as no on a.NOTA_PENJUALAN_ID = no.NOTA_PENJUALAN_ID\r\nINNER JOIN (\r\nselect \r\nsubstring_index(NPWP_NO,\'.\',1) as E,\r\nsubstring_index(substring_index(NPWP_NO,\'.\',2),\'.\',-1) as F,\r\nsubstring_index(substring_index(NPWP_NO,\'.\',3),\'.\',-1) as G,\r\nsubstring_index(substring_index(NPWP_NO,\'-\',1),\'.\',-1) as H,\r\nsubstring_index(substring_index(NPWP_NO,\'-\',-1),\'.\',1) as I,\r\nsubstring_index(NPWP_NO,\'.\',-1) as J,\r\nPERSON_ID from person\r\n) as NPWP on a.PERSON_ID=npwp.PERSON_ID\r\nLEFT JOIN INVENTORY_OUT d on (d.INVENTORY_OUT_ID=a.INVENTORY_OUT_ID)\r\nWHERE a.DOCUMENT_TYPE_ID = 1 and  c.npwp_no <> \'00.000.000.0-000.00\'\r\n&KONDISI\r\ngroup by a.nota_penjualan_id', 'SELECT \r\nb.ENTERED_QTY,\r\nb.BASE_QTY,\r\nb.UNIT_PRICE,\r\n(b.DISCOUNT_PRICE+b.DISCOUNT_PRICE1) as DISCOUNT_PRICE,\r\nb.DISCOUNT_PRICE1,\r\nb.DISCOUNT_PERCEN,\r\n(b.SUBTOTAL-b.DISCOUNT_PRICE1) as SUBTOTAL,\r\nb.NOTE,\r\nb.HPP,\r\nb.ENTERED_UOM,\r\nb.NOTA_PENJUALAN_ID,\r\nb.ITEM_DESCRIPTION,\r\ni.ITEM_CODE,\r\nc.DOCUMENT_NO,\r\n((b.subtotal-b.DISCOUNT_PRICE1)* 0.1) as PPN\r\nFROM\r\nnota_penjualan_detail b\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nLEFT JOIN INVENTORY_OUT c ON  b.INVENTORY_OUT_ID=c.INVENTORY_OUT_ID\r\nWHERE\r\nb.NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID\r\n&KONDISI\r\nORDER BY b.NOTA_PENJUALAN_DETAIL_ID\r\n', NULL, NULL, NULL, NULL, 'efaktur', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL),
