(519, 'BKM', 'BKM (Bukti Kas/Bank Masuk)', NULL, 500, 519, 'Y', '2010-08-06 11:49:27', 0, 1, '2008-01-26 17:58:02', 'KM-', 'RECEIPT', 'FORM\\F_FIN_BKM', 1, NULL, 'DOCUMENT_DATE', NULL, 'select if(s.flag_supp=0, v.account_name,va.account_name) as account_name\r\n,if(s.flag_supp=0, v.account_code,va.account_code) as account_code,\r\nrd.* ,\r\n ifnull(n.document_no , i.document_no) as document_no \r\n\r\n\r\n from receipt r\r\ninner join receipt_detail rd on (r.receipt_id=rd.receipt_id)\r\ninner join person s on (r.person_id=s.person_id)\r\nleft join v_account v on (s.coa_piut_id=v.coa_id) and s.flag_supp=0\r\nleft join v_account va on (s.coa_hut_id = va.coa_id) and s.flag_supp=1\r\nleft join nota_penjualan_aging n on (rd.nota_penjualan_aging_id = n.nota_penjualan_aging_id)\r\nleft join invoice_due i on (rd.invoice_due_id = i.invoice_due_id)\r\n\r\nwhere r.receipt_id = :receipt_id and rd.coa_id is null \r\n', NULL, NULL, NULL, 18, 'BKM', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, 'N', NULL, NULL, NULL),
