$tbl_erp_menu = "CREATE TABLE IF NOT EXISTS `erp_menu` (
        `ERP_MENU_ID` int(11) NOT NULL DEFAULT '0',
        `ERP_MENU_NAME` varchar(40) NOT NULL DEFAULT '',
        `PROMPT` varchar(40) DEFAULT NULL,
        `DESCRIPTION` varchar(80) DEFAULT NULL,
        `PARENT_ID` int(4) NOT NULL DEFAULT '0',
        `SEQ` int(4) DEFAULT NULL,
        `ACTIVE_FLAG` char(1) DEFAULT 'Y',
        `LAST_UPDATE_DATE` datetime DEFAULT '0000-00-00 00:00:00',
        `LAST_UPDATE_BY` int(11) NOT NULL DEFAULT '1',
        `CREATED_BY` int(11) NOT NULL DEFAULT '1',
        `CREATED_DATE` datetime DEFAULT '0000-00-00 00:00:00',
        `KATA` varchar(10) DEFAULT NULL,
        `TABLE_NAME` varchar(40) DEFAULT NULL,
        `TEMPLATE_FILE` varchar(40) DEFAULT NULL,
        `TYPE_ID` tinyint(2) DEFAULT NULL,
        `KONDISI_ITEM` varchar(80) DEFAULT NULL,
        `ORDERBY` varchar(80) DEFAULT NULL,
        `SQL_HEADER` text,
        `SQL_DETAIL` text,
        `SQL_OTHER_H` text,
        `SQL_OTHER_D` text,
        `NOTE` varchar(240) DEFAULT NULL,
        `ERP_TABLE_ID` int(11) DEFAULT NULL,
        `NAMA_TEMPLATE` varchar(40) DEFAULT NULL,
        `NAMA_TEMPLATE2` varchar(40) DEFAULT NULL,
        `TEMPLATE_FILE2` varchar(40) DEFAULT NULL,
        `ORDERBY_DETAIL` varchar(80) DEFAULT NULL,
        `ORDERBY_DETAIL3` varchar(80) DEFAULT NULL,
        `NAMA_TEMPLATE3` varchar(40) DEFAULT NULL,
        `TEMPLATE_FILE3` varchar(40) DEFAULT NULL,
        `TGL_EDIT` int(2) DEFAULT '1',
        `BARCODE_FLAG` tinyint(1) DEFAULT '0',
        `CUSTOM1` varchar(35) DEFAULT NULL,
        `CUSTOM2` varchar(35) DEFAULT NULL,
        `CUSTOM3` varchar(35) DEFAULT NULL,
        `CUSTOM4` varchar(35) DEFAULT NULL,
        `CUSTOM5` varchar(35) DEFAULT NULL,
        PRIMARY KEY (`ERP_MENU_ID`),
        UNIQUE KEY `ERP_MENU_NAME` (`ERP_MENU_NAME`),
        KEY `ERP_TABLE_ID` (`ERP_TABLE_ID`)
      ) ENGINE=InnoDB DEFAULT CHARSET=latin1;";

