      $insert_cetakan = "INSERT INTO `cetakan` (`CETAKAN_ID`, `ERP_MENU_ID`, `CETAKAN_NAMA1`, `CETAKAN_NAMA2`, `CETAKAN_NAMA3`, `CETAKAN_FILE1`, `CETAKAN_FILE2`, `CETAKAN_FILE3`, `QUERY_HEADER`, `QUERY_DETAIL`, `QUERY_OTHER_H`, `QUERY_OTHER_D`) VALUES
      (1, 415, 'SALES ORDER', '', '', 'FORM\\F_SLS_SO', '', '', 'SELECT \r\n@abc2:=0,\r\nNO, \r\nso.SO_ID,\r\nso.DOCUMENT_NO,\r\nDATE_FORMAT(so.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.ADDRESS1,\r\nps.SITE_NAME,\r\npt.PAYMENT_TERM_NAME,\r\nk.FIRST_NAME,\r\nso.DOCUMENT_REFF_NO,\r\nso.PPN_CODE,\r\nDATE_FORMAT(so.NEED_DATE,\'%d-%b-%Y\' ) as NEED_DATE,\r\nso.NOTE,\r\nFORMAT(so.PPN_AMOUNT,2)PPN_AMOUNT,\r\nFORMAT(so.TOTAL_DISCOUNT,2)TOTAL_DISCOUNT,\r\nFORMAT(so.TOTAL_NET,2)TOTAL_NET,\r\nFORMAT(so.TOTAL_AMOUNT,2)TOTAL_AMOUNT\r\nFROM ERP_NO_FORM, SO \r\nINNER JOIN PERSON p ON so.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON so.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nLEFT JOIN KARYAWAN k ON so.KARYAWAN_ID=k.KARYAWAN_ID\r\nINNER JOIN PAYMENT_TERM pt ON so.PAYMENT_TERM_ID=pt.PAYMENT_TERM_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM SO_DETAIL\r\nWHERE SO_ID = :SO_ID\r\nORDER BY SO_DETAIL_ID )\r\nAND SO_ID = :SO_ID', '\r\nSELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.*,@nomer:=0 from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\n(@nomer:= @nomer+ 1) AS NO,\r\na.ITEM_DESCRIPTION,\r\na.ENTERED_UOM,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\nFORMAT(a.UNIT_PRICE,2)UNIT_PRICE, \r\nFORMAT(a.DISCOUNT_PERCEN,2)DISCOUNT_PERCEN,\r\nFORMAT(a.DISCOUNT_PRICE,2)DISCOUNT_PRICE,\r\nFORMAT(a.SUBTOTAL,2)SUBTOTAL,\r\na.NOTE,\r\ni.ITEM_CODE\r\nFROM\r\nSO_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.SO_id=  :SO_ID\r\norder by  a.SO_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO\r\n', '', ''),
      (2, 310, 'PO', '', '', 'FORM\\F_PUR_PO', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\npo.PO_ID,\r\npo.DOCUMENT_NO,\r\npo.DOCUMENT_REFF_NO,\r\nDATE_FORMAT(po.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.SITE_NAME,\r\npo.NOTE,\r\npt.PAYMENT_TERM_NAME,\r\nDATE_FORMAT(po.NEED_DATE,\'%d-%b-%Y\' ) as NEED_DATE ,\r\nad.ADDRESS_CODE,\r\nps.ADDRESS1,\r\nad.ADDRESS1 as A1,\r\npo.PPN_CODE,\r\nFORMAT(po.PPN_AMOUNT,2)PPN_AMOUNT,\r\nFORMAT(po.TOTAL_AMOUNT,2)TOTAL_AMOUNT,\r\nFORMAT(po.TOTAL_DISCOUNT,2)TOTAL_DISCOUNT,\r\nFORMAT(po.TOTAL_NET,2)TOTAL_NET\r\nFROM ERP_NO_FORM,PO\r\nINNER JOIN PERSON p ON po.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON po.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nINNER JOIN PAYMENT_TERM pt ON po.PAYMENT_TERM_ID=pt.PAYMENT_TERM_ID\r\nINNER JOIN ADDRESS ad ON po.ADDRESS_ID=ad.ADDRESS_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM PO_DETAIL\r\nWHERE PO_ID = :PO_ID )\r\nAND PO_ID = :PO_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.ITEM_DESCRIPTION,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\na.ENTERED_UOM,\r\nFORMAT(a.UNIT_PRICE,2)UNIT_PRICE,\r\nFORMAT(a.DISCOUNT_PRICE,2)DISCOUNT_PRICE,\r\nFORMAT(a.SUBTOTAL,2)SUBTOTAL,\r\na.NOTE, \r\ni.ITEM_CODE\r\nFROM\r\nPO_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.PO_id=  :PO_ID\r\norder by  a.PO_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (3, 220, 'PAG', '', '', 'FORM\\F_INV_PAG', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\nTAG.TAG_ID,\r\nTAG.DOCUMENT_NO,\r\nDATE_FORMAT(TAG.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\nTAG.DOCUMENT_REFF_NO,\r\nTAG.NOTE,\r\nw.WAREHOUSE_NAME,\r\nw2.WAREHOUSE_NAME WAREHOUSE_NAME_1\r\nFROM ERP_NO_FORM,TAG\r\nINNER JOIN WAREHOUSE w ON TAG.WAREHOUSE_ID=w.WAREHOUSE_ID\r\nINNER JOIN WAREHOUSE w2 ON TAG.TO_WH_ID=w2.WAREHOUSE_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM TAG_DETAIL\r\nWHERE TAG_ID = :TAG_ID )\r\nAND TAG_ID = :TAG_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\nROUND(a.ENTERED_QTY,2)ENTERED_QTY, \r\na.ENTERED_UOM,\r\ni.ITEM_CODE,i.ITEM_DESCRIPTION\r\nFROM\r\nTAG_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.TAG_id=  :TAG_ID\r\norder by  a.TAG_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (4, 321, 'INVOICE', '', '', 'FORM\\F_PUR_INVBLIX', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\ninvoice.INVOICE_ID,\r\ninvoice.DOCUMENT_NO,\r\nDATE_FORMAT(invoice.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.ADDRESS1,\r\nps.SITE_NAME,\r\nDATE_FORMAT(invoice.JTEMPO,\'%d-%b-%Y\' ) as JTEMPO,\r\npo.DOCUMENT_NO NO_BPB,\r\ninvoice.DOCUMENT_REFF_NO,\r\ninvoice.PPN_CODE,\r\ninvoice.NOTE,\r\npt.PAYMENT_TERM_NAME,\r\nFORMAT(invoice.TOTAL_AMOUNT,2)TOTAL_AMOUNT,\r\nFORMAT(invoice.PPN_AMOUNT,2)PPN_AMOUNT,\r\nFORMAT(invoice.TOTAL_DISCOUNT,2)TOTAL_DISCOUNT,\r\nFORMAT(invoice.TOTAL_NET,2)TOTAL_NET\r\nFROM ERP_NO_FORM,INVOICE\r\nLEFT JOIN inventory_in po on invoice.INVENTORY_IN_ID=po.INVENTORY_IN_ID\r\nINNER JOIN PERSON p ON invoice.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON invoice.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nINNER JOIN PAYMENT_TERM pt ON invoice.PAYMENT_TERM_ID=pt.PAYMENT_TERM_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM INVOICE_DETAIL\r\nWHERE INVOICE_ID = :INVOICE_ID )\r\nAND INVOICE_ID = :INVOICE_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.ITEM_DESCRIPTION, \r\na.ENTERED_UOM,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\nFORMAT(a.UNIT_PRICE,2)UNIT_PRICE,\r\nFORMAT(a.DISCOUNT_PRICE,2)DISCOUNT_PRICE,\r\nFORMAT(a.SUBTOTAL,2)SUBTOTAL,\r\na.NOTE,\r\ni.ITEM_CODE\r\nFROM\r\nINVOICE_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.INVOICE_id=  :INVOICE_ID\r\norder by  a.INVOICE_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (5, 311, 'PO', '', NULL, 'FORM\\F_PUR_POX', '', NULL, 'SELECT \r\n@abc2:=0,\r\nNO,\r\npo.PO_ID,\r\npo.DOCUMENT_NO,\r\npo.DOCUMENT_REFF_NO,\r\nDATE_FORMAT(po.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.SITE_NAME,\r\npo.NOTE,\r\npt.PAYMENT_TERM_NAME,\r\nDATE_FORMAT(po.NEED_DATE,\'%d-%b-%Y\' ) as NEED_DATE ,\r\nad.ADDRESS_CODE,\r\nps.ADDRESS1,\r\nad.ADDRESS1 as A1,\r\npo.PPN_CODE,\r\nFORMAT(po.PPN_AMOUNT,2)PPN_AMOUNT,\r\nFORMAT(po.TOTAL_AMOUNT,2)TOTAL_AMOUNT,\r\nFORMAT(po.TOTAL_DISCOUNT,2)TOTAL_DISCOUNT,\r\nFORMAT(po.TOTAL_NET,2)TOTAL_NET\r\nFROM ERP_NO_FORM,PO\r\nINNER JOIN PERSON p ON po.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON po.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nINNER JOIN PAYMENT_TERM pt ON po.PAYMENT_TERM_ID=pt.PAYMENT_TERM_ID\r\nINNER JOIN ADDRESS ad ON po.ADDRESS_ID=ad.ADDRESS_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM PO_DETAIL\r\nWHERE PO_ID = :PO_ID )\r\nAND PO_ID = :PO_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.ITEM_DESCRIPTION,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\na.ENTERED_UOM,\r\nFORMAT(a.UNIT_PRICE,2)UNIT_PRICE,\r\nFORMAT(a.DISCOUNT_PRICE,2)DISCOUNT_PRICE,\r\nFORMAT(a.SUBTOTAL,2)SUBTOTAL,\r\na.NOTE, \r\ni.ITEM_CODE\r\nFROM\r\nPO_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.PO_id=  :PO_ID\r\norder by  a.PO_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (6, 315, 'BD', '', '', 'FORM\\F_INV_BD', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\ninventory_in.DOCUMENT_NO,\r\nDATE_FORMAT(inventory_in.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.ADDRESS1,\r\nps.SITE_NAME,\r\npo.DOCUMENT_NO NO_PO,\r\ninventory_in.DOCUMENT_REFF_NO,\r\nw.WAREHOUSE_NAME,\r\ninventory_in.NOTE\r\nFROM ERP_NO_FORM, INVENTORY_IN\r\nINNER JOIN PERSON p ON inventory_in.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON inventory_in.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nLEFT JOIN PO ON inventory_in.PO_ID=po.PO_ID\r\nINNER JOIN WAREHOUSE w ON inventory_in.WAREHOUSE_ID=w.WAREHOUSE_ID\r\nWHERE NO<= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 )  FROM  INVENTORY_IN_DETAIL\r\nWHERE INVENTORY_IN_ID = :INVENTORY_IN_ID )\r\nAND INVENTORY_IN_ID = :INVENTORY_IN_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.ITEM_DESCRIPTION,\r\nROUND(a.ENTERED_QTY,2)ENTERED_QTY,\r\na.ENTERED_UOM,\r\na.NOTE,\r\ni.ITEM_CODE\r\nFROM\r\nINVENTORY_IN_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.INVENTORY_IN_id=  :INVENTORY_IN_ID\r\norder by  a.INVENTORY_IN_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (7, 320, 'INVOICE', '', '', 'FORM\\F_PUR_INVBLI', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\ninvoice.INVOICE_ID,\r\ninvoice.DOCUMENT_NO,\r\nDATE_FORMAT(invoice.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.ADDRESS1,\r\nps.SITE_NAME,\r\nDATE_FORMAT(invoice.JTEMPO,\'%d-%b-%Y\' ) as JTEMPO,\r\npo.DOCUMENT_NO NO_PO,\r\ninvoice.DOCUMENT_REFF_NO,\r\ninvoice.PPN_CODE,\r\ninvoice.NOTE,\r\npt.PAYMENT_TERM_NAME,\r\nFORMAT(invoice.TOTAL_AMOUNT,2)TOTAL_AMOUNT,\r\nFORMAT(invoice.PPN_AMOUNT,2)PPN_AMOUNT,\r\nFORMAT(invoice.TOTAL_DISCOUNT,2)TOTAL_DISCOUNT,\r\nFORMAT(invoice.TOTAL_NET,2)TOTAL_NET\r\nFROM ERP_NO_FORM,INVOICE\r\nLEFT JOIN PO on invoice.PO_ID=po.PO_ID\r\nINNER JOIN PERSON p ON invoice.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON invoice.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nINNER JOIN PAYMENT_TERM pt ON invoice.PAYMENT_TERM_ID=pt.PAYMENT_TERM_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM INVOICE_DETAIL\r\nWHERE INVOICE_ID = :INVOICE_ID )\r\nAND INVOICE_ID = :INVOICE_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.ITEM_DESCRIPTION, \r\na.ENTERED_UOM,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\nFORMAT(a.UNIT_PRICE,2)UNIT_PRICE,\r\nFORMAT(a.DISCOUNT_PRICE,2)DISCOUNT_PRICE,\r\nFORMAT(a.SUBTOTAL,2)SUBTOTAL,\r\na.NOTE,\r\ni.ITEM_CODE\r\nFROM\r\nINVOICE_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.INVOICE_id=  :INVOICE_ID\r\norder by  a.INVOICE_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (8, 330, 'RETUR BELI', '', '', 'FORM\\F_PUR_RETBLI', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\ninvoice.INVOICE_ID,\r\ninvoice.DOCUMENT_NO,\r\nDATE_FORMAT(invoice.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.ADDRESS1,\r\nps.SITE_NAME,\r\nDATE_FORMAT(invoice.JTEMPO,\'%d-%b-%Y\' ) as JTEMPO,\r\npo.DOCUMENT_NO NO_PO,\r\ninvoice.DOCUMENT_REFF_NO,\r\ninvoice.TOTAL_DISCOUNT_PERCEN,\r\ninvoice.PPN_CODE,\r\ninvoice.NOTE,\r\npt.PAYMENT_TERM_NAME,\r\nFORMAT(ABS(invoice.TOTAL_AMOUNT),2)TOTAL_AMOUNT,\r\nFORMAT(ABS(invoice.PPN_AMOUNT),2)PPN_AMOUNT,\r\nFORMAT(ABS(invoice.TOTAL_DISCOUNT),2)TOTAL_DISCOUNT,\r\nFORMAT(ABS(invoice.TOTAL_NET),2)TOTAL_NET\r\nFROM ERP_NO_FORM,INVOICE\r\nLEFT JOIN INVOICE PO on invoice.INVOICE_OLD_ID=po.INVOICE_ID\r\nINNER JOIN PERSON p ON invoice.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON invoice.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nINNER JOIN PAYMENT_TERM pt ON invoice.PAYMENT_TERM_ID=pt.PAYMENT_TERM_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM INVOICE_DETAIL\r\nWHERE INVOICE_ID = :INVOICE_ID )\r\nAND invoice.INVOICE_ID = :INVOICE_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.ITEM_DESCRIPTION,\r\na.ENTERED_UOM,\r\nFORMAT(ABS(a.ENTERED_QTY),2 )ENTERED_QTY,\r\nFORMAT(ABS(a.UNIT_PRICE),2 )UNIT_PRICE,\r\nFORMAT(ABS(a.DISCOUNT_PRICE),2 )DISCOUNT_PRICE,\r\nFORMAT(ABS(a.SUBTOTAL),2 )SUBTOTAL,\r\na.NOTE,\r\n i.ITEM_CODE\r\nFROM\r\nINVOICE_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.INVOICE_id=  :INVOICE_ID\r\norder by  a.INVOICE_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (9, 331, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
      (10, 416, 'SALES ORDER', '', '', 'FORM\\F_SLS_SO', '', '', 'SELECT \r\n@abc2:=0,\r\nNO, \r\nso.SO_ID,\r\nso.DOCUMENT_NO,\r\nDATE_FORMAT(so.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.ADDRESS1,\r\nps.SITE_NAME,\r\npt.PAYMENT_TERM_NAME,\r\nk.FIRST_NAME,\r\nso.DOCUMENT_REFF_NO,\r\nso.PPN_CODE,\r\nDATE_FORMAT(so.NEED_DATE,\'%d-%b-%Y\' ) as NEED_DATE,\r\nso.NOTE,\r\nFORMAT(so.PPN_AMOUNT,2)PPN_AMOUNT,\r\nFORMAT(so.TOTAL_DISCOUNT,2)TOTAL_DISCOUNT,\r\nFORMAT(so.TOTAL_NET,2)TOTAL_NET,\r\nFORMAT(so.TOTAL_AMOUNT,2)TOTAL_AMOUNT\r\nFROM ERP_NO_FORM, SO \r\nINNER JOIN PERSON p ON so.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON so.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nLEFT JOIN KARYAWAN k ON so.KARYAWAN_ID=k.KARYAWAN_ID\r\nINNER JOIN PAYMENT_TERM pt ON so.PAYMENT_TERM_ID=pt.PAYMENT_TERM_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM SO_DETAIL\r\nWHERE SO_ID = :SO_ID\r\nORDER BY SO_DETAIL_ID )\r\nAND SO_ID = :SO_ID', '\r\nSELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.*,@nomer:=0 from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\n(@nomer:= @nomer+ 1) AS NO,\r\na.ITEM_DESCRIPTION,\r\na.ENTERED_UOM,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\nFORMAT(a.UNIT_PRICE,2)UNIT_PRICE, \r\nFORMAT(a.DISCOUNT_PERCEN,2)DISCOUNT_PERCEN,\r\nFORMAT(a.DISCOUNT_PRICE,2)DISCOUNT_PRICE,\r\nFORMAT(a.SUBTOTAL,2)SUBTOTAL,\r\na.NOTE,\r\ni.ITEM_CODE\r\nFROM\r\nSO_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.SO_id=  :SO_ID\r\norder by  a.SO_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO\r\n', '', ''),
      (11, 417, 'SURAT JALAN', '', '', 'FORM\\F_INV_SJ', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\ninventory_out.DOCUMENT_NO,\r\nDATE_FORMAT(inventory_out.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.ADDRESS1,\r\nps.SITE_NAME,\r\nso.DOCUMENT_NO NO_PO,\r\ninventory_out.DOCUMENT_REFF_NO,\r\nw.WAREHOUSE_NAME,\r\ninventory_out.NOTE\r\nFROM ERP_NO_FORM, INVENTORY_OUT\r\nINNER JOIN PERSON p ON inventory_out.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON inventory_out.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nLEFT JOIN SO ON inventory_out.SO_ID=so.SO_ID\r\nINNER JOIN WAREHOUSE w ON inventory_out.WAREHOUSE_ID=w.WAREHOUSE_ID\r\nWHERE NO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM INVENTORY_OUT_DETAIL\r\nWHERE INVENTORY_OUT_ID = :INVENTORY_OUT_ID )\r\nAND INVENTORY_OUT_ID = :INVENTORY_OUT_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.ITEM_DESCRIPTION,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\na.NOTE,\r\na.ENTERED_UOM,\r\n i.ITEM_CODE\r\nFROM\r\nINVENTORY_OUT_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.INVENTORY_OUT_id=  :INVENTORY_OUT_ID\r\norder by  a.INVENTORY_OUT_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (15, 431, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
      (16, 519, 'BKM', '', '', 'FORM\\F_FIN_BKM', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\nreceipt.RECEIPT_ID,\r\nreceipt.DOCUMENT_NO,\r\nDATE_FORMAT(receipt.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\nreceipt.DOCUMENT_REFF_NO,\r\np.PERSON_NAME,\r\nreceipt.NOTE,\r\ncoa.COA_NAME BANK_NAME\r\nFROM ERP_NO_FORM,RECEIPT\r\nLEFT JOIN PERSON p ON receipt.PERSON_ID=p.PERSON_ID\r\nINNER JOIN COA on receipt.BANK_ID=coa.COA_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM RECEIPT_DETAIL\r\nWHERE RECEIPT_ID = :RECEIPT_ID )\r\nAND RECEIPT_ID = :RECEIPT_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* FROM\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 AS X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.RECEIPT_ID,\r\nCONCAT(DOCUMENT_NO,\' - \',ACCOUNT_NAME,IF(ACCOUNT_CODE IS NULL, \'\',CONCAT(\' - \',ACCOUNT_CODE)))AS URAI,\r\na.NOTE,\r\nFORMAT(a.AMOUNT,2) AMOUNT\r\nFROM(\r\nSELECT \r\n1 AS URUT,\r\na.RECEIPT_ID,\r\na.RECEIPT_DETAIL_ID,\r\nIFNULL(npa.DOCUMENT_NO,i.DOCUMENT_NO) DOCUMENT_NO,\r\nIF(p.FLAG_SUPP=0, v.ACCOUNT_NAME,va.ACCOUNT_NAME) AS ACCOUNT_NAME,\r\nIF(p.FLAG_SUPP=0, v.ACCOUNT_CODE,va.ACCOUNT_CODE) AS ACCOUNT_CODE,\r\na.NOTE,\r\na.AMOUNT\r\nFROM\r\nRECEIPT_DETAIL a\r\nINNER JOIN RECEIPT b ON a.RECEIPT_ID=b.RECEIPT_ID\r\nINNER JOIN PERSON p ON b.PERSON_ID=p.PERSON_ID\r\nLEFT JOIN V_ACCOUNT v ON (p.COA_PIUT_ID=v.COA_ID) AND p.FLAG_SUPP=0\r\nLEFT JOIN V_ACCOUNT va ON (p.COA_HUT_ID = va.COA_ID) AND p.FLAG_SUPP=1\r\nLEFT JOIN NOTA_PENJUALAN_AGING npa ON a.NOTA_PENJUALAN_AGING_ID=npa.NOTA_PENJUALAN_AGING_ID\r\nLEFT JOIN INVOICE_DUE i ON a.INVOICE_DUE_ID=i.INVOICE_DUE_ID\r\nWHERE a.RECEIPT_id=  :RECEIPT_ID AND a.COA_ID IS NULL\r\n\r\nUNION ALL\r\n\r\nSELECT \r\n2 AS URUT,\r\na.RECEIPT_ID,\r\na.RECEIPT_DETAIL_ID,\r\nc.COA_CODE AS DOCUMENT_NO,\r\nc.COA_NAME AS ACCOUNT_NAME,\r\nNULL AS ACCOUNT_CODE,\r\na.NOTE,\r\na.AMOUNT\r\nFROM\r\nRECEIPT_DETAIL a\r\nINNER JOIN V_ACCOUNT c ON a.COA_ID=c.COA_ID\r\nWHERE a.RECEIPT_id=  :RECEIPT_ID AND a.COA_ID IS NOT NULL\r\n)AS A\r\nORDER BY  a.URUT,a.RECEIPT_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (17, 520, 'BKK', '', '', 'FORM\\F_FIN_BKK', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\npayment.PAYMENT_ID,\r\npayment.DOCUMENT_NO,\r\nDATE_FORMAT(payment.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\npayment.DOCUMENT_REFF_NO,\r\np.PERSON_NAME,\r\npayment.NOTE,\r\ncoa.COA_NAME BANK_NAME\r\nFROM ERP_NO_FORM,PAYMENT\r\nLEFT JOIN PERSON p ON payment.PERSON_ID=p.PERSON_ID\r\nINNER JOIN COA on payment.BANK_ID=coa.COA_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM PAYMENT_DETAIL\r\nWHERE PAYMENT_ID = :PAYMENT_ID )\r\nAND PAYMENT_ID = :PAYMENT_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* FROM\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 AS X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.PAYMENT_ID,\r\nCONCAT(DOCUMENT_NO,\' - \',ACCOUNT_NAME,IF(ACCOUNT_CODE IS NULL, \'\',CONCAT(\' - \',ACCOUNT_CODE)))AS URAI,\r\na.NOTE,\r\nFORMAT(a.AMOUNT,2) AMOUNT\r\nFROM(\r\nSELECT \r\n1 AS URUT,\r\na.PAYMENT_ID,\r\na.PAYMENT_DETAIL_ID,\r\nIFNULL(npa.DOCUMENT_NO,i.DOCUMENT_NO) DOCUMENT_NO,\r\nIF(p.FLAG_SUPP=0, v.ACCOUNT_NAME,va.ACCOUNT_NAME) AS ACCOUNT_NAME,\r\nIF(p.FLAG_SUPP=0, v.ACCOUNT_CODE,va.ACCOUNT_CODE) AS ACCOUNT_CODE,\r\na.NOTE,\r\na.AMOUNT\r\nFROM\r\nPAYMENT_DETAIL a\r\nINNER JOIN PAYMENT b ON a.PAYMENT_ID=b.PAYMENT_ID\r\nINNER JOIN PERSON p ON b.PERSON_ID=p.PERSON_ID\r\nLEFT JOIN V_ACCOUNT v ON (p.COA_PIUT_ID=v.COA_ID) AND p.FLAG_SUPP=0\r\nLEFT JOIN V_ACCOUNT va ON (p.COA_HUT_ID = va.COA_ID) AND p.FLAG_SUPP=1\r\nLEFT JOIN NOTA_PENJUALAN_AGING npa ON a.NOTA_PENJUALAN_AGING_ID=npa.NOTA_PENJUALAN_AGING_ID\r\nLEFT JOIN INVOICE_DUE i ON a.INVOICE_DUE_ID=i.INVOICE_DUE_ID\r\nWHERE a.PAYMENT_id=  :PAYMENT_ID AND a.COA_ID IS NULL\r\n\r\nUNION ALL\r\n\r\nSELECT \r\n2 AS URUT,\r\na.PAYMENT_ID,\r\na.PAYMENT_DETAIL_ID,\r\nc.COA_CODE AS DOCUMENT_NO,\r\nc.COA_NAME AS ACCOUNT_NAME,\r\nNULL AS ACCOUNT_CODE,\r\na.NOTE,\r\na.AMOUNT\r\nFROM\r\nPAYMENT_DETAIL a\r\nINNER JOIN V_ACCOUNT c ON a.COA_ID=c.COA_ID\r\nWHERE a.PAYMENT_id=  :PAYMENT_ID AND a.COA_ID IS NOT NULL\r\n)AS A\r\nORDER BY  a.URUT,a.PAYMENT_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
      (18, 720, 'TRANSAKSI JURNAL', '', '', 'FORM\\F_GL_TRANJUR', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\njournal.JOURNAL_ID,\r\njournal.DOCUMENT_NO,\r\nDATE_FORMAT(journal.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\njournal.DOCUMENT_REFF_NO,\r\njournal.NOTE\r\nFROM ERP_NO_FORM,JOURNAL\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM JOURNAL_DETAIL\r\nWHERE JOURNAL_ID = :JOURNAL_ID )\r\nAND JOURNAL_ID = :JOURNAL_ID', '\r\nSELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.*,@nomer:=0 from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\n(@nomer:= @nomer+ 1) AS NO,\r\na.NOTE,\r\nFORMAT(a.AMOUNT,2)AMOUNT,\r\nFORMAT(a.AMOUNT_CR,2)AMOUNT_CR,\r\nc.COA_CODE,\r\nc.COA_NAME\r\nFROM\r\nJOURNAL_DETAIL a\r\nINNER JOIN COA c ON(a.COA_ID = c.COA_ID)\r\nWHERE a.JOURNAL_ID=  :JOURNAL_ID\r\norder by  a.JOURNAL_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO\r\n', '', ''),
      (19, 240, 'ADJUSMENT QTY', '', '', 'FORM\\F_INV_SADJ', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\nadjust_qty.ADJUST_QTY_ID,\r\nadjust_qty.DOCUMENT_NO,\r\nDATE_FORMAT(adjust_qty.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\nadjust_qty.DOCUMENT_REFF_NO,\r\nadjust_qty.NOTE,\r\nc.ACCOUNT_CODE,\r\nc.ACCOUNT_NAME\r\nFROM ERP_NO_FORM,ADJUST_QTY\r\nINNER JOIN V_ACCOUNT c ON adjust_qty.COA_ID=c.COA_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM ADJUST_QTY_DETAIL\r\nWHERE ADJUST_QTY_ID = :ADJUST_QTY_ID )\r\nAND ADJUST_QTY_ID = :ADJUST_QTY_ID', '\r\nSELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.*,@nomer:=0 from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\n(@nomer:= @nomer+ 1) AS NO,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\na.ENTERED_UOM,\r\ni.ITEM_CODE,\r\nr.DISPLAY_NAME,\r\ni.ITEM_DESCRIPTION\r\nFROM\r\nADJUST_QTY_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN ERP_LOOKUP_VALUE r ON a.REASON_ID=r.ERP_LOOKUP_VALUE_ID\r\nWHERE a.ADJUST_QTY_id=  :ADJUST_QTY_ID\r\norder by  a.ADJUST_QTY_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO\r\n', '', ''),
      (20, 418, 'NOTA PENJUALAN', '', '', 'FORM\\F_SLS_NOTACASH', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\nnota_penjualan.NOTA_PENJUALAN_ID,\r\nnota_penjualan.DOCUMENT_NO,\r\nDATE_FORMAT(nota_penjualan.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\np.PERSON_NAME,\r\nps.ADDRESS1,\r\nps.SITE_NAME,\r\nps.PHONE1,\r\nk.FIRST_NAME,\r\nDATE_FORMAT(nota_penjualan.JTEMPO,\'%d-%b-%Y\' ) as JTEMPO,\r\nnota_penjualan.DOCUMENT_REFF_NO,\r\nnota_penjualan.PPN_CODE,\r\nnota_penjualan.NOTE,\r\npt.PAYMENT_TERM_NAME,\r\nFORMAT(nota_penjualan.TOTAL_AMOUNT,2)TOTAL_AMOUNT,\r\nFORMAT(nota_penjualan.PPN_AMOUNT,2)PPN_AMOUNT,\r\nFORMAT(nota_penjualan.TOTAL_DISCOUNT,2)TOTAL_DISCOUNT,\r\nFORMAT(nota_penjualan.TOTAL_NET,2)TOTAL_NET,\r\nFORMAT(nota_penjualan.TOTAL_BYR1,2)TOTAL_BAYAR,\r\nFORMAT(nota_penjualan.TOTAL_BYR1-nota_penjualan.NET_BYR1,2)TOTAL_KEMBALIAN\r\nFROM ERP_NO_FORM,NOTA_PENJUALAN\r\nLEFT JOIN KARYAWAN k ON nota_penjualan.KARYAWAN_ID=k.KARYAWAN_ID\r\nINNER JOIN PERSON p ON nota_penjualan.PERSON_ID=p.PERSON_ID\r\nINNER JOIN PERSON_SITE ps ON nota_penjualan.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nINNER JOIN PAYMENT_TERM pt ON nota_penjualan.PAYMENT_TERM_ID=pt.PAYMENT_TERM_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM NOTA_PENJUALAN_DETAIL\r\nWHERE NOTA_PENJUALAN_DETAIL. NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID )\r\nAND NOTA_PENJUALAN.NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.*,@nomer:=0 from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\n(@nomer:= @nomer+ 1) AS NO,\r\na.ITEM_DESCRIPTION, \r\na.ENTERED_UOM, \r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\nFORMAT(a.UNIT_PRICE,2)UNIT_PRICE,\r\nFORMAT(a.DISCOUNT_PRICE,2)DISCOUNT_PRICE,\r\nFORMAT(a.SUBTOTAL,2)SUBTOTAL,\r\na.NOTE,\r\ni.ITEM_CODE\r\nFROM\r\nNOTA_PENJUALAN_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.NOTA_PENJUALAN_id=  :NOTA_PENJUALAN_ID  AND NOT (a.NOTA_PENJUALAN_PRE_ID IS not  NULL  AND  i.jenis_id = 3)\r\norder by  a.NOTA_PENJUALAN_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', 'SELECT p.*, ps.*\r\nFROM PERSON p\r\nINNER JOIN PERSON_SITE ps ON(p.PERSON_ID=ps.PERSON_ID)\r\nWHERE p.PERSON_ID=:PERSON_ID', ''),
