      (22, 245, 'ADJUSMENT NILAI', '', '', 'FORM\\F_INV_SADJN', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\nadjust_amount.ADJUST_AMOUNT_ID,\r\nadjust_amount.DOCUMENT_NO,\r\nDATE_FORMAT(adjust_amount.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\nadjust_amount.DOCUMENT_REFF_NO,\r\nadjust_amount.NOTE,\r\nc.ACCOUNT_CODE,\r\nc.ACCOUNT_NAME\r\nFROM ERP_NO_FORM,ADJUST_AMOUNT\r\nINNER JOIN V_ACCOUNT c ON adjust_amount.COA_ID=c.COA_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM ADJUST_AMOUNT_DETAIL\r\nWHERE ADJUST_AMOUNT_ID = :ADJUST_AMOUNT_ID )\r\nAND ADJUST_AMOUNT_ID = :ADJUST_AMOUNT_ID', '\r\nSELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.*,@nomer:=0 from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\n(@nomer:= @nomer+ 1) AS NO,\r\nFORMAT(a.ENTERED_AMOUNT,2)ENTERED_AMOUNT,\r\ni.ITEM_CODE,\r\na.NOTE,\r\ni.ITEM_DESCRIPTION\r\nFROM\r\nADJUST_AMOUNT_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.ADJUST_AMOUNT_ID=  :ADJUST_AMOUNT_ID\r\norder by  a.ADJUST_AMOUNT_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO\r\n', '', ''),
      (23, 260, 'PERAKITAN', '', '', 'FORM\\F_INV_PROD', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\nbuild.BUILD_ID,\r\nbuild.DOCUMENT_NO,\r\nDATE_FORMAT(build.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\nbuild.DOCUMENT_REFF_NO,\r\nbuild.NOTE,\r\nFORMAT(build.ENTERED_QTY,2)ENTERED_QTY,\r\nb.UOM_CODE,\r\ni.ITEM_CODE,\r\ni.ITEM_DESCRIPTION,\r\nw.WAREHOUSE_NAME\r\nFROM ERP_NO_FORM,BUILD\r\nINNER JOIN ITEM i ON build.ITEM_ID=i.ITEM_ID\r\nINNER JOIN BOM b ON build.ITEM_ID=b.ITEM_ID\r\nINNER JOIN WAREHOUSE w ON build.WAREHOUSE_ID=w.WAREHOUSE_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM BUILD_DETAIL\r\nWHERE BUILD_ID = :BUILD_ID )\r\nAND BUILD_ID = :BUILD_ID', '\r\nSELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.*,@nomer:=0 from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\n(@nomer:= @nomer+ 1) AS NO,\r\nFORMAT(a.STAND_QTY,2)STAND_QTY,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\na.ENTERED_UOM,\r\ni.ITEM_CODE,\r\ni.ITEM_DESCRIPTION\r\nFROM\r\nBUILD_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.BUILD_ID=  :BUILD_ID\r\norder by  a.BUILD_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO\r\n', '', ''),
      (24, 1010, 'STOK OPNAME', '', '', 'FORM\\F_INV_Opname', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\nadjust_qty.ADJUST_QTY_ID,\r\nadjust_qty.DOCUMENT_NO,\r\nDATE_FORMAT(adjust_qty.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\nadjust_qty.DOCUMENT_REFF_NO,\r\nadjust_qty.NOTE,\r\nw.WAREHOUSE_NAME\r\nFROM ERP_NO_FORM,ADJUST_QTY\r\nINNER JOIN WAREHOUSE w ON adjust_qty.WAREHOUSE_ID=w.WAREHOUSE_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM ADJUST_QTY_DETAIL\r\nWHERE ADJUST_QTY_ID = :ADJUST_QTY_ID )\r\nAND ADJUST_QTY_ID = :ADJUST_QTY_ID', '\r\nSELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.*,@nomer:=0 from\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 as X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\n(@nomer:= @nomer+ 1) AS NO,\r\nFORMAT(a.ENTERED_QTY,2)ENTERED_QTY,\r\nFORMAT(a.SALDO_AKHIR,2)SALDO_AKHIR,\r\nFORMAT(a.SALDO_AKHIR-a.ENTERED_QTY,2)STOK_SISTEM,\r\na.ENTERED_UOM,\r\na.NOTE,\r\ni.ITEM_CODE,\r\ni.ITEM_DESCRIPTION\r\nFROM\r\nADJUST_QTY_DETAIL a\r\nINNER JOIN ITEM i ON(a.ITEM_ID = i.ITEM_ID)\r\nWHERE a.ADJUST_QTY_id=  :ADJUST_QTY_ID\r\norder by  a.ADJUST_QTY_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO\r\n', '', '');";

