      $insert_erp_rpt_sql = "INSERT INTO `erp_rpt_sql` (`STR_SQL`, `ERP_RPT_SQL_ID`, `ERP_RPT_ID`, `NO_URUT`, `NOTE`, `CREATED_BY`, `CREATED_DATE`, `LAST_UPDATE_DATE`, `LAST_UPDATE_BY`, `MASTER_SOURCE`, `LABEL_SOURCE`, `KONDISI`, `PARAM_YG_DIAMBIL`, `PARENT_SQL`, `COUNT_COLUMN`) VALUES
      ('SELECT \r\n  a.*, \r\n  w.WAREHOUSE_NAME,\r\n  v.DISPLAY_NAME,\r\n  sum(ad.entered_qty) as total_entered_qty\r\nFROM\r\n  adjust_qty a\r\n  inner join adjust_qty_detail ad on a.adjust_qty_id=ad.adjust_qty_id\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN erp_lookup_value v ON (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\n INNER JOIN ITEM i ON (ad.item_id = i.item_id)\r\nWHERE\r\nTRUE\r\n&KONDISI\r\ngroup by a.adjust_qty_id\r\nORDER BY\r\n  a.DOCUMENT_NO\r\n', 1, 100, 0, NULL, NULL, NULL, '2008-05-12 15:59:00', 0, NULL, NULL, NULL, NULL, 77, 1),
      ('SELECT \r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.TOTAL_NET,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  cs.PERSON_SITE_CODE,\r\n  s.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME\r\nFROM\r\n  nota_penjualan a\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n&Kondisi\r\nORDER BY   a.DOCUMENT_NO \r\n', 3, NULL, 1, NULL, 4, NULL, NULL, 4, NULL, NULL, '', NULL, 77, 1),
      ('SELECT \r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,  \r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  cs.PERSON_SITE_CODE,\r\n  s.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME,\r\n  SUM(b.SUBTOTAL) TOTAL_NET\r\nFROM\r\n  nota_penjualan a\r\n  INNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n&Kondisi\r\nGROUP BY \r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  cs.PERSON_SITE_CODE,\r\n  s.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME\r\nORDER BY   a.DOCUMENT_NO \r\n', 4, NULL, 2, NULL, 4, NULL, NULL, 4, NULL, NULL, '', NULL, 77, 1),
      ('SELECT \r\n  v.DISPLAY_NAME AS JENIS,\r\n  v.ERP_LOOKUP_VALUE_ID\r\nFROM\r\n  erp_lookup_set vs\r\n  INNER JOIN erp_lookup_value v ON (vs.ERP_LOOKUP_SET_ID = v.ERP_LOOKUP_SET_ID)\r\nWHERE\r\n  vs.PROGRAM_CODE = \'JENIS\'  and (  v.active_flag = \'Y\' )\r\n&Kondisi\r\nORDER BY v.DISPLAY_NAME\r\n', 28, 670, 9, NULL, 4, NULL, '2007-10-29 21:27:25', 0, NULL, NULL, NULL, NULL, 77, 1),
      ('SELECT \r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  cs.SITE_NAME,\r\n  s.DISPLAY_NAME STATUS_DOC,\r\n  w.WAREHOUSE_NAME,\r\n  i.JENIS_ID,\r\n  SUM(b.SUBTOTAL - b.DISCOUNT_PRICE1) TOTAL_NET  \r\nFROM\r\n  nota_penjualan a\r\n  INNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID) \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nWHERE\r\n   i.JENIS_ID = :ERP_LOOKUP_VALUE_ID\r\n&KONDISI\r\nGROUP BY\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  SITE_NAME, \r\n  s.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME,\r\n  i.JENIS_ID\r\nORDER BY\r\n  a.DOCUMENT_NO\r\n', 29, 670, 0, NULL, 4, NULL, '2008-03-10 10:36:52', 0, NULL, NULL, NULL, NULL, 9, 1),
      ('SELECT \r\n  a.TAG_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.TO_WH_ID,\r\n  a.PERIOD_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.CONFIRMED_FLAG,\r\n  a.PRINT_COUNT,\r\n  a.NOTE,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  w.WAREHOUSE_NAME,\r\n  w2.WAREHOUSE_NAME,\r\n  a.STATUS_ID,\r\n  s.DISPLAY_NAME AS STATUS_DOC\r\nFROM\r\n  tag a\r\n  INNER JOIN tag_detail b\r\n ON (a.TAG_ID =b.TAG_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN warehouse w2 ON (a.TO_WH_ID = w2.WAREHOUSE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n&Kondisi\r\nGROUP BY\r\n  a.TAG_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.TO_WH_ID,\r\n  a.PERIOD_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.CONFIRMED_FLAG,\r\n  a.PRINT_COUNT,\r\n  a.NOTE,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  w.WAREHOUSE_NAME,\r\n  w2.WAREHOUSE_NAME,\r\n  a.STATUS_ID,\r\n  s.DISPLAY_NAME \r\nORDER BY\r\n  a.DOCUMENT_NO\r\n', 38, 140, 0, NULL, 4, NULL, '2008-05-09 17:13:01', 0, NULL, NULL, NULL, NULL, 77, 1),
      ('SELECT \r\n  i.ITEM_ID,\r\n  i.ITEM_DESCRIPTION, \r\n  i.ITEM_CODE \r\nFROM\r\n  tag a\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN tag_detail b ON (a.TAG_ID = b.TAG_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n WHERE TRUE\r\n&Kondisi\r\nGROUP BY \r\ni.ITEM_CODE,\r\n  i.ITEM_ID,\r\n    i.ITEM_DESCRIPTION \r\n     ', 41, 1281, 0, NULL, 4, NULL, '2008-05-12 11:32:21', 0, NULL, NULL, NULL, NULL, 77, 1),
      ('SELECT \r\n  a.TAG_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.TO_WH_ID,\r\n  a.PERIOD_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.CONFIRMED_FLAG,\r\n  a.PRINT_COUNT,\r\n  a.NOTE,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  w.WAREHOUSE_NAME,\r\n  w2.WAREHOUSE_NAME,\r\n  a.STATUS_ID,\r\n  b.ENTERED_QTY,\r\n  b.ENTERED_UOM,\r\n  b.NOTE,\r\n  s.DISPLAY_NAME AS STATUS_DOC\r\nFROM\r\n  tag a\r\n  INNER JOIN tag_detail b  ON (a.TAG_ID =b.TAG_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN warehouse w2 ON (a.TO_WH_ID = w2.WAREHOUSE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\nWHERE b.ITEM_ID = :ITEM_ID\r\n&Kondisi\r\nGROUP BY\r\n  a.TAG_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.TO_WH_ID,\r\n  a.PERIOD_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.CONFIRMED_FLAG,\r\n  a.PRINT_COUNT,\r\n  a.NOTE,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  w.WAREHOUSE_NAME,\r\n  w2.WAREHOUSE_NAME,\r\n  a.STATUS_ID,\r\n  s.DISPLAY_NAME ,\r\n  b.ENTERED_QTY,\r\n  b.ENTERED_UOM,\r\n  b.NOTE\r\nORDER BY\r\n  a.DOCUMENT_DATE\r\n', 42, 1281, 3, NULL, 4, NULL, '2008-05-12 11:30:37', 0, NULL, NULL, NULL, NULL, 0, 1),
      ('SELECT \r\n  a.*, \r\n  bank.COA_NAME BANK_NAME,\r\n  s.DISPLAY_NAME STATUS_DOC\r\nFROM\r\n  RECEIPT a\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA bank ON (a.BANK_ID = bank.COA_ID)\r\nWHERE  A.PERSON_ID IS NULL \r\n&Kondisi\r\nORDER BY\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_NO', 51, 800, 0, NULL, 4, NULL, '2007-09-14 17:40:41', 1, NULL, NULL, NULL, NULL, 77, 1),
      ('SELECT \r\n  a.*, \r\n  bank.COA_NAME BANK_NAME,\r\n  s.DISPLAY_NAME STATUS_DOC\r\nFROM\r\n  RECEIPT a\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA bank ON (a.BANK_ID = bank.COA_ID)\r\nWHERE  A.PERSON_ID IS NULL\r\n&Kondisi\r\nORDER BY\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_NO', 54, 810, 0, NULL, 4, NULL, '2007-09-14 18:28:34', 1, NULL, NULL, NULL, NULL, 77, 1),
