      ('  SELECT\r\nSUM(LABA) AS LABA,\r\nSUM(LABA_LALU) AS LABA_LALU\r\nFROM\r\n(\r\n   SELECT  sum(COA_CREDIT- COA_DEBET)   as LABA,\r\n  0 AS LABA_LALU\r\n   FROM\r\n     COA_BALANCE a \r\n     INNER JOIN v_account c ON (a.COA_ID = c.COA_ID) \r\n   WHERE  left(c.ACCOUNT_CODE,1) > \'3\'\r\n   &kondisi\r\nUNION ALL\r\n   SELECT  \r\n0 AS TOTAL  ,\r\nsum(COA_CREDIT- COA_DEBET)   as LABA_LALU\r\n   FROM\r\n     COA_BALANCE a \r\n     INNER JOIN v_account c ON (a.COA_ID = c.COA_ID) \r\n   WHERE  left(c.ACCOUNT_CODE,1) > \'3\'\r\nAND PERIOD_NAME = :PERIOD_LALU\r\n) AS XX \r\n/*  \r\n   SELECT  sum(amount - amount_cr) * -1  as total\r\n   FROM\r\n\r\n     account acc \r\n     INNER JOIN coa c ON (acc.ACCOUNT_ID = c.ACCOUNT_ID) \r\n     INNER JOIN journal_detail b ON (c.COA_ID = b.COA_ID) \r\n     INNER JOIN journal a ON (b.JOURNAL_ID = a.JOURNAL_ID) \r\n   WHERE \r\n     (acc.STATUS_FLAG = \'R\') \r\n   &kondisi\r\n*/', 1752, 7001, 11, NULL, 4, '2006-06-29 19:12:27', '2006-09-15 16:46:14', 4, NULL, NULL, NULL, ':PERIOD_LALU', 77, NULL),
      ('SELECT ACCOUNT_CODE \r\nFROM \r\n    coa_setup cs\r\n     inner join Account x on (cs.ACCOUNT_ID = x.ACCOUNT_ID) \r\nWHERE \r\n      (cs.PROGRAM_ACCOUNT = \'LABA_BLN_INI\')\r\n   &kondisi\r\n', 1753, 7001, 5, NULL, 4, '2006-07-01 14:41:29', '2006-09-14 12:29:10', 4, NULL, NULL, NULL, '', 77, NULL),
      ('SELECT * FROM \r\n(\r\nSELECT\r\n   ITEM_DESCRIPTION, ITEM_CODE,\r\n   UOM_CODE AS SATUAN,\r\n   SUM(SALDO) AS SALDO,\r\n   SUM(SALDO_RP) AS SALDO_RP,\r\n   SUM(MASUK) AS MASUK,\r\n   SUM(MASUK_RP) AS MASUK_RP,\r\n   SUM(KELUAR) AS KELUAR,\r\n   SUM(KELUAR_RP) AS KELUAR_RP,\r\n   SUM(BAHAN) AS BAHAN,\r\n   SUM(BAHAN_RP) AS BAHAN_RP,\r\n   SUM(JADI) AS JADI, \r\n   SUM(JADI_RP) AS JADI_RP,\r\n     SUM(ADJ) AS ADJ, \r\n   SUM(ADJ_RP) AS ADJ_RP\r\n FROM \r\n     ( SQL1)\r\nAS XX\r\n   INNER JOIN ITEM i ON (XX.ITEM_ID = i.ITEM_ID)\r\nWHERE\r\n   \r\nTRUE\r\n  &KONDISI\r\nGROUP BY\r\n  XX.ITEM_ID\r\nORDER BY ITEM_CODE\r\n) AS YY WHERE \r\n(ABS(SALDO) + ABS(KELUAR)+    ABS(BAHAN)+  ABS(JADI)+  ABS(ADJ)+  ABS( MASUK ) > 0 )\r\nOR \r\n(ABS(SALDO_RP)+ABS(MASUK_RP)+ABS(KELUAR_RP)+  ABS( BAHAN_RP)+ABS(JADI_RP)+  ABS(ADJ_RP) > 0 )\r\n\r\n', 1766, 2003, 0, '71', 4, NULL, '2007-09-11 21:19:54', 2, 71, 'SQL1', '&Y1= and (A.period_name = :PERIOD_NAME1)', ':PERIOD_NAME1', 77, 8),
      ('Select  \r\ne.display_name,\r\ndocument_date, \r\ndocument_no, \r\nitem_description, \r\nentered_qty,\r\ni.uom_code, b.*\r\nfrom build b\r\n  inner join item i on (b.item_id = i.item_id)\r\n  inner join erp_lookup_value e on (b.status_id = e.erp_lookup_value_id)\r\nwhere (b.status_id  <> @delete)\r\n&kondisi\r\norder by i.item_description ,  document_date', 1768, 2004, 0, NULL, 4, NULL, '2007-05-28 21:18:08', 4, NULL, NULL, NULL, NULL, 77, NULL),
      ('Select \r\n  max(x.build_id),\r\n  max(x.document_no), \r\n  a.item_id,\r\n  a.item_code, \r\n  a.item_description, \r\n  a.uom_code,\r\n  if (sum(x.entered_qty*x.base_qty) is null,0,sum(x.entered_qty*x.base_qty)) as jum\r\nfrom \r\n  item a \r\n  left join (SELECT * from build b where true &T1 &T2) as x on (a.item_id=x.item_id)\r\nwhere \r\n true\r\n  &kondisi \r\ngroup by \r\n  a.item_id, \r\n  a.item_description,\r\n  a.uom_code\r\norder by \r\n  a.item_description', 1772, 2009, 3, NULL, 120, NULL, '2007-05-28 21:18:02', 4, NULL, NULL, '&T1= AND (DATE_FORMAT(b.DOCUMENT_DATE,\'%Y-%m-%d\' ) = :11) and (b.Period_Name = :PERIOD_NAME1)\r\n&T2= AND (B.warehouse_id=:12)', ':11|:PERIOD_NAME1|:12', 77, NULL),
      ('SELECT \r\n  gab.item_id,\r\n  gab.item_description,\r\n  sum(jum_bul) as jum_bul,\r\n 0 as angg,\r\n  sum(saldo) as saldo\r\n from\r\n(\r\nSelect \r\n  a.item_id,\r\n  a.item_description, \r\n  if(sum(x.entered_qty*x.base_qty) is null,0,sum(x.entered_qty*x.base_qty)) as jum_bul,\r\n  0 as angg,\r\n  0 as saldo\r\nfrom \r\n  item a\r\n  left join (SELECT * from build b where true &B1 &B2) as x on (a.item_id=x.item_id)\r\nwhere \r\n \r\n  a.item_id=:item_id\r\n  &kondisi \r\ngroup by \r\n  a.item_id, \r\n  a.item_description\r\n\r\n\r\nunion all\r\n\r\nSelect\r\n  a.item_id,\r\n  a.item_description,\r\n  0 as jum_bul,\r\n  0 as angg,\r\n  x.qty_awal as saldo\r\nfrom\r\n  item a\r\n  left join (SELECT * from item_balance ib where true &S1 &S2) as x on (a.item_id=x.item_id)\r\nwhere\r\n  a.item_id=:item_id\r\n  &kondisi\r\ngroup by\r\n  a.item_id,\r\n  a.item_description\r\n) as gab\r\ngroup by\r\n  gab.item_id\r\norder by\r\n  gab.item_description', 1773, 2009, 13, NULL, 120, NULL, '2007-05-28 21:09:37', 4, NULL, NULL, '&B1= AND (DATE_FORMAT(b.DOCUMENT_DATE,\'%Y-%m-%d\' ) <= :11) and (b.Period_Name = :PERIOD_NAME1)\r\n&B2= AND (B.warehouse_id=:12)\r\n&S1= and (ib.Period_Name = :PERIOD_NAME1)\r\n&S2= AND (iB.warehouse_id=:12)', ':11|:PERIOD_NAME1|:12', 3, NULL),
      ('SELECT @No := @No + 1 as No,\r\n  ITEM_ID, \r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE, \r\n  ENTERED_UOM, \r\n SUM(HARIAN) AS HARIAN ,\r\nSUM(BULAN) AS BULAN\r\nFROM\r\n(\r\nSELECT a.ITEM_ID,\r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE, \r\n  ENTERED_UOM, \r\n sum(entered_qty) HARIAN  , \r\n  0 AS BULAN\r\nfrom \r\n build a \r\n inner join item i using (item_id)\r\nwhere   status_id <> @delete\r\nand DOCUMENT_DATE BETWEEN :11 AND :21\r\n&kondisi\r\nGROUP BY a.ITEM_ID,\r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE,\r\n  ENTERED_UOM\r\n\r\nUNION ALL\r\nSELECT a.ITEM_ID, \r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE, \r\n  ENTERED_UOM, \r\n0, sum(entered_qty)  AS BULAN\r\nfrom \r\n build a \r\n inner join item i using (item_id)\r\nwhere  \r\n \r\n  Period_name = :Period_name1\r\nand (status_id  <> @delete)\r\n&Kondisi\r\nGROUP BY a.ITEM_ID,\r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE,\r\n  ENTERED_UOM\r\n) AS XX\r\nGROUP BY ITEM_CODE,  ITEM_ID, ITEM_DESCRIPTION ', 1779, 2011, 9, NULL, 4, NULL, '2007-05-28 21:18:23', 4, NULL, '', NULL, ':Period_name1|:11|:21', 77, 1),
      ('/* material bahan baku */\r\nSELECT i.item_description, i.item_code, group_id, i.item_id , i.UOM_CODE,\r\n  SUM(BHN_HARIAN) AS BHN_HARIAN,\r\n  SUM(BHN_BULANAN) AS BHN_BULANAN\r\nFROM\r\n(  \r\nSELECT b.item_id ,\r\n  SUM(b.ENTERED_QTY) AS BHN_HARIAN ,\r\n  0 AS BHN_BULANAN\r\nfrom \r\n build a \r\n inner join build_detail b using (build_id)\r\nwhere a.item_id = :item_id   \r\n&kondisi\r\ngroup by  b.item_id \r\nUNION ALL\r\nSELECT b.item_id , 0 ,\r\n   SUM(b.ENTERED_QTY)  AS BHN_BULANAN\r\nfrom \r\n build a \r\n inner join build_detail b using (build_id)\r\nwhere a.item_id = :item_id   \r\nand Period_name = :Period_name1\r\ngroup by  b.item_id \r\n) AS XX \r\n inner join item i on (XX.item_id = i.item_id)\r\ngroup by  i.item_description, i.item_code, group_id, i.item_id , i.UOM_CODE\r\n', 1780, 2011, 0, NULL, 4, NULL, '2007-05-28 20:38:18', 4, NULL, NULL, NULL, ':Period_name1', 9, NULL),
      ('Select \r\n  a.item_id, \r\n  a.item_code,\r\n  a.item_description, \r\n  a.uom_code,\r\n jum_bln,\r\n jum_thn\r\n  \r\nfrom \r\n  item a\r\n/* REALISASI BULANAN*/\r\n  INNER join \r\n(\r\nSelect item_id, \r\nsum(a.entered_qty*a.base_qty) as jum_bln  \r\n from build a where true \r\n&Kondisi\r\ngroup by item_id\r\n) b on (a.item_id = b.item_id)\r\n/* REALISASI TAHUNAN */\r\nLEFT JOIN \r\n(\r\nSelect x.item_id, \r\nsum(x.entered_qty*x.base_qty) as jum_thn\r\n from build x\r\n \r\n where\r\nPeriod_name < :PERIOD_NAME1\r\nAND LEFT(PERIOD_NAME,4) = :YEAR1\r\ngroup by x.item_id\r\n) c on (a.item_id = c.item_id)\r\n\r\nwhere \r\n \r\n true\r\ngroup by \r\n  a.item_description,\r\n  a.item_id, \r\n  a.uom_code\r\n', 1784, 2002, 3, NULL, 4, NULL, '2007-05-28 21:18:18', 4, NULL, NULL, NULL, ':PERIOD_NAME1|:YEAR1', 0, 1),
      ('Select \r\n@no := @no + 1 as nmr,\r\nio.DOCUMENT_NO,\r\n \r\nPERSON_NAME,\r\nADDRESS1,\r\niod.ITEM_DESCRIPTION,\r\nio.NOTE,\r\niod.ENTERED_QTY,\r\n(iod.ENTERED_QTY/iod.BASE_QTY) AS QTY2,\r\niod.ENTERED_UOM\r\nfrom inventory_out io\r\ninner join inventory_out_detail iod on(io.INVENTORY_OUT_ID = iod.INVENTORY_OUT_ID)\r\ninner join PERSON c on(io.PERSON_ID = c.PERSON_ID)\r\ninner join PERSON_site cs on(io.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\ninner join item i on(iod.ITEM_ID = i.ITEM_ID)\r\nwhere (io.STATUS_ID <> @DELETE)\r\n&KONDISI\r\norder by io.INVENTORY_OUT_ID\r\n', 1789, 2015, 0, NULL, 262, NULL, '2007-07-17 09:55:38', 9, NULL, NULL, NULL, NULL, 77, NULL),
