      ('SELECT \r\n  ACCOUNT_CODE,\r\n  ACCOUNT_NAME,\r\n  ACCOUNT_ID,\r\n  PARENT_FLAG,\r\n  ACTIVE_FLAG,\r\n  SUM(TOTAL)  * -1 AS TOTAL  ,\r\n  SUM(TOTAL_LALU) * -1 AS TOTAL_LALU\r\n\r\nFROM\r\n(\r\n/* INDUK SAJA */\r\nSELECT \r\n  A.ACCOUNT_CODE,\r\n  A.ACCOUNT_NAME,\r\n   A.ACCOUNT_ID,\r\n  A.PARENT_FLAG,\r\n  A.ACTIVE_FLAG,\r\n  SUM(TOTAL) AS TOTAL ,\r\n  SUM(TOTAL_LALU) AS TOTAL_LALU\r\nFROM\r\n  ACCOUNT A\r\n  INNER JOIN ACCOUNT B ON (A.ACCOUNT_ID = B.PARENT_ID )\r\n  INNER JOIN COA O ON (B.ACCOUNT_ID = O.ACCOUNT_ID)\r\n  LEFT JOIN\r\n( \r\nSELECT COA_ID , SUM(C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT ) AS TOTAL\r\nFROM COA_BALANCE C   WHERE (C.PERIOD_NAME = :PERIOD_NAME1)\r\nGROUP BY COA_ID \r\n) AS X1 ON (O.COA_ID = X1.COA_ID)\r\n  LEFT JOIN\r\n( \r\nSELECT COA_ID , SUM(C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT ) AS TOTAL_LALU\r\nFROM COA_BALANCE C   WHERE \r\n  (C.PERIOD_NAME = :PERIOD_LALU)\r\nGROUP BY COA_ID \r\n) AS X2 ON (O.COA_ID = X2.COA_ID)\r\n\r\nWHERE\r\n (A.ACCOUNT_TYPE_ID = :ERP_LOOKUP_VALUE_ID  )\r\nAND A.PARENT_FLAG = \'Y\' \r\n &KONDISI\r\n \r\nGROUP BY   A.ACCOUNT_CODE,\r\n  A.ACCOUNT_NAME,\r\n   A.ACCOUNT_ID\r\nHAVING (TOTAL <> 0 ) OR (TOTAL_LALU <> 0)\r\n\r\nUNION ALL \r\n/* ANAK PALING BUNCIT */ \r\n\r\nSELECT \r\n A.ACCOUNT_CODE ,\r\n  CONCAT(\'    - \' ,A.ACCOUNT_NAME) ACCOUNT_NAME,\r\n   A.ACCOUNT_ID,\r\n  A.PARENT_FLAG,\r\n  A.ACTIVE_FLAG,\r\n  SUM(TOTAL) AS TOTAL ,\r\n  SUM(TOTAL_LALU) AS TOTAL_LALU\r\nFROM\r\n  ACCOUNT A\r\n  LEFT JOIN ACCOUNT I ON (A.PARENT_ID =  I.ACCOUNT_ID)\r\n  INNER JOIN COA O ON (A.ACCOUNT_ID = O.ACCOUNT_ID)\r\n  LEFT JOIN\r\n( \r\nSELECT COA_ID , SUM(C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT ) AS TOTAL\r\nFROM COA_BALANCE C   WHERE (C.PERIOD_NAME = :PERIOD_NAME1)\r\nGROUP BY COA_ID \r\n)\r\n AS X1 ON (O.COA_ID = X1.COA_ID)\r\n  LEFT JOIN\r\n( \r\nSELECT COA_ID , SUM(C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT ) AS TOTAL_LALU\r\nFROM COA_BALANCE C   WHERE \r\n  (C.PERIOD_NAME = :PERIOD_LALU)\r\nGROUP BY COA_ID \r\n) AS X2 ON (O.COA_ID = X2.COA_ID)\r\nWHERE\r\n  (A.ACCOUNT_TYPE_ID = :ERP_LOOKUP_VALUE_ID) \r\nOR (A.ACCOUNT_TYPE_ID IN (@ACC_KASBANK,@ACC_GIRO) AND  (:ERP_LOOKUP_VALUE_ID = @P1) AND (I.ACCOUNT_TYPE_ID = @P1) )\r\nAND  A.PARENT_FLAG <> \'Y\' \r\n &KONDISI\r\n\r\n\r\n \r\nGROUP BY   A.ACCOUNT_CODE,\r\n  A.ACCOUNT_NAME,\r\n   A.ACCOUNT_ID\r\nHAVING (TOTAL <> 0 ) OR (TOTAL_LALU <> 0)\r\n\r\nUNION ALL \r\nSELECT\r\n  A.ACCOUNT_CODE,\r\n  A.ACCOUNT_NAME,\r\n   A.ACCOUNT_ID,\r\n   \'R\' AS PARENT_FLAG,\r\n  A.ACTIVE_FLAG,\r\n  0 , 0 \r\n   FROM \r\n       \r\n       coa_setup cs\r\n        inner join Account x on (cs.ACCOUNT_ID = x.ACCOUNT_ID) \r\n        inner join Account A on (x.PARENT_ID = A.Account_id)\r\n   WHERE \r\n         (cs.PROGRAM_ACCOUNT = \'LABA_BLN_INI\')\r\n  AND   :ERP_LOOKUP_VALUE_ID  = @P7\r\nUNION ALL\r\nSELECT\r\n A.ACCOUNT_CODE ,\r\n  CONCAT(\'    - \' ,A.ACCOUNT_NAME) ACCOUNT_NAME,\r\n   A.ACCOUNT_ID,\r\n   \'N\' AS PARENT_FLAG,\r\n  A.ACTIVE_FLAG,\r\n  0 , 0 \r\n   FROM \r\n       \r\n       coa_setup cs\r\n        inner join Account A on  (A.ACCOUNT_ID = cs.ACCOUNT_ID) \r\n   WHERE \r\n         (cs.PROGRAM_ACCOUNT = \'LABA_BLN_INI\')\r\n  AND   :ERP_LOOKUP_VALUE_ID  = @P7\r\n) \r\n AS XX\r\nGROUP BY   ACCOUNT_CODE,\r\n  ACCOUNT_NAME,\r\n  ACCOUNT_ID\r\nORDER BY   ACCOUNT_CODE,\r\n  ACCOUNT_NAME,\r\n  ACCOUNT_ID\r\n\r\n', 2019, 4106, 7, 'HEADER PASIVA', 0, '2007-11-22 12:42:04', '2007-12-12 13:07:30', 0, NULL, NULL, NULL, ':PERIOD_LALU|:PERIOD_NAME1', 6, NULL),
