D = :ERP_LOOKUP_VALUE_ID) \r\nOR (A.ACCOUNT_TYPE_ID IN (@ACC_KASBANK,@ACC_GIRO) AND  (:ERP_LOOKUP_VALUE_ID = @A1) AND (I.ACCOUNT_TYPE_ID = @A1) )\r\nAND  A.PARENT_FLAG <> \'Y\' \r\n &KONDISI\r\n \r\nGROUP BY   A.ACCOUNT_CODE,\r\n  A.ACCOUNT_NAME,\r\n   A.ACCOUNT_ID\r\nHAVING (TOTAL <> 0 ) OR (TOTAL_LALU <> 0)\r\n) AS XX\r\nGROUP BY   ACCOUNT_CODE,\r\n  ACCOUNT_NAME,\r\n  ACCOUNT_ID\r\nHAVING (TOTAL <> 0 ) OR (TOTAL_LALU <> 0)', 2025, 4107, 2, 'RINCIAN ACCOUNT', 0, '2007-11-21 22:23:33', '2007-12-12 13:10:14', 0, NULL, NULL, NULL, ':PERIOD_LALU|:PERIOD_NAME1', 1, NULL),
