      ('  SELECT\r\nSUM(LABA) AS LABA,\r\nSUM(LABA_LALU) AS LABA_LALU\r\nFROM\r\n(\r\n   SELECT  sum(COA_CREDIT- COA_DEBET)   as LABA,\r\n  0 AS LABA_LALU\r\n   FROM\r\n     ACCOUNT v\r\n     INNER JOIN COA c ON (v.ACCOUNT_ID = c.ACCOUNT_ID) \r\n     INNER JOIN COA_BALANCE a ON (c.COA_ID = a.COA_ID) \r\n   WHERE  ACCOUNT_TYPE_ID IN ( @R1,@R2,@R3,@R4,@R5,@R6,@R7,@R8,@R9)\r\nAND (PERIOD_NAME = :PERIOD_NAME1)\r\n&KONDISI\r\nUNION ALL\r\n   SELECT  \r\n0 AS TOTAL  ,\r\nsum(COA_CREDIT- COA_DEBET)   as LABA_LALU\r\n   FROM\r\n     ACCOUNT v\r\n     INNER JOIN COA c ON (v.ACCOUNT_ID = c.ACCOUNT_ID) \r\n     INNER JOIN   COA_BALANCE a ON (c.COA_ID = a.COA_ID) \r\n   WHERE  ACCOUNT_TYPE_ID IN ( @R1,@R2,@R3,@R4,@R5,@R6,@R7,@R8,@R9)\r\n\r\nAND (PERIOD_NAME = :PERIOD_LALU)\r\n&KONDISI\r\n) AS XX \r\n', 2036, 4108, 0, 'LABA RUGI', 0, '2007-11-22 14:14:20', '2007-11-22 15:16:04', 0, NULL, NULL, NULL, ':PERIOD_LALU|:PERIOD_NAME1', 77, NULL),
