      ('SELECT \r\n q.DOCUMENT_NO as CHECK_NO,  a.DOCUMENT_NO,\r\n q.CREATED_date as DOCUMENT_DATE,   \r\nq.document_date AS TGL_CAIR,   \r\nq.Jtempo,\r\nq.TOTAL_AMOUNT, \r\n  a.NAMA_BANK,\r\n IF( (q.DOCUMENT_DATE BETWEEN :13 AND :23) AND (q.STATUS_ID in (@CLEAR,@TOLAK)) , q.TOTAL_AMOUNT, 0)  \r\n - IF( (q.TGL_TOLAK  BETWEEN :13 AND :23) AND (q.STATUS_ID = @TOLAK) , q.TOTAL_AMOUNT, 0)  + \r\n+  IFNULL(GANTI_TOLAK.DEBET,0)\r\nDEBET,   \r\n  k.COA_NAME ,\r\n  c.PERSON_NAME,\r\n  X.DISPLAY_NAME AS STATUS\r\nFROM\r\n  CHEQUE Q \r\n  LEFT JOIN PAYMENT a ON (Q.CHEQUE_ID = a.CHEQUE_ID)\r\n  INNER JOIN ERP_LOOKUP_VALUE X on (Q.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)  \r\n  LEFT JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n    LEFT JOIN coa k ON (q.BANK_ID_DEST = k.COA_ID)\r\n  LEFT JOIN \r\n(\r\nSELECT  Y.CHEQUE_ID , SUM(AMOUNT)  AS DEBET FROM \r\n  PAYMENT X\r\n  INNER JOIN PAYMENT_detail Y ON (X.PAYMENT_ID = Y.PAYMENT_ID)\r\n  WHERE (X.DOCUMENT_DATE BETWEEN :13 AND :23)\r\nGROUP BY Y.CHEQUE_ID \r\n) AS GANTI_TOLAK ON ( a.CHEQUE_ID = GANTI_TOLAK.CHEQUE_ID)\r\n\r\nWHERE (q.status_id <> @DELETE)\r\nAND (STATUS_FLAG = \'K\')\r\nAND q.CREATED_DATE BETWEEN :13 AND :23\r\n&KONDISI\r\nORDER BY Q.DOCUMENT_DATE\r\n\r\n', 2468, 460, 0, NULL, 4, '2009-12-12 14:15:25', '2009-12-10 18:05:01', 0, NULL, NULL, NULL, ':13|:23', 77, 1),
      ('\r\nSELECT \r\n q.DOCUMENT_NO as CHECK_NO,  a.DOCUMENT_NO,\r\n q.CREATED_date as DOCUMENT_DATE,   \r\nq.document_date AS TGL_CAIR,   \r\nq.Jtempo,\r\nq.TOTAL_AMOUNT, \r\n  a.NAMA_BANK,\r\n  IF( (q.DOCUMENT_DATE BETWEEN :13 AND :23) AND (q.STATUS_ID in (@CLEAR,@TOLAK)) , q.TOTAL_AMOUNT, 0)  \r\n - IF( (q.TGL_TOLAK  BETWEEN :13 AND :23) AND (q.STATUS_ID = @TOLAK) , q.TOTAL_AMOUNT, 0)  + \r\n+  IFNULL(GANTI_TOLAK.KREDIT,0)\r\nKREDIT,   \r\n  k.COA_NAME ,\r\n  c.PERSON_NAME,\r\n  X.DISPLAY_NAME AS STATUS\r\nFROM\r\n  CHEQUE Q \r\n  LEFT JOIN RECEIPT a ON (q.CHEQUE_ID = a.CHEQUE_ID)\r\n  INNER JOIN ERP_LOOKUP_VALUE X on (Q.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)  \r\n  LEFT JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n    LEFT JOIN coa k ON (q.BANK_ID_DEST = k.COA_ID)\r\n  LEFT JOIN \r\n(\r\nSELECT  Y.CHEQUE_ID , SUM(AMOUNT)  AS KREDIT FROM \r\n  RECEIPT X\r\n  INNER JOIN RECEIPT_detail Y ON (X.RECEIPT_ID = Y.RECEIPT_ID)\r\n  WHERE (X.DOCUMENT_DATE BETWEEN :13 AND :23)\r\nGROUP BY Y.CHEQUE_ID \r\n) AS GANTI_TOLAK ON ( a.CHEQUE_ID = GANTI_TOLAK.CHEQUE_ID)\r\n\r\nWHERE (q.status_id <> @DELETE)\r\nAND (STATUS_FLAG = \'M\')\r\nAND (Q.CREATED_DATE BETWEEN :13 AND :23)\r\n&KONDISI\r\nORDER BY  Q.CREATED_DATE \r\n\r\n\r\n\r\n\r\n\r\n', 2469, 3100, 0, NULL, 4, '2009-12-12 14:15:25', '2009-12-10 18:10:55', 0, NULL, NULL, NULL, ':13|:23', 77, 1),
      ('SELECT \r\n  Q.document_no as CHECK_NO,\r\n a.DOCUMENT_NO,\r\nq.*,\r\n  a.*,\r\n  k.COA_NAME,\r\n  c.PERSON_ID,\r\n  c.PERSON_CODE,\r\n  c.PERSON_NAME,\r\n  X.DISPLAY_NAME AS STATUS\r\nFROM\r\n  CHEQUE Q  \r\n  LEFT JOIN PAYMENT a  ON (Q.CHEQUE_ID = a.CHEQUE_ID)\r\n  INNER JOIN ERP_LOOKUP_VALUE X on (Q.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)  \r\n  LEFT JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID) \r\n    LEFT JOIN coa k ON (q.BANK_ID_DEST = k.COA_ID)\r\nWHERE\r\n (Q.STATUS_FLAG = \'K\') AND (DATE_FORMAT(q.CREATED_DATE,\'%Y-%m-%d\') <= :13 AND q.STATUS_ID <> @DELETE)\r\nAND (q.STATUS_ID=@NEW OR ((DATE_FORMAT(q.DOCUMENT_DATE,\'%Y-%m-%d\')) > :13 AND q.STATUS_ID IN (@CLEAR, @TOLAK))) &KONDISI\r\nORDER BY c.person_name  \r\n', 2470, 3320, 0, NULL, 4, '2009-05-26 15:43:17', '2009-12-12 11:32:44', 0, NULL, NULL, NULL, ':13', 77, 1),
      ('SELECT DISTINCT \r\n  Q.document_no as CHECK_NO,\r\n a.DOCUMENT_NO,\r\n q.*, \r\n   a.*,\r\n  k.COA_NAME,\r\n  c.PERSON_ID,\r\n  c.PERSON_CODE,\r\n  ifnull(c.PERSON_NAME,\'Saldo Awal\' ) as PERSON_NAME,\r\n  X.DISPLAY_NAME AS STATUS\r\nFROM\r\n  CHEQUE Q  \r\n  LEFT JOIN RECEIPT a  ON (Q.CHEQUE_ID = a.CHEQUE_ID)\r\n  INNER JOIN ERP_LOOKUP_VALUE X on (Q.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)  \r\n  LEFT JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n    LEFT JOIN coa k ON (q.BANK_ID_DEST = k.COA_ID)\r\nWHERE (Q.STATUS_FLAG = \'M\') AND DATE_FORMAT(q.CREATED_DATE,\'%Y-%m-%d\') <= :13 AND q.STATUS_ID <> @DELETE\r\nAND (q.STATUS_ID=@NEW OR ((DATE_FORMAT(q.DOCUMENT_DATE,\'%Y-%m-%d\')) > :13 AND q.STATUS_ID IN (@CLEAR, @TOLAK)))\r\n\r\n&KONDISI\r\nORDER BY c.person_name\r\n\r\n', 2471, 860, 0, NULL, 4, '2009-05-26 15:48:49', '2009-12-12 11:15:33', 0, NULL, NULL, NULL, ':13', 77, NULL),
      ('SELECT a.NOTA_PENJUALAN_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.TOTAL_NET,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  \r\nSITE_NAME, \r\n  s.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME,\r\n  SUM(b.subtotal )  AS TOTAL_AMOUNT  ,\r\n  X.RECEIVED_AMOUNT + NET_BYR1 + TOTAL_BYR2 AS RECEIVED_AMOUNT \r\nFROM\r\n  nota_penjualan a\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n LEFT JOIN NOTA_PENJUALAN_AGING  x ON (a.NOTA_PENJUALAN_ID = x.NOTA_PENJUALAN_ID)\r\n  \r\n INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN Item i ON (b.item_id = i.item_id)\r\nWHERE (PPN_AMOUNT > 0)\r\n\r\n&Kondisi\r\nGROUP BY \r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.TOTAL_NET,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\nSITE_NAME, \r\n  s.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME\r\nORDER BY   a.DOCUMENT_NO \r\n', 2477, 1314, 0, NULL, 4, '2005-10-22 13:06:54', '2010-03-12 00:24:41', 0, NULL, NULL, NULL, NULL, 77, 1),
      ('SELECT \r\n  a.*, I.*\r\nFROM\r\n nota_penjualan_detail a \r\n  INNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID )\r\nwhere a.NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID\r\n \r\n\r\n&Kondisi\r\nORDER BY   a.ITEM_DESCRIPTION\r\n', 2481, 1314, 3, NULL, 0, '2010-03-12 00:24:20', '2010-03-12 00:24:20', 0, NULL, NULL, NULL, NULL, 0, NULL),
      ('SELECT @NO := @NO +1 AS NO,\r\nITEM_ID,\r\nitem_description,\r\nitem_code,\r\nuom_code SATUAN,\r\nSUM(AWAL) AS AWAL ,\r\nSUM(BELI ) AS BELI,\r\nSUM(RB ) AS RB,\r\nSUM(JUAL ) AS JUAL,\r\nSUM(RJ) AS RJ,\r\nSUM(ADJ) AS ADJ,\r\nSUM(PAG) AS PAG,\r\nSUM(BUILD) AS BUILD,\r\nSUM(AKHIR) as AKHIR,\r\nSUM(AWAL_rp) as AWAL_rp, \r\nSUM(BELI_rp) as BELI_rp, \r\nSUM(RB_rp) as RB_rp,\r\nSUM(JUAL_rp) as JUAL_rp, \r\nSUM(RJ_rp) as RJ_rp,\r\nSUM(ADJ_rp) as ADJ_rp, \r\nSUM(PAG_rp) as PAG_rp,\r\nSUM(BUILD_rp) as BUILD_rp,\r\nSUM(ADJ_NILAI_rp) as ADJ_NILAI_rp,\r\nSUM(AKHIR_rp ) as AKHIR_rp\r\nFROM\r\n(\r\nSELECT\r\nITEM_ID,\r\nitem_description,\r\nitem_code,\r\nuom_code ,\r\nSUM(MASUK - KELUAR) AS AWAL ,\r\n0 AS BELI,\r\n0 AS RB,\r\n0 AS JUAL,\r\n0 AS RJ,\r\n0 AS ADJ,\r\n0 AS PAG,\r\n0 AS BUILD,\r\n0 as AKHIR,\r\nSUM(MASUK * harga- KELUAR * harga) AS AWAL_rp ,\r\n0 as BELI_rp, \r\n0 as RB_rp,\r\n0 as JUAL_rp, \r\n0 as RJ_rp,\r\n0 as ADJ_rp, \r\n0 as PAG_rp,\r\n0 as BUILD_rp,\r\n0 as ADJ_NILAI_rp,\r\n0 as AKHIR_rp\r\nFROM\r\n(\r\nSELECT\r\n0 as URUT,\r\n0 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE ,\r\np.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\n\'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n\'\' AS DOCUMENT_NO,\r\n\'\' AS DOCUMENT_REFF_NO,\r\nSTART_DATE as DOCUMENT_DATE,\r\na.WAREHOUSE_ID ,\r\n\'\' AS NOTE,\r\nI.ITEM_ID,\r\na.QTY_AWAL AS MASUK,\r\n0 AS KELUAR,\r\na.HPP as HARGA,\r\n0 as DISKON,\r\na.QTY_AWAL * a.HPP   AS SUBTOTAL,\r\n-1 as PERSON_ID\r\nFROM\r\nitem_balance a\r\nINNER JOIN PERIOD p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE True\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)     \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVOICE         */\r\nSELECT\r\n1 as URUT,\r\n1 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n1  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE, \r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))   AS SUBTOTAL,\r\na.PERSON_ID\r\nFROM\r\nINVENTORY_IN a\r\nINNER JOIN INVENTORY_IN_detail b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE TRUE\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n1 as URUT,\r\n2 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n0  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.hpp/b.BASE_QTY  AS HARGA  ,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))    AS SUBTOTAL,\r\na.PERSON_ID\r\nFROM\r\nINVENTORY_OUT a\r\nINNER JOIN INVENTORY_OUT_detail b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE TRUE\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI Pembelian yg ga pake BPB  */\r\n/* INVOICE          */\r\nSELECT\r\n1 as URUT,\r\n3 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1) )  AS SUBTOTAL,\r\na.PERSON_ID\r\nFROM\r\nINVOICE a\r\nINNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE b.INVENTORY_IN_DETAIL_ID is NULL\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI Penjualan yg ga pake SJ  */\r\n/* NOTA_PENJUALAN           */\r\nSELECT\r\n1 as URUT,\r\n4 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY    as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1)  )  AS SUBTOTAL,\r\na.PERSON_ID\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE b.INVENTORY_OUT_DETAIL_ID IS NULL\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n2 as URUT,\r\n5 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\nb.UNIT_PRICE /b.BASE_QTY as HARGA,\r\n0 as DISKON ,\r\nb.Subtotal AS SUBTOTAL,\r\n-1 as PERSON_ID\r\nFROM\r\nadjust_qty a\r\nINNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE b.ENTERED_QTY  > 0\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n2 as URUT,\r\n5 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * -1 * b.BASE_QTY    AS KELUAR,\r\nb.UNIT_PRICE / b.BASE_QTY as HARGA,\r\n0 as DISKON ,\r\nb.Subtotal AS SUBTOTAL ,\r\n-1 as PERSON_ID\r\nFROM\r\nadjust_qty a\r\nINNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE b.ENTERED_QTY  < 0\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI PAG, GUDANG ASAL  */\r\n/* TAG         */\r\nSELECT\r\n1 as URUT,\r\n6 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\nb.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nifnull(l.Hpp,0) as HARGA,\r\n0 AS DISKON, \r\n0 AS SUBTOTAL,\r\n-1 as PERSON_ID\r\nFROM\r\ntag a\r\nINNER JOIN tag_detail b ON (a.tag_ID = b.tag_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nLEFT  JOIN Item_balance l on (b.item_id = l.item_id) and (a.period_name = l.period_name) and (a.warehouse_id = l.warehouse_id)\r\nWHERE TRUE\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI PAG, GUDANG TUJUAN  */\r\n/* TAG         */\r\nSELECT\r\n1 as URUT,\r\n6 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.to_wh_ID as WAREHOUSE_ID,\r\nb.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\nifnull(l.Hpp,0) as HARGA,\r\n0 as DISKON ,\r\n0 AS SUBTOTAL,\r\n-1 as PERSON_ID\r\nFROM\r\ntag a\r\nINNER JOIN tag_detail b ON (a.tag_ID = b.tag_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nLEFT  JOIN Item_balance l on (b.item_id = l.item_id) and (a.period_name = l.period_name) and (a.to_wh_id = l.warehouse_id)\r\ninner join warehouse w on (a.to_wh_id=w.warehouse_id)\r\nWHERE TRUE\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  BAHAN BAKU       */\r\nSELECT\r\n1 as URUT,\r\n7 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.UNIT_PRICE /b.BASE_QTY   AS HARGA,\r\n0 as DISKON ,\r\nb.SUBTOTAL  AS SUBTOTAL, \r\n-1 as PERSON_ID\r\nFROM\r\nbuild a\r\nINNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE  true\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  ITEM YG DIBUAT       */\r\nSELECT\r\n1 as URUT,\r\n7 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\na.ENTERED_QTY *  a.BASE_QTY  AS MASUK,\r\n0 AS KELUAR,\r\na.TOTAL_AMOUNT / a.ENTERED_QTY / a.BASE_QTY  AS HARGA,\r\n0 as DISKON ,\r\na.TOTAL_AMOUNT AS SUBTOTAL,\r\n-1 as PERSON_ID\r\nFROM\r\nbuild a\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE true \r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <  :11 ) \r\n&KONDISI\r\n) AS AA\r\nGROUP BY  ITEM_ID \r\nhaving SUM(MASUK - KELUAR) <> 0\r\nUNION ALL \r\nSELECT\r\ni.ITEM_ID,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\n0 as  AWAL, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF( xx.Masuk>0,xx.masuk,0) + IF( xx.keluar<0 , abs(xx.keluar), 0),\r\n0)\r\n) as BELI, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF (xx.Masuk<0, abs(xx.masuk), 0) + IF(xx.keluar>0,xx.keluar,0),\r\n0)\r\n) as RB,\r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4), IF( xx.Keluar>0,xx.keluar,0) + IF( xx.masuk<0 , abs(xx.masuk), 0),\r\n0)\r\n) as JUAL, \r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4), IF (xx.Keluar<0, abs(xx.keluar), 0) + IF(xx.masuk>0,xx.masuk,0),\r\n0) \r\n) as RJ,\r\nSUM( IF( xx.TIPE=5, xx.Masuk-xx.keluar, 0)  ) as ADJ, \r\nSUM( IF( xx.TIPE=6, xx.Masuk-xx.keluar, 0)  ) as PAG,\r\nSUM( IF( xx.TIPE=7, xx.Masuk-xx.keluar, 0)  ) as BUILD,\r\nSUM(if(xx.Masuk>0,xx.masuk,0)+if(xx.keluar<0,abs(xx.keluar),0))-SUM(if(xx.Masuk<0,abs(xx.masuk),0)+if(xx.keluar>0,xx.keluar,0)) as AKHIR,\r\n0  as AWAL_rp, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF( xx.Masuk>0,xx.masuk,0) + IF( xx.keluar<0 , abs(xx.keluar), 0),\r\n0)*xx.HARGA\r\n) as BELI_rp, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF (xx.Masuk<0, abs(xx.masuk), 0) + IF(xx.keluar>0,xx.keluar,0),\r\n0)*xx.HARGA\r\n) as RB_rp,\r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4),IF( xx.Keluar>0,xx.keluar,0) + IF( xx.Masuk<0 , abs(xx.masuk), 0),\r\n0)*xx.HARGA\r\n) as JUAL_rp, \r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4),  IF (xx.Keluar<0, abs(xx.keluar), 0) + IF(xx.masuk>0,xx.masuk,0),\r\n0)*xx.HARGA\r\n) as RJ_rp,\r\nSUM( IF( xx.TIPE=5, xx.Masuk-xx.keluar, 0) *xx.HARGA ) as ADJ_rp, \r\nSUM( IF( xx.TIPE=6, xx.Masuk-xx.keluar, 0) *xx.HARGA ) as PAG_rp,\r\nSUM( IF( xx.TIPE=7, xx.Masuk-xx.keluar, 0) *xx.HARGA ) as BUILD_rp,\r\n(SELECT SUM(ENTERED_AMOUNT) \r\nFrom adjust_amount_detail b,adjust_amount a \r\nWHERE b.item_id=i.item_id and a.adjust_amount_id=b.adjust_amount_id &P1 \r\nGROUP BY ITEM_ID\r\n) as ADJ_NILAI_rp,\r\nSUM(if(xx.Masuk>0,xx.masuk,0)+if(xx.keluar<0,abs(xx.keluar),0)*xx.HARGA)- SUM(if(xx.Masuk<0,abs(xx.masuk),0)+if(xx.keluar>0,xx.keluar,0)*xx.HARGA) as AKHIR_rp\r\nFROM \r\nITEM i \r\ninner join (SQL1) as xx on (i.item_id=xx.item_id)\r\ninner join warehouse w on (xx.warehouse_id = w.warehouse_id) \r\nWHERE \r\n(i.ACTIVE_FLAG = \'Y\')  AND \r\n(i.JENIS_ID = @GOODS)\r\nand (xx.masuk<>0 or xx.keluar<>0)\r\n&KONDISI\r\nGROUP BY i.ITEM_ID\r\n/*having SUM(if(Masuk>0,masuk,0)+if(keluar<0,abs(keluar),0))-SUM(if(Masuk<0,abs(masuk),0)+if(keluar>0,keluar,0))  <>0*/\r\n) AS YY\r\nGROUP BY ITEM_ID\r\nORDER BY ITEM_CODE\r\n', 2540, 4016, 0, 'SALDO AWAL', 130, '2010-06-19 00:16:53', '2010-06-19 00:16:53', 0, 70, 'SQL1', '&X1= AND FALSE\r\n&X2= AND    (a.PERIOD_NAME >= :PERIOD_NAME1) \r\n&X3=  AND   (a.PERIOD_NAME >= :PERIOD_NAME1) \r\n&X4=  AND (a.PERIOD_NAME >= :PERIOD_NAME1) \r\n&T1= AND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') >=  :11 AND DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') <= 