      (' SELECT \r\nSUM(DEBET - KREDIT)  AS SALDO \r\nFROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   STATUS_FLAG, \r\n   IF(:STATUS_FLAG = \'M\',C.TOTAL_AMOUNT,0) AS DEBET,\r\n   IF(:STATUS_FLAG <> \'M\',C.TOTAL_AMOUNT,0) AS KREDIT\r\n  FROM CHEQUE C\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND (C.STATUS_FLAG = :STATUS_FLAG)\r\nAND (C.TGL_TOLAK  < :12)\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE  (Q.STATUS_FLAG = :STATUS_FLAG)\r\nAND (R.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKK */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   RD.AMOUNT DEBET,\r\n   0 AS KREDIT\r\nFROM \r\nPAYMENT R \r\nINNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE  (Q.STATUS_FLAG = :STATUS_FLAG)\r\nAND (R.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT  R.STATUS_FLAG,\r\n IF(:STATUS_FLAG <> \'M\',R.TOTAL_AMOUNT,0) AS DEBET,\r\n  IF(:STATUS_FLAG = \'M\',R.TOTAL_AMOUNT,0) AS KREDIT\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\nWHERE   (R.STATUS_FLAG = :STATUS_FLAG)\r\nAND (R.CREATED_DATE < :12 )\r\n\r\n) \r\nAS XX\r\nWHERE TRUE\r\n&KONDISI\r\nGROUP BY  STATUS_FLAG', 2586, 3004, 14, 'SALDO AWAL TOLAKKAN', 2, '2007-09-11 13:42:34', '2007-09-11 18:55:57', 2, NULL, NULL, NULL, ':12', 13, NULL),
