      (' SELECT \r\nSUM(DEBET ) AS DEBET , \r\nSUM(KREDIT ) AS KREDIT\r\nFROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   STATUS_FLAG, \r\n   IF(:STATUS_FLAG = \'M\',C.TOTAL_AMOUNT,0) AS DEBET,\r\n   IF(:STATUS_FLAG <> \'M\',C.TOTAL_AMOUNT,0) AS KREDIT\r\n  FROM CHEQUE C\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND (C.STATUS_FLAG = :STATUS_FLAG)\r\nAND ( C.TGL_TOLAK  BETWEEN   :12  AND :22)\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE  (Q.STATUS_FLAG = :STATUS_FLAG)\r\nAND (R.DOCUMENT_DATE  BETWEEN   :12  AND :22 )\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKK */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   RD.AMOUNT DEBET,\r\n   0 AS KREDIT\r\nFROM \r\nPAYMENT R \r\nINNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE  (Q.STATUS_FLAG = :STATUS_FLAG)\r\nAND (Date_format(R.DOCUMENT_DATE , \'%Y-%m-%d\') =   :12)\r\n\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT  R.STATUS_FLAG,\r\n IF(:STATUS_FLAG <> \'M\',R.TOTAL_AMOUNT,0) AS DEBET,\r\n  IF(:STATUS_FLAG = \'M\',R.TOTAL_AMOUNT,0) AS KREDIT\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\nWHERE   (R.STATUS_FLAG = :STATUS_FLAG)\r\nAND (R.CREATED_DATE  BETWEEN   :12  AND :22)\r\n \r\n\r\n) \r\nAS XX\r\nWHERE TRUE\r\n&KONDISI\r\nGROUP BY  STATUS_FLAG', 2590, 2006, 15, 'TOLAKAN : DEBET DAN KREDIT', 2, '2007-09-11 19:06:25', '2007-09-11 19:10:01', 2, NULL, NULL, NULL, ':12|:22', 13, NULL),
      ('SELECT\r\nSUM(SALDO + DEBET - KREDIT ) AS SALDO\r\nFROM \r\n( SQL1\r\n) AS A \r\nWHERE TRUE\r\n&KONDISI', 2638, 4002, 0, 'saldo awal', 4, '2008-06-16 12:48:56', '2011-09-07 11:29:06', 0, 76, 'SQL1', '&X1= AND B.COA_ID = :BANK_ID\r\n&X2= AND B.COA_ID = :BANK_ID\r\n&T1= AND (a.DOCUMENT_DATE < :12 ) \r\n&T2= AND (a.TGL_TOLAK< :12 ) \r\n&P1= AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n&P2= AND (p.PERIOD_NAME = :PERIOD_NAME1)', ':12|:PERIOD_NAME1', 9, 1),
      ('SELECT\r\n  AAA.*, \r\n\r\n  ifnull(document_no, ifnull(document_no,coa_code)) as no_detail,\r\n  ifnull(document_reff_no, ifnull(document_reff_no,coa_name)) as no_reff_detail,\r\nDEBET as DEBET_DETAIL,\r\n  KREDIT as KREDIT_DETAIL\r\n\r\nFROM \r\n( SQL1\r\n) AS AAA \r\nWHERE TRUE\r\n&KONDISI\r\nORDER BY DOCUMENT_DATE\r\n', 2639, 4002, 3, 'DETAIL', 4, '2008-06-16 12:48:56', '2009-01-04 23:50:41', 0, 76, 'SQL1', '&X1= AND FALSE\r\n&X2= AND B.COA_ID = :BANK_ID\r\n&T1= AND (Date_format(a.DOCUMENT_DATE,\'%Y-%m-%d\') = :12 )\r\n&T2= AND (Date_format(a.TGL_TOLAK,\'%Y-%m-%d\') = :12 )\r\n&P1= AND (a.PERIOD_NAME = :PERIOD_NAME1)', ':12|:PERIOD_NAME1', 9, 1),
      ('SELECT a.COA_ID, b.ACCOUNT_NAME BANK_NAME,COA_CODE, a.COA_ID AS BANK_ID\r\nFROM\r\nCOA a\r\nINNER JOIN ACCOUNT b ON (a.ACCOUNT_ID=b.ACCOUNT_ID)\r\nWHERE a.ACTIVE_FLAG = \'Y\' and (b.ACCOUNT_TYPE_ID in (@ACC_KASBANK,@ACC_GIRO))\r\n&kondisi\r\n&kondisi2\r\norder by PRIMARY_FLAG DESC , ACCOUNT_NAME\r\n', 2640, 4002, 9, NULL, 4, '2008-06-16 12:48:56', '2011-09-07 11:29:06', 1, NULL, NULL, NULL, NULL, 77, 1),
      ('SELECT * FROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   \r\n  1 AS URUT, \r\n   C.DOCUMENT_NO as CHECK_NO ,\r\n   \' \'   NO_BG_LAMA ,\r\n   C.CREATED_DATE, \r\n  C.JTEMPO,\r\n   C.TGL_TOLAK , \r\n   C.DOCUMENT_DATE,\r\n   C.TOTAL_AMOUNT AS DEBET,\r\n   0 AS KREDIT,\r\n   b.COA_NAME AS NAMA_BANK ,\r\n   bd.COA_NAME ,\r\n   R.DOCUMENT_NO,\r\n   DISPLAY_NAME  AS STATUS\r\n  FROM CHEQUE C\r\n    left join PAYMENT R ON (C.PAYMENT_ID = R.PAYMENT_ID)\r\n    left join coa b on (R.BANK_ID = b.COA_ID)\r\n    left join coa bd on (c.BANK_ID_DEST = bd.COA_ID)\r\nLEFT JOIN ERP_LOOKUP_VALUE X ON (C.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND IF(:PERSON_ID is null, ((c.payment_id is null and c.PAYMENT_ID is null) or(R.PERSON_ID IS NULL)), R.PERSON_ID = :PERSON_ID )\r\nAND (C.STATUS_FLAG = \'K\')\r\nAND (C.TGL_TOLAK BETWEEN :11 AND :21)\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT \r\n  2 AS URUT,\r\n   Q.DOCUMENT_NO ,\r\n   \' \'   NO_BG_LAMA ,\r\n       R.DOCUMENT_DATE,\r\n   R.JTEMPO,\r\n   \'\',\r\n   R.DOCUMENT_DATE,\r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT,\r\n   b.COA_NAME AS BANK ,\r\n   \'\'AS BANK_TUJUAN,\r\n   R.DOCUMENT_NO ,\r\n   DISPLAY_NAME  AS STATUS\r\nFROM \r\nPAYMENT R \r\nINNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID) \r\nleft join coa b on (R.BANK_ID = b.COA_ID)\r\nLEFT JOIN ERP_LOOKUP_VALUE X ON (R.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)\r\n        \r\nWHERE  (Q.STATUS_FLAG = \'K\')\r\nAND IF(:PERSON_ID is null, R.PERSON_ID is null, R.PERSON_ID = :PERSON_ID )\r\nAND (R.DOCUMENT_DATE BETWEEN :11 AND :21)\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT \r\n  3 AS URUT,\r\n   R.DOCUMENT_NO ,\r\n   Q.DOCUMENT_NO NO_BG_LAMA ,\r\n  R.CREATED_DATE, \r\n  R.JTEMPO,\r\n   \'\', \r\n   R.DOCUMENT_DATE,\r\n   0 AS DEBET , \r\n   R.TOTAL_AMOUNT,\r\n   b.COA_NAME AS BANK ,\r\n   \'\'AS BANK_TUJUAN, \r\n \' \' ,\r\n   DISPLAY_NAME  AS STATUS\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID ) \r\n    inner join PAYMENT R1 ON (Q.PAYMENT_ID = R1.PAYMENT_ID)\r\nleft join coa b on (R.BANK_ID = b.COA_ID)\r\n\r\nLEFT JOIN ERP_LOOKUP_VALUE X ON (R.STATUS_ID = X.ERP_LOOKUP_VALUE_ID) \r\n        \r\nWHERE   (R.STATUS_FLAG = \'K\')\r\nAND IF(:PERSON_ID is null, R1.PERSON_ID is null, R1.PERSON_ID = :PERSON_ID )\r\nAND (R.CREATED_DATE BETWEEN :11 AND :21)\r\n\r\n) AS XX\r\n&kondisi\r\nORDER BY    Date_format(CREATED_DATE , \'%Y-%m-%d\') , URUT', 2644, 4014, 13, '', 2, '2007-09-11 15:27:32', '2007-09-11 18:48:53', 2, NULL, '', NULL, ':11|:21', 3, NULL),
      (' SELECT \r\nSUM(DEBET - KREDIT)  AS SALDO \r\nFROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   STATUS_FLAG, \r\n   C.TOTAL_AMOUNT AS DEBET,\r\n   0 AS KREDIT\r\n  FROM CHEQUE C\r\n  left join PAYMENT r on(c.PAYMENT_ID = r.PAYMENT_ID)\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND (C.STATUS_FLAG = \'K\')\r\nAND (C.TGL_TOLAK  < :11)\r\nAND IF(:PERSON_ID is null, ((c.payment_id is null and c.PAYMENT_ID is null) OR(R.PERSON_ID IS NULL)), R.PERSON_ID = :PERSON_ID )\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT\r\nFROM \r\nPAYMENT R \r\nINNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE  (Q.STATUS_FLAG = \'K\')\r\nAND (R.DOCUMENT_DATE < :11 )\r\nAND IF(:PERSON_ID is null, R.PERSON_ID is null, R.PERSON_ID = :PERSON_ID )\r\n\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT  R.STATUS_FLAG,\r\n   0 AS DEBET , \r\n   R.TOTAL_AMOUNT\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\ninner join PAYMENT R1 ON (Q.PAYMENT_ID = R1.PAYMENT_ID)\r\nWHERE   (R.STATUS_FLAG = \'K\')\r\nAND (R.CREATED_DATE < :11 )\r\nAND IF(:PERSON_ID is null, R1.PERSON_ID is null, R1.PERSON_ID = :PERSON_ID )\r\n\r\n) \r\nAS XX\r\nWHERE TRUE\r\n&KONDISI\r\n', 2645, 4014, 14, NULL, 0, '2011-08-18 15:48:54', '2011-08-18 15:48:54', 0, NULL, NULL, NULL, ':11', 3, NULL),
      ('SELECT * from(\r\n	/*YANG ADA TRANSAKSI DI ANTARA TGL PARAMETER*/\r\n	SELECT *\r\n	FROM \r\n		(\r\n			SELECT \r\n			PERSON_NAME, PERSON_ID, PERSON_CODE,\r\n			SUM(DEBET)  AS NILAI\r\n			FROM\r\n			(\r\n			/* BG TOLAKAN */ \r\n			SELECT   \r\n				p.PERSON_NAME , P.Person_code, P.PERSON_ID,\r\n				C.TOTAL_AMOUNT AS DEBET\r\n			FROM CHEQUE C\r\n				inner join PAYMENT R ON (C.PAYMENT_ID = R.PAYMENT_ID)\r\n				left join PERSON p ON (R.PERSON_ID = p.PERSON_ID )\r\n			WHERE \r\n				(C.STATUS_ID = @TOLAK )\r\n				AND (C.STATUS_FLAG = \'K\')\r\n				AND (C.TGL_TOLAK BETWEEN :11 AND :21)\r\n				\r\n			UNION ALL\r\n			\r\n			/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\n			SELECT \r\n				p.PERSON_NAME , P.Person_code, P.PERSON_ID,\r\n				RD.AMOUNT * -1\r\n			FROM \r\n				PAYMENT R \r\n				INNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\n				INNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID) \r\n				left join PERSON p ON (R.PERSON_ID = p.PERSON_ID )     \r\n			WHERE  (Q.STATUS_FLAG = \'K\')\r\n				AND (R.DOCUMENT_DATE BETWEEN :11 AND :21)\r\n				\r\n			UNION ALL \r\n			\r\n			/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\n			SELECT \r\n				p.PERSON_NAME , P.Person_code, P.PERSON_ID, R.TOTAL_AMOUNT *-1\r\n			FROM \r\n				CHEQUE R\r\n				INNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID ) \r\n				inner join PAYMENT R1 ON (Q.PAYMENT_ID = R1.PAYMENT_ID)\r\n				left join PERSON p ON (R1.PERSON_ID = p.PERSON_ID )\r\n			WHERE   (R.STATUS_FLAG = \'K\')\r\n				AND (R.CREATED_DATE BETWEEN :11 AND :21)\r\n			) AS XX\r\n		GROUP BY\r\n		PERSON_NAME , Person_code, PERSON_ID\r\n		) AS YY \r\n	/*WHERE \r\n	NILAI <> 0 */\r\n\r\n\r\n	union all\r\n\r\n	/*YANG PUNYA SALDO AWAL SAMPAI TANGGAL PARAMETER*/\r\n	SELECT\r\n		person_name, p.person_id, p.person_code,\r\n		sum(debet-kredit) as saldo_awal\r\n	FROM\r\n	(\r\n		/* BG TOLAKAN */ \r\n		SELECT   STATUS_FLAG, \r\n			C.TOTAL_AMOUNT AS DEBET,\r\n			0 AS KREDIT,\r\n			r.person_id\r\n		FROM CHEQUE C\r\n			left join PAYMENT r on(c.PAYMENT_ID = r.PAYMENT_ID)\r\n		WHERE \r\n			(C.STATUS_ID = @TOLAK )\r\n			AND (C.STATUS_FLAG = \'K\')\r\n			AND (C.TGL_TOLAK  < :11)\r\n\r\n		UNION ALL\r\n		/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\n		SELECT  Q.STATUS_FLAG,  \r\n			0 AS DEBET,  \r\n			RD.AMOUNT KREDIT,\r\n			r.person_id\r\n		FROM \r\n			PAYMENT R \r\n			INNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\n			INNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\n		WHERE  (Q.STATUS_FLAG = \'K\')\r\n			AND (R.DOCUMENT_DATE < :11 )\r\n\r\n		UNION ALL \r\n		/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\n		SELECT  R.STATUS_FLAG,\r\n			0 AS DEBET , \r\n			R.TOTAL_AMOUNT,\r\n			r1.person_id\r\n		FROM \r\n			CHEQUE R\r\n			INNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\n			inner join PAYMENT R1 ON (Q.PAYMENT_ID = R1.PAYMENT_ID)\r\n		WHERE   (R.STATUS_FLAG = \'K\')\r\n			AND (R.CREATED_DATE < :11 )\r\n\r\n	) \r\n	AS XX\r\n	left join person p on(xx.person_id = p.person_id)\r\n	WHERE TRUE\r\n\r\n	group by person_id\r\n	having saldo_awal <> 0\r\n) as bb\r\n&KONDISI\r\ngroup by person_id\r\nORDER BY PERSON_NAME\r\n', 2646, 4014, 3, 'debet kredit TOLAKAN', 0, '2011-08-18 15:50:48', '2011-08-18 15:50:48', 0, NULL, NULL, NULL, ':11|:21', 77, NULL),
      ('SELECT * from(\r\nSELECT *\r\nFROM \r\n(\r\nSELECT \r\nPERSON_NAME, PERSON_ID, PERSON_CODE,\r\nSUM(DEBET)  AS NILAI\r\n FROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   \r\n   p.PERSON_NAME , P.Person_code, P.PERSON_ID,\r\n   C.TOTAL_AMOUNT AS DEBET\r\n  FROM CHEQUE C\r\n    inner join RECEIPT R ON (C.RECEIPT_ID = R.RECEIPT_ID)\r\n    left join PERSON p ON (R.PERSON_ID = p.PERSON_ID )\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND (C.STATUS_FLAG = \'M\')\r\nAND (C.TGL_TOLAK BETWEEN :11 AND :21)\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT \r\n   p.PERSON_NAME , P.Person_code, P.PERSON_ID,\r\n   RD.AMOUNT * -1\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID) \r\nleft join PERSON p ON (R.PERSON_ID = p.PERSON_ID )     \r\nWHERE  (Q.STATUS_FLAG = \'M\')\r\nAND (R.DOCUMENT_DATE BETWEEN :11 AND :21)\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT \r\n   p.PERSON_NAME , P.Person_code, P.PERSON_ID, R.TOTAL_AMOUNT *-1\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID ) \r\n    inner join RECEIPT R1 ON (Q.RECEIPT_ID = R1.RECEIPT_ID)\r\nleft join PERSON p ON (R1.PERSON_ID = p.PERSON_ID )\r\n        \r\nWHERE   (R.STATUS_FLAG = \'M\')\r\nAND (R.CREATED_DATE BETWEEN :11 AND :21)\r\n) AS XX\r\nGROUP BY\r\n   PERSON_NAME , Person_code, PERSON_ID\r\n) AS YY \r\n/*WHERE \r\nNILAI <> 0 */\r\n\r\n\r\nunion all\r\n\r\nSELECT\r\n	person_name, p.person_id, p.person_code,\r\n	sum(debet-kredit) as saldo_awal\r\nFROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   STATUS_FLAG, \r\n   C.TOTAL_AMOUNT AS DEBET,\r\n   0 AS KREDIT,\r\n   r.person_id\r\n  FROM CHEQUE C\r\n  left join receipt r on(c.receipt_id = r.receipt_id)\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND (C.STATUS_FLAG = \'M\')\r\nAND (C.TGL_TOLAK  < :11)\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT,\r\n   r.person_id\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE  (Q.STATUS_FLAG = \'M\')\r\nAND (R.DOCUMENT_DATE < :11 )\r\n\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT  R.STATUS_FLAG,\r\n   0 AS DEBET , \r\n   R.TOTAL_AMOUNT,\r\n   r1.person_id\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\ninner join RECEIPT R1 ON (Q.RECEIPT_ID = R1.RECEIPT_ID)\r\nWHERE   (R.STATUS_FLAG = \'M\')\r\nAND (R.CREATED_DATE < :11 )\r\n\r\n) \r\nAS XX\r\nleft join person p on(xx.person_id = p.person_id)\r\nWHERE TRUE\r\n\r\ngroup by person_id\r\nhaving saldo_awal <> 0\r\n) as bb\r\n&KONDISI\r\ngroup by person_id\r\nORDER BY PERSON_NAME\r\n', 2647, 4013, 3, 'debet kredit TOLAKAN', 2, '2007-09-11 15:27:32', '2007-09-21 11:29:41', 2, NULL, '', NULL, ':11|:21', 77, NULL),
      ('SELECT * FROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   \r\n  1 AS URUT, \r\n   C.DOCUMENT_NO as CHECK_NO ,\r\n   \' \'   NO_BG_LAMA ,\r\n   C.CREATED_DATE, \r\n  C.JTEMPO,\r\n   C.TGL_TOLAK , \r\n   C.DOCUMENT_DATE,\r\n   C.TOTAL_AMOUNT AS DEBET,\r\n   0 AS KREDIT,\r\n   b.COA_NAME AS NAMA_BANK ,\r\n   bd.COA_NAME ,\r\n   R.DOCUMENT_NO,\r\n   DISPLAY_NAME  AS STATUS\r\n  FROM CHEQUE C\r\n    left join RECEIPT R ON (C.RECEIPT_ID = R.RECEIPT_ID)\r\n    left join coa b on (R.BANK_ID = b.COA_ID)\r\n    left join coa bd on (c.BANK_ID_DEST = bd.COA_ID)\r\nLEFT JOIN ERP_LOOKUP_VALUE X ON (C.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND IF(:PERSON_ID is null, ((c.payment_id is null and c.receipt_id is null) or(R.PERSON_ID IS NULL)), R.PERSON_ID = :PERSON_ID )\r\nAND (C.STATUS_FLAG = \'M\')\r\nAND (C.TGL_TOLAK BETWEEN :11 AND :21)\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT \r\n  2 AS URUT,\r\n   Q.DOCUMENT_NO ,\r\n   \' \'   NO_BG_LAMA ,\r\n       R.DOCUMENT_DATE,\r\n   R.JTEMPO,\r\n   \'\',\r\n   R.DOCUMENT_DATE,\r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT,\r\n   b.COA_NAME AS BANK ,\r\n   \'\'AS BANK_TUJUAN,\r\n   R.DOCUMENT_NO ,\r\n   DISPLAY_NAME  AS STATUS\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID) \r\nleft join coa b on (R.BANK_ID = b.COA_ID)\r\nLEFT JOIN ERP_LOOKUP_VALUE X ON (R.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)\r\n        \r\nWHERE  (Q.STATUS_FLAG = \'M\')\r\nAND IF(:PERSON_ID is null, R.PERSON_ID is null, R.PERSON_ID = :PERSON_ID )\r\nAND (R.DOCUMENT_DATE BETWEEN :11 AND :21)\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT \r\n  3 AS URUT,\r\n   R.DOCUMENT_NO ,\r\n   Q.DOCUMENT_NO NO_BG_LAMA ,\r\n  R.CREATED_DATE, \r\n  R.JTEMPO,\r\n   \'\', \r\n   R.DOCUMENT_DATE,\r\n   0 AS DEBET , \r\n   R.TOTAL_AMOUNT,\r\n   b.COA_NAME AS BANK ,\r\n   \'\'AS BANK_TUJUAN, \r\n \' \' ,\r\n   DISPLAY_NAME  AS STATUS\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID ) \r\n    inner join RECEIPT R1 ON (Q.RECEIPT_ID = R1.RECEIPT_ID)\r\nleft join coa b on (R.BANK_ID = b.COA_ID)\r\n\r\nLEFT JOIN ERP_LOOKUP_VALUE X ON (R.STATUS_ID = X.ERP_LOOKUP_VALUE_ID) \r\n        \r\nWHERE   (R.STATUS_FLAG = \'M\')\r\nAND IF(:PERSON_ID is null, R1.PERSON_ID is null, R1.PERSON_ID = :PERSON_ID )\r\nAND (R.CREATED_DATE BETWEEN :11 AND :21)\r\n\r\n) AS XX\r\n&kondisi\r\nORDER BY    Date_format(CREATED_DATE , \'%Y-%m-%d\') , URUT\r\n\r\n', 2648, 4013, 13, NULL, 1, '2007-09-14 15:25:30', '2007-09-14 16:32:27', 1, NULL, NULL, NULL, ':11|:21', 3, NULL),
      (' SELECT \r\nSUM(DEBET - KREDIT)  AS SALDO \r\nFROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   STATUS_FLAG, \r\n   C.TOTAL_AMOUNT AS DEBET,\r\n   0 AS KREDIT\r\n  FROM CHEQUE C\r\n  left join receipt r on(c.receipt_id = r.receipt_id)\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND (C.STATUS_FLAG = \'M\')\r\nAND (C.TGL_TOLAK  < :11)\r\nAND IF(:PERSON_ID is null, ((c.payment_id is null and c.receipt_id is null) OR(R.PERSON_ID IS NULL)), R.PERSON_ID = :PERSON_ID )\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE  (Q.STATUS_FLAG = \'M\')\r\nAND (R.DOCUMENT_DATE < :11 )\r\nAND IF(:PERSON_ID is null, R.PERSON_ID is null, R.PERSON_ID = :PERSON_ID )\r\n\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT  R.STATUS_FLAG,\r\n   0 AS DEBET , \r\n   R.TOTAL_AMOUNT\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\ninner join RECEIPT R1 ON (Q.RECEIPT_ID = R1.RECEIPT_ID)\r\nWHERE   (R.STATUS_FLAG = \'M\')\r\nAND (R.CREATED_DATE < :11 )\r\nAND IF(:PERSON_ID is null, R1.PERSON_ID is null, R1.PERSON_ID = :PERSON_ID )\r\n\r\n) \r\nAS XX\r\nWHERE TRUE\r\n&KONDISI\r\n\r\n \r\n\r\n', 2649, 4013, 14, NULL, 0, '2011-08-09 14:13:11', '2011-08-09 14:13:11', 0, NULL, NULL, NULL, ':11', 3, NULL),
      ('SELECT \r\na.NOTA_PENJUALAN_ID,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\na.NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\nd.DOCUMENT_REFF_NO,\r\na.PERIOD_NAME,\r\na.TOTAL_AMOUNT,\r\na.TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\na.PPN_PERCEN,\r\na.PPN_AMOUNT,\r\na.PRINT_COUNT,\r\na.KARYAWAN_ID,\r\na.PPN_CODE,\r\na.JTEMPO,\r\nc.PERSON_NAME,\r\nSITE_NAME, \r\nv.DISPLAY_NAME as STATUS_DOC,\r\nw.WAREHOUSE_NAME,\r\na.TOTAL_NET,\r\ncs.ADDRESS1,\r\na.TOTAL_BYR1,\r\na.TOTAL_BYR2\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nLEFT JOIN INVENTORY_OUT d on (d.INVENTORY_OUT_ID=a.INVENTORY_OUT_ID)\r\nWHERE (a.DOCUMENT_TYPE_ID<5) and\r\n(a.CREATED_BY = :CREATED_BY )\r\n&KONDISI\r\nGROUP BY\r\na.NOTA_PENJUALAN_ID,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\na.NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\nd.DOCUMENT_REFF_NO,\r\na.PERIOD_NAME,\r\na.TOTAL_AMOUNT,\r\na.TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\na.PPN_PERCEN,\r\na.PPN_AMOUNT,\r\na.PRINT_COUNT,\r\na.KARYAWAN_ID,\r\na.PPN_CODE,\r\na.JTEMPO,\r\nc.PERSON_NAME,\r\nSITE_NAME, \r\nv.DISPLAY_NAME,\r\nw.WAREHOUSE_NAME  \r\nORDER BY\r\na.DOCUMENT_NO\r\n', 2657, 3311, 0, NULL, 4, '2011-09-21 09:11:00', '2011-09-21 09:11:00', 0, NULL, NULL, NULL, NULL, 9, 1),
      ('SELECT \r\nb.ENTERED_QTY,\r\nb.BASE_QTY,\r\nb.UNIT_PRICE,\r\nb.DISCOUNT_PRICE,\r\nb.DISCOUNT_PRICE1,\r\nb.DISCOUNT_PERCEN,\r\nb.SUBTOTAL,\r\nb.NOTE,\r\nb.HPP,\r\nb.ENTERED_UOM,\r\nb.NOTA_PENJUALAN_ID,\r\nb.ITEM_DESCRIPTION,\r\ni.ITEM_CODE,\r\nc.DOCUMENT_NO\r\nFROM\r\nnota_penjualan_detail b\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nLEFT JOIN INVENTORY_OUT c ON  b.INVENTORY_OUT_ID=c.INVENTORY_OUT_ID\r\nWHERE\r\nb.NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID\r\n&KONDISI\r\nORDER BY b.NOTA_PENJUALAN_DETAIL_ID\r\n', 2658, 3311, 3, NULL, 4, '2011-09-21 09:11:00', '2008-10-18 13:53:48', 0, NULL, NULL, NULL, NULL, 0, 1),
