      ('SELECT a.DOCUMENT_NO, a.DOCUMENT_DATE, p.PERSON_CODE, nl.DOCUMENT_NO as DOCUMENT_NO_PO,\r\na.DOCUMENT_REFF_NO, a.TOTAL_DISCOUNT_PERCEN, a.TOTAL_DISCOUNT, #a.PPN_CODE,\r\na.NOTE, i.ITEM_CODE, b.ENTERED_QTY, b.ENTERED_UOM,\r\nb.UNIT_PRICE, b.DISCOUNT_PRICE, b.DISCOUNT_PERCEN, b.NOTE as Note_detail\r\nfrom inventory_in a \r\ninner join inventory_in_detail b on(a.INVENTORY_IN_ID = b.INVENTORY_IN_ID)\r\ninner join po nl on(a.PO_ID = nl.PO_ID)\r\ninner join person p on(a.PERSON_ID = p.PERSON_ID)\r\ninner join item i on(b.ITEM_ID = i.ITEM_ID)\r\nwhere true\r\n&Kondisi\r\nORDER BY a.DOCUMENT_NO, a.DOCUMENT_DATE', 2666, 29, 0, NULL, 4, '2008-06-16 12:48:56', '2013-09-05 11:23:44', 0, NULL, NULL, NULL, '', 77, NULL),
