      ('SELECT \r\n     a.DOCUMENT_NO,\r\n     a.DOCUMENT_REFF_NO,\r\n     a.DOCUMENT_DATE,\r\n     a.TOTAL_NET,\r\n     (SELECT SUM(SUBTOTAL) FROM NOTA_PENJUALAN_DETAIL WHERE NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID AND NOTA_PENJUALAN_PRE_ID=:NOTA_PENJUALAN_ID  ) as UM\r\n\r\n\r\nFROM \r\n     NOTA_PENJUALAN_DETAIL b\r\nLEFT JOIN NOTA_PENJUALAN a ON a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID\r\n\r\nWHERE\r\n   b.NOTA_PENJUALAN_PRE_ID=:NOTA_PENJUALAN_ID\r\n\r\n&KONDISI\r\n\r\nORDER BY a.DOCUMENT_DATE', 2884, 4011, 3, NULL, 1, '2007-08-16 12:27:22', '2007-11-22 16:22:21', 0, NULL, NULL, NULL, NULL, 0, NULL),
      ('SELECT * FROM \r\n(\r\nSELECT c.PERSON_id,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) < 0, (TOTAL )  ,0)) AS BLM,\r\nSUM(IF(DATEDIFF(:12, JTEMPO)  BETWEEN 0 and 30, (TOTAL ) ,0)) AS 1BLN,\r\nSUM(IF(DATEDIFF(:12 , JTEMPO) BETWEEN 31 and 60, (TOTAL ) ,0)) AS 2BLN,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) BETWEEN 61 and 90, (TOTAL ) ,0)) AS 3BLN,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) > 91, (TOTAL ) ,0)) AS 3BLNLEBIH,\r\nSUM(TOTAL) AS TOTAL, \r\nc.PERSON_NAME, \r\nc.PERSON_CODE\r\nFROM\r\nPERSON c \r\nINNER JOIN \r\n( \r\n/* PENJUALAN DAN PELUNASAN */\r\n(SELECT a.PERSON_ID, (TOTAL_NET - IFNULL(BKM_JUAL,0) + IFNULL(BKK_JUAL,0))  AS TOTAL  ,  JTEMPO\r\nFROM nota_penjualan  a \r\nINNER JOIN NOTA_PENJUALAN_AGING z on (a.NOTA_PENJUALAN_ID = z.NOTA_PENJUALAN_ID) AND (JUAL_FLAG = \'Y\')\r\nLEFT JOIN \r\n(  SELECT y.NOTA_PENJUALAN_AGING_ID ,\r\nSUM(AMOUNT +DISCOUNT) AS BKM_JUAL\r\nFROM \r\nreceipt x \r\nINNER JOIN RECEIPT_DETAIL y on  (x.receipt_id = y.receipt_id)\r\nWHERE \r\n( date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY y.NOTA_PENJUALAN_AGING_ID \r\n) AS REC ON (z.NOTA_PENJUALAN_AGING_ID = REC.NOTA_PENJUALAN_AGING_ID) \r\nLEFT JOIN \r\n(\r\nSELECT pd.NOTA_PENJUALAN_AGING_ID ,\r\nSUM(AMOUNT + DISCOUNT ) AS BKK_JUAL\r\nFROM \r\nPAYMENT P\r\nINNER JOIN PAYMENT_DETAIL PD on  (P.PAYMENT_id = Pd.PAYMENT_id)\r\nWHERE \r\n( date_format(P.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY Pd.NOTA_PENJUALAN_AGING_ID \r\n) AS PAY ON (z.NOTA_PENJUALAN_AGING_ID = PAY.NOTA_PENJUALAN_AGING_ID) \r\nWHERE \r\n  (a.NET_BYR1 = 0) AND \r\n(date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :12 )\r\n)\r\nUNION  ALL \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT A.PERSON_ID, (G.DUE_AMOUNT  - IFNULL(BKM_JUAL,0) )  AS TOTAL  , A.DUE_DATE\r\nFROM \r\nPERSON_SALDO A\r\nINNER JOIN PERSON P ON (A.PERSON_ID = P.PERSON_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING G ON (A.PERSON_SALDO_ID = G.PERSON_SALDO_ID  )\r\nLEFT JOIN \r\n(  SELECT y.NOTA_PENJUALAN_AGING_ID ,\r\nSUM(AMOUNT + DISCOUNT) AS BKM_JUAL \r\nFROM \r\nreceipt x \r\nINNER JOIN RECEIPT_DETAIL y on (x.receipt_id = y.receipt_id)\r\nWHERE \r\n( date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY y.NOTA_PENJUALAN_AGING_ID \r\n) AS REC1 ON (G.NOTA_PENJUALAN_AGING_ID = REC1.NOTA_PENJUALAN_AGING_ID) \r\nWHERE p.FLAG_SUPP <> 1\r\n)\r\nUNION ALL\r\n/* SO DAN PELUNASAN */\r\n(SELECT a.PERSON_ID, (UANG_MUKA - IFNULL(BKM_JUAL,0) + IFNULL(BKK_JUAL,0))  AS TOTAL  ,  JTEMPO\r\nFROM SO  a \r\nINNER JOIN NOTA_PENJUALAN_AGING z on (a.SO_ID = z.NOTA_PENJUALAN_ID) AND (JUAL_FLAG = \'N\')\r\nLEFT JOIN \r\n(  SELECT y.NOTA_PENJUALAN_AGING_ID ,\r\nSUM(AMOUNT +DISCOUNT) AS BKM_JUAL\r\nFROM \r\nreceipt x \r\nINNER JOIN RECEIPT_DETAIL y on  (x.receipt_id = y.receipt_id)\r\nWHERE \r\n( date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY y.NOTA_PENJUALAN_AGING_ID \r\n) AS REC ON (z.NOTA_PENJUALAN_AGING_ID = REC.NOTA_PENJUALAN_AGING_ID) \r\nLEFT JOIN \r\n(\r\nSELECT pd.NOTA_PENJUALAN_AGING_ID ,\r\nSUM(AMOUNT + DISCOUNT ) AS BKK_JUAL\r\nFROM \r\nPAYMENT P\r\nINNER JOIN PAYMENT_DETAIL PD on  (P.PAYMENT_id = Pd.PAYMENT_id)\r\nWHERE \r\n( date_format(P.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY Pd.NOTA_PENJUALAN_AGING_ID \r\n) AS PAY ON (z.NOTA_PENJUALAN_AGING_ID = PAY.NOTA_PENJUALAN_AGING_ID) \r\nWHERE \r\n(a.UANG_MUKA > 0 ) AND\r\n(date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :12 )\r\n)\r\n) as J  ON (C.PERSON_ID = J.PERSON_ID ) \r\nWHERE     RIGHT(TIPE,1) = 1 \r\n&KONDISI\r\nGROUP BY   c.PERSON_NAME, c.PERSON_CODE\r\nORDER BY  c.PERSON_NAME \r\n) AS XX WHERE TOTAL <> 0 \r\n', 2907, 840, 0, NULL, 4, '2014-07-17 12:38:05', '2014-07-17 12:50:14', 0, NULL, NULL, '&X1= AND (DUE_DATE <= :11)', ':12', 77, 1),
      ('SELECT * FROM \r\n(\r\nSELECT c.PERSON_id,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) < 0, (TOTAL )  ,0)) AS BLM,\r\nSUM(IF(DATEDIFF(:12, JTEMPO)  BETWEEN 0 and 30, (TOTAL ) ,0)) AS 1BLN,\r\nSUM(IF(DATEDIFF(:12 , JTEMPO) BETWEEN 31 and 60, (TOTAL ) ,0)) AS 2BLN,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) BETWEEN 61 and 90, (TOTAL ) ,0)) AS 3BLN,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) > 91, (TOTAL ) ,0)) AS 3BLNLEBIH,\r\nSUM(TOTAL) AS TOTAL, \r\nc.PERSON_NAME, \r\nc.PERSON_CODE\r\nFROM\r\nPERSON c \r\nINNER JOIN \r\n( \r\n/* PEMBELIAN DAN PELUNASAN */\r\n(SELECT a.PERSON_ID, (TOTAL_NET - IFNULL(BKK_BELI,0) + IFNULL(BKM_BELI,0))  AS TOTAL  ,  JTEMPO\r\nFROM INVOICE  a \r\nINNER JOIN INVOICE_DUE z on (a.INVOICE_ID = z.INVOICE_ID) AND (INVOICE_FLAG = \'Y\')\r\nLEFT JOIN \r\n(\r\n/* BKK */\r\nSELECT pd.INVOICE_DUE_ID ,\r\nSUM(AMOUNT + DISCOUNT ) AS BKK_BELI\r\nFROM \r\nPAYMENT P\r\nINNER JOIN PAYMENT_DETAIL PD on  (P.PAYMENT_id = Pd.PAYMENT_id)\r\nWHERE \r\n( date_format(P.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY Pd.INVOICE_DUE_ID \r\n) AS PAY ON (z.INVOICE_DUE_ID = PAY.INVOICE_DUE_ID) \r\nLEFT JOIN \r\n/* BKM */ \r\n(  SELECT y.INVOICE_DUE_ID ,\r\nSUM(AMOUNT +DISCOUNT) AS BKM_BELI\r\nFROM \r\nreceipt x \r\nINNER JOIN RECEIPT_DETAIL y on  (x.receipt_id = y.receipt_id)\r\nWHERE \r\n( date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY y.INVOICE_DUE_ID \r\n) AS REC ON (z.INVOICE_DUE_ID = REC.INVOICE_DUE_ID) \r\nWHERE \r\n(a.DOCUMENT_TYPE_ID NOT IN (3,4) ) AND \r\n(date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :12 )\r\n)\r\nUNION  ALL \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT A.PERSON_ID, (G.DUE_AMOUNT  - IFNULL(BKK_BELI,0) )  AS TOTAL  , A.DUE_DATE\r\nFROM \r\nPERSON_SALDO A\r\nINNER JOIN PERSON P ON (A.PERSON_ID = P.PERSON_ID)\r\nINNER JOIN INVOICE_DUE G ON (A.PERSON_SALDO_ID = G.PERSON_SALDO_ID  )\r\nLEFT JOIN \r\n(  SELECT y.INVOICE_DUE_ID ,\r\nSUM(AMOUNT + DISCOUNT) AS BKK_BELI \r\nFROM \r\nPAYMENT x \r\nINNER JOIN PAYMENT_DETAIL y on (x.PAYMENT_id = y.PAYMENT_id)\r\nWHERE \r\n( date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY y.INVOICE_DUE_ID \r\n) AS PAY1 ON (G.INVOICE_DUE_ID = PAY1.INVOICE_DUE_ID) \r\nWHERE (FLAG_SUPP = 1)\r\n)\r\nUNION ALL\r\n/* PO DAN PELUNASAN */\r\n(SELECT a.PERSON_ID, (UANG_MUKA -  IFNULL(BKK_BELI,0) + IFNULL(BKM_BELI,0))  AS TOTAL  ,  JTEMPO\r\nFROM PO  a \r\nINNER JOIN INVOICE_DUE z on (a.PO_ID = z.INVOICE_ID) AND (INVOICE_FLAG = \'N\')\r\nLEFT JOIN \r\n(\r\nSELECT pd.INVOICE_DUE_ID ,\r\nSUM(AMOUNT + DISCOUNT ) AS BKK_BELI\r\nFROM \r\nPAYMENT P\r\nINNER JOIN PAYMENT_DETAIL PD on  (P.PAYMENT_id = Pd.PAYMENT_id)\r\nWHERE \r\n( date_format(P.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY Pd.INVOICE_DUE_ID \r\n) AS PAY ON (z.INVOICE_DUE_ID = PAY.INVOICE_DUE_ID) \r\nLEFT JOIN \r\n(  SELECT y.INVOICE_DUE_ID ,\r\nSUM(AMOUNT +DISCOUNT) AS BKM_BELI\r\nFROM \r\nreceipt x \r\nINNER JOIN RECEIPT_DETAIL y on  (x.receipt_id = y.receipt_id)\r\nWHERE \r\n( date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY y.INVOICE_DUE_ID \r\n) AS REC ON (z.INVOICE_DUE_ID = REC.INVOICE_DUE_ID) \r\nWHERE \r\n(a.UANG_MUKA > 0 ) AND\r\n(date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :12 )\r\n)\r\n) as J  ON (C.PERSON_ID = J.PERSON_ID ) \r\nWHERE      ( left(c.TIPE,1) = 1 ) \r\n&KONDISI\r\nGROUP BY   c.PERSON_NAME, c.PERSON_CODE\r\nORDER BY  c.PERSON_NAME \r\n) AS XX WHERE TOTAL <> 0 \r\n', 2909, 1404, 0, NULL, 4, '2008-06-16 12:48:56', '2014-07-17 13:11:24', 0, NULL, NULL, NULL, ':12', 77, 1),
      ('SELECT \r\nP.PERSON_NAME,\r\nP.PERSON_ID,\r\nP.PERSON_CODE,\r\nSUM(SALDO) AS SALDO,\r\nSUM(DEBET) as DEBET,\r\nSUM(KREDIT) as KREDIT \r\nFROM\r\nPERSON P\r\nINNER JOIN \r\n(\r\nSQL1\r\n)\r\nAS XX ON (P.PERSON_ID =  XX.PERSON_ID)\r\nWHERE  RIGHT(TIPE,1) = 1 \r\n&KONDISI\r\nGROUP BY PERSON_NAME,   PERSON_CODE,PERSON_id\r\n', 2915, 1288, 0, '74', 4, '2008-06-16 12:48:56', '2014-04-12 12:23:17', 0, 74, 'SQL1', '&X1= AND (A.PERIOD_NAME = :11)\r\n&T1= AND (A.PERIOD_NAME BETWEEN :11 AND  :21)', ':11|:21', 77, 1),
      ('SELECT * FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\nk.PERSON_CODE,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS BELI,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKK,\r\n0  AS BAYAR_BKM,\r\n0 RETUR_BELI\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nINVOICE_DUE s\r\nINNER JOIN PAYMENT_DETAIL y on (s.INVOICE_DUE_ID = y.INVOICE_DUE_ID)\r\nINNER JOIN PAYMENT x on (x.PAYMENT_id = y.PAYMENT_id) and (date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE \r\nA.PERSON_ID = :PERSON_ID\r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PEMBELIAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\nk.PERSON_CODE,\r\n0 AS AWAL,\r\na.TOTAL_NET  AS BELI,\r\nIFNULL(pay.bayar,0) +    IFNULL(PAY_RET.BAYAR_RET,0)  AS BAYAR_BKK,   \r\nIFNULL(rec.bayar,0) +    IFNULL(REC_RET.BAYAR_RET,0)   AS BAYAR_BKM,\r\nIFNULL( c.retur,0) RETUR_BELI\r\nFROM\r\nINVOICE a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nINNER JOIN INVOICE_DUE g ON (a.INVOICE_ID  = g.INVOICE_ID)  AND (INVOICE_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.INVOICE_DUE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\ninner join invoice_due id on(y.INVOICE_DUE_ID = id.INVOICE_DUE_ID)\r\ninner join invoice iv on(id.invoice_id = iv.invoice_id)\r\nWHERE \r\nz.PERSON_ID = :PERSON_ID AND\r\ndate_format(z.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12\r\nand iv.document_type_id <> 5\r\nGROUP BY\r\ny.INVOICE_DUE_ID\r\n) AS rec ON (g.INVOICE_DUE_ID = rec.INVOICE_DUE_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.INVOICE_DUE_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\ninner join invoice_due id on(pd.INVOICE_DUE_ID = id.INVOICE_DUE_ID)\r\ninner join invoice iv on(id.invoice_id = iv.invoice_id)\r\nWHERE \r\np.PERSON_ID = :PERSON_ID AND\r\n(date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12)\r\nand (pd.INVOICE_DUE_ID IS NOT NULL)\r\nand iv.document_type_id <> 5\r\nGROUP BY pd.INVOICE_DUE_ID\r\n) AS pay ON (g.INVOICE_DUE_ID = pay.INVOICE_DUE_ID)\r\nLEFT JOIN \r\n/* RETUR REFER KE NO PEMBELIAN */ \r\n(SELECT a.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(a.TOTAL_NET) * -1 as RETUR\r\nFROM\r\nINVOICE a\r\nWHERE\r\nA.PERSON_ID = :PERSON_ID AND\r\n(DOCUMENT_TYPE_ID  = 5 )\r\nAND  (a.INVOICE_OLD_ID IS NOT NULL )\r\nAND   (date_format(DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )&kondisi\r\nGROUP BY a.INVOICE_OLD_ID\r\n)  AS c on (a.INVOICE_id = c.INVOICE_id)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR */ \r\n(SELECT r.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nPAYMENT z        \r\nINNER JOIN PAYMENT_DETAIL y ON (z.PAYMENT_ID = y.PAYMENT_ID)\r\nINNER JOIN INVOICE_DUE n ON (y.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\nINNER JOIN INVOICE r ON (n.INVOICE_ID = r.INVOICE_ID)    \r\nWHERE \r\nz.PERSON_ID =  :PERSON_ID AND\r\n(date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :12)  \r\nGROUP BY\r\nr.INVOICE_old_ID        \r\n) AS PAY_RET ON (a.INVOICE_ID = PAY_RET.INVOICE_ID)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR BKM*/ \r\n(SELECT r.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nINNER JOIN INVOICE_DUE n ON (y.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\nINNER JOIN INVOICE r ON (n.INVOICE_ID = r.INVOICE_ID)    \r\nWHERE \r\nz.PERSON_ID = :PERSON_ID AND\r\n(date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :12)       \r\nGROUP 