      ('SELECT\r\n@no := @no + 1 as Nmr,\r\n period_NAME AS PERIOD,\r\nt.item_id,\r\nt.item_code,\r\nt.item_description,\r\n ifnull(iuom.UOM_CODE, t.UOM_CODE)  as uom_code,\r\nb.qty_awal / ifnull(to_qty,1) as qty_awal\r\nfrom  item t \r\nLeft join \r\nitem_balance b on (b.item_id = t.item_id) &Kondisi\r\n  left join item_uom iuom on t.item_id = iuom.item_id  and iuom.BASE_UOM_FLAG = \'Y\' \r\nwhere TRUE \r\n&Kondisi\r\ngroup by t.item_id\r\n', 3012, 2021, 0, NULL, 130, '2008-05-12 10:24:37', '2015-03-23 10:09:35', 0, NULL, NULL, NULL, '', 77, NULL),
