      ('SELECT * FROM \r\n( \r\nSELECT\r\n   ITEM_DESCRIPTION, ITEM_CODE,\r\n   ifnull(iuom.UOM_CODE, i.UOM_CODE)  AS SATUAN,\r\n   SUM(SALDO) / ifnull(to_qty,1) AS SALDO,\r\n   SUM(SALDO_RP) AS SALDO_RP,\r\n   SUM(MASUK) / ifnull(to_qty,1)  AS MASUK,\r\n   SUM(MASUK_RP) AS MASUK_RP,\r\n   SUM(KELUAR) / ifnull(to_qty,1)  AS KELUAR,\r\n   SUM(KELUAR_RP) AS KELUAR_RP,\r\n   SUM(BAHAN) / ifnull(to_qty,1)  AS BAHAN,\r\n   SUM(BAHAN_RP) AS BAHAN_RP,\r\n   SUM(JADI) / ifnull(to_qty,1)  AS JADI, \r\n   SUM(JADI_RP) AS JADI_RP,\r\n     SUM(ADJ) / ifnull(to_qty,1)  AS ADJ, \r\n   SUM(ADJ_RP) AS ADJ_RP\r\n FROM \r\n     ( SQL1)\r\nAS XX\r\n   INNER JOIN ITEM i ON (XX.ITEM_ID = i.ITEM_ID)\r\n  left join item_uom iuom on i.item_id = iuom.item_id  and iuom.BASE_UOM_FLAG = \'Y\' \r\nWHERE\r\nTRUE\r\n  &KONDISI\r\nGROUP BY\r\n  XX.ITEM_ID\r\nORDER BY ITEM_CODE\r\n) AS YY WHERE \r\n(ABS(SALDO) + ABS(KELUAR)+    ABS(BAHAN)+  ABS(JADI)+  ABS(ADJ)+  ABS( MASUK ) > 0 )\r\nOR \r\n(ABS(SALDO_RP)+ABS(MASUK_RP)+ABS(KELUAR_RP)+  ABS( BAHAN_RP)+ABS(JADI_RP)+  ABS(ADJ_RP) > 0 )\r\n', 3016, 4001, 0, '71', 4, '2016-08-01 11:01:15', '2015-03-23 10:21:05', 0, 71, 'SQL1', '&X1= AND  (i.GROUP_ID = :GROUP_ID)\r\n&X2= AND  (i.GROUP_ID = :GROUP_ID) \r\n&X3= AND (i.GROUP_ID = :GROUP_ID)  \r\n&X4= AND (i.GROUP_ID = :GROUP_ID)  \r\n&Y1= and (A.period_name = :PERIOD_NAME1)', ':PERIOD_NAME1', 9, 8),
