      ('SELECT * FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\nk.PERSON_CODE,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS BELI,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKK,\r\n0  AS BAYAR_BKM,\r\n0 RETUR_BELI\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT) AS AMOUNT \r\nFROM \r\nINVOICE_DUE s\r\nINNER JOIN PAYMENT_DETAIL y on (s.INVOICE_DUE_ID = y.INVOICE_DUE_ID)\r\nINNER JOIN PAYMENT x on (x.PAYMENT_id = y.PAYMENT_id) and (date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE   ( left(TIPE,1) = 1 ) \r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PEMBELIAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\nk.PERSON_CODE,\r\n0 AS AWAL,\r\na.TOTAL_NET  AS BELI,\r\nIFNULL(pay.bayar,0)+ IFNULL(PAY_RET.bayar_RET,0)    AS BAYAR_BKK,   \r\nIFNULL(rec.bayar,0)+ IFNULL(REC_RET.bayar_RET,0)    AS BAYAR_BKM,\r\nIFNULL( c.retur,0) RETUR_BELI\r\nFROM\r\nINVOICE a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nINNER JOIN INVOICE_DUE g ON (a.INVOICE_ID  = g.INVOICE_ID)  AND (INVOICE_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.INVOICE_DUE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12\r\nGROUP BY\r\ny.INVOICE_DUE_ID\r\n) AS rec ON (g.INVOICE_DUE_ID = rec.INVOICE_DUE_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.INVOICE_DUE_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nWHERE (date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12)\r\nand (pd.INVOICE_DUE_ID IS NOT NULL)\r\nGROUP BY pd.INVOICE_DUE_ID\r\n) AS pay ON (g.INVOICE_DUE_ID = pay.INVOICE_DUE_ID)\r\nLEFT JOIN \r\n/* RETUR REFER KE NO PEMBELIAN */ \r\n(SELECT a.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(a.TOTAL_NET) * -1 as RETUR\r\nFROM\r\nINVOICE a\r\nWHERE\r\n(DOCUMENT_TYPE_ID  = 5 )\r\nAND  (a.INVOICE_OLD_ID IS NOT NULL )\r\nAND   (date_format(DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY a.INVOICE_OLD_ID\r\n)  AS c on (a.INVOICE_id = c.INVOICE_id)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR */ \r\n(SELECT r.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nPAYMENT z        \r\nINNER JOIN PAYMENT_DETAIL y ON (z.PAYMENT_ID = y.PAYMENT_ID)\r\nINNER JOIN INVOICE_DUE n ON (y.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\nINNER JOIN INVOICE r ON (n.INVOICE_ID = r.INVOICE_ID)    \r\nWHERE (date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :12)  \r\nGROUP BY\r\nn.INVOICE_ID        \r\n) AS PAY_RET ON (a.INVOICE_ID = PAY_RET.INVOICE_ID)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR BKM*/ \r\n(SELECT r.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nINNER JOIN INVOICE_DUE n ON (y.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\nINNER JOIN INVOICE r ON (n.INVOICE_ID = r.INVOICE_ID)    \r\nWHERE (date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :12)  \r\nGROUP BY\r\nn.INVOICE_ID        \r\n) AS rec_ret ON (a.INVOICE_ID = rec_ret.INVOICE_ID)\r\nWHERE \r\n(\r\n(a.DOCUMENT_TYPE_ID   <  5)  OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   = 5) AND (a.INVOICE_OLD_ID IS NULL)  \r\n)\r\n)\r\n&kondisi\r\nAND (date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\n/* end PEMBELIAN */\r\n) as yy\r\nWHERE  \r\n(\r\n(YY.AWAL +YY.BELI  + bayar_BKM - bayar_BKK - RETUR_BELI) <> 0\r\n) \r\nORDER BY DOCUMENT_DATE\r\n', 3043, 470, 0, NULL, 4, '2008-06-16 12:48:56', '2014-08-21 14:49:01', 0, NULL, '', '&X1=  AND