      ('SELECT\r\ngab.item_description,\r\ngab.item_code,\r\ngab.PERSON_name,\r\nmax(gab.entered_uom) as Entered_uom,\r\nsum(gab.total_barang_bulan_lalu) as T_B_bulan_lalu,\r\nsum(gab.total_nilai_bulan_lalu) as T_N_bulan_lalu,\r\nsum(gab.total_barang_bulan_sekarang) as T_B_bulan_sekarang,\r\nsum(gab.total_nilai_bulan_sekarang) as T_N_bulan_sekarang\r\nfrom\r\n(\r\nSELECT\r\nnd.item_description,\r\nt.item_code,\r\ncs.PERSON_name,\r\nmax(nd.entered_uom) as Entered_uom,\r\nsum(nd.entered_qty) as total_barang_bulan_lalu,\r\nsum(nd.subtotal - nd.discount_price1)  as total_nilai_bulan_lalu,\r\n0 as total_barang_bulan_sekarang,\r\n0 as total_nilai_bulan_sekarang\r\nfrom\r\nPERSON cs\r\ninner join nota_penjualan n on(cs.PERSON_id=n.PERSON_id)\r\ninner join nota_penjualan_detail nd on (n.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join item t on (nd.item_id = t.item_id)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nwhere true \r\nand n.period_name<:PERIOD_NAME1\r\nand (n.status_id  <> @delete) \r\nand left(n.period_name,4)=left(:PERIOD_NAME1,4)\r\nand n.PERSON_id=:PERSON_id\r\n&kondisi\r\ngroup by n.PERSON_id,nd.item_id\r\nunion all\r\nSELECT\r\nnd.item_description,\r\nt.item_code,\r\ncs.PERSON_name,\r\nmax(nd.entered_uom) as Entered_uom,\r\n0 as total_barang_bulan_lalu,\r\n0 as total_nilai_bulan_lalu,\r\nsum(nd.entered_qty) as total_barang_bulan_sekarang,\r\nsum(nd.subtotal - nd.discount_price1)  as total_nilai_bulan_sekarang\r\nfrom\r\nPERSON cs\r\ninner join nota_penjualan n on(cs.PERSON_id=n.PERSON_id)\r\ninner join nota_penjualan_detail nd on (n.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join item t on (nd.item_id = t.item_id)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nwhere\r\ntrue\r\nand n.period_name=:PERIOD_NAME1\r\nand (n.status_id  <> @delete) \r\nand n.PERSON_id=:PERSON_id	\r\n&kondisi\r\ngroup by n.PERSON_id,nd.item_id\r\n)\r\nas gab\r\nwhere true\r\ngroup by gab.PERSON_name,gab.item_description, gab.entered_