      ('SELECT *from\r\n(\r\nSELECT\r\nnp.DOCUMENT_NO,\r\nnp.DOCUMENT_DATE,\r\nnp.LAST_UPDATE_BY,\r\ncoa1.COA_NAME,\r\nIF(PPN_AMOUNT <> 0,(TOTAL_BYR2/1.1),TOTAL_BYR2) as TOTAL_AMOUNT,\r\nIF(PPN_AMOUNT <> 0,(TOTAL_BYR2/1.1)*0.1,0)  as PPN_AMOUNT,\r\nIF(PPN_AMOUNT <> 0,(TOTAL_BYR2/1.1)+((TOTAL_BYR2/1.1)*0.1),TOTAL_BYR2) as TOTAL_NET\r\nfrom nota_penjualan np\r\ninner join nota_penjualan_detail npd on(np.NOTA_PENJUALAN_ID = npd.NOTA_PENJUALAN_ID)\r\ninner join erp_user eu on(np.LAST_UPDATE_BY = eu.ERP_USER_ID)\r\ninner join erp_lookup_value elv on(np.STATUS_ID = elv.ERP_LOOKUP_VALUE_ID)\r\ninner join coa coa1 on(np.COA_BYR2 = coa1.COA_ID)\r\nwhere np.UMUKA_FLAG= \'N\'\r\nand elv.PROGRAM_CODE1 = \'full_paid\'\r\nAND np.LAST_UPDATE_BY=:LAST_UPDATE_BY\r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\nSELECT\r\nnp.DOCUMENT_NO,\r\nnp.DOCUMENT_DATE,\r\nnp.LAST_UPDATE_BY,\r\ncoa.COA_NAME,\r\nIF(PPN_AMOUNT <> 0,(NET_BYR1/1.1),NET_BYR1) as TOTAL_AMOUNT ,\r\nIF(PPN_AMOUNT <> 0,(NET_BYR1/1.1)*0.1,0)  as PPN_AMOUNT,\r\nIF(PPN_AMOUNT <> 0,(NET_BYR1/1.1)+((NET_BYR1/1.1)*0.1),NET_BYR1) as TOTAL_NET \r\nfrom nota_penjualan np\r\ninner join nota_penjualan_detail npd on(np.NOTA_PENJUALAN_ID = npd.NOTA_PENJUALAN_ID)\r\ninner join erp_user eu on(np.LAST_UPDATE_BY = eu.ERP_USER_ID)\r\ninner join erp_lookup_value elv on(np.STATUS_ID = elv.ERP_LOOKUP_VALUE_ID)\r\ninner join coa on(np.COA_BYR1 = coa.COA_ID)\r\nwhere np.UMUKA_FLAG= \'N\'\r\nand elv.PROGRAM_CODE1 = \'full_paid\'\r\nAND np.LAST_UPDATE_BY=:LAST_UPDATE_BY\r\n&KONDISI\r\ngroup by np.NOTA_PENJUALAN_ID\r\n)as xx\r\nwhere coa_name=:coa_name\r\ngroup by coa_name', 3072, 707, 3, NULL, 0, '2016-02-20 11:11:05', '2017-01-24 12:35:50', 1, NULL, NULL, NULL, NULL, 0, NULL),
      ('SELECT \r\nn.document_no,\r\nn.document_date, \r\nc.person_name,\r\nn.nota_penjualan_id as id\r\n\r\nfrom \r\nnota_penjualan n \r\ninner join PERSON c on(c.PERSON_id=n.PERSON_id)\r\ninner join nota_penjualan_detail nd on (n.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\nwhere true\r\nand (n.status_id  <> @delete) \r\nand (i.JENIS_ID <> @UM)\r\nand (n.DOCUMENT_TYPE_ID <> @PREPAYMENT)\r\nand n.inventory_out_id is null\r\n\r\n&kondisi\r\ngroup by n.document_no\r\n\r\nunion all\r\n\r\nSELECT \r\nn.document_no,\r\nn.document_date, \r\nc.person_name,\r\nn.inventory_out_id as id\r\n\r\nfrom \r\ninventory_out n \r\ninner join PERSON c on(c.PERSON_id=n.PERSON_id)\r\ninner join inventory_out_detail nd on (n.inventory_out_id=nd.inventory_out_id)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\nwhere true\r\nand (n.status_id  <> @delete) \r\nand (i.JENIS_ID <> @UM)\r\nand (n.DOCUMENT_TYPE_ID <> @PREPAYMENT)\r\n\r\n&kondisi\r\ngroup by n.document_no\r\norder by  1', 3073, 1265, 0, NULL, 37, '2017-04-04 09:29:48', '2017-03-09 10:33:17', 0, NULL, NULL, NULL, '', 77, NULL),
