      ('SELECT* from(\r\nSELECT\r\ni.item_id, \r\ni.item_description,\r\ni.item_code\r\nfrom nota_penjualan_detail nd\r\ninner join nota_penjualan n on (n.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\ninner join person cs on(n.person_id=cs.person_id)\r\nwhere true\r\nand (n.status_id  <> @delete) \r\nand (nd.entered_qty <> 0)\r\nand (i.JENIS_ID <> @UM)\r\nand (n.DOCUMENT_TYPE_ID <> @PREPAYMENT)\r\nand n.inventory_out_id is null\r\n\r\n&KONDISI\r\ngroup by i.item_id\r\nunion all\r\n\r\nSELECT\r\ni.item_id, \r\ni.item_description,\r\ni.item_code\r\nfrom inventory_out_detail nd\r\ninner join inventory_out n on (n.inventory_out_id=nd.inventory_out_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\ninner join person cs on(n.person_id=cs.person_id)\r\nwhere true\r\nand (n.status_id  <> @delete) \r\nand (nd.entered_qty <> 0)\r\nand (i.JENIS_ID <> @UM)\r\nand (n.DOCUMENT_TYPE_ID <> @PREPAYMENT)\r\n\r\n&KONDISI\r\ngroup by i.item_id\r\n)as xx \r\ngroup by item_id\r\norder by 2\r\n', 3077, 1263, 0, NULL, 37, '2008-12-17 11:56:48', '2017-03-09 10:28:54', 0, NULL, NULL, NULL, '', 77, NULL),
      ('SELECT\r\ncs.PERSON_name,\r\ncs.person_code,\r\nsum(nd.entered_qty) as total,\r\nsum(nd.subtotal - nd.DISCOUNT_PRICE1)  as total_rp,\r\nsum(nd.entered_qty  * nd.HPP)  as total_hpp\r\nfrom\r\nPERSON cs\r\ninner join nota_penjualan n on(cs.PERSON_id=n.PERSON_id)\r\ninner join nota_penjualan_detail nd on (n.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nwhere true\r\nand (n.status_id  <> @delete) \r\nand nd.item_id= :item_id\r\nand (n.DOCUMENT_TYPE_ID <> @PREPAYMENT)\r\nand n.inventory_out_id is null\r\n&kondisi\r\ngroup by \r\nnd.item_id,\r\ncs.PERSON_id\r\n\r\nunion all\r\n\r\nSELECT\r\ncs.PERSON_name,\r\ncs.person_code,\r\nsum(nd.entered_qty) as total,\r\nsum(nd.subtotal - nd.DISCOUNT_PRICE1)  as total_rp,\r\nsum(nd.entered_qty  * nd.HPP)  as total_hpp\r\nfrom\r\nPERSON cs\r\ninner join inventory_out n on(cs.PERSON_id=n.PERSON_id)\r\ninner join inventory_out_detail nd on (n.inventory_out_id=nd.inventory_out_id)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nwhere true\r\nand (n.status_id  <> @delete) \r\nand nd.item_id= :item_id\r\nand (n.DOCUMENT_TYPE_ID <> @PREPAYMENT)\r\n&kondisi\r\ngroup by \r\nnd.item_id,\r\ncs.PERSON_id\r\norder by 1', 3078, 1263, 3, NULL, 4, '2008-12-17 11:56:48', '2017-03-09 10:29:21', 0, NULL, NULL, NULL, '', 0, NULL),
      ('SELECT DISTINCT * FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS JUAL,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKM,\r\n0  AS BAYAR_BKK,\r\n0 RETUR_JUAL,\r\nkk.FIRST_NAME,\r\nk.limit_piutang\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN KARYAWAN kk ON k.KARYAWAN_ID=kk.KARYAWAN_ID\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nNOTA_PENJUALAN_AGING s\r\nINNER JOIN RECEIPT_DETAIL y on (s.Nota_Penjualan_aging_id = y.Nota_Penjualan_aging_id)\r\nINNER JOIN receipt x on (x.receipt_id = y.receipt_id) and (date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :13 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE a.PERSON_ID  = :PERSON_ID\r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\n0 AS AWAL,\r\na.TOTAL_NET  AS JUAL,\r\nIFNULL(rec.bayar,0)    +    IFNULL(rec_ret.bayar_ret,0)   AS BAYAR_BKM,\r\nIFNULL(pay.bayar,0)  +   IFNULL(pay_ret.bayar_ret,0)  AS BAYAR_BKK,   \r\nIFNULL( c.retur,0) RETUR_JUAL,\r\nkk.FIRST_NAME,\r\nk.limit_piutang\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN KARYAWAN kk ON a.KARYAWAN_ID=kk.KARYAWAN_ID\r\nINNER JOIN NOTA_PENJUALAN_AGING g ON (a.NOTA_PENJUALAN_ID  = g.NOTA_PENJUALAN_ID)  AND (JUAL_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.NOTA_PENJUALAN_AGING_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE(date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\'))   <= :13\r\nAND (z.PERSON_ID  = :PERSON_ID)\r\nGROUP BY\r\ny.NOTA_PENJUALAN_AGING_ID\r\n) AS rec ON (g.NOTA_PENJUALAN_AGING_ID = rec.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.NOTA_PENJUALAN_AGING_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nWHERE (date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\')<= :13)\r\nAND (p.PERSON_ID  = :PERSON_ID)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY pd.NOTA_PENJUALAN_AGING_ID\r\n) AS pay ON (g.NOTA_PENJUALAN_AGING_ID = pay.NOTA_PENJUALAN_AGING_ID)\r\n    LEFT JOIN \r\n   /* RETUR REFER KE NO PENJUALAN */ \r\n    (SELECT a.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\n       SUM(a.TOTAL_NET) * -1 as RETUR\r\n        FROM\r\n          nota_penjualan a\r\n        WHERE\r\n        (DOCUMENT_TYPE_ID  >= 5 )\r\n    AND (a.PERSON_ID  = :PERSON_ID)\r\n    AND  (a.NOTA_PENJUALAN_OLD_ID IS NOT NULL )\r\n    AND   (date_format(DOCUMENT_DATE, \'%Y-%m-%d\') <= :13 )&kondisi\r\n    GROUP BY a.NOTA_PENJUALAN_OLD_ID\r\n       )  AS c on (a.nota_penjualan_id = c.nota_penjualan_id)\r\n left join\r\n/* PELUNASAN NYA RETUR */ \r\n      (SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\n      SUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\n       FROM\r\n         RECEIPT z        \r\n         INNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\n         INNER JOIN NOTA_PENJUALAN_AGING n ON (y.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\n         INNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\n     WHERE (date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :13)  \r\n    AND (z.PERSON_ID  = :PERSON_ID)\r\n     GROUP BY\r\n        r.NOTA_PENJUALAN_OLD_ID        \r\n      ) AS rec_ret ON (a.NOTA_PENJUALAN_ID = rec_ret.NOTA_PENJUALAN_ID)\r\n   left join\r\n\r\n  /* PELUNASAN RETUR DGN BKK */ \r\n   (SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\n      SUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR_RET\r\n       FROM\r\n         PAYMENT p\r\n         INNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\n         INNER JOIN NOTA_PENJUALAN_AGING n ON (pd.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\n         INNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\n     WHERE (date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\')<= :13)\r\n    AND (p.PERSON_ID  = :PERSON_ID)\r\n     and (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\n     GROUP BY r.NOTA_PENJUALAN_OLD_ID     \r\n   ) AS pay_ret ON (a.NOTA_PENJUALAN_ID = pay_ret.NOTA_PENJUALAN_ID)\r\n\r\n\r\nWHERE \r\n(a.PERSON_ID  = :PERSON_ID ) AND\r\n(\r\n ((a.NET_BYR1 = 0) and   (a.DOCUMENT_TYPE_ID < 5)) OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   >= 5) AND (a.NOTA_PENJUALAN_OLD_ID IS NULL)  \r\n)\r\n)\r\nAND (date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\')<=  :13 )\r\n&kondisi\r\n/* end penjualan */\r\n) as yy\r\nWHERE \r\n(\r\n(YY.AWAL  + YY.JUAL - Bayar_BKM   + bayar_BKK - RETUR_JUAL) <> 0\r\n) \r\nORDER BY PERSON_NAME, DOCUMENT_DATE\r\n', 3079, 1402, 0, NULL, 4, '2008-06-16 12:48:56', '2017-03-01 15:16:12', 0, NULL, '', NULL, ':13', 9, NULL),
      ('SELECT :13 AS TGL,\r\nPERSON_ID, \r\nPERSON_CODE, \r\nPERSON_NAME\r\nFROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_CODE,\r\nk.PERSON_ID,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS JUAL,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKM,\r\n0  AS BAYAR_BKK,\r\n0 RETUR_JUAL\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nNOTA_PENJUALAN_AGING s\r\nINNER JOIN RECEIPT_DETAIL y on (s.Nota_Penjualan_aging_id = y.Nota_Penjualan_aging_id)\r\nINNER JOIN receipt x on (x.receipt_id = y.receipt_id) and (date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :13 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE  k.FLAG_SUPP <> 1\r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_CODE, \r\nk.PERSON_ID,\r\n0 AS AWAL,\r\na.TOTAL_NET  AS JUAL,\r\nIFNULL(rec.bayar,0)    +    IFNULL(rec_ret.bayar_ret,0)   AS BAYAR_BKM,\r\nIFNULL(pay.bayar,0)   +    IFNULL(pay_ret.bayar_ret,0)  AS BAYAR_BKK,   \r\nIFNULL( c.retur,0) RETUR_JUAL\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING g ON (a.NOTA_PENJUALAN_ID  = g.NOTA_PENJUALAN_ID)  AND (JUAL_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.NOTA_PENJUALAN_AGING_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE (date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')   <= :13)\r\nGROUP BY\r\ny.NOTA_PENJUALAN_AGING_ID\r\n) AS rec ON (g.NOTA_PENJUALAN_AGING_ID = rec.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.NOTA_PENJUALAN_AGING_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nWHERE (date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\')<= :13)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY pd.NOTA_PENJUALAN_AGING_ID\r\n) AS pay ON (g.NOTA_PENJUALAN_AGING_ID = pay.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* RETUR REFER KE NO PENJUALAN */ \r\n(SELECT a.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(a.TOTAL_NET) * -1 as RETUR\r\nFROM\r\nnota_penjualan a\r\nWHERE\r\n(DOCUMENT_TYPE_ID  >= 5 )\r\nAND  (a.NOTA_PENJUALAN_OLD_ID IS NOT NULL )\r\nAND   (date_format(DOCUMENT_DATE, \'%Y-%m-%d\') <= :13 ) \r\nGROUP BY a.NOTA_PENJUALAN_OLD_ID    \r\n)  AS c on (a.nota_penjualan_id = c.nota_penjualan_id)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (y.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :13)  \r\nGROUP BY\r\nr.NOTA_PENJUALAN_OLD_ID        \r\n) AS rec_ret ON (a.NOTA_PENJUALAN_ID = rec_ret.NOTA_PENJUALAN_ID)\r\nleft join\r\n/* PELUNASAN RETUR DGN BKK */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR_RET\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (pd.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\')<= :13)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY n.NOTA_PENJUALAN_ID     \r\n) AS pay_ret ON (a.NOTA_PENJUALAN_ID = pay_ret.