      ('Select P.PERSON_NAME,\r\nP.PERSON_CODE,\r\ncount(P.PERSON_ID) as Frek,\r\nsum(xx.nilai) as Nilai,\r\nsum(xx.laba)  as LABA,\r\nsum(xx.gross)  as GROSS,\r\nsum(xx.diskon)  as DISKON\r\nFrom Nota_penjualan a\r\ninner join \r\n(\r\nSELECT \r\na.nota_penjualan_id,\r\nsum(b.subtotal - discount_price1) as Nilai,\r\nsum(b.subtotal - discount_price1 - (b.entered_qty*b.HPP))  as LABA,\r\nsum(b.entered_qty*b.unit_price)  as GROSS,\r\nsum(b.entered_qty* b.discount_price + discount_price1 )  as DISKON\r\nFrom Nota_penjualan a\r\ninner join nota_Penjualan_Detail b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\ninner join item i on (b.item_id = i.item_id)\r\nwhere document_type_id<> 5\r\n&KONDISI\r\ngroup by nota_penjualan_id\r\n)as xx on a.nota_penjualan_id=xx.nota_penjualan_id\r\ninner join person p on (a.person_id=p.person_id)\r\ngroup by person_name\r\norder by Frek desc limit 10\r\n', 3081, 17103, 0, NULL, 4, '2008-06-16 12:48:56', '2017-02-22 12:26:48', 0, NULL, NULL, NULL, '', 77, NULL),
      ('Select P.PERSON_NAME,\r\nP.PERSON_CODE,\r\ncount(P.PERSON_ID) as Frek,\r\nsum(xx.nilai) as Nilai,\r\nsum(xx.laba)  as LABA,\r\nsum(xx.gross)  as GROSS,\r\nsum(xx.diskon)  as DISKON\r\nFrom Nota_penjualan a\r\ninner join \r\n(\r\nSELECT \r\na.nota_penjualan_id,\r\nsum(b.subtotal - discount_price1) as Nilai,\r\nsum(b.subtotal - discount_price1 - (b.entered_qty*b.HPP))  as LABA,\r\nsum(b.entered_qty*b.unit_price)  as GROSS,\r\nsum(b.entered_qty* b.discount_price + discount_price1 )  as DISKON\r\nFrom Nota_penjualan a\r\ninner join nota_Penjualan_Detail b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\ninner join item i on (b.item_id = i.item_id)\r\nwhere document_type_id<> 5\r\n&KONDISI\r\ngroup by nota_penjualan_id\r\n)as xx on a.nota_penjualan_id=xx.nota_penjualan_id\r\ninner join person p on (a.person_id=p.person_id)\r\ngroup by person_name\r\norder by Nilai desc limit 10\r\n', 3082, 17103, 2, NULL, 0, '2008-06-16 12:48:56', '2017-02-22 12:28:27', 0, NULL, NULL, NULL, NULL, 77, NULL),
      ('Select P.PERSON_NAME,\r\nP.PERSON_CODE,\r\ncount(P.PERSON_ID) as Frek,\r\nsum(xx.nilai) as Nilai,\r\nsum(xx.laba)  as LABA,\r\nsum(xx.gross)  as GROSS,\r\nsum(xx.diskon)  as DISKON\r\nFrom Nota_penjualan a\r\ninner join \r\n(\r\nSELECT \r\na.nota_penjualan_id,\r\nsum(b.subtotal - discount_price1) as Nilai,\r\nsum(b.subtotal - discount_price1 - (b.entered_qty*b.HPP))  as LABA,\r\nsum(b.entered_qty*b.unit_price)  as GROSS,\r\nsum(b.entered_qty* b.discount_price + discount_price1 )  as DISKON\r\nFrom Nota_penjualan a\r\ninner join nota_Penjualan_Detail b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\ninner join item i on (b.item_id = i.item_id)\r\nwhere true\r\nand (a.status_id  <> @delete) \r\nand (i.JENIS_ID <> @UM)\r\nand (a.DOCUMENT_TYPE_ID <> @PREPAYMENT)\r\n&KONDISI\r\ngroup by nota_penjualan_id\r\n)as xx on a.nota_penjualan_id=xx.nota_penjualan_id\r\ninner join person p on (a.person_id=p.person_id)\r\ngroup by person_name\r\norder by LABA desc limit 10', 3083, 17103, 10, NULL, 0, '2008-06-16 12:48:56', '2017-03-01 11:40:41', 0, NULL, NULL, NULL, NULL, 77, NULL),
      ('Select count(P.PERSON_ID) as Frek,\r\nsum(b.subtotal - discount_price1) as Nilai,\r\nsum(b.subtotal - discount_price1 - (b.entered_qty*b.HPP))  as LABA,\r\nsum(b.entered_qty*b.unit_price)  as GROSS,\r\nsum(b.entered_qty* b.discount_price + discount_price1 )  as DISKON\r\nFrom Nota_penjualan a\r\ninner join Nota_Penjualan_Detail b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\ninner join person p on (a.person_id=p.person_id)\r\ninner join item i on (b.item_id = i.item_id)\r\nwhere TRUE\r\n&KONDISI\r\n', 3084, 17103, 9, NULL, 0, '2008-06-16 12:48:56', '2009-08-27 10:40:25', 0, NULL, NULL, NULL, NULL, 77, NULL),
      ('SELECT \r\n	P2.DOCUMENT_DATE, P2.DOCUMENT_NO, C.PERSON_NAME, \r\n	P.AMOUNT  AS BKM,P2.PERIOD_NAME, G.DOCUMENT_NO AS NO_FAKTUR, V.COA_NAME\r\nFROM \r\n	RECEIPT P2\r\n	INNER JOIN RECEIPT_DETAIL P  ON (P2.RECEIPT_ID=P.RECEIPT_ID)\r\n	INNER JOIN PERSON C ON (P2.PERSON_ID = C.PERSON_ID)\r\n	INNER JOIN NOTA_PENJUALAN_AGING G ON (P.NOTA_PENJUALAN_AGING_ID =G.NOTA_PENJUALAN_AGING_ID)\r\n	INNER JOIN COA V ON (P2.BANK_ID =V.COA_ID) \r\n	INNER JOIN ACCOUNT A ON (V.ACCOUNT_ID = A.ACCOUNT_ID)  \r\nWHERE \r\n	P2.PERIOD_NAME = :11 \r\n	/* SUPLIER 10, CUSTOMER 01\r\n	AND A.ACCOUNT_TYPE_ID = @ACC_KASBANK\r\n	AND A.ACCOUNT_TYPE_ID <> @ACC_GIRO */\r\n	&KONDISI\r\n\r\nUNION ALL\r\n\r\nSELECT \r\n	P2.DOCUMENT_DATE, P2.DOCUMENT_NO, C.PERSON_NAME, \r\n	P2.TOTAL_AMOUNT AS BKM,\r\n	P2.PERIOD_NAME, P.DOCUMENT_NO AS NO_FAKTUR, NULL AS COA_NAME\r\nFROM CHEQUE P2\r\n	LEFT JOIN RECEIPT P ON (P2.RECEIPT_ID = P.RECEIPT_ID)\r\n	LEFT JOIN PAYMENT PAY ON (P2.PAYMENT_ID = PAY.PAYMENT_ID)\r\n	LEFT JOIN PERSON C ON (P.PERSON_ID = C.PERSON_ID)\r\nWHERE \r\n	P2.PERIOD_NAME=:11\r\n	AND P2.STATUS_FLAG = \'M\' \r\n	AND P2.STATUS_ID = @CLEAR\r\n	&KONDISI\r\n\r\nUNION ALL \r\n\r\nSELECT \r\n	P2.DOCUMENT_DATE, P2.DOCUMENT_NO, C.PERSON_NAME, \r\n	P.AMOUNT  AS BKM,P2.PERIOD_NAME, G.DOCUMENT_NO AS NO_FAKTUR, V.COA_NAME\r\nFROM \r\n	RECEIPT P2\r\n	INNER JOIN RECEIPT_DETAIL P  ON (P2.RECEIPT_ID=P.RECEIPT_ID)\r\n	INNER JOIN PERSON C ON (P2.PERSON_ID = C.PERSON_ID)\r\n	INNER JOIN INVOICE_DUE G ON (P.INVOICE_DUE_ID =G.INVOICE_DUE_ID)\r\n	INNER JOIN COA V ON (P2.BANK_ID =V.COA_ID) \r\n	INNER JOIN ACCOUNT A ON (V.ACCOUNT_ID = A.ACCOUNT_ID)  \r\nWHERE \r\n	P2.PERIOD_NAME = :11 \r\n	/* SUPLIER 10, CUSTOMER 01\r\n	AND A.ACCOUNT_TYPE_ID = @ACC_KASBANK\r\n	AND A.ACCOUNT_TYPE_ID <> @ACC_GIRO */\r\n	&KONDISI \r\n	\r\nUNION ALL	\r\n	\r\nSELECT \r\n	P2.DOCUMENT_DATE, P2.DOCUMENT_NO,  b.COA_NAME as PERSON_NAME, \r\n	P.AMOUNT*-1   AS BKM,P2.PERIOD_NAME, \'\' AS NO_FAKTUR, x.COA_NAME\r\nFROM \r\n	RECEIPT P2\r\n	INNER JOIN RE