      ('SELECT \r\n  a.STATUS_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.NOTE,\r\n  SUM(b.SUBTOTAL ) AS TOTAL_AMOUNT,\r\n  a.PERSON_ID,\r\n  a.PERSON_SITE_ID,\r\n  a.PAYMENT_TERM_ID,\r\n  a.PERIOD_NAME,\r\n  a.PPN_CODE,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  x.PAID_AMOUNT, \r\n (SUM(b.SUBTOTAL)-a.TOTAL_DISCOUNT+a.PPN_AMOUNT) AS TOTAL_NET,\r\n  a.JTEMPO,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  c.PERSON_NAME,\r\n  cs.SITE_NAME,\r\n  IF((a.TOTAL_NET-x.PAID_AMOUNT)=0,\'LUNAS\',l.DISPLAY_NAME)AS DISPLAY_NAME,\r\n  p.DOCUMENT_NO,\r\n  a.INVOICE_ID,\r\n  w.WAREHOUSE_NAME,\r\n  um.SUBTOTAL*-1 as UANG_MUKA\r\nFROM\r\n  invoice a\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value l ON (a.STATUS_ID = l.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN po p ON (a.PO_ID = p.PO_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID=b.INVOICE_ID)\r\n  INNER JOIN ITEM i ON (b.ITEM_ID=i.ITEM_ID  )\r\n  INNER JOIN INVOICE_DUE x on (a.INVOICE_ID = x.INVOICE_ID)\r\n    LEFT JOIN(\r\n  SELECT \r\n  a.SUBTOTAL,\r\n  a.INVOICE_ID\r\n  FROM INVOICE_DETAIL a\r\n  INNER JOIN INVOICE b ON b.INVOICE_ID=a.INVOICE_PRE_ID\r\n  )AS  UM on a.INVOICE_ID=um.INVOICE_ID\r\nwhere\r\nTrue\r\nAND (a.DOCUMENT_TYPE_ID<>5)  AND b.INVOICE_PRE_ID IS NULL\r\n&KONDISI\r\nGROUP BY \r\n  a.STATUS_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.NOTE,\r\n  a.PERSON_ID,\r\n  a.PERSON_SITE_ID,\r\n  a.PAYMENT_TERM_ID,\r\n  a.PERIOD_NAME,\r\n  a.PPN_CODE,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.JTEMPO,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  c.PERSON_NAME,\r\n  cs.SITE_NAME,\r\n  l.DISPLAY_NAME,\r\n  p.DOCUMENT_NO,\r\n  a.INVOICE_ID,\r\n  w.WAREHOUSE_NAME\r\nORDER BY\r\n  a.DOCUMENT_NO\r\n', 3099, 250, 0, NULL, 4, '2010-03-12 00:32:24', '2016-07-11 14:02:53', 0, NULL, NULL, NULL, NULL, 77, 1),
