      ('SELECT\r\n  SUM(SALDO + DEBET - KREDIT ) AS SALDO\r\n  FROM \r\n( SQL1\r\n) AS A \r\nWHERE TRUE\r\n&KONDISI', 3105, 4000, 0, 'saldo awal', 4, '2009-03-05 15:08:05', '2012-09-13 16:54:49', 0, 76, 'SQL1', '&X1= AND B.COA_ID = :BANK_ID\r\n&X2= AND B.COA_ID = :BANK_ID\r\n&T1= AND (a.DOCUMENT_DATE < :12 ) \r\n&T2= AND (a.TGL_TOLAK< :12 ) \r\n&P1= AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n&P2= AND (p.PERIOD_NAME = :PERIOD_NAME1)', ':12|:PERIOD_NAME1', 9, 1),
      ('SELECT\r\n  URUT, \r\n  TIPE,\r\n  COA_ID,\r\n  COA_CODE,\r\n  COA_NAME,\r\n  SALDO,\r\n  DEBET,\r\n  KREDIT,\r\n  PERSON_ID,\r\n  PERSON_CODE,\r\n  PERSON_NAME,\r\n  DOCUMENT_NO,\r\n  DOCUMENT_REFF_NO,\r\n  DOCUMENT_DATE,\r\n  NOTE,\r\n  PERIOD_NAME,\r\n  check_no,\r\n  no_detail,\r\n  no_reff_detail,\r\n  (DEBET_DETAIL) as DEBET_DETAIL,\r\n  (KREDIT_DETAIL) as KREDIT_DETAIL\r\n  FROM \r\n( \r\n/* \r\nX1 = COA  UTK SALDO AWAL \r\nX2 = COA  UTK TRANSAKSI \r\nP = PERIODE\r\nT = TANGGAL\r\n\r\n*/\r\n\r\n/* BKM*/\r\nSELECT \r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_AMOUNT as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(ad.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no,\r\n  ifnull(n.document_no, ifnull(i.document_no,c.coa_code)) as no_detail,\r\n  ifnull(nn.document_reff_no, ifnull(ii.document_reff_no,c.coa_name)) as no_reff_detail,\r\n  ad.amount as DEBET_DETAIL,\r\n  0 as KREDIT_DETAIL\r\nFROM\r\nRECEIPT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\ninner join receipt_detail ad on (a.receipt_id=ad.receipt_id)\r\nleft join nota_penjualan_aging n on (ad.nota_penjualan_aging_id=n.nota_penjualan_aging_id)\r\nleft join nota_penjualan nn on (n.nota_penjualan_id=nn.nota_penjualan_id)\r\nleft join invoice_due i on (ad.invoice_due_id=i.invoice_due_id)\r\nleft join invoice ii on (i.invoice_id=ii.invoice_id)\r\nleft join coa c on (ad.coa_id=c.coa_id)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.NET_BYR1  as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no,\r\n  a.document_no as no_detail,\r\n  a.document_reff_no as no_reff_detail,\r\n  a.net_byr1 as DEBET_DETAIL,\r\n  0 as KREDIT_DETAIL\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR1 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\n and (a.DOCUMENT_TYPE_ID IN (3,4) )\r\nAND ( a.Status_ID<>@DELETE )\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT \r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_BYR2  as DEBET,\r\n  0 as KREDIT,\r\n a.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no,\r\n  a.document_no as no_detail,\r\n  a.document_reff_no as no_reff_detail,\r\n  a.total_byr2 as DEBET_DETAIL,\r\n  0 as KREDIT_DETAIL\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR2 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n a.DOCUMENT_TYPE_ID IN (3,4) \r\nAND  ( a.TOTAL_BYR2   > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_AMOUNT<>0 )\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* BKK*/\r\nSELECT \r\n  5 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  0 as DEBET,\r\n  a.TOTAL_AMOUNT as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(ad.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no,\r\n  ifnull(n.document_no, ifnull(i.document_no,c.coa_code)) as no_detail,\r\n  ifnull(nn.document_reff_no, ifnull(ii.document_reff_no,c.coa_name)) as no_reff_detail,\r\n  0 as DEBET_DETAIL,\r\n  ad.amount as KREDIT_DETAIL\r\nFROM\r\nPAYMENT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\ninner join payment_detail ad on (a.payment_id=ad.payment_id)\r\nleft join nota_penjualan_aging n on (ad.nota_penjualan_aging_id=n.nota_penjualan_aging_id)\r\nleft join nota_penjualan nn on (n.nota_penjualan_id=nn.nota_penjualan_id)\r\nleft join invoice_due i on (ad.invoice_due_id=i.invoice_due_id)\r\nleft join invoice ii on (i.invoice_id=ii.invoice_id)\r\nleft join coa c on (ad.coa_id=c.coa_id)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* BKK DETAIL YG BANK */\r\nSELECT \r\n  2 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  d.AMOUNT  as DEBET,\r\n  0 as KREDIT,\r\n  0 PERSON_ID,\r\n  \' \'AS PERSON_CODE,\r\n  \'\' ASPERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(x.COA_NAME,\'\'),\' - \', IFNULL(d.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no,\r\n  a.document_no as no_detail,\r\n  a.document_reff_no as no_reff_detail,\r\n  d.amount as DEBET_DETAIL,\r\n  0 as KREDIT_DETAIL\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_DETAIL d on (a.PAYMENT_ID = d.PAYMENT_ID)\r\nINNER JOIN COA b on (d.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nINNER JOIN COA x on (a.BANK_ID=x.COA_ID)\r\nWHERE (c.ACCOUNT_TYPE_ID IN (@ACC_KASBANK ,@ACC_GIRO))\r\nand (a.Status_ID<>@DELETE )\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* BKM YG BANK */\r\nSELECT \r\n  6 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO,\r\n  0 as DEBET,\r\n   d.AMOUNT as KREDIT,\r\n  0 PERSON_ID,\r\n  \' \'AS PERSON_CODE,\r\n  \'\' ASPERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(x.COA_NAME,\'\'),\' - \', IFNULL(d.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no,\r\n  a.document_no as no_detail,\r\n  a.document_reff_no as no_reff_detail,\r\n  0 as DEBET_DETAIL,\r\n  d.amount as KREDIT_DETAIL  \r\nFROM\r\nRECEIPT a\r\nINNER JOIN RECEIPT_DETAIL d on (a.RECEIPT_ID = d.RECEIPT_ID )\r\nINNER JOIN COA b on (d.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nINNER JOIN COA x on (a.BANK_ID=x.COA_ID)\r\nWHERE (c.ACCOUNT_TYPE_ID in (@ACC_KASBANK,@ACC_GIRO))\r\nand (a.Status_ID<>@DELETE )\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE TUJUAN */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',3 ,7) as URUT, \r\n  \'PENCAIRAN \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no,\r\n  a.document_no as no_detail,\r\n  \' \' as no_reff_detail,\r\n  (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as DEBET_detail,\r\n  (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT_detail\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n INNER JOIN COA b on (a.BANK_ID_DEST =b.COA_ID)\r\nWHERE\r\n  a.STATUS_ID in ( @CLEAR, @TOLAK)\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE ASAL */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',7,3) as URUT, \r\n  \'PENCAIRAN \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0) ) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no,\r\n  a.document_no as no_detail,\r\n  \' \' as no_reff_detail,\r\n  (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET_detail,\r\n  (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as KREDIT_detail\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n INNER JOIN COA b on (a.BANK_ID =b.COA_ID)\r\nWHERE\r\n  a.STATUS_ID in ( @CLEAR, @TOLAK)\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE TUJUAN yg tolak  */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',3 ,7) as URUT, \r\n  \'TOLAK \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.TGL_TOLAK as DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no,\r\n  a.document_no as no_detail,\r\n  \' \' as no_reff_detail,\r\n  (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET_detail,\r\n  (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as KREDIT_detail \r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n INNER JOIN COA b on (a.BANK_ID_DEST =b.COA_ID)\r\nWHERE\r\n  a.STATUS_ID in (  @TOLAK)\r\n&X2\r\n&P1\r\n&T2\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE ASAL YG TOLAK */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',7,3) as URUT, \r\n  \'TOLAK \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0) ) as DEBET,\r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.TGL_TOLAK AS DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no,\r\n  a.document_no as no_detail,\r\n  \' \' as no_reff_detail,\r\n  (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as DEBET_detail,\r\n  (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT_detail\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n INNER JOIN COA b on (a.BANK_ID =b.COA_ID)\r\nWHERE\r\n  (a.STATUS_ID =  @TOLAK)\r\n&X2\r\n&P1\r\n&T2\r\n\r\nUNION ALL\r\n/* JURNAL */\r\nSELECT \r\n  4 as URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n  AMOUNT as DEBET,\r\n  AMOUNT_CR as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no,\r\n  a.document_no as no_detail,\r\n  \' \' as no_reff_detail,\r\n  AMOUNT as DEBET_detail,\r\n  AMOUNT_CR as KREDIT_detail\r\nFROM\r\n  JOURNAL a   \r\n  INNER JOIN JOURNAL_DETAIL d ON (a.JOURNAL_ID = d.JOURNAL_ID)\r\n INNER JOIN COA b on (d.COA_ID =b.COA_ID)\r\nWHERE\r\n true \r\n&X2\r\n&P1\r\n&T1\r\n) AS AAA \r\nWHERE TRUE\r\n&KONDISI\r\nORDER BY DOCUMENT_DATE\r\n', 3106, 4000, 3, 'DETAIL', 4, '2009-03-05 15:08:05', '2017-06-15 10:12:58', 0, NULL, '', '&X1= AND FALSE\r\n&X2= AND B.COA_ID = :BANK_ID\r\n&T1= AND (DATE_FORMAT(a.DOCUMENT_DATE,\'%Y-%m-%d\') BETWEEN :12 AND :22 )\r\n&T2= AND (Date_format(a.TGL_TOLAK,\'%Y-%m-%d\') BETWEEN :12 AND :22)\r\n&P1= AND (a.PERIOD_NAME BETWEEN :PERIOD_NAME1 AND :PERIOD_NAME2)', ':12|:22|:PERIOD_NAME1|:PERIOD_NAME2', 9, 1),
      ('SELECT a.COA_ID, b.ACCOUNT_NAME BANK_NAME,COA_CODE, a.COA_ID AS BANK_ID\r\nFROM\r\n  COA a\r\nINNER JOIN ACCOUNT b ON (a.ACCOUNT_ID=b.ACCOUNT_ID)\r\nWHERE a.ACTIVE_FLAG = \'Y\' and (b.ACCOUNT_TYPE_ID in (@ACC_KASBANK,@ACC_GIRO))\r\n &kondisi\r\n &kondisi2\r\norder by PRIMARY_FLAG DESC , ACCOUNT_NAME\r\n\r\n\r\n\r\n', 3107, 4000, 9, NULL, 4, '2009-03-05 15:08:05', '2012-09-13 16:54:49', 1, NULL, NULL, NULL, NULL, 77, 1),
      ('SELECT DISTINCT * FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\nkk.first_name,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS JUAL,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKM,\r\n0  AS BAYAR_BKK,\r\n0 RETUR_JUAL\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN KARYAWAN kk ON k.KARYAWAN_ID=kk.KARYAWAN_ID\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(y.AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nNOTA_PENJUALAN_AGING s\r\nINNER JOIN RECEIPT_DETAIL y on (s.Nota_Penjualan_aging_id = y.Nota_Penjualan_aging_id)\r\nINNER JOIN receipt x on (x.receipt_id = y.receipt_id) and (date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :13 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE (FLAG_SUPP = 0 )\r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\nkk.first_name,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\n0 AS AWAL,\r\na.TOTAL_NET  AS JUAL,\r\nIFNULL(rec.bayar ,0)  + IFNULL(rec_ret.BAYAR_RET,0) AS BAYAR_BKM,\r\nIFNULL(pay.bayar,0)  + IFNULL(pay_ret.BAYAR_RET,0)   AS BAYAR_BKK,   \r\nIFNULL( c.retur,0) RETUR_JUAL\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN KARYAWAN kk ON k.KARYAWAN_ID=kk.KARYAWAN_ID\r\nINNER JOIN NOTA_PENJUALAN_AGING g ON (a.NOTA_PENJUALAN_ID  = g.NOTA_PENJUALAN_ID)  AND (JUAL_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.NOTA_PENJUALAN_AGING_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :13\r\nGROUP BY\r\ny.NOTA_PENJUALAN_AGING_ID\r\n) AS rec ON (g.NOTA_PENJUALAN_AGING_ID = rec.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.NOTA_PENJUALAN_AGING_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nWHERE (date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\')<= :13)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY pd.NOTA_PENJUALAN_AGING_ID\r\n) AS pay ON (g.NOTA_PENJUALAN_AGING_ID = pay.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* RETUR REFER KE NO PENJUALAN */ \r\n(SELECT NOTA_PENJUALAN_ID AS RETUR_ID, a.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(a.TOTAL_NET) * -1 as RETUR\r\nFROM\r\nnota_penjualan a\r\nWHERE\r\n(DOCUMENT_TYPE_ID  >= 5 )\r\nAND  (a.NOTA_PENJUALAN_OLD_ID IS NOT NULL )\r\nAND   (date_format(DOCUMENT_DATE, \'%Y-%m-%d\') <= :13 )\r\nGROUP BY a.NOTA_PENJUALAN_OLD_ID\r\n)  AS c on (a.nota_penjualan_id = c.nota_penjualan_id)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (y.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :13)  \r\nGROUP BY\r\nr.NOTA_PENJUALAN_OLD_ID\r\n) AS rec_ret ON (a.NOTA_PENJUALAN_ID = rec_ret.NOTA_PENJUALAN_ID)\r\nLEFT JOIN \r\n/* PELUNASAN RETUR DGN BKK */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR_RET\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (pd.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\')<= :13)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY n.NOTA_PENJUALAN_ID     \r\n) AS pay_ret ON (a.NOTA_PENJUALAN_ID = pay_ret.NOTA_PENJUALAN_ID)   \r\nWHERE \r\n \r\n(\r\n ((a.NET_BYR1 = 0) and   (a.DOCUMENT_TYPE_ID < 5)) OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   >= 5) AND (a.NOTA_PENJUALAN_OLD_ID IS NULL)  \r\n)\r\n)\r\nAND (date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\')<=  :13 )\r\n&kondisi\r\n/* end penjualan */\r\n) as yy\r\nWHERE \r\n(\r\n(YY.AWAL  + YY.JUAL - Bayar_BKM   + bayar_BKK - RETUR_JUAL) <> 0\r\n) \r\nORDER BY PERSON_NAME, DOCUMENT_DATE\r\n', 3108, 3304, 0, NULL, 4, '2017-06-12 14:22:00', '2017-06-12 14:22:55', 0, NULL, '', NULL, ':13', 77, 1),
      ('Select j.document_date, j.document_no, a.JTEMPO,  a.document_date as tgl_terima, s.document_no as so_no, c.PERSON_name, sum(b.amount) TERIMA,\r\nc.PERSON_CODE, \r\nmax(j.total_net) total_amount ,\r\nmax(j.total_net) as   total_net \r\nfrom\r\nreceipt a \r\ninner join receipt_detail b on  (a.receipt_id = b.receipt_id)\r\ninner join nota_penjualan_aging g on (b.NOTA_PENJUALAN_AGING_ID =g.NOTA_PENJUALAN_AGING_ID)  \r\ninner join nota_penjualan j on  (g.NOTA_PENJUALAN_ID = j.NOTA_PENJUALAN_ID) \r\ninner join PERSON c on (a.PERSON_id = c.PERSON_id)\r\ninner join warehouse w on (j.warehouse_id = w.warehouse_id)\r\nleft join so s on (j.so_id = s.so_id)\r\nwhere \r\n(j.karyawan_id = :karyawan_id)\r\nand (DATEDIFF(a.document_date ,j.jtempo)<=15)\r\nand (j.invoice_type_id <>  @PREPAYMENT) \r\n&kondisi\r\ngroup by \r\nj.document_date, j.document_no, PERSON_name\r\nUNION ALL\r\nSelect a.document_date, a.document_no, a.JTEMPO,   a.document_date as tgl_terima, s.document_no as so_no, c.PERSON_name, sum(nd.subtotal) *-1 TERIMA,\r\nc.PERSON_CODE, \r\nsum(nd.subtotal)*-1  total_amount ,\r\nsum(nd.subtotal)*-1  as   total_net \r\nfrom\r\nnota_penjualan a\r\ninner join nota_penjualan_detail nd on (a.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join PERSON c on (a.PERSON_id = c.PERSON_id)\r\ninner join warehouse w on (a.warehouse_id = w.warehouse_id)\r\nleft join so s on (a.so_id = s.so_id)\r\nwhere \r\n(a.karyawan_id = :karyawan_id)\r\nand (DATEDIFF(a.document_date ,a.jtempo)<=15)\r\nand (a.invoice_type_id <>  @PREPAYMENT) \r\nand (i.JENIS_ID = @UM)\r\n&kondisi\r\ngroup by \r\na.document_date, a.document_no, PERSON_name\r\n\r\n\r\n', 3109, 1050, 0, NULL, 4, '2008-12-16 18:00:03', '2017-05-31 10:10:33', 0, NULL, NULL, NULL, NULL, 9, 1),
      ('Select b.karyawan_id, a.document_date, a.document_no, b.first_name, c.PERSON_name, a.total_amount, a.total_net\r\nfrom\r\nnota_penjualan a\r\ninner join nota_penjualan_detail nd on (a.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join warehouse w on (a.warehouse_id = w.warehouse_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join PERSON c on (a.PERSON_id = c.PERSON_id)\r\ninner join karyawan b on(a.karyawan_id=b.karyawan_id)\r\nwhere true\r\n&kondisi\r\ngroup by b.first_name\r\norder by b.first_name\r\n ', 3110, 1050, 9, NULL, 4, '2008-12-16 18:00:03', '2017-06-20 12:44:24', 0, NULL, NULL, NULL, NULL, 77, 1),
      ('SELECT \r\n  a.INVOICE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  c.PERSON_NAME,\r\nSITE_NAME, \r\n  cs.ADDRESS1,\r\n  v.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME, \r\n  p.DOCUMENT_NO AS PO\r\nFROM\r\n  INVOICE a\r\n  INNER JOIN INVOICE_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n  INNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN PO p ON (a.PO_ID = p.PO_ID)\r\nWHERE a.DOCUMENT_TYPE_ID<5\r\nAND a.DOCUMENT_CLASS_CODE=\'Barang Datang\'\r\n&KONDISI\r\nGROUP BY\r\na.INVOICE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  c.PERSON_NAME,\r\nSITE_NAME, \r\n  cs.ADDRESS1,\r\n  v.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME,\r\n  p.DOCUMENT_NO\r\nORDER BY\r\n  a.DOCUMENT_NO\r\n', 3111, 20052017, 0, NULL, 4, '2017-06-20 12:44:41', '2017-05-19 08:51:56', 0, NULL, NULL, NULL, NULL, 77, 1),
