and asset_id is not null and left(j.period_name,4) = :YEAR1 AND j.period_name <= :PERIOD_NAME1\r\n		group by asset_id\r\n			\r\n		union\r\n\r\n		SELECT jd.ASSET_ID, j.PERIOD_NAME, 0 as jan, 0 as feb, 0 as mar, 0 as apr, 0 as may, 0 as jun, 0 as jul, 0 as aug, 0 as sep, 0 as okt, sum(jd.AMOUNT_CR) as nov, 0 as des\r\n		FROM journal j\r\n			INNER JOIN journal_detail jd ON(j.JOURNAL_ID = jd.JOURNAL_ID)\r\n		WHERE jd.AMOUNT_CR <> 0\r\n			and right(j.PERIOD_NAME, 2) = \'11\'\r\n			and asset_id is not null and left(j.period_name,4) = :YEAR1 AND j.period_name <= :PERIOD_NAME1\r\n		group by asset_id\r\n			\r\n		union\r\n\r\n		SELECT jd.ASSET_ID, j.PERIOD_NAME, 0 as jan, 0 as feb, 0 as mar, 0 as apr, 0 as may, 0 as jun, 0 as jul, 0 as aug, 0 as sep, 0 as okt, 0 as nov, sum(jd.AMOUNT_CR) as des\r\n		FROM journal j\r\n			INNER JOIN journal_detail jd ON(j.JOURNAL_ID = jd.JOURNAL_ID)\r\n		WHERE jd.AMOUNT_CR <> 0\r\n			and right(j.PERIOD_NAME, 2) = \'12\'\r\n			and asset_id is not null and left(j.period_name,4) = :YEAR1 AND j.period_name <= :PERIOD_NAME1\r\n		group by asset_id\r\n			)\r\n			as y \r\n		group by asset_id\r\n	) \r\n	as x on(a.asset_id = x.asset_id) \r\n	/*END of Tabel nilai penyusutan*/\r\n	\r\n	/*Tabel Akumulasi Susut Tahun Lalu*/\r\n	left join (\r\n		SELECT jd.ASSET_ID, sum(jd.AMOUNT_CR) as amount_susut\r\n		FROM journal j\r\n			INNER JOIN journal_detail jd ON(j.JOURNAL_ID = jd.JOURNAL_ID)\r\n		WHERE jd.AMOUNT_CR <> 0\r\n			and asset_id is not null\r\n			and left(j.period_name,4) < :YEAR1\r\n		group by jd.ASSET_ID\r\n	)\r\n	as z on(a.asset_id = z.asset_id)\r\n	/*END of Tabel Akumulasi Susut Tahun Lalu*/\r\nWHERE a.coa_id = :coa_id\r\n	and date_format(a.buying_date,\'%Y%m\') <= :PERIOD_NAME1\r\n	and (date_format(a.using_date,\'%Y\') >= :YEAR1  OR a.using_date is null)\r\n	&kondisi	\r\nORDER BY a.asset_name\r\n', 3117, 1711, 0, NULL, 120, '2009-12-02 13:28:52', '2017-05-18 09:15:24', 0, NULL, NULL, NULL, ':YEAR1|:PERIOD_NAME1', 9, NULL),
      ('SELECT \r\n  e.coa_code,\r\n  e.coa_name,\r\n  a.coa_id,\r\n  sum(a.nilai_asset * a.entered_qty) as tot,\r\n  :PERIOD_NAME1 as tahun\r\nfrom asset a\r\ninner join coa e on a.coa_id=e.coa_id\r\nwhere true\r\nand (date_format(a.using_date,\'%Y\') >= :YEAR1 OR a.using_date is null)\r\n&kondisi\r\ngroup by \r\n  e.coa_name,\r\n  a.coa_id\r\norder by e.coa_code', 3118, 1711, 9, NULL, 120, '2009-12-02 13:28:52', '2017-02-10 10:43:45', 0, NULL, NULL, NULL, ':PERIOD_NAME1', 77, NULL),
