      ('SELECT a.NOTA_PENJUALAN_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  sum(b.SUBTOTAL) - a.TOTAL_DISCOUNT+a.PPN_AMOUNT as TOTAL_NET,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  sum(b.SUBTOTAL) as TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  cs.SITE_NAME,\r\n  s.display_name AS DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME\r\n,\r\n  um.SUBTOTAL*-1 as UANG_MUKA,\r\n X.RECEIVED_AMOUNT + NET_BYR1 + TOTAL_BYR2 AS RECEIVED_AMOUNT \r\nFROM\r\n  nota_penjualan a\r\n  LEFT JOIN NOTA_PENJUALAN_AGING  x ON (a.NOTA_PENJUALAN_ID = x.NOTA_PENJUALAN_ID)\r\n\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID )\r\n  LEFT JOIN(\r\n  SELECT \r\n  a.SUBTOTAL,\r\n  a.NOTA_PENJUALAN_ID\r\n  FROM NOTA_PENJUALAN_DETAIL a\r\n  INNER JOIN NOTA_PENJUALAN b ON b.NOTA_PENJUALAN_ID=a.NOTA_PENJUALAN_PRE_ID\r\n  )AS  UM on a.NOTA_PENJUALAN_ID=um.NOTA_PENJUALAN_ID\r\nwhere true\r\nand (a.document_type_id <> 5) AND b.NOTA_PENJUALAN_PRE_ID IS NULL\r\n&Kondisi\r\nGROUP BY\r\na.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.TOTAL_NET,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  cs.SITE_NAME,\r\n  s.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME\r\nORDER BY a.DOCUMENT_DATE', 3132, 650, 0, NULL, 4, '2010-03-12 00:32:24', '201