      ('SELECT \r\n      I.ITEM_CODE,\r\n      b.ITEM_DESCRIPTION,\r\n      b.ENTERED_QTY,\r\n      b.UNIT_PRICE,\r\n      b.SUBTOTAL,\r\n      null as KIRIM,\r\n      (SELECT SUM(ENTERED_QTY) FROM NOTA_PENJUALAN_DETAIL WHERE NOTA_PENJUALAN_OLD_ID=b1.NOTA_PENJUALAN_ID AND ITEM_ID=b.ITEM_ID AND DOCUMENT_TYPE_ID=5) as RETUR,\r\n      null as KURANG\r\n\r\nFROM \r\n     NOTA_PENJUALAN_DETAIL b\r\nLEFT JOIN NOTA_PENJUALAN b1 ON b1.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID\r\nLEFT JOIN ITEM I ON I.ITEM_ID=b.ITEM_ID\r\n\r\nWHERE\r\n   I.JENIS_ID<>@UM AND\r\n   b.NOTA_PENJUALAN_ID=:NOTA_PENJUALAN_ID\r\n&KONDISI\r\n\r\nORDER BY b1.DOCUMENT_DATE\r\n      ', 3142, 4012, 14, NULL, 1, '2007-08-16 15:14:55', '2007-08-20 10:14:53', 1, NULL, NULL, NULL, NULL, 3, NULL),
      ('SELECT \r\n      I.ITEM_CODE,\r\n      b.ITEM_DESCRIPTION,\r\n      b.ENTERED_QTY,\r\n      b.UNIT_PRICE,\r\n      b.SUBTOTAL,\r\n      b.RECEIVED_ENTERED_QTY as KIRIM,\r\n      null as RETUR,\r\n      b.ENTERED_QTY- b.RECEIVED_ENTERED_QTY as KURANG\r\n\r\nFROM \r\n     SO_DETAIL b\r\nLEFT JOIN ITEM I ON I.ITEM_ID=b.ITEM_ID\r\n\r\nWHERE\r\n   I.JENIS_ID<>@UM AND\r\n   b.SO_ID=:SO_ID\r\n&KONDISI\r\n', 3143, 4012, 13, NULL, 1, '2007-08-20 08:37:14', '2007-08-20 10:13:58', 1, NULL, NULL, NULL, NULL, 0, NULL);";

