      $insert_web_rpt_sql = "INSERT INTO `web_rpt_sql` (`WEB_RPT_SQL_ID`, `WEB_RPT_ID`, `WEB_RPT_GROUP_ID`, `STR_SQL`, `NO_URUT`, `NOTE`, `HEAD_ID`) VALUES
      (1, 10, 0, 'SELECT \r\n  i.ITEM_ID,\r\n  i.LOKASI,\r\n  i.ITEM_CODE,\r\n  i.ITEM_DESCRIPTION,\r\n  i.PART_NUMBER,\r\n  i.UOM_CODE,\r\n  i.MIN_STOCK,\r\n  i.ACTIVE_FLAG,\r\n  i.NOTE,\r\n  JENIS.DISPLAY_NAME AS JENIS,\r\n  GROUP_ITEM.DISPLAY_NAME AS KELOMPOK,\r\n  TIPE.DISPLAY_NAME AS TIPE,\r\n  MEREK.DISPLAY_NAME AS MEREK,\r\n  MADEIN.DISPLAY_NAME AS MADEIN,\r\n  VGRADE.DISPLAY_NAME AS GRADE\r\nFROM\r\n  item i\r\n  LEFT OUTER JOIN erp_lookup_value JENIS ON (i.JENIS_ID = JENIS.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value GROUP_ITEM ON (i.GROUP_ID = GROUP_ITEM.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value TIPE ON (i.TIPE_ID = TIPE.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value MEREK ON (i.MEREK_ID = MEREK.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value MADEIN ON (i.MADE_IN_ID = MADEIN.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value VGRADE ON (i.GRADE_ID = VGRADE.ERP_LOOKUP_VALUE_ID)\r\nwhere\r\ntrue \r\n&KONDISI\r\nORDER BY\r\n  i.ITEM_DESCRIPTION', 1, 'i', NULL),
      (2, 15, 0, 'SELECT \r\n  a.TO_QTY  AS QTY_KONVERSI,\r\n  i.ITEM_DESCRIPTION,\r\n  a.UOM_CODE,\r\n  i.ITEM_ID,\r\n  i.ITEM_CODE ,\r\n  i.PART_NUMBER,\r\n  i.UOM_CODE satuan,\r\n  i.MIN_STOCK,\r\n  i.ACTIVE_FLAG,\r\n  i.CREATED_BY,\r\n  i.CREATED_DATE,\r\n  i.LAST_UPDATE_DATE,\r\n  i.LAST_UPDATE_BY,\r\n  i.JENIS_ID,\r\n  i.GROUP_ID,\r\n  i.MADE_IN_ID,\r\n  i.GRADE_ID,\r\n  i.TIPE_ID,\r\n  i.MEREK_ID,\r\n  i.LOKASI\r\nFROM\r\n  item i \r\n  INNER JOIN item_uom a ON (a.ITEM_ID = i.ITEM_ID)\r\n \r\nWHERE true\r\n  &kondisi\r\nORDER BY\r\n  i.ITEM_DESCRIPTION', 1, 'ze', NULL),
