SUBTOTAL, \r\n-1 as PERSON_ID\r\nFROM\r\nbuild a\r\nINNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE  true\r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE(a.DOCUMENT_DATE) <  :11 ) \r\n&KONDISI\r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  ITEM YG DIBUAT       */\r\nSELECT\r\n1 as URUT,\r\n7 as TIPE,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\na.ENTERED_QTY *  a.BASE_QTY  AS MASUK,\r\n0 AS KELUAR,\r\na.TOTAL_AMOUNT / a.ENTERED_QTY / a.BASE_QTY  AS HARGA,\r\n0 as DISKON ,\r\na.TOTAL_AMOUNT AS SUBTOTAL,\r\n-1 as PERSON_ID\r\nFROM\r\nbuild a\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\ninner join warehouse w on (a.warehouse_id=w.warehouse_id)\r\nWHERE true \r\nAND    (a.PERIOD_NAME = :PERIOD_NAME1) \r\nAND (DATE(a.DOCUMENT_DATE) <  :11 ) \r\n&KONDISI\r\n) AS AA\r\nGROUP BY  ITEM_ID \r\nhaving SUM(MASUK - KELUAR) <> 0\r\nUNION ALL \r\nSELECT\r\ni.ITEM_ID,\r\ni.ITEM_DESCRIPTION, \r\ni.ITEM_CODE,\r\ni.UOM_CODE AS SATUAN,\r\n0 as  AWAL, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF( xx.Masuk>0,xx.masuk,0) + IF( xx.keluar<0 , abs(xx.keluar), 0),\r\n0)\r\n) as BELI, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF (xx.Masuk<0, abs(xx.masuk), 0) + IF(xx.keluar>0,xx.keluar,0),\r\n0)\r\n) as RB,\r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4), IF( xx.Keluar>0,xx.keluar,0) + IF( xx.masuk<0 , abs(xx.masuk), 0),\r\n0)\r\n) as JUAL, \r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4), IF (xx.Keluar<0, abs(xx.keluar), 0) + IF(xx.masuk>0,xx.masuk,0),\r\n0) \r\n) as RJ,\r\nSUM( IF( xx.TIPE=5, xx.Masuk-xx.keluar, 0)  ) as ADJ, \r\nSUM( IF( xx.TIPE=6, xx.Masuk-xx.keluar, 0)  ) as PAG,\r\nSUM( IF( xx.TIPE=7, xx.Masuk-xx.keluar, 0)  ) as BUILD,\r\nSUM(if(xx.Masuk>0,xx.masuk,0)+if(xx.keluar<0,abs(xx.keluar),0))-SUM(if(xx.Masuk<0,abs(xx.masuk),0)+if(xx.keluar>0,xx.keluar,0)) as AKHIR,\r\n0  as AWAL_rp, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF( xx.Masuk>0,xx.masuk,0) + IF( xx.keluar<0 , abs(xx.keluar), 0),\r\n0)*xx.HARGA\r\n) as BELI_rp, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF (xx.Masuk<0, abs(xx.masuk), 0) + IF(xx.keluar>0,xx.keluar,0),\r\n0)*xx.HARGA\r\n) as RB_rp,\r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4),IF( xx.Keluar>0,xx.keluar,0) + IF( xx.Masuk<0 , abs(xx.masuk), 0),\r\n0)*xx.HARGA\r\n) as JUAL_rp, \r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4),  IF (xx.Keluar<0, abs(xx.keluar), 0) + IF(xx.masuk>0,xx.masuk,0),\r\n0)*xx.HARGA\r\n) as RJ_rp,\r\nSUM( IF( xx.TIPE=5, xx.Masuk-xx.keluar, 0) *xx.HARGA ) as ADJ_rp, \r\nSUM( IF( xx.TIPE=6, xx.Masuk-xx.keluar, 0) *xx.HARGA ) as PAG_rp,\r\nSUM( IF( xx.TIPE=7, xx.Masuk-xx.keluar, 0) *xx.HARGA ) as BUILD_rp,\r\n(SELECT SUM(ENTERED_AMOUNT) \r\nFrom adjust_amount_detail b,adjust_amount a \r\nWHERE b.item_id=i.item_id and a.adjust_amount_id=b.adjust_amount_id AND (DATE(a.DOCUMENT_DATE) >=  :11 AND DATE(a.DOCUMENT_DATE) <= :21 )   \r\nGROUP BY ITEM_ID\r\n) as ADJ_NILAI_rp,\r\nSUM(if(xx.Masuk>0,xx.masuk,0)+if(xx.keluar<0,abs(xx.keluar),0)*xx.HARGA)- SUM(if(xx.Masuk<0,abs(xx.masuk),0)+if(xx.keluar>0,xx.keluar,0)*xx.HARGA) as AKHIR_rp\r\nFROM \r\nITEM i \r\ninner join (\r\n/*=====*/\r\n\r\n\r\nSELECT\r\n0 as URUT,\r\n0 as TIPE,\r\np.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\n\'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n\'\' AS DOCUMENT_NO,\r\n\'\' AS DOCUMENT_REFF_NO,\r\nSTART_DATE as DOCUMENT_DATE,\r\na.WAREHOUSE_ID ,\r\n\'\' AS NOTE,\r\nI.ITEM_ID,\r\na.QTY_AWAL AS MASUK,\r\n0 AS KELUAR,\r\na.HPP as HARGA,\r\n0 as DISKON,\r\na.QTY_AWAL * a.HPP   AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\n0 as ID\r\nFROM\r\nitem_balance a\r\nINNER JOIN PERIOD p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\nWHERE True\r\nAND FALSE\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVOICE         */\r\nSELECT\r\n1 as URUT,\r\n1 as TIPE,\r\nPERIOD_NAME,\r\n1  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE, \r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))   AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_IN_ID AS ID\r\nFROM\r\nINVENTORY_IN a\r\nINNER JOIN INVENTORY_IN_detail b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\nWHERE TRUE\r\nAND (DATE(a.DOCUMENT_DATE) >=  :11 AND DATE(a.DOCUMENT_DATE) <= :21) \r\nAND    (a.PERIOD_NAME >= :PERIOD_NAME1) \r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n1 as URUT,\r\n2 as TIPE,\r\na.PERIOD_NAME,\r\n0  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY  AS HARGA  ,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))    AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_OUT_ID AS ID\r\nFROM\r\nINVENTORY_OUT a\r\nINNER JOIN INVENTORY_OUT_detail b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\nWHERE TRUE\r\nAND (DATE(a.DOCUMENT_DATE) >=  :11 AND DATE(a.DOCUMENT_DATE) <= :21) \r\nAND    (a.PERIOD_NAME >= :PERIOD_NAME1) \r\nUNION ALL\r\n/* TRANSAKSI Pembelian yg ga pake BPB  */\r\n/* INVOICE          */\r\nSELECT\r\n1 as URUT,\r\n3 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1) )  AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVOICE_ID AS ID\r\nFROM\r\nINVOICE a\r\nINNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\nWHERE b.INVENTORY_IN_DETAIL_ID is NULL\r\nAND (DATE(a.DOCUMENT_DATE) >=  :11 AND DATE(a.DOCUMENT_DATE) <= :21) \r\nAND    (a.PERIOD_NAME >= :PERIOD_NAME1) \r\nUNION ALL\r\n/* TRANSAKSI Penjualan yg ga pake SJ  */\r\n/* NOTA_PENJUALAN           */\r\nSELECT\r\n1 as URUT,\r\n4 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY   as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1)  )  AS SUBTOTAL,\r\na.PERSON_ID,\r\na.NOTA_PENJUALAN_ID AS ID\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\nWHERE b.INVENTORY_OUT_DETAIL_ID IS NULL\r\nAND (DATE(a.DOCUMENT_DATE) >=  :11 AND DATE(a.DOCUMENT_DATE) <= :21) \r\nAND    (a.PERIOD_NAME >= :PERIOD_NAME1) \r\nUNION ALL\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n2 as URUT,\r\n5 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\nb.UNIT_PRICE /b.BASE_QTY as HARGA,\r\n0 as DISKON ,\r\nb.Subtotal AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.ADJUST_QTY_ID AS ID\r\nFROM\r\nadjust_qty a\r\nINNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\nWHERE b.ENTERED_QTY  > 0\r\nAND (DATE(a.DOCUMENT_DATE) >=  :11 AND DATE(a.DOCUMENT_DATE) <= :21) \r\nAND    (a.PERIOD_NAME >= :PERIOD_NAME1) \r\n\r\nUNION ALL\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n2 as URUT,\r\n5 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * -1 * b.BASE_QTY    AS KELUAR,\r\nb.UNIT_PRICE / b.BASE_QTY as HARGA,\r\n0 as DISKON ,\r\nb.Subtotal AS SUBTOTAL ,\r\n-1 as PERSON_ID,\r\na.ADJUST_QTY_ID AS ID\r\nFROM\r\nadjust_qty a\r\nINNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\nWHERE b.ENTERED_QTY  < 0\r\nAND (DATE(a.DOCUMENT_DATE) >=  :11 AND DATE(a.DOCUMENT_DATE) <= :21) \r\nAND    (a.PERIOD_NAME >= :PERIOD_NAME1) \r\n\r\nUNION ALL\r\n/* TRANSAKSI PAG, GUDANG ASAL  */\r\n/* TAG         */\r\nSELECT\r\n1 as URUT,\r\n6 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\nb.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nifnull(l.Hpp,0) as HARGA,\r\n0 AS DISKON, \r\n0 AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.TAG_ID AS ID\r\nFROM\r\ntag a\r\nINNER JOIN tag_detail b ON (a.tag_ID = b.tag_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\nLEFT  JOIN Item_balance l on (b.item_id = l.item_id) and (a.period_name = l.period_name) and (a.warehouse_id = l.warehouse_id)\r\nWHERE TRUE\r\nAND (DATE(a.DOCUMENT_DATE) >=  :11 AND DATE(a.DOCUMENT_DATE) <= :21) \r\nAND    (a.PERIOD_NAME >= :PERIOD_NAME1) \r\nUNION ALL\r\n/* TRANSAKSI PAG, GUDANG TUJUAN  */\r\n/* TAG      