      (56, 33, 3, 'select a.document_no,a.document_date, a.need_date, b.item_id, b.item_description,\r\n  b.Entered_QTY,\r\n  b.RECEIVED_Entered_QTY,\r\n  c.PERSON_NAME,\r\n  (b.entered_QTY-b.RECEIVED_entered_QTY) as SISA_QTY,\r\n  (select sum(qty_awal+qty_masuk-qty_keluar) from item_stok s where s.item_id=i.item_id) as stok\r\nfrom so a\r\n     INNER JOIN So_detail b ON (a.SO_ID = b.SO_ID)\r\n     INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n     INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nwhere\r\n(b.entered_QTY-b.RECEIVED_entered_QTY) > 0\r\nAND A.STATUS_ID not in ( @CLOSE,@DELETE)\r\n&kondisi\r\nORDER BY\r\n  A.DOCUMENT_DATE, i.ITEM_DESCRIPTION', 2, NULL, NULL),
