      (1145, 145, 0, 'SELECT @No := @No + 1 as No,\r\n  ITEM_ID, \r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE, \r\n  ENTERED_UOM, \r\n SUM(HARIAN) AS HARIAN ,\r\nSUM(BULAN) AS BULAN\r\nFROM\r\n(\r\nselect a.ITEM_ID,\r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE, \r\n  ENTERED_UOM, \r\n sum(entered_qty) HARIAN  , \r\n  0 AS BULAN\r\nfrom \r\n build a \r\n inner join item i using (item_id)\r\nwhere   status_id <> @delete\r\nand DOCUMENT_DATE BETWEEN :11 AND :21\r\n&kondisi\r\nGROUP BY a.ITEM_ID,\r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE,\r\n  ENTERED_UOM\r\n\r\nUNION ALL\r\nselect a.ITEM_ID, \r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE, \r\n  ENTERED_UOM, \r\n0, sum(entered_qty)  AS BULAN\r\nfrom \r\n build a \r\n inner join item i using (item_id)\r\nwhere  \r\n \r\n  Period_name = :Period_name1\r\nand (status_id  <> @delete)\r\n&Kondisi\r\nGROUP BY a.ITEM_ID,\r\n  ITEM_DESCRIPTION , \r\n  ITEM_CODE,\r\n  ENTERED_UOM\r\n) AS XX\r\nGROUP BY ITEM_CODE,  ITEM_ID, ITEM_DESCRIPTION ', NULL, '', NULL),
      (1146, 145, 3, '\r\n/* material bahan baku */\r\nselect i.item_description, i.item_code, group_id, i.item_id , i.UOM_CODE,\r\n  XX.bahan_id,\r\n  SUM(BHN_HARIAN) AS BHN_HARIAN,\r\n  SUM(BHN_BULANAN) AS BHN_BULANAN\r\nFROM\r\n(  \r\nselect b.item_id , a.item_id bahan_id ,\r\n(b.ENTERED_QTY) AS BHN_HARIAN ,\r\n0 AS BHN_BULANAN\r\nfrom \r\n build a \r\n inner join build_detail b using (build_id)\r\nwhere true /*a.item_id = :item_id  */ \r\n&kondisi\r\nUNION ALL\r\nselect b.item_id , a.item_id bahan_id , 0 ,\r\n(b.ENTERED_QTY)  AS BHN_BULANAN\r\nfrom \r\n build a \r\n inner join build_detail b using (build_id)\r\nwhere /*a.item_id = :item_id   \r\nand*/ Period_name = :Period_name1\r\n) AS XX \r\n inner join item i on (XX.item_id = i.item_id)\r\ngroup by  i.ITEM_ID,BAHAN_ID', NULL, '', 0),
      (1148, 510, 0, 'SELECT \r\nif( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,to_qty),b.entered_qty) as ENTERED_QTY,\r\nb.BASE_QTY,\r\nif(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,to_qty),b.unit_price) as UNIT_PRICE,\r\nb.DISCOUNT_PRICE,\r\nb.DISCOUNT_PRICE1,\r\nb.DISCOUNT_PERCEN,\r\nb.SUBTOTAL,\r\nb.ENTERED_UOM,\r\nb.inventory_in_ID,\r\nb.ITEM_DESCRIPTION,\r\ni.ITEM_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\na.NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\nd.DOCUMENT_NO PO,\r\na.PERIOD_NAME,\r\na.TOTAL_AMOUNT,\r\na.TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\nc.PERSON_NAME,\r\nSITE_NAME, \r\nv.DISPLAY_NAME,\r\nw.WAREHOUSE_NAME, \r\ncs.ADDRESS1\r\nFROM\r\ninventory_in_detail b\r\nINNER JOIN inventory_in a ON b.inventory_in_id=a.inventory_in_id\r\nINNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nINNER JOIN PO d on (a.PO_ID = d.PO_ID)\r\nleft join item_uom dd on i.item_id=dd.ITEM_ID and dd.BASE_UOM_FLAG=\'y\'\r\nWHERE TRUE\r\n&KONDISI\r\nORDER BY \r\na.DOCUMENT_NO, b.inventory_in_DETAIL_ID', NULL, '', NULL),
      (1150, 500, 0, 'SELECT \r\n  a.STATUS_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.NOTE,\r\n  a.TOTAL_AMOUNT,\r\n  a.PERSON_ID,\r\n  a.PERSON_SITE_ID,\r\n  a.PAYMENT_TERM_ID,\r\n  a.PERIOD_NAME,\r\n  a.PPN_CODE,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n(um.SUBTOTAL*-1) as UANG_MUKA,\r\n  x.PAID_AMOUNT, \r\n a.TOTAL_NET,\r\n  a.JTEMPO,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  cs.SITE_NAME,\r\n  l.DISPLAY_NAME,\r\n  a.INVOICE_ID,\r\n  w.WAREHOUSE_NAME,\r\n  \r\n   I.ITEM_DESCRIPTION,\r\n   I.ITEM_CODE,\r\n   b.DISCOUNT_PRICE,\r\n   b.DISCOUNT_PERCEN,\r\n   b.SUBTOTAL,\r\n   b.NOTE AS KET,\r\n   if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,bb.to_qty),b.entered_qty) AS ENTERED_QTY,\r\n   if(bb.uom_code is null, b.entered_uom,bb.uom_code) as entered_uom,\r\n   if(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,bb.to_qty),b.unit_price) AS UNIT_PRICE\r\nFROM\r\n  invoice a\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value l ON (a.STATUS_ID = l.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID=b.INVOICE_ID)\r\n  INNER JOIN ITEM i ON (b.ITEM_ID=i.ITEM_ID  )\r\nleft join item_uom bb on i.item_id=bb.ITEM_ID and bb.BASE_UOM_FLAG=\'y\'\r\n  LEFT JOIN(\r\n  SELECT \r\n  sum(a.SUBTOTAL) SUBTOTAL,\r\n  a.INVOICE_ID\r\n  FROM INVOICE_DETAIL a\r\n  INNER JOIN INVOICE b ON b.INVOICE_ID=a.INVOICE_PRE_ID\r\n  GROUP BY a.INVOICE_ID\r\n  )AS  UM on a.INVOICE_ID=um.INVOICE_ID\r\n  INNER JOIN INVOICE_DUE x on (a.INVOICE_ID = x.INVOICE_ID)\r\nwhere\r\nTrue\r\n AND b.INVOICE_PRE_ID IS NULL \r\n&KONDISI\r\nORDER BY\r\n  a.DOCUMENT_NO\r\n', NULL, '', NULL),
      (1152, 550, 0, 'SELECT DISTINCT \r\n  b.*,\r\n  i.ITEM_DESCRIPTION,\r\n  i.ITEM_CODE,\r\n  i.UOM_CODE,\r\n  b.LAST_UPDATE_DATE AS DOCUMENT_DATE\r\nFROM\r\n  price_list_detail b\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE\r\n  \r\n    b.ACTIVE_FLAG = \'Y\'\r\n&KONDISI\r\nORDER BY\r\n  i.ITEM_DESCRIPTION\r\n', NULL, '', NULL),
      (1153, 515, 0, 'select\r\ni.item_description,\r\ni.item_code,\r\ni.item_id,\r\ncs.PERSON_name,\r\ncs.person_code,\r\nsum(if( nd.BASE_QTY=1,nd.ENTERED_QTY /if( to_qty is null,1,b.to_qty),nd.entered_qty)) AS total,\r\n   if(b.uom_code is null, nd.entered_uom,b.uom_code) as entered_uom,\r\nsum(nd.subtotal - nd.DISCOUNT_PRICE1)  as total_rp\r\nfrom\r\nPERSON cs\r\ninner join invoice n on(cs.PERSON_id=n.PERSON_id)\r\ninner join invoice_detail nd on (n.invoice_id=nd.invoice_id)\r\nINNER JOIN ITEM I ON (nd.ITEM_ID=I.ITEM_ID )\r\nleft join item_uom b on i.item_id=b.ITEM_ID and b.BASE_UOM_FLAG=\'y\'\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nwhere true\r\nand (n.status_id  <> @delete) \r\n&kondisi\r\ngroup by \r\nnd.item_id,\r\ncs.PERSON_id\r\norder by i.item_description, item_code,cs.person_name\r\n', NULL, '', NULL),
      (1155, 520, 0, 'select\r\ncs.PERSON_id,\r\ncs.PERSON_name,\r\ncs.person_code,\r\ni.item_description,\r\ni.item_code,\r\nsum(nd.entered_qty)  as total,\r\nsum(nd.subtotal - nd.discount_price1)  as total_rp,\r\nnd.entered_uom\r\nfrom\r\nPERSON cs\r\ninner join invoice n on(cs.PERSON_id=n.PERSON_id)\r\ninner join invoice_detail nd on (n.invoice_id=nd.invoice_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nleft join item_uom b on i.item_id=b.ITEM_ID and b.BASE_UOM_FLAG=\'y\'\r\nwhere true \r\nand (n.status_id  <> @delete) \r\n&kondisi\r\ngroup by \r\ncs.PERSON_id,\r\nnd.item_id\r\norder by cs.person_name, cs.person_code, i.item_description', NULL, '', NULL),
      (1157, 90, 0, 'SELECT   i.ITEM_CODE,\r\n  i.ITEM_DESCRIPTION,\r\n  i.PART_NUMBER,\r\n  i.UOM_CODE,\r\n  i.MIN_STOCK,\r\n  i.ACTIVE_FLAG,\r\n  i.LOKASI, sub1.stok1\r\nFROM\r\nitem i ,\r\n(SELECT\r\n  stok.ITEM_ID,\r\n  SUM(stok.QTY_AWAL + stok.QTY_MASUK - stok.QTY_KELUAR) AS STOK1\r\nFROM\r\n  item_stok stok\r\nGROUP BY\r\n  stok.ITEM_ID) as sub1\r\nWHERE\r\n   i.ACTIVE_FLAG = \'Y\' and\r\n   i.item_id = sub1.item_id and\r\n   i.min_stock >= sub1.stok1\r\nand i.jenis_id<>@UM\r\n  &Kondisi\r\nOrder By sub1.stok1 , i.ITEM_DESCRIPTION\r\n  \r\n  ', 1, '', NULL),
