      (1161, 1005, 0, 'select \r\n*\r\nFrom\r\n(\r\n\r\n\r\n\r\n/*\r\nTIPE --> digit menunjukan Jurnal BELI-JUAL-INV-BKK-BKM-GL\r\n\r\n  = PERIODE\r\n  = TANGGAL\r\nAND (p.TGL_TOLAK between :11 and :21 ) = TANGGAL TOLAK\r\n\r\n = ACCOUNT\r\n*/\r\n\r\n/*saldo awal */\r\nselect\r\n  \'111111A\' as TIPE,\r\n  \'SALDO AWAL\' AS DOCUMENT_CLASS_CODE,\r\n  \' \' AS DOCUMENT_NO ,\r\nBALANCE_DATE AS  DOCUMENT_DATE,\r\n  \' \' AS DOCUMENT_REFF_NO ,\r\n  0  AS URUT,\r\n  1 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,\r\nC.ACCOUNT_ID ,\r\n \' \'  ITEM_DESCRIPTION ,\r\n  0 AS ID,\r\n  \' SALDO AWAL \' AS NOTE,\r\n   COA_SALDO AS AWAL,\r\n  0 AS DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  COA_BALANCE a\r\n  INNER join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\nWHERE TRUE \r\nAND FALSE  \r\n\r\n\r\nUNION ALL \r\n\r\n/*---- ADJUSTMENT */\r\n/* persediaan */\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_QTY_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,b.SUBTOTAL *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n  INNER JOIN ADJUST_QTY_DETAIL B on (a.ADJUST_QTY_ID = B.ADJUST_QTY_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\n    \r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_QTY_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\n    \r\n&KONDISI\r\n/* END ADJUST_QTY */\r\n\r\nUNION ALL \r\n/*---- ADJUSTMENT NILAI*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.ENTERED_AMOUNT>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_AMOUNT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.ENTERED_AMOUNT>=0,b.ENTERED_AMOUNT ,0) AS DEBET,\r\n  IF(b.ENTERED_AMOUNT<0,b.ENTERED_AMOUNT *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n  INNER JOIN ADJUST_AMOUNT_DETAIL B on (a.ADJUST_AMOUNT_ID = B.ADJUST_AMOUNT_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.ENTERED_AMOUNT <> 0\r\n    \r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_AMOUNT_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\n    \r\n&KONDISI\r\n/* END ADJUST_AMOUNT */\r\n\r\nUNION ALL \r\n\r\n\r\n/*---- INVENTORY_IN*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_IN_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL -b.DISCOUNT_PRICE1 ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,(b.SUBTOTAL -b.DISCOUNT_PRICE1 )*-1 ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\n    \r\n&KONDISI\r\nUNION ALL \r\n/*  hutang suspend */\r\n\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(SUBTOTAL>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n I.ITEM_DESCRIPTION ,\r\n  a.INVENTORY_IN_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(SUBTOTAL<0, -1*(SUBTOTAL -b.DISCOUNT_PRICE1),0) AS DEBET,\r\n  if(SUBTOTAL>=0, SUBTOTAL-b.DISCOUNT_PRICE1,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_SUSPEND_ID  = c.COA_id)\r\n\r\nWHERE  B.SUBTOTAL <> 0\r\n    \r\n&KONDISI\r\n/* END INVENTORY_IN */\r\n\r\nUNION ALL\r\n/* INVOICE\r\n biaya-biaya yang ada di detail\r\n Biaya   1000\r\n ppn      90\r\n diskon        100\r\n hutang        990\r\n\r\n */\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as DEBET,\r\n  IF(b.SUBTOTAL<0,-1*(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN invoice_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n  B.SUBTOTAL <> 0 \r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* INVOICE\r\n/*PPN*/\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN \' AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n    IF(PPN_AMOUNT>0, PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as KREDIT\r\n\r\nFROM\r\n  invoice a,\r\n  V_ACCOUNT c\r\nWHERE TRUE\r\nand (c.COA_ID = @BELI_PPN)\r\nAND PPN_AMOUNT <> 0\r\nAND  (STATUS_ID <> @DELETE)\r\n    \r\n&KONDISI\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/*hutang dagang yang ada di header*/\r\nSELECT\r\n   IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_AMOUNT>=0,1,0) AS URUT,\r\n 20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_HUT_ID)\r\n\r\nWHERE TOTAL_NET <> 0\r\n    \r\n&KONDISI\r\n\r\n\r\n\r\n/* END INVOICE */\r\n\r\nUNION ALL\r\n\r\n/*  BUILD BARANG JADI */\r\nSELECT\r\n   \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(A.TOTAL_AMOUNT>= 0, 0,1) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  A.BUILD_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(A.TOTAL_AMOUNT>= 0, A.TOTAL_AMOUNT, 0) AS DEBET,\r\n  IF(A.TOTAL_AMOUNT< 0, -1*A.TOTAL_AMOUNT, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN item i ON (A.ITEM_ID = i.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\n WHERE A.STATUS_ID = @APPROVED AND (TOTAL_AMOUNT <> 0 )\r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/*  BUILD BAHAN BAKU NYA */\r\nSELECT\r\n  \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.SUBTOTAL>= 0, 1,0) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.BUILD_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.SUBTOTAL< 0, -1*B.SUBTOTAL, 0) AS DEBET,\r\n  IF(B.SUBTOTAL>= 0, B.SUBTOTAL, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN BUILD_DETAIL B ON (A.BUILD_ID = B.BUILD_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE A.STATUS_ID = @APPROVED AND \r\nB.SUBTOTAL<> 0\r\n    \r\n&KONDISI\r\n\r\n/* END BUILD */\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/* PENJUALAN */\r\n/*piutang dagang*/\r\nSELECT\r\n   \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_NET>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (B.COA_PIUT_ID = C.COA_ID )\r\nWHERE\r\n   (TOTAL_NET <> 0)\r\n and ( a.TOTAL_BYR1 = 0 )  \r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(NET_BYR1>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH1 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  NET_BYR1>=0, NET_BYR1, 0) as DEBET,\r\n  IF(NET_BYR1<0, -1*NET_BYR1, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER  join V_ACCOUNT c on (a.COA_BYR1 =c.COA_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\n    \r\n&KONDISI\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT\r\n  \'010001F\' AS TIPE, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_BYR2>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH2 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  TOTAL_BYR2>=0, TOTAL_BYR2, 0) as DEBET,\r\n  IF(TOTAL_BYR2<0, -1*TOTAL_BYR2, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER Join V_ACCOUNT c on (a.COA_BYR2 =c.COA_ID)\r\n\r\nWHERE \r\n   ( a.TOTAL_BYR2   > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/*DISCOUNT TOTAL*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_DISCOUNT>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'DISKON TOTAL \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_DISCOUNT>=0, TOTAL_DISCOUNT, 0) as DEBET,\r\n  IF(TOTAL_DISCOUNT<0, -1*TOTAL_DISCOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE   TRUE\r\n    \r\n&KONDISI\r\nand (c.COA_ID = @JUAL_DISC)\r\nAND TOTAL_DISCOUNT <> 0\r\n\r\n\r\nUNION ALL\r\n/*DISCOUNT PER ITEM */\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0, B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as DEBET,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY<0, -1*B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (A.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_DISC_JUAL_ID  = c.COA_ID)\r\nWHERE   (STATUS_ID <> @DELETE)\r\n    \r\n&KONDISI\r\nAND B.DISCOUNT_PRICE * B.ENTERED_QTY <> 0\r\nUNION ALL\r\n\r\n/*Penjualan*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,1,0) AS URUT,\r\n 50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY <0,(-1*b.UNIT_PRICE * b.ENTERED_QTY),0)  as DEBET,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY>=0,(b.UNIT_PRICE * b.ENTERED_QTY),0)  as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (B.COA_ID  = c.COA_ID)\r\n\r\nWHERE\r\n  (b.UNIT_PRICE * b.ENTERED_QTY <> 0)\r\n    \r\n&KONDISI\r\nUNION ALL\r\n/*ppn*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,1,0) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN PENJUALAN \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT>=0, PPN_AMOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE\r\n  ( PPN_AMOUNT <> 0)\r\n    \r\n&KONDISI\r\nand c.COA_ID = @JUAL_PPN\r\n\r\n\r\nUNION ALL \r\n/* HPP PADA PERSEDIAAN */\r\n/* HPP */\r\nSELECT\r\n  \'011001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,0 ,1)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY >=0, B.HPP   * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY <0 ,-1*B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN  NOTA_PENJUALAN_DETAIL B on (a.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT C ON (I.COA_HPP_ID = C.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0) AND (B.INVENTORY_OUT_DETAIL_ID IS NULL )\r\nAND (I.JENIS_ID = @GOODS)\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* \r\n PERSEDIAAN\r\n*/\r\nSELECT\r\n  \'011001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,1 ,0)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.NOTA_PENJUALAN_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY <0,-1*B.HPP * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN  NOTA_PENJUALAN_DETAIL B on (a.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0) AND (B.INVENTORY_OUT_DETAIL_ID IS NULL )\r\nAND (I.JENIS_ID = @GOODS)\r\n    \r\n&KONDISI\r\n\r\nUNION ALL \r\n/* HPP PADA PERSEDIAAN UTK SJALAN*/\r\n/* HPP */\r\nSELECT\r\n  \'001001G\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,0 ,1)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,B.HPP * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY <0,-1*B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A\r\n  INNER JOIN  INVENTORY_OUT_DETAIL B on (a.INVENTORY_OUT_ID = B.INVENTORY_OUT_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT C ON (I.COA_HPP_ID = C.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0) \r\nAND (I.JENIS_ID = @GOODS)\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* \r\n PERSEDIAAN (DARI SURAT JALAN)\r\n*/\r\nSELECT\r\n  \'001001G\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,1 ,0)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY <0,-1*B.HPP * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A\r\n  INNER JOIN  INVENTORY_OUT_DETAIL B on (a.INVENTORY_OUT_ID = B.INVENTORY_OUT_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0)\r\nAND (I.JENIS_ID = @GOODS)\r\n    \r\n&KONDISI\r\n\r\n/* END PENJUALAN */\r\nUNION ALL\r\n\r\n\r\n/*\r\n  BKM\r\nBANK\r\n\r\nKAS                   1000\r\nPOT. PIUTANG   100\r\n  PIUTANG              1100\r\n\r\n*/\r\nSELECT\r\n  \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  0 AS URUT,\r\n  60 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  A.TOTAL_AMOUNT  DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  RECEIPT A\r\n  INNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE\r\n(A.TOTAL_AMOUNT  <> 0)  \r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONGAN  BKM */\r\nSELECT\r\n  \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(DISCOUNT >=0,0,1) AS URUT,\r\n  60 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN PIUTANG\' AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(DISCOUNT > 0, DISCOUNT , 0) AS DEBET,\r\n  IF( DISCOUNT < 0,-1* DISCOUNT , 0) AS KREDIT\r\nFROM\r\n  RECEIPT A\r\n  INNER JOIN RECEIPT_DETAIL  B ON (A.RECEIPT_ID = B.RECEIPT_ID),\r\n  V_ACCOUNT c\r\nWHERE \r\n(B.COA_ID IS NULL AND B.DISCOUNT <> 0 )\r\nAND (((c.COA_ID = @POT_HUTANG) AND (B.INVOICE_DUE_ID IS NOT NULL )) OR\r\n ((c.COA_ID = @POT_PIUTANG) AND (B.NOTA_PENJUALAN_AGING_ID IS NOT NULL )))\r\n    \r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n/* BUKAN PELUNASAN BKM */\r\nSELECT\r\n  \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT>=0,1,0) AS URUT,\r\n  60 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  A1.NOTE AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A1.NOTE,   0 AS AWAL,\r\n  IF(A1.AMOUNT<0,-1*A1.AMOUNT, 0) AS DEBET,\r\n  IF(A1.AMOUNT>=0,A1.AMOUNT, 0) AS KREDIT\r\nFROM\r\n  RECEIPT  A\r\n  INNER JOIN RECEIPT_DETAIL  A1 ON (A.RECEIPT_ID = A1.RECEIPT_ID)\r\n  INNER JOIN V_ACCOUNT C ON (A1.COA_ID = C.COA_ID)\r\nWHERE\r\n(A1.AMOUNT  <> 0) AND \r\n(A1.COA_ID IS NOT NULL)\r\n    \r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n/* PELUNASAN BKM */\r\nSELECT\r\n   \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT  >=0,1,0) AS URUT,\r\n  60 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(AMOUNT + B.DISCOUNT   <0,-1*(AMOUNT + B.DISCOUNT ) , 0) AS DEBET,\r\n  IF(AMOUNT + B.DISCOUNT   >=0,AMOUNT + B.DISCOUNT   , 0) AS KREDIT\r\nFROM\r\n  RECEIPT A\r\n  INNER JOIN RECEIPT_DETAIL  B ON (A.RECEIPT_ID = B.RECEIPT_ID)\r\n  INNER JOIN PERSON V ON (A.PERSON_ID = V.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT C ON ((V.COA_HUT_ID = C.COA_ID AND B.INVOICE_DUE_ID IS NOT NULL) OR \r\n  (V.COA_PIUT_ID = C.COA_ID AND B.NOTA_PENJUALAN_AGING_ID IS NOT NULL) )\r\nWHERE\r\n((B.AMOUNT + B.DISCOUNT)  <> 0) AND \r\n(B.COA_ID IS NULL )\r\n    \r\n&KONDISI\r\n\r\n/* END BKM */\r\n\r\n\r\nUNION ALL\r\n/* BKK\r\nHUTANG    1000\r\n   KAS         900\r\n   POT.	       100\r\n\r\n*/\r\n/* BKK */\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT>=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  C.COA_NAME,\r\nC.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  A1.NOTE AS ITEM_DESCRIPTION ,\r\nA.PAYMENT_ID,\r\nA.NOTE,   0 AS AWAL,\r\nIF(AMOUNT>=0,AMOUNT, 0) AS DEBET,\r\nIF(AMOUNT<0,-1*AMOUNT, 0) AS KREDIT\r\nFROM\r\nPAYMENT A\r\nINNER JOIN PAYMENT_DETAIL A1 ON (A.PAYMENT_ID = A1.PAYMENT_ID)\r\nINNER JOIN V_ACCOUNT C ON (A1.COA_ID = C.COA_ID)\r\nWHERE\r\n(A1.AMOUNT  <> 0) \r\nAND A1.COA_ID IS NOT NULL\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* PELUNASAN HUTANG */\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT >=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(AMOUNT + B.DISCOUNT >=0,AMOUNT + B.DISCOUNT , 0) AS DEBET,\r\n  IF(AMOUNT + B.DISCOUNT <0,-1*(AMOUNT + B.DISCOUNT ) , 0) AS KREDIT\r\nFROM\r\n  PAYMENT  A\r\n  INNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n  INNER JOIN PERSON V ON (A.PERSON_ID = V.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT C ON ((V.COA_HUT_ID = C.COA_ID AND B.INVOICE_DUE_ID IS NOT NULL) OR \r\n  (V.COA_PIUT_ID = C.COA_ID AND B.NOTA_PENJUALAN_AGING_ID IS NOT NULL) )\r\nWHERE (AMOUNT + B.DISCOUNT) <> 0 \r\nAND (B.COA_ID IS NULL ) \r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONG HUTANG*/\r\n\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(DISCOUNT>=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN HUTANG\' AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(DISCOUNT<0,-1*DISCOUNT, 0)  AS DEBET,\r\n  IF(DISCOUNT>=0,DISCOUNT, 0)AS KREDIT\r\nFROM\r\nPAYMENT  A\r\nINNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n,\r\nV_ACCOUNT c\r\nWHERE \r\nDISCOUNT <> 0\r\nAND (((c.COA_ID = @POT_HUTANG) AND (B.INVOICE_DUE_ID IS NOT NULL )) OR\r\n ((c.COA_ID = @POT_PIUTANG) AND (B.NOTA_PENJUALAN_AGING_ID IS NOT NULL )))\r\nAND (B.COA_ID IS NULL)\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\n/*\r\n  BKK\r\nBANK\r\n*/\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 AS URUT,\r\n  70 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  0  AS DEBET,\r\n  A.TOTAL_AMOUNT KREDIT\r\nFROM\r\n  PAYMENT A\r\n  INNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE   \r\n(A.TOTAL_AMOUNT  <> 0) \r\n    \r\n&KONDISI\r\n\r\n/* END OF BKK */\r\n\r\n\r\n\r\nUNION ALL\r\n/* JURNAL MEMO RIAL  */\r\nSELECT\r\n  \'000001J\' AS TIPE,\r\n\'JURNAL MEMO \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  80 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   a.NOTE as  ITEM_DESCRIPTION ,\r\n  a.JOURNAL_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  AMOUNT as DEBET,\r\nAMOUNT_CR as KREDIT\r\nFROM\r\n  JOURNAL a\r\n  INNER JOIN JOURNAL_detail b ON (a.JOURNAL_ID = b.JOURNAL_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n    \r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n\r\n/* BG */ \r\n\r\n/*  BG  CAIR / TOLAK BANK TUJUAN */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 0, 1)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID_DEST = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in (@CLEAR, @TOLAK ))\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* BG  CAIR / TOLAK BANK ASAL */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 1, 0)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID  = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in  (@CLEAR, @TOLAK ))\r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/* BG  TOLAK BANK ASAL */\r\nSELECT\r\n   IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n  AND (p.TGL_TOLAK between :11 and :21 )  \r\nUNION ALL\r\n/* BG   TOLAK BANK TUJUAN */\r\nSELECT\r\n    IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  2 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT  ,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID_DEST = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n  AND (p.TGL_TOLAK between :11 and :21 )  \r\n\r\n)  AS XX\r\nWHERE TRUE \r\nORDER BY DOCUMENT_CLASS_CODE, DOCUMENT_NO, URUT, URUT1,  COA_ID,   DOCUMENT_DATE', NULL, '', NULL),
      (1162, 1010, 0, 'select \r\n*\r\nFrom\r\n(\r\n\r\n\r\n\r\n/*\r\nTIPE --> digit menunjukan Jurnal BELI-JUAL-INV-BKK-BKM-GL\r\n\r\n  = PERIODE\r\n  = TANGGAL\r\nAND (p.TGL_TOLAK between :11 and :21 ) = TANGGAL TOLAK\r\n\r\n = ACCOUNT\r\n*/\r\n\r\n/*saldo awal */\r\nselect\r\n  \'111111A\' as TIPE,\r\n  \'SALDO AWAL\' AS DOCUMENT_CLASS_CODE,\r\n  \' \' AS DOCUMENT_NO ,\r\nBALANCE_DATE AS  DOCUMENT_DATE,\r\n  \' \' AS DOCUMENT_REFF_NO ,\r\n  0  AS URUT,\r\n  1 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,\r\nC.ACCOUNT_ID ,\r\n \' \'  ITEM_DESCRIPTION ,\r\n  0 AS ID,\r\n  \' SALDO AWAL \' AS NOTE,\r\n   COA_SALDO AS AWAL,\r\n  0 AS DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  COA_BALANCE a\r\n  INNER join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\nWHERE TRUE \r\nAND FALSE  \r\n\r\n\r\nUNION ALL \r\n\r\n/*---- ADJUSTMENT */\r\n/* persediaan */\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_QTY_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,b.SUBTOTAL *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n  INNER JOIN ADJUST_QTY_DETAIL B on (a.ADJUST_QTY_ID = B.ADJUST_QTY_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\n    \r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_QTY_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\n    \r\n&KONDISI\r\n/* END ADJUST_QTY */\r\n\r\nUNION ALL \r\n/*---- ADJUSTMENT NILAI*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.ENTERED_AMOUNT>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_AMOUNT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.ENTERED_AMOUNT>=0,b.ENTERED_AMOUNT ,0) AS DEBET,\r\n  IF(b.ENTERED_AMOUNT<0,b.ENTERED_AMOUNT *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n  INNER JOIN ADJUST_AMOUNT_DETAIL B on (a.ADJUST_AMOUNT_ID = B.ADJUST_AMOUNT_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.ENTERED_AMOUNT <> 0\r\n    \r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_AMOUNT_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\n    \r\n&KONDISI\r\n/* END ADJUST_AMOUNT */\r\n\r\nUNION ALL \r\n\r\n\r\n/*---- INVENTORY_IN*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_IN_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL -b.DISCOUNT_PRICE1 ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,(b.SUBTOTAL -b.DISCOUNT_PRICE1 )*-1 ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\n    \r\n&KONDISI\r\nUNION ALL \r\n/*  hutang suspend */\r\n\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(SUBTOTAL>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n I.ITEM_DESCRIPTION ,\r\n  a.INVENTORY_IN_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(SUBTOTAL<0, -1*(SUBTOTAL -b.DISCOUNT_PRICE1),0) AS DEBET,\r\n  if(SUBTOTAL>=0, SUBTOTAL-b.DISCOUNT_PRICE1,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_SUSPEND_ID  = c.COA_id)\r\n\r\nWHERE  B.SUBTOTAL <> 0\r\n    \r\n&KONDISI\r\n/* END INVENTORY_IN */\r\n\r\nUNION ALL\r\n/* INVOICE\r\n biaya-biaya yang ada di detail\r\n Biaya   1000\r\n ppn      90\r\n diskon        100\r\n hutang        990\r\n\r\n */\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as DEBET,\r\n  IF(b.SUBTOTAL<0,-1*(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN invoice_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n  B.SUBTOTAL <> 0 \r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* INVOICE\r\n/*PPN*/\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN \' AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n    IF(PPN_AMOUNT>0, PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as KREDIT\r\n\r\nFROM\r\n  invoice a,\r\n  V_ACCOUNT c\r\nWHERE TRUE\r\nand (c.COA_ID = @BELI_PPN)\r\nAND PPN_AMOUNT <> 0\r\nAND  (STATUS_ID <> @DELETE)\r\n    \r\n&KONDISI\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/*hutang dagang yang ada di header*/\r\nSELECT\r\n   IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_AMOUNT>=0,1,0) AS URUT,\r\n 20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_HUT_ID)\r\n\r\nWHERE TOTAL_NET <> 0\r\n    \r\n&KONDISI\r\n\r\n\r\n\r\n/* END INVOICE */\r\n\r\nUNION ALL\r\n\r\n/*  BUILD BARANG JADI */\r\nSELECT\r\n   \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(A.TOTAL_AMOUNT>= 0, 0,1) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  A.BUILD_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(A.TOTAL_AMOUNT>= 0, A.TOTAL_AMOUNT, 0) AS DEBET,\r\n  IF(A.TOTAL_AMOUNT< 0, -1*A.TOTAL_AMOUNT, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN item i ON (A.ITEM_ID = i.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\n WHERE A.STATUS_ID = @APPROVED AND (TOTAL_AMOUNT <> 0 )\r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/*  BUILD BAHAN BAKU NYA */\r\nSELECT\r\n  \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.SUBTOTAL>= 0, 1,0) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.BUILD_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.SUBTOTAL< 0, -1*B.SUBTOTAL, 0) AS DEBET,\r\n  IF(B.SUBTOTAL>= 0, B.SUBTOTAL, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN BUILD_DETAIL B ON (A.BUILD_ID = B.BUILD_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE A.STATUS_ID = @APPROVED AND \r\nB.SUBTOTAL<> 0\r\n    \r\n&KONDISI\r\n\r\n/* END BUILD */\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/* PENJUALAN */\r\n/*piutang dagang*/\r\nSELECT\r\n   \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_NET>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (B.COA_PIUT_ID = C.COA_ID )\r\nWHERE\r\n   (TOTAL_NET <> 0)\r\n and ( a.TOTAL_BYR1 = 0 )  \r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(NET_BYR1>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH1 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  NET_BYR1>=0, NET_BYR1, 0) as DEBET,\r\n  IF(NET_BYR1<0, -1*NET_BYR1, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER  join V_ACCOUNT c on (a.COA_BYR1 =c.COA_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\n    \r\n&KONDISI\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT\r\n  \'010001F\' AS TIPE, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_BYR2>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH2 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  TOTAL_BYR2>=0, TOTAL_BYR2, 0) as DEBET,\r\n  IF(TOTAL_BYR2<0, -1*TOTAL_BYR2, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER Join V_ACCOUNT c on (a.COA_BYR2 =c.COA_ID)\r\n\r\nWHERE \r\n   ( a.TOTAL_BYR2   > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/*DISCOUNT TOTAL*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_DISCOUNT>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'DISKON TOTAL \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_DISCOUNT>=0, TOTAL_DISCOUNT, 0) as DEBET,\r\n  IF(TOTAL_DISCOUNT<0, -1*TOTAL_DISCOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE   TRUE\r\n    \r\n&KONDISI\r\nand (c.COA_ID = @JUAL_DISC)\r\nAND TOTAL_DISCOUNT <> 0\r\n\r\n\r\nUNION ALL\r\n/*DISCOUNT PER ITEM */\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0, B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as DEBET,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY<0, -1*B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (A.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_DISC_JUAL_ID  = c.COA_ID)\r\nWHERE   (STATUS_ID <> @DELETE)\r\n    \r\n&KONDISI\r\nAND B.DISCOUNT_PRICE * B.ENTERED_QTY <> 0\r\nUNION ALL\r\n\r\n/*Penjualan*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,1,0) AS URUT,\r\n 50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY <0,(-1*b.UNIT_PRICE * b.ENTERED_QTY),0)  as DEBET,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY>=0,(b.UNIT_PRICE * b.ENTERED_QTY),0)  as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (B.COA_ID  = c.COA_ID)\r\n\r\nWHERE\r\n  (b.UNIT_PRICE * b.ENTERED_QTY <> 0)\r\n    \r\n&KONDISI\r\nUNION ALL\r\n/*ppn*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,1,0) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN PENJUALAN \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT>=0, PPN_AMOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE\r\n  ( PPN_AMOUNT <> 0)\r\n    \r\n&KONDISI\r\nand c.COA_ID = @JUAL_PPN\r\n\r\n\r\nUNION ALL \r\n/* HPP PADA PERSEDIAAN */\r\n/* HPP */\r\nSELECT\r\n  \'011001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,0 ,1)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY >=0, B.HPP   * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY <0 ,-1*B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN  NOTA_PENJUALAN_DETAIL B on (a.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT C ON (I.COA_HPP_ID = C.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0) AND (B.INVENTORY_OUT_DETAIL_ID IS NULL )\r\nAND (I.JENIS_ID = @GOODS)\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* \r\n PERSEDIAAN\r\n*/\r\nSELECT\r\n  \'011001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,1 ,0)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.NOTA_PENJUALAN_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY <0,-1*B.HPP * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN  NOTA_PENJUALAN_DETAIL B on (a.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0) AND (B.INVENTORY_OUT_DETAIL_ID IS NULL )\r\nAND (I.JENIS_ID = @GOODS)\r\n    \r\n&KONDISI\r\n\r\nUNION ALL \r\n/* HPP PADA PERSEDIAAN UTK SJALAN*/\r\n/* HPP */\r\nSELECT\r\n  \'001001G\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,0 ,1)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,B.HPP * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY <0,-1*B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A\r\n  INNER JOIN  INVENTORY_OUT_DETAIL B on (a.INVENTORY_OUT_ID = B.INVENTORY_OUT_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT C ON (I.COA_HPP_ID = C.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0) \r\nAND (I.JENIS_ID = @GOODS)\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* \r\n PERSEDIAAN (DARI SURAT JALAN)\r\n*/\r\nSELECT\r\n  \'001001G\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,1 ,0)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY <0,-1*B.HPP * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A\r\n  INNER JOIN  INVENTORY_OUT_DETAIL B on (a.INVENTORY_OUT_ID = B.INVENTORY_OUT_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0)\r\nAND (I.JENIS_ID = @GOODS)\r\n    \r\n&KONDISI\r\n\r\n/* END PENJUALAN */\r\nUNION ALL\r\n\r\n\r\n/*\r\n  BKM\r\nBANK\r\n\r\nKAS                   1000\r\nPOT. PIUTANG   100\r\n  PIUTANG              1100\r\n\r\n*/\r\nSELECT\r\n  \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  0 AS URUT,\r\n  60 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  A.TOTAL_AMOUNT  DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  RECEIPT A\r\n  INNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE\r\n(A.TOTAL_AMOUNT  <> 0)  \r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONGAN  BKM */\r\nSELECT\r\n  \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(DISCOUNT >=0,0,1) AS URUT,\r\n  60 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN PIUTANG\' AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(DISCOUNT > 0, DISCOUNT , 0) AS DEBET,\r\n  IF( DISCOUNT < 0,-1* DISCOUNT , 0) AS KREDIT\r\nFROM\r\n  RECEIPT A\r\n  INNER JOIN RECEIPT_DETAIL  B ON (A.RECEIPT_ID = B.RECEIPT_ID),\r\n  V_ACCOUNT c\r\nWHERE \r\n(B.COA_ID IS NULL AND B.DISCOUNT <> 0 )\r\nAND (((c.COA_ID = @POT_HUTANG) AND (B.INVOICE_DUE_ID IS NOT NULL )) OR\r\n ((c.COA_ID = @POT_PIUTANG) AND (B.NOTA_PENJUALAN_AGING_ID IS NOT NULL )))\r\n    \r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n/* BUKAN PELUNASAN BKM */\r\nSELECT\r\n  \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT>=0,1,0) AS URUT,\r\n  60 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  A1.NOTE AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A1.NOTE,   0 AS AWAL,\r\n  IF(A1.AMOUNT<0,-1*A1.AMOUNT, 0) AS DEBET,\r\n  IF(A1.AMOUNT>=0,A1.AMOUNT, 0) AS KREDIT\r\nFROM\r\n  RECEIPT  A\r\n  INNER JOIN RECEIPT_DETAIL  A1 ON (A.RECEIPT_ID = A1.RECEIPT_ID)\r\n  INNER JOIN V_ACCOUNT C ON (A1.COA_ID = C.COA_ID)\r\nWHERE\r\n(A1.AMOUNT  <> 0) AND \r\n(A1.COA_ID IS NOT NULL)\r\n    \r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n/* PELUNASAN BKM */\r\nSELECT\r\n   \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT  >=0,1,0) AS URUT,\r\n  60 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(AMOUNT + B.DISCOUNT   <0,-1*(AMOUNT + B.DISCOUNT ) , 0) AS DEBET,\r\n  IF(AMOUNT + B.DISCOUNT   >=0,AMOUNT + B.DISCOUNT   , 0) AS KREDIT\r\nFROM\r\n  RECEIPT A\r\n  INNER JOIN RECEIPT_DETAIL  B ON (A.RECEIPT_ID = B.RECEIPT_ID)\r\n  INNER JOIN PERSON V ON (A.PERSON_ID = V.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT C ON ((V.COA_HUT_ID = C.COA_ID AND B.INVOICE_DUE_ID IS NOT NULL) OR \r\n  (V.COA_PIUT_ID = C.COA_ID AND B.NOTA_PENJUALAN_AGING_ID IS NOT NULL) )\r\nWHERE\r\n((B.AMOUNT + B.DISCOUNT)  <> 0) AND \r\n(B.COA_ID IS NULL )\r\n    \r\n&KONDISI\r\n\r\n/* END BKM */\r\n\r\n\r\nUNION ALL\r\n/* BKK\r\nHUTANG    1000\r\n   KAS         900\r\n   POT.	       100\r\n\r\n*/\r\n/* BKK */\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT>=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  C.COA_NAME,\r\nC.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  A1.NOTE AS ITEM_DESCRIPTION ,\r\nA.PAYMENT_ID,\r\nA.NOTE,   0 AS AWAL,\r\nIF(AMOUNT>=0,AMOUNT, 0) AS DEBET,\r\nIF(AMOUNT<0,-1*AMOUNT, 0) AS KREDIT\r\nFROM\r\nPAYMENT A\r\nINNER JOIN PAYMENT_DETAIL A1 ON (A.PAYMENT_ID = A1.PAYMENT_ID)\r\nINNER JOIN V_ACCOUNT C ON (A1.COA_ID = C.COA_ID)\r\nWHERE\r\n(A1.AMOUNT  <> 0) \r\nAND A1.COA_ID IS NOT NULL\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* PELUNASAN HUTANG */\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT >=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(AMOUNT + B.DISCOUNT >=0,AMOUNT + B.DISCOUNT , 0) AS DEBET,\r\n  IF(AMOUNT + B.DISCOUNT <0,-1*(AMOUNT + B.DISCOUNT ) , 0) AS KREDIT\r\nFROM\r\n  PAYMENT  A\r\n  INNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n  INNER JOIN PERSON V ON (A.PERSON_ID = V.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT C ON ((V.COA_HUT_ID = C.COA_ID AND B.INVOICE_DUE_ID IS NOT NULL) OR \r\n  (V.COA_PIUT_ID = C.COA_ID AND B.NOTA_PENJUALAN_AGING_ID IS NOT NULL) )\r\nWHERE (AMOUNT + B.DISCOUNT) <> 0 \r\nAND (B.COA_ID IS NULL ) \r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONG HUTANG*/\r\n\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(DISCOUNT>=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN HUTANG\' AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(DISCOUNT<0,-1*DISCOUNT, 0)  AS DEBET,\r\n  IF(DISCOUNT>=0,DISCOUNT, 0)AS KREDIT\r\nFROM\r\nPAYMENT  A\r\nINNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n,\r\nV_ACCOUNT c\r\nWHERE \r\nDISCOUNT <> 0\r\nAND (((c.COA_ID = @POT_HUTANG) AND (B.INVOICE_DUE_ID IS NOT NULL )) OR\r\n ((c.COA_ID = @POT_PIUTANG) AND (B.NOTA_PENJUALAN_AGING_ID IS NOT NULL )))\r\nAND (B.COA_ID IS NULL)\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\n/*\r\n  BKK\r\nBANK\r\n*/\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 AS URUT,\r\n  70 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  0  AS DEBET,\r\n  A.TOTAL_AMOUNT KREDIT\r\nFROM\r\n  PAYMENT A\r\n  INNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE   \r\n(A.TOTAL_AMOUNT  <> 0) \r\n    \r\n&KONDISI\r\n\r\n/* END OF BKK */\r\n\r\n\r\n\r\nUNION ALL\r\n/* JURNAL MEMO RIAL  */\r\nSELECT\r\n  \'000001J\' AS TIPE,\r\n\'JURNAL MEMO \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  80 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   a.NOTE as  ITEM_DESCRIPTION ,\r\n  a.JOURNAL_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  AMOUNT as DEBET,\r\nAMOUNT_CR as KREDIT\r\nFROM\r\n  J