      (1171, 110, 0, 'select\r\n@no := @no + 1 as nmr,\r\n 0 AS URUT, \r\nv.PERSON_id,\r\nv.PERSON_name,\r\nv.PERSON_code,\r\n i.DOCUMENT_NO, i.DOCUMENT_DATE,\r\n id.ENTERED_QTY, id.ENTERED_UOM, id.note,\r\n t.ITEM_CODE,\r\n t.ITEM_DESCRIPTION, \r\n w.warehouse_name,  \r\n  p.document_no NO_PO,\r\npd.Entered_qty QTY_PO,\r\n  pd.ENTERED_QTY - (id.ENTERED_QTY  ) AS SISA\r\nFROM\r\ninventory_in i\r\ninner join inventory_in_detail id on (i.inventory_in_id = id.inventory_in_id)\r\ninner join Po p on (i.po_id = p.po_id) \r\ninner join po_detail pd on (id.PO_DETAIL_ID = pd.PO_DETAIL_ID)\r\ninner join item t on (id.item_id = t.item_id)\r\ninner join warehouse w on (i.warehouse_id = w.warehouse_id)\r\ninner join PERSON v on (i.PERSON_id = v.PERSON_id)\r\nwhere i.status_id <>  230082\r\n \r\n \r\n&kondisi\r\norder by URUT, document_no', NULL, NULL, NULL),
      (1172, 115, 0, 'select\r\n@no := @no + 1 as nmr,\r\n 0 AS URUT, \r\nv.PERSON_id,\r\nv.PERSON_name,\r\nv.PERSON_code,\r\n i.DOCUMENT_NO, i.DOCUMENT_DATE,\r\n id.ENTERED_QTY, id.ENTERED_UOM, id.note,\r\n t.ITEM_CODE,\r\n t.ITEM_DESCRIPTION, \r\n w.warehouse_name,  \r\n  p.document_no NO_So,\r\npd.Entered_qty QTY_So,\r\n  pd.ENTERED_QTY - (id.ENTERED_QTY  ) AS SISA\r\nFROM\r\ninventory_out i\r\ninner join inventory_out_detail id on (i.inventory_out_id = id.inventory_out_id)\r\ninner join So p on (i.So_id = p.So_id) \r\ninner join So_detail pd on (id.So_DETAIL_ID = pd.So_DETAIL_ID)\r\ninner join item t on (id.item_id = t.item_id)\r\ninner join warehouse w on (i.warehouse_id = w.warehouse_id)\r\ninner join PERSON v on (i.PERSON_id = v.PERSON_id)\r\nwhere i.status_id <>  230082\r\n \r\n \r\n&kondisi\r\norder by URUT, document_no', NULL, NULL, NULL),
      (1175, 1160, 0, 'SELECT COA_ID,COA_CODE,  COA_NAME  ,\r\n  SUM(AWAL)  as AWAL, \r\n  SUM(DEBET)  AS DEBET,\r\n  SUM(KREDIT) AS KREDIT\r\n FROM\r\n(\r\n\r\n/*\r\nTIPE --> digit menunjukan Jurnal BELI-JUAL-INV-BKK-BKM-GL\r\n\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)  = PERIODE\r\n  = TANGGAL\r\n = TANGGAL TOLAK\r\n\r\n = ACCOUNT\r\n*/\r\n\r\n/*saldo awal */\r\nselect\r\n  \'111111A\' as TIPE,\r\n  \'SALDO AWAL\' AS DOCUMENT_CLASS_CODE,\r\n  \' \' AS DOCUMENT_NO ,\r\nBALANCE_DATE AS  DOCUMENT_DATE,\r\n  \' \' AS DOCUMENT_REFF_NO ,\r\n  0  AS URUT,\r\n  1 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,\r\nC.ACCOUNT_ID ,\r\n \' \'  ITEM_DESCRIPTION ,\r\n  0 AS ID,\r\n  \' SALDO AWAL \' AS NOTE,\r\n   COA_SALDO AS AWAL,\r\n  0 AS DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  COA_BALANCE a\r\n  INNER join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\nWHERE TRUE \r\n  \r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n\r\nUNION ALL \r\n\r\n/*---- ADJUSTMENT */\r\n/* persediaan */\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_QTY_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,b.SUBTOTAL *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n  INNER JOIN ADJUST_QTY_DETAIL B on (a.ADJUST_QTY_ID = B.ADJUST_QTY_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_QTY_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n/* END ADJUST_QTY */\r\n\r\nUNION ALL \r\n/*---- ADJUSTMENT NILAI*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.ENTERED_AMOUNT>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_AMOUNT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.ENTERED_AMOUNT>=0,b.ENTERED_AMOUNT ,0) AS DEBET,\r\n  IF(b.ENTERED_AMOUNT<0,b.ENTERED_AMOUNT *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n  INNER JOIN ADJUST_AMOUNT_DETAIL B on (a.ADJUST_AMOUNT_ID = B.ADJUST_AMOUNT_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.ENTERED_AMOUNT <> 0\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_AMOUNT_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n/* END ADJUST_AMOUNT */\r\n\r\nUNION ALL \r\n\r\n\r\n/*---- INVENTORY_IN*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_IN_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL -b.DISCOUNT_PRICE1 ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,(b.SUBTOTAL -b.DISCOUNT_PRICE1 )*-1 ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nUNION ALL \r\n/*  hutang suspend */\r\n\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(SUBTOTAL>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n I.ITEM_DESCRIPTION ,\r\n  a.INVENTORY_IN_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(SUBTOTAL<0, -1*(SUBTOTAL -b.DISCOUNT_PRICE1),0) AS DEBET,\r\n  if(SUBTOTAL>=0, SUBTOTAL-b.DISCOUNT_PRICE1,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_SUSPEND_ID  = c.COA_id)\r\n\r\nWHERE  B.SUBTOTAL <> 0\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n/* END INVENTORY_IN */\r\n\r\nUNION ALL\r\n/* INVOICE\r\n biaya-biaya yang ada di detail\r\n Biaya   1000\r\n ppn      90\r\n diskon        100\r\n hutang        990\r\n\r\n */\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as DEBET,\r\n  IF(b.SUBTOTAL<0,-1*(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN invoice_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n  B.SUBTOTAL <> 0 \r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* INVOICE\r\n/*PPN*/\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN \' AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n    IF(PPN_AMOUNT>0, PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as KREDIT\r\n\r\nFROM\r\n  invoice a,\r\n  V_ACCOUNT c\r\nWHERE TRUE\r\nand (c.COA_ID = @BELI_PPN)\r\nAND PPN_AMOUNT <> 0\r\nAND  (STATUS_ID <> @DELETE)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/*hutang dagang yang ada di header*/\r\nSELECT\r\n   IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_AMOUNT>=0,1,0) AS URUT,\r\n 20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_HUT_ID)\r\n\r\nWHERE TOTAL_NET <> 0\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\n\r\n\r\n/* END INVOICE */\r\n\r\nUNION ALL\r\n\r\n/*  BUILD BARANG JADI */\r\nSELECT\r\n   \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(A.TOTAL_AMOUNT>= 0, 0,1) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  A.BUILD_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(A.TOTAL_AMOUNT>= 0, A.TOTAL_AMOUNT, 0) AS DEBET,\r\n  IF(A.TOTAL_AMOUNT< 0, -1*A.TOTAL_AMOUNT, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN item i ON (A.ITEM_ID = i.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\n WHERE (TOTAL_AMOUNT <> 0 )\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/*  BUILD BAHAN BAKU NYA */\r\nSELECT\r\n  \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.SUBTOTAL>= 0, 1,0) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.BUILD_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.SUBTOTAL< 0, -1*B.SUBTOTAL, 0) AS DEBET,\r\n  IF(B.SUBTOTAL>= 0, B.SUBTOTAL, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN BUILD_DETAIL B ON (A.BUILD_ID = B.BUILD_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE\r\nB.SUBTOTAL<> 0\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\n/* END BUILD */\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/* PENJUALAN */\r\n/*piutang dagang*/\r\nSELECT\r\n   \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_NET>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (B.COA_PIUT_ID = C.COA_ID )\r\nWHERE\r\n   (TOTAL_NET <> 0)\r\n and ( a.TOTAL_BYR1 = 0 )  \r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(NET_BYR1>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH1 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  NET_BYR1>=0, NET_BYR1, 0) as DEBET,\r\n  IF(NET_BYR1<0, -1*NET_BYR1, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER  join V_ACCOUNT c on (a.COA_BYR1 =c.COA_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT\r\n  \'010001F\' AS TIPE, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_BYR2>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH2 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  TOTAL_BYR2>=0, TOTAL_BYR2, 0) as DEBET,\r\n  IF(TOTAL_BYR2<0, -1*TOTAL_BYR2, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER Join V_ACCOUNT c on (a.COA_BYR2 =c.COA_ID)\r\n\r\nWHERE \r\n   ( a.TOTAL_BYR2   > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/*DISCOUNT TOTAL*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_DISCOUNT>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'DISKON TOTAL \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_DISCOUNT>=0, TOTAL_DISCOUNT, 0) as DEBET,\r\n  IF(TOTAL_DISCOUNT<0, -1*TOTAL_DISCOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE   TRUE\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nand (c.COA_ID = @JUAL_DISC)\r\nAND TOTAL_DISCOUNT <> 0\r\n\r\n\r\nUNION ALL\r\n/*DISCOUNT PER ITEM */\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0, B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as DEBET,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY<0, -1*B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (A.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_DISC_JUAL_ID  = c.COA_ID)\r\nWHERE   (STATUS_ID <> @DELETE)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nAND B.DISCOUNT_PRICE * B.ENTERED_QTY <> 0\r\nUNION ALL\r\n\r\n/*Penjualan*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,1,0) AS URUT,\r\n 50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY <0,(-1*b.UNIT_PRICE * b.ENTERED_QTY),0)  as DEBET,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY>=0,(b.UNIT_PRICE * b.ENTERED_QTY),0)  as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (B.COA_ID  = c.COA_ID)\r\n\r\nWHERE\r\n  (b.UNIT_PRICE * b.ENTERED_QTY <> 0)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nUNION ALL\r\n/*ppn*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,1,0) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN PENJUALAN \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT>=0, PPN_AMOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE\r\n  ( PPN_AMOUNT <> 0)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nand c.COA_ID = @JUAL_PPN\r\n\r\n\r\nUNION ALL \r\n/* HPP PADA PERSEDIAAN */\r\n/* HPP */\r\nSELECT\r\n  \'011001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,0 ,1)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY >=0, B.HPP   * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY <0 ,-1*B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN  NOTA_PENJUALAN_DETAIL B on (a.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT C ON (I.COA_HPP_ID = C.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0) AND (B.INVENTORY_OUT_DETAIL_ID IS NULL )\r\nAND (I.JENIS_ID = @GOODS)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* \r\n PERSEDIAAN\r\n*/\r\nSELECT\r\n  \'011001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,1 ,0)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.NOTA_PENJUALAN_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY <0,-1*B.HPP * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN  NOTA_PENJUALAN_DETAIL B on (a.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0) AND (B.INVENTORY_OUT_DETAIL_ID IS NULL )\r\nAND (I.JENIS_ID = @GOODS)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\nUNION ALL \r\n/* HPP PADA PERSEDIAAN UTK SJALAN*/\r\n/* HPP */\r\nSELECT\r\n  \'001001G\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,0 ,1)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,B.HPP * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY <0,-1*B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A\r\n  INNER JOIN  INVENTORY_OUT_DETAIL B on (a.INVENTORY_OUT_ID = B.INVENTORY_OUT_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT C ON (I.COA_HPP_ID = C.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0) \r\nAND (I.JENIS_ID = @GOODS)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* \r\n PERSEDIAAN (DARI SURAT JALAN)\r\n*/\r\nSELECT\r\n  \'001001G\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,1 ,0)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY <0,-1*B.HPP * B.ENTERED_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY >=0,B.HPP * B.ENTERED_QTY ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A\r\n  INNER JOIN  INVENTORY_OUT_DETAIL B on (a.INVENTORY_OUT_ID = B.INVENTORY_OUT_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE (B.HPP * B.ENTERED_QTY <> 0)\r\nAND (I.JENIS_ID = @GOODS)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\n/* END PENJUALAN */\r\nUNION ALL\r\n\r\n\r\n/*\r\n  BKM\r\nBANK\r\n\r\nKAS                   1000\r\nPOT. PIUTANG   100\r\n  PIUTANG              1100\r\n\r\n*/\r\nSELECT\r\n  \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  0 AS URUT,\r\n  60 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  A.TOTAL_AMOUNT  DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  RECEIPT A\r\n  INNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE\r\n(A.TOTAL_AMOUNT  <> 0)  \r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONGAN  BKM */\r\nSELECT\r\n  \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(DISCOUNT >=0,0,1) AS URUT,\r\n  60 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN PIUTANG\' AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(DISCOUNT > 0, DISCOUNT , 0) AS DEBET,\r\n  IF( DISCOUNT < 0,-1* DISCOUNT , 0) AS KREDIT\r\nFROM\r\n  RECEIPT A\r\n  INNER JOIN RECEIPT_DETAIL  B ON (A.RECEIPT_ID = B.RECEIPT_ID),\r\n  V_ACCOUNT c\r\nWHERE \r\n(B.COA_ID IS NULL AND B.DISCOUNT <> 0 )\r\nAND (((c.COA_ID = @POT_HUTANG) AND (B.INVOICE_DUE_ID IS NOT NULL )) OR\r\n ((c.COA_ID = @POT_PIUTANG) AND (B.NOTA_PENJUALAN_AGING_ID IS NOT NULL )))\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n/* BUKAN PELUNASAN BKM */\r\nSELECT\r\n  \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT>=0,1,0) AS URUT,\r\n  60 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  A1.NOTE AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A1.NOTE,   0 AS AWAL,\r\n  IF(A1.AMOUNT<0,-1*A1.AMOUNT, 0) AS DEBET,\r\n  IF(A1.AMOUNT>=0,A1.AMOUNT, 0) AS KREDIT\r\nFROM\r\n  RECEIPT  A\r\n  INNER JOIN RECEIPT_DETAIL  A1 ON (A.RECEIPT_ID = A1.RECEIPT_ID)\r\n  INNER JOIN V_ACCOUNT C ON (A1.COA_ID = C.COA_ID)\r\nWHERE\r\n(A1.AMOUNT  <> 0) AND \r\n(A1.COA_ID IS NOT NULL)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n/* PELUNASAN BKM */\r\nSELECT\r\n   \'000011H\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT  >=0,1,0) AS URUT,\r\n  60 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  A.RECEIPT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(AMOUNT + B.DISCOUNT   <0,-1*(AMOUNT + B.DISCOUNT ) , 0) AS DEBET,\r\n  IF(AMOUNT + B.DISCOUNT   >=0,AMOUNT + B.DISCOUNT   , 0) AS KREDIT\r\nFROM\r\n  RECEIPT A\r\n  INNER JOIN RECEIPT_DETAIL  B ON (A.RECEIPT_ID = B.RECEIPT_ID)\r\n  INNER JOIN PERSON V ON (A.PERSON_ID = V.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT C ON ((V.COA_HUT_ID = C.COA_ID AND B.INVOICE_DUE_ID IS NOT NULL) OR \r\n  (V.COA_PIUT_ID = C.COA_ID AND B.NOTA_PENJUALAN_AGING_ID IS NOT NULL) )\r\nWHERE\r\n((B.AMOUNT + B.DISCOUNT)  <> 0) AND \r\n(B.COA_ID IS NULL )\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\n/* END BKM */\r\n\r\n\r\nUNION ALL\r\n/* BKK\r\nHUTANG    1000\r\n   KAS         900\r\n   POT.	       100\r\n\r\n*/\r\n/* BKK */\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT>=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  C.COA_NAME,\r\nC.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  A1.NOTE AS ITEM_DESCRIPTION ,\r\nA.PAYMENT_ID,\r\nA.NOTE,   0 AS AWAL,\r\nIF(AMOUNT>=0,AMOUNT, 0) AS DEBET,\r\nIF(AMOUNT<0,-1*AMOUNT, 0) AS KREDIT\r\nFROM\r\nPAYMENT A\r\nINNER JOIN PAYMENT_DETAIL A1 ON (A.PAYMENT_ID = A1.PAYMENT_ID)\r\nINNER JOIN V_ACCOUNT C ON (A1.COA_ID = C.COA_ID)\r\nWHERE\r\n(A1.AMOUNT  <> 0) \r\nAND A1.COA_ID IS NOT NULL\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* PELUNASAN HUTANG */\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(AMOUNT >=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(AMOUNT + B.DISCOUNT >=0,AMOUNT + B.DISCOUNT , 0) AS DEBET,\r\n  IF(AMOUNT + B.DISCOUNT <0,-1*(AMOUNT + B.DISCOUNT ) , 0) AS KREDIT\r\nFROM\r\n  PAYMENT  A\r\n  INNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n  INNER JOIN PERSON V ON (A.PERSON_ID = V.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT C ON ((V.COA_HUT_ID = C.COA_ID AND B.INVOICE_DUE_ID IS NOT NULL) OR \r\n  (V.COA_PIUT_ID = C.COA_ID AND B.NOTA_PENJUALAN_AGING_ID IS NOT NULL) )\r\nWHERE (AMOUNT + B.DISCOUNT) <> 0 \r\nAND (B.COA_ID IS NULL ) \r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONG HUTANG*/\r\n\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(DISCOUNT>=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN HUTANG\' AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(DISCOUNT<0,-1*DISCOUNT, 0)  AS DEBET,\r\n  IF(DISCOUNT>=0,DISCOUNT, 0)AS KREDIT\r\nFROM\r\nPAYMENT  A\r\nINNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n,\r\nV_ACCOUNT c\r\nWHERE \r\nDISCOUNT <> 0\r\nAND (((c.COA_ID = @POT_HUTANG) AND (B.INVOICE_DUE_ID IS NOT NULL )) OR\r\n ((c.COA_ID = @POT_PIUTANG) AND (B.NOTA_PENJUALAN_AGING_ID IS NOT NULL )))\r\nAND (B.COA_ID IS NULL)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\n/*\r\n  BKK\r\nBANK\r\n*/\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 AS URUT,\r\n  70 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  0  AS DEBET,\r\n  A.TOTAL_AMOUNT KREDIT\r\nFROM\r\n  PAYMENT A\r\n  INNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE   \r\n(A.TOTAL_AMOUNT  <> 0) \r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\n/* END OF BKK */\r\n\r\n\r\n\r\nUNION ALL\r\n/* JURNAL MEMO RIAL  */\r\nSELECT\r\n  \'000001J\' AS TIPE,\r\n\'JURNAL MEMO \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  80 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   a.NOTE as  ITEM_DESCRIPTION ,\r\n  a.JOURNAL_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  AMOUNT as DEBET,\r\nAMOUNT_CR as KREDIT\r\nFROM\r\n  JOURNAL a\r\n  INNER JOIN JOURNAL_detail b ON (a.JOURNAL_ID = b.JOURNAL_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n\r\n/* BG */ \r\n\r\n/*  BG  CAIR / TOLAK BANK TUJUAN */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 0, 1)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID_DEST = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in (@CLEAR, @TOLAK ))\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* BG  CAIR / TOLAK BANK ASAL */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 1, 0)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID  = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in  (@CLEAR, @TOLAK ))\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/* BG  TOLAK BANK ASAL */\r\nSELECT\r\n   IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\nUNION ALL\r\n/* BG   TOLAK BANK TUJUAN */\r\nSELECT\r\n    IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  2 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT  ,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID_DEST = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\nAND (a.PERIOD_NAME = :PERIOD_NAME1)    \r\n\r\n)\r\n AS XX \r\nWHERE TRUE\r\nGROUP BY COA_CODE,  COA_NAME ,COA_ID  \r\n', NULL, NULL, NULL),
      (1176, 675, 0, 'SELECT \r\n  c.PERSON_ID,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  c.NOTE,\r\n  c.LIMIT_HUTANG,\r\n  p.PAYMENT_TERM_NAME,\r\n  cs.SITE_NAME,\r\n  cs.ADDRESS1,\r\n  cs.ADDRESS2,\r\n  cs.ADDRESS3,\r\n  cs.CITY,\r\n  cs.PROVINCE,\r\n  cs.CONTACT_PERSON,\r\n  cs.PHONE2,\r\n  cs.PHONE1,\r\n  cs.FAX1,\r\n  cs.FAX2,\r\n  cs.ACTIVE_FLAG\r\nFROM\r\n  PERSON c\r\n  INNER JOIN PERSON_site cs ON (c.PERSON_ID = cs.PERSON_ID)\r\n  INNER JOIN payment_term p ON (c.DEFAULT_TERM_ID = p.PAYMENT_TERM_ID)\r\nwhere  FLAG_SUPP = 1\r\n \r\n&Kondisi\r\nOrder By PERSON_NAME', NULL, NULL, NULL),
      (1177, 615, 0, 'SELECT \r\n  a.INVOICE_ID,\r\n  a.STATUS_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.NOTE,\r\n  a.TOTAL_AMOUNT,\r\n  a.PERSON_ID,\r\n  a.PERSON_SITE_ID,\r\n  a.PAYMENT_TERM_ID,\r\n  a.PERIOD_NAME,\r\n  a.PPN_CODE,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.TOTAL_DISCOUNT,  \r\n  a.JTEMPO,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  a.WAREHOUSE_ID,\r\n  MAX(TOTAL_NET) as TOTAL_NET,\r\n  g.WAREHOUSE_NAME ,\r\n  v.PERSON_CODE,\r\n  v.PERSON_NAME,\r\n  vs.SITE_NAME,\r\n  vs.ADDRESS1,\r\n  vs.ADDRESS2,\r\n  vs.ADDRESS3,\r\n  vs.CITY,\r\n  vs.PROVINCE,\r\n  p.PAYMENT_TERM_NAME,\r\n  p.DESCRIPTION,\r\n  s.DISPLAY_NAME AS STATUS_DOC\r\nFROM\r\n  INVOICE a\r\n  INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n  INNER JOIN warehouse g ON (a.WAREHOUSE_ID = g.WAREHOUSE_ID )\r\n  LEFT JOIN PERSON v ON (a.PERSON_ID = v.PERSON_ID)\r\n  LEFT JOIN PERSON_SITE vs ON (a.PERSON_SITE_ID = vs.PERSON_SITE_ID)\r\n  LEFT JOIN PAYMENT_TERM p ON (a.PAYMENT_TERM_ID = p.PAYMENT_TERM_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\nwhere (DOCUMENT_TYPE_ID = 5)\r\n \r\n&Kondisi\r\nGROUP BY \r\n  a.INVOICE_ID,\r\n  a.STATUS_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.NOTE,\r\n  a.TOTAL_AMOUNT,\r\n  a.PERSON_ID,\r\n  a.PERSON_SITE_ID,\r\n  a.PAYMENT_TERM_ID,\r\n  a.PERIOD_NAME,\r\n  a.PPN_CODE,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.TOTAL_DISCOUNT,  \r\n  a.JTEMPO,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  a.WAREHOUSE_ID,\r\n  a.TOTAL_NET,\r\n  g.WAREHOUSE_NAME ,\r\n  v.PERSON_CODE,\r\n  v.PERSON_NAME,\r\n  vs.SITE_NAME,\r\n  vs.ADDRESS1,\r\n  vs.ADDRESS2,\r\n  vs.ADDRESS3,\r\n  vs.CITY,\r\n  vs.PROVINCE,\r\n  p.PAYMENT_TERM_NAME,\r\n  p.DESCRIPTION,\r\n  s.DISPLAY_NAME \r\nORDER BY a.DOCUMENT_NO\r\n', NULL, NULL, NULL),
      (1178, 662, 0, 'SELECT * FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\nk.PERSON_CODE,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS BELI,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKK,\r\n0  AS BAYAR_BKM,\r\n0 RETUR_BELI\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nINVOICE_DUE s\r\nINNER JOIN PAYMENT_DETAIL y on (s.INVOICE_DUE_ID = y.INVOICE_DUE_ID)\r\nINNER JOIN PAYMENT x on (x.PAYMENT_id = y.PAYMENT_id) and (date(x.DOCUMENT_DATE) <= :12 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE  (K.FLAG_SUPP = 1) \r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PEMBELIAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\nk.PERSON_CODE,\r\n0 AS AWAL,\r\n(a.TOTAL_NET)  AS BELI,\r\nIFNULL(pay.bayar,0)+ IFNULL(PAY_RET.bayar_RET,0)    AS BAYAR_BKK,   \r\nIFNULL(rec.bayar,0)+ IFNULL(REC_RET.bayar_RET,0)    AS BAYAR_BKM,\r\n(IFNULL( c.retur,0)) RETUR_BELI\r\nFROM\r\nINVOICE a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nINNER JOIN INVOICE_DUE g ON (a.INVOICE_ID  = g.INVOICE_ID)  AND (INVOICE_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.INVOICE_DUE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE date(z.DOCUMENT_DATE) <= :12\r\nGROUP BY\r\ny.INVOICE_DUE_ID\r\n) AS rec ON (g.INVOICE_DUE_ID = rec.INVOICE_DUE_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.INVOICE_DUE_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nWHERE (date(p.DOCUMENT_DATE) <= :12)\r\nand (pd.INVOICE_DUE_ID IS NOT NULL)\r\nGROUP BY pd.INVOICE_DUE_ID\r\n) AS pay ON (g.INVOICE_DUE_ID = pay.INVOICE_DUE_ID)\r\nLEFT JOIN \r\n/* RETUR REFER KE NO PEMBELIAN */ \r\n(SELECT a.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(a.TOTAL_NET) * -1 as RETUR\r\nFROM\r\nINVOICE a\r\nWHERE\r\n(DOCUMENT_TYPE_ID  = 5 )\r\nAND  (a.INVOICE_OLD_ID IS NOT NULL )\r\nAND   (date(DOCUMENT_DATE) <= :12 )\r\nGROUP BY a.INVOICE_OLD_ID\r\n)  AS c on (a.INVOICE_id = c.INVOICE_id)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR */ \r\n(SELECT r.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nPAYMENT z        \r\nINNER JOIN PAYMENT_DETAIL y ON (z.PAYMENT_ID = y.PAYMENT_ID)\r\nINNER JOIN INVOICE_DUE n ON (y.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\nINNER JOIN INVOICE r ON (n.INVOICE_ID = r.INVOICE_ID)    \r\nWHERE (date(z.DOCUMENT_DATE)  <= :12)  \r\nGROUP BY\r\nr.INVOICE_OLD_ID      \r\n) AS PAY_RET ON (a.INVOICE_ID = PAY_RET.INVOICE_ID)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR BKM*/ \r\n(SELECT r.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nINNER JOIN INVOICE_DUE n ON (y.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\nINNER JOIN INVOICE r ON (n.INVOICE_ID = r.INVOICE_ID)    \r\nWHERE (date(z.DOCUMENT_DATE)  <= :12)  \r\nGROUP BY\r\nn.INVOICE_ID        \r\n) AS rec_ret ON (a.INVOICE_ID = rec_ret.INVOICE_ID)\r\nWHERE \r\n(\r\n(a.DOCUMENT_TYPE_ID   <  5)  OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   = 5) AND (a.INVOICE_OLD_ID IS NULL)  \r\n)\r\n)\r\nAND (date(a.DOCUMENT_DATE) <= :12 )\r\n&kondisi\r\n/* end PEMBELIAN */\r\n) as yy\r\nWHERE  \r\n(\r\n(YY.AWAL +YY.BELI  + bayar_BKM - bayar_BKK - RETUR_BELI) <> 0\r\n) \r\nORDER BY DOCUMENT_DATE', NULL, NULL, NULL),
      (1179, 670, 0, 'SELECT * FROM \r\n(\r\nSELECT c.PERSON_id,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) < 0, (TOTAL )  ,0)) AS BLM,\r\nSUM(IF(DATEDIFF(:12, JTEMPO)  BETWEEN 0 and 30, (TOTAL ) ,0)) AS BLN1,\r\nSUM(IF(DATEDIFF(:12 , JTEMPO) BETWEEN 31 and 60, (TOTAL ) ,0)) AS BLN2,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) BETWEEN 61 and 90, (TOTAL ) ,0)) AS BLN3,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) > 91, (TOTAL ) ,0)) AS BLNLEBIH3,\r\nSUM(TOTAL) AS TOTAL, \r\nc.PERSON_NAME, \r\nc.PERSON_CODE\r\nFROM\r\nPERSON c \r\nINNER JOIN \r\n( \r\n/* PEMBELIAN DAN PELUNASAN */\r\n(SELECT a.PERSON_ID, (TOTAL_NET - IFNULL(BKK_BELI,0) + IFNULL(BKM_BELI,0))  AS TOTAL  ,  JTEMPO\r\nFROM INVOICE  a \r\nINNER JOIN INVOICE_DUE z on (a.INVOICE_ID = z.INVOICE_ID) AND (INVOICE_FLAG = \'Y\')\r\nLEFT JOIN \r\n(\r\n/* BKK */\r\nSELECT pd.INVOICE_DUE_ID ,\r\nSUM(AMOUNT + DISCOUNT ) AS BKK_BELI\r\nFROM \r\nPAYMENT P\r\nINNER JOIN PAYMENT_DETAIL PD on  (P.PAYMENT_id = Pd.PAYMENT_id)\r\nWHERE \r\n( date(P.DOCUMENT_DATE) <= :12 )\r\nGROUP BY Pd.INVOICE_DUE_ID \r\n) AS PAY ON (z.INVOICE_DUE_ID = PAY.INVOICE_DUE_ID) \r\nLEFT JOIN \r\n/* BKM */ \r\n(  SELECT y.INVOICE_DUE_ID ,\r\nSUM(AMOUNT +DISCOUNT) AS BKM_BELI\r\nFROM \r\nreceipt x \r\nINNER JOIN RECEIPT_DETAIL y on  (x.receipt_id = y.receipt_id)\r\nWHERE \r\n( date(x.DOCUMENT_DATE) <= :12 )\r\nGROUP BY y.INVOICE_DUE_ID \r\n) AS REC ON (z.INVOICE_DUE_ID = REC.INVOICE_DUE_ID) \r\nWHERE \r\n(a.DOCUMENT_TYPE_ID NOT IN (3,4) ) AND \r\n(date(a.DOCUMENT_DATE)  <= :12 )\r\n)\r\nUNION  ALL \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT A.PERSON_ID, (G.DUE_AMOUNT  - IFNULL(BKK_BELI,0) )  AS TOTAL  , A.DUE_DATE\r\nFROM \r\nPERSON_SALDO A\r\nINNER JOIN PERSON P ON (A.PERSON_ID = P.PERSON_ID)\r\nINNER JOIN INVOICE_DUE G ON (A.PERSON_SALDO_ID = G.PERSON_SALDO_ID  )\r\nLEFT JOIN \r\n(  SELECT y.INVOICE_DUE_ID ,\r\nSUM(AMOUNT + DISCOUNT) AS BKK_BELI \r\nFROM \r\nPAYMENT x \r\nINNER JOIN PAYMENT_DETAIL y on (x.PAYMENT_id = y.PAYMENT_id)\r\nWHERE \r\n( date(x.DOCUMENT_DATE) <= :12 )\r\nGROUP BY y.INVOICE_DUE_ID \r\n) AS PAY1 ON (G.INVOICE_DUE_ID = PAY1.INVOICE_DUE_ID) \r\nWHERE (FLAG_SUPP = 1)\r\n)\r\nUNION ALL\r\n/* PO DAN PELUNASAN */\r\n(SELECT a.PERSON_ID, (UANG_MUKA -  IFNULL(BKK_BELI,0) + IFNULL(BKM_BELI,0))  AS TOTAL  ,  JTEMPO\r\nFROM PO  a \r\nINNER JOIN INVOICE_DUE z on (a.PO_ID = z.INVOICE_ID) AND (INVOICE_FLAG = \'N\')\r\nLEFT JOIN \r\n(\r\nSELECT pd.INVOICE_DUE_ID ,\r\nSUM(AMOUNT + DISCOUNT ) AS BKK_BELI\r\nFROM \r\nPAYMENT P\r\nINNER JOIN PAYMENT_DETAIL PD on  (P.PAYMENT_id = Pd.PAYMENT_id)\r\nWHERE \r\n( date(P.DOCUMENT_DATE) <= :12 )\r\nGROUP BY Pd.INVOICE_DUE_ID \r\n) AS PAY ON (z.INVOICE_DUE_ID = PAY.INVOICE_DUE_ID) \r\nLEFT JOIN \r\n(  SELECT y.INVOICE_DUE_ID ,\r\nSUM(AMOUNT +DISCOUNT) AS BKM_BELI\r\nFROM \r\nreceipt x \r\nINNER JOIN RECEIPT_DETAIL y on  (x.receipt_id = y.receipt_id)\r\nWHERE \r\n( date(x.DOCUMENT_DATE) <= :12 )\r\nGROUP BY y.INVOICE_DUE_ID \r\n) AS REC ON (z.INVOICE_DUE_ID = REC.INVOICE_DUE_ID) \r\nWHERE \r\n(a.UANG_MUKA > 0 ) AND\r\n(date(a.DOCUMENT_DATE)  <= :12 )\r\n)\r\n) as J  ON (C.PERSON_ID = J.PERSON_ID ) \r\nWHERE      ( left(c.TIPE,1) = 1 ) \r\n&KONDISI\r\nGROUP BY   c.PERSON_NAME, c.PERSON_CODE\r\nORDER BY  c.PERSON_NAME \r\n) AS XX WHERE TOTAL <> 0 \r\n', NULL, '', NULL),
      (1180, 650, 0, 'SELECT \r\na.*, \r\n( a.DUE_AMOUNT - a.PAID_AMOUNT ) AS SISA,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\nSITE_NAME\r\nFROM\r\nInvoice_Due a\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nLEFT OUTER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nWHERE\r\n ( left(TIPE,1) = 1 ) AND\r\n(abs(a.PAID_AMOUNT - a.DUE_AMOUNT) > 0   )\r\n AND (a.DUE_DATE <= :11 )\r\n&KONDISI\r\nORDER BY a.DUE_DATE\r\n', NULL, NULL, NULL),
      (1181, 655, 0, 'SELECT \r\nP.PERSON_NAME,\r\nP.PERSON_ID,\r\nP.PERSON_CODE,\r\nSUM(SALDO) AS SALDO,\r\nSUM(DEBET) as DEBET,\r\nSUM(KREDIT) as KREDIT \r\nFROM\r\nPERSON P\r\nINNER JOIN \r\n(\r\n \r\n/* \r\nHUTANG \r\nX1 = PERIODE UTK SALDO AWAL \r\nV1 = TRANSAKSI \r\nT1 = TANGGAL / PERIODE\r\n\r\n*/\r\n\r\n/* SALDO AWAL */ \r\nSELECT\r\n  0 AS URUT, \r\n \'SALDO\' as DOCUMENT_CLASS_CODE,\r\n  \'\' as DOCUMENT_NO,\r\n  null as DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  a.SALDO_HUTANG AS SALDO, \r\n  0 AS KREDIT,\r\n  0 AS DEBET,\r\n  a.PERSON_ID,\r\n  \'SALDO AWAL\' as NOTE\r\n FROM \r\n  PERSON_balance a\r\n INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE   LEFT(c.TIPE,1) = 1  \r\n\r\nAND (A.PERIOD_NAME = :12)\r\n\r\nUNION ALL\r\n/* PEMBELIAN */ \r\nSELECT\r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  a.TOTAL_NET as KREDIT,\r\n  0 as DEBET,\r\n  /*IF(a.TOTAL_NET<>0,a.TOTAL_NET,(SELECT SUM(SUBTOTAL) FROM INVOICE_DETAIL b,ITEM i WHERE a.INVOICE_ID=b.INVOICE_ID AND i.ITEM_ID=b.ITEM_ID AND i.JENIS_ID<>@UM)) AS    KREDIT,\r\n  IF(a.TOTAL_NET<>0,0,(SELECT SUM(SUBTOTAL*-1) FROM INVOICE_DETAIL b,ITEM i WHERE a.INVOICE_ID=b.INVOICE_ID AND i.ITEM_ID=b.ITEM_ID AND i.JENIS_ID=@UM)) AS DEBET,*/\r\n  a.PERSON_ID,\r\n  a.NOTE\r\nFROM\r\n  INVOICE a\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE TRUE\r\n\r\nAND (A.PERIOD_NAME BETWEEN :12 AND  :22)\r\n\r\nUNION ALL\r\n/* PELUNASAN DGN BKK */\r\nSELECT\r\n  2 AS URUT, \r\n a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n0 AS SALDO,\r\n  0  AS KREDIT,\r\n  b.AMOUNT+b.DISCOUNT AS DEBET,\r\n\r\n  a.PERSON_ID,\r\n  CONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\n  PAYMENT a\r\n  INNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID)\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN INVOICE_DUE x ON ( b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE TRUE\r\n\r\nAND (A.PERIOD_NAME BETWEEN :12 AND  :22)\r\n\r\n\r\nUNION ALL\r\n\r\n/* PELUNASAN DGN BKM */\r\n\r\nSELECT\r\n  4 AS URUT, \r\n a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  0  AS KREDIT,  \r\n  (b.AMOUNT+b.DISCOUNT) * -1  AS DEBET,\r\n  \r\n  a.PERSON_ID,\r\n  CONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\n  receipt a\r\n   INNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\n INNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN INVOICE_DUE x ON ( b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE  TRUE\r\n    \r\n  AND (A.PERIOD_NAME BETWEEN :12 AND  :22)\r\n\r\n)\r\nAS XX ON (P.PERSON_ID =  XX.PERSON_ID)\r\nWHERE  ( left(TIPE,1) = 1 ) \r\n&KONDISI\r\nGROUP BY PERSON_NAME,   PERSON_CODE,PERSON_id\r\n', NULL, NULL, NULL),
      (1183, 600, 0, 'SELECT \r\n  a.PO_ID,\r\n  a.STATUS_ID,\r\n  a.ADDRESS_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.NOTE,\r\n  a.TOTAL_AMOUNT,\r\n  a.PERSON_ID,\r\n  a.PERSON_SITE_ID,\r\n  a.PAYMENT_TERM_ID,\r\n  a.PERIOD_NAME,\r\n  a.PPN_CODE,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_NET,\r\n  a.NEED_DATE,\r\n  a.PRINT_COUNT,\r\n  a.JTEMPO,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  c.PERSON_NAME,\r\n  cs.SITE_NAME,\r\n  d.ADDRESS_CODE,\r\n  a.DELIVER_ON,\r\n  l.DISPLAY_NAME\r\nFROM\r\n  PO a\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN address d ON (a.ADDRESS_ID = d.ADDRESS_ID)\r\n  INNER JOIN erp_lookup_value l ON (a.STATUS_ID = l.ERP_LOOKUP_VALUE_ID)\r\n&KONDISI\r\nORDER BY   a.DOCUMENT_NO\r\n', NULL, '', NULL),
      (1184, 660, 0, 'SELECT \r\nP.PERSON_NAME,\r\nP.PERSON_ID,\r\nP.PERSON_CODE,\r\nSUM(xx.DEBET) as DEBET,\r\nSUM(xx.KREDIT) as KREDIT, \r\nSUM(xx.TUNAI) as TUNAI,\r\nSUM(xx.saldo) AS SALDO\r\nFROM\r\nPERSON P\r\nLEFT JOIN \r\n(\r\n/* SALDO AWAL */\r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' \' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\na.SALDO_HUTANG AS SALDO, \r\nnol  AS KREDIT,\r\nnol  AS DEBET,\r\nnol AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM \r\nPERSON_balance a,\r\nnol \r\nWHERE true\r\nand a.period_name= :PERIOD_NAME1\r\nUNION ALL\r\n/* PENJUALAN DAN RETUR */\r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' \' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\na.TOTAL_NET AS SALDO,\r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\nINVOICE a\r\nWHERE  a.document_type_id not in (3,4)\r\nand a.period_name= :PERIOD_NAME1\r\nand a.document_date < :12\r\nUNION ALL\r\n/* PELUNASAN DGN BKM */\r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' \' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\n(b.AMOUNT + b.DISCOUNT)   AS SALDO,\r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\nreceipt a\r\nINNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\nINNER JOIN INVOICE_DUE x ON (b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE  TRUE\r\nand a.period_name= :PERIOD_NAME1\r\nand a.document_date < :12\r\nUNION ALL\r\n/* PELUNASAN DGN BKK */ \r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' \' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\n(b.AMOUNT + b.DISCOUNT ) * -1 AS SALDO,\r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID) \r\nINNER JOIN INVOICE_DUE x ON (b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE  TRUE\r\nand a.period_name= :PERIOD_NAME1\r\nand a.document_date < :12\r\nUNION ALL\r\n/* PEMBELIAN DAN RETUR */\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\n0 AS SALDO,\r\n/*IF(a.TOTAL_NET<>0,0,(SELECT SUM(SUBTOTAL*-1) FROM INVOICE_DETAIL b,ITEM i WHERE a.INVOICE_ID=b.INVOICE_ID AND i.ITEM_ID=b.ITEM_ID AND i.JENIS_ID=@UM)) AS KREDIT,\r\nIF(a.TOTAL_NET<>0,a.TOTAL_NET,(SELECT SUM(SUBTOTAL) FROM INVOICE_DETAIL b,ITEM i WHERE a.INVOICE_ID=b.INVOICE_ID AND i.ITEM_ID=b.ITEM_ID AND i.JENIS_ID<>@UM)) AS    DEBET,*/\r\n0 as KREDIT,\r\na.TOTAL_NET AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\na.NOTE\r\nFROM\r\nINVOICE a\r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nWHERE  (A.DOCUMENT_DATE BETWEEN :12 AND :22)\r\nUNION ALL\r\n/* PELUNASAN DGN BKM */\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\n0 AS SALDO,\r\nif(DATE(a.DOCUMENT_DATE) = DATE(n.DOCUMENT_DATE), 0,b.AMOUNT + b.DISCOUNT) * -1  AS KREDIT,\r\n0  AS DEBET,\r\nif(DATE(a.DOCUMENT_DATE) = DATE(n.DOCUMENT_DATE), b.AMOUNT + b.DISCOUNT, 0) * -1   AS TUNAI,\r\na.PERSON_ID,\r\nCONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\nreceipt a\r\nINNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN INVOICE_DUE x ON (b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nleft join INVOICE n on (x.INVOICE_id=n.INVOICE_id)\r\nWHERE  TRUE\r\nAND (A.DOCUMENT_DATE BETWEEN :12 AND :22)\r\nUNION ALL\r\n/* PELUNASAN DGN BKK*/ \r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\n0 AS SALDO,\r\nif(DATE(a.DOCUMENT_DATE) = DATE(n.DOCUMENT_DATE), 0,(b.AMOUNT + b.DISCOUNT)  ) AS KREDIT,\r\n0  AS DEBET,\r\nif(DATE(a.DOCUMENT_DATE) = DATE(n.DOCUMENT_DATE), (b.AMOUNT + b.DISCOUNT)  , 0) AS TUNAI,\r\na.PERSON_ID,\r\nCONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID) \r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN INVOICE_DUE x ON (b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nleft join INVOICE n on (x.INVOICE_id=n.INVOICE_id)\r\nWHERE  TRUE\r\nAND (A.DOCUMENT_DATE BETWEEN :12 AND :22)\r\n) AS XX ON (P.PERSON_ID =  XX.PERSON_ID)\r\nWHERE  ( left(P.TIPE,1) = 1 ) \r\n&KONDISI\r\nGROUP BY p.PERSON_NAME,   p.PERSON_CODE,p.PERSON_id\r\nhaving\r\nSUM(xx.DEBET)<>0 or SUM(xx.KREDIT)<>0 or SUM(xx.TUNAI)<>0 or SUM(xx.saldo)<>0\r\n', NULL, NULL, NULL),
      (1185, 1161, 0, 'SELECT \r\na.ITEM_STOK_ID,\r\na.ITEM_ID,\r\na.STOCK_DATE,\r\n(a.QTY_AWAL +  a.QTY_MASUK -   a.QTY_KELUAR) as STOK,\r\nw.WAREHOUSE_NAME,\r\ni.ITEM_DESCRIPTION,\r\ni.ITEM_CODE,\r\ni.UOM_CODE,\r\ni.MIN_STOCK,\r\ne.DISPLAY_NAME\r\nFROM\r\nitem_stok a\r\nINNER JOIN item i ON (a.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nJOIN erp_lookup_value e\r\nWHERE (JENIS_ID =1) and e.ERP_LOOKUP_SET_ID = 69 \r\n&KONDISI\r\nAND CASE WHEN e.DISPLAY_NAME = \'YA\' THEN true\r\nELSE (a.QTY_AWAL +  a.QTY_MASUK -   a.QTY_KELUAR) <> 0 \r\nEND \r\nORDER BY  i.Item_code,  i.ITEM_DESCRIPTION', NULL, NULL, NULL),
