      (1197, 1174, 0, 'SELECT \r\n q.DOCUMENT_NO as CHECK_NO,  a.DOCUMENT_NO,\r\n q.CREATED_date as DOCUMENT_DATE,   \r\nq.document_date AS TGL_CAIR,   \r\nq.Jtempo,\r\nq.TOTAL_AMOUNT, \r\n  a.NAMA_BANK,\r\n IF( (q.DOCUMENT_DATE BETWEEN :13 AND :23) AND (q.STATUS_ID in (230103,230104)) , q.TOTAL_AMOUNT, 0)  \r\n - IF( (q.TGL_TOLAK  BETWEEN :13 AND :23) AND (q.STATUS_ID = 230104) , q.TOTAL_AMOUNT, 0)  + \r\n+  IFNULL(GANTI_TOLAK.DEBET,0)\r\nDEBET,   \r\n  k.COA_NAME ,\r\n  c.PERSON_NAME,\r\n  X.DISPLAY_NAME AS STATUS\r\nFROM\r\n  CHEQUE Q \r\n  LEFT JOIN PAYMENT a ON (Q.CHEQUE_ID = a.CHEQUE_ID)\r\n  INNER JOIN ERP_LOOKUP_VALUE X on (Q.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)  \r\n  LEFT JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n    LEFT JOIN coa k ON (q.BANK_ID_DEST = k.COA_ID)\r\n  LEFT JOIN \r\n(\r\nSELECT  Y.CHEQUE_ID , SUM(AMOUNT)  AS DEBET FROM \r\n  PAYMENT X\r\n  INNER JOIN PAYMENT_detail Y ON (X.PAYMENT_ID = Y.PAYMENT_ID)\r\n  WHERE (X.DOCUMENT_DATE BETWEEN :13 AND :23)\r\nGROUP BY Y.CHEQUE_ID \r\n) AS GANTI_TOLAK ON ( a.CHEQUE_ID = GANTI_TOLAK.CHEQUE_ID)\r\n\r\nWHERE (q.status_id <> 230082)\r\nAND (STATUS_FLAG = \'K\')\r\nAND q.CREATED_DATE BETWEEN :13 AND :23\r\n&KONDISI\r\nORDER BY Q.DOCUMENT_DATE\r\n\r\n', NULL, NULL, NULL),
      (1198, 1175, 0, 'SELECT DISTINCT \r\n  Q.document_no as CHECK_NO,\r\n a.DOCUMENT_NO,\r\n q.*, \r\n   a.*,\r\n  k.COA_NAME,\r\n  c.PERSON_ID,\r\n  c.PERSON_CODE,\r\n  ifnull(c.PERSON_NAME,\'Saldo Awal\' ) as PERSON_NAME,\r\n  X.DISPLAY_NAME AS STATUS\r\nFROM\r\n  CHEQUE Q  \r\n  LEFT JOIN RECEIPT a  ON (Q.CHEQUE_ID = a.CHEQUE_ID)\r\n  INNER JOIN ERP_LOOKUP_VALUE X on (Q.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)  \r\n  LEFT JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n    LEFT JOIN coa k ON (q.BANK_ID_DEST = k.COA_ID)\r\nWHERE (Q.STATUS_FLAG = \'M\') AND DATE(q.CREATED_DATE) <= :13 AND q.STATUS_ID <> 230082\r\nAND (q.STATUS_ID=230039 OR ((DATE(q.DOCUMENT_DATE)) > :13 AND q.STATUS_ID IN (230103, 230104)))\r\n\r\n&KONDISI\r\nORDER BY c.person_name\r\n\r\n', NULL, NULL, NULL),
