      (1228, 1198, 0, 'SELECT a.NOTA_PENJUALAN_ID,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\n./*(b.SUBTOTAL) - a.TOTAL_DISCOUNT+a.PPN_AMOUNT as TOTAL_NET,*/\r\na.NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_REFF_NO,\r\na.PERIOD_NAME,\r\n./*(b.SUBTOTAL) as TOTAL_AMOUNT,*/\r\na.TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\npt.PAYMENT_TERM_NAME,\r\na.PPN_PERCEN,\r\na.PPN_AMOUNT,\r\na.PRINT_COUNT,\r\na.KARYAWAN_ID,\r\na.PPN_CODE,\r\na.JTEMPO,\r\nc.CUSTOM1 AS BATAS_JTEMPO,\r\nk.first_name,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\ncs.SITE_NAME,\r\n/* IF((X.RECEIVED_AMOUNT-NET_BYR1-TOTAL_BYR2)=0,\'LUNAS\',s.DISPLAY_NAME)AS   DISPLAY_NAME,*/\r\ns.DISPLAY_NAME,\r\nw.WAREHOUSE_NAME,\r\n(um.SUBTOTAL*-1) as UANG_MUKA,\r\nX.RECEIVED_AMOUNT + NET_BYR1 + TOTAL_BYR2 AS RECEIVED_AMOUNT ,\r\nb.ITEM_DESCRIPTION,\r\ni.ITEM_CODE,\r\nb.ENTERED_UOM,\r\nb.ENTERED_QTY,\r\nb.UNIT_PRICE,\r\nb.DISCOUNT_PRICE,\r\nb.DISCOUNT_PERCEN,\r\nb.SUBTOTAL,\r\nb.NOTE as ket\r\nFROM\r\n  nota_penjualan a\r\n  LEFT JOIN NOTA_PENJUALAN_AGING  x ON (a.NOTA_PENJUALAN_ID = x.NOTA_PENJUALAN_ID)\r\n\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PAYMENT_TERM pt ON a.PAYMENT_TERM_ID=pt.PAYMENT_TERM_ID\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  inner join kaRyawan k on (a.karyawan_id=k.karyawan_id)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID )\r\n  LEFT JOIN(\r\n  SELECT \r\n  sum(a.SUBTOTAL) SUBTOTAL,\r\n  a.NOTA_PENJUALAN_ID\r\n  FROM NOTA_PENJUALAN_DETAIL a\r\n  INNER JOIN NOTA_PENJUALAN b ON b.NOTA_PENJUALAN_ID=a.NOTA_PENJUALAN_PRE_ID\r\n  GROUP BY a.NOTA_PENJUALAN_ID\r\n  )AS  UM on a.NOTA_PENJUALAN_ID=um.NOTA_PENJUALAN_ID\r\nwhere true AND b.NOTA_PENJUALAN_PRE_ID IS NULL \r\nAND (PPN_AMOUNT > 0)\r\n&Kondisi\r\nORDER BY a.DOCUMENT_DATE, b.ITEM_DESCRIPTION', NULL, '', NULL),
      (1229, 1199, 0, 'SELECT  yy.*, :13 as TGL FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_CODE,\r\nk.PERSON_ID,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS JUAL,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKM,\r\n0  AS BAYAR_BKK,\r\n0 RETUR_JUAL\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nNOTA_PENJUALAN_AGING s\r\nINNER JOIN RECEIPT_DETAIL y on (s.Nota_Penjualan_aging_id = y.Nota_Penjualan_aging_id)\r\nINNER JOIN receipt x on (x.receipt_id = y.receipt_id) and (date(x.DOCUMENT_DATE) <= :13 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE k.FLAG_SUPP <> 1\r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_CODE,\r\nk.PERSON_ID,\r\n0 AS AWAL,\r\na.TOTAL_NET  AS JUAL,\r\nIFNULL(rec.bayar,0)    +    IFNULL(rec_ret.bayar_ret,0)   AS BAYAR_BKM,\r\nIFNULL(pay.bayar,0)  +   IFNULL(pay_ret.bayar_ret,0)  AS BAYAR_BKK,   \r\nIFNULL( c.retur,0) RETUR_JUAL\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING g ON (a.NOTA_PENJUALAN_ID  = g.NOTA_PENJUALAN_ID)  AND (JUAL_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.NOTA_PENJUALAN_AGING_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE(date(z.DOCUMENT_DATE))   <= :13\r\nGROUP BY\r\ny.NOTA_PENJUALAN_AGING_ID\r\n) AS rec ON (g.NOTA_PENJUALAN_AGING_ID = rec.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.NOTA_PENJUALAN_AGING_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nWHERE (date(p.DOCUMENT_DATE)<= :13)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY pd.NOTA_PENJUALAN_AGING_ID\r\n) AS pay ON (g.NOTA_PENJUALAN_AGING_ID = pay.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* RETUR REFER KE NO PENJUALAN */ \r\n(SELECT a.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(a.TOTAL_NET) * -1 as RETUR\r\nFROM\r\nnota_penjualan a\r\nWHERE\r\n(DOCUMENT_TYPE_ID  >= 5 )\r\nAND  (a.NOTA_PENJUALAN_OLD_ID IS NOT NULL )\r\nAND   (date(DOCUMENT_DATE) <= :13 )\r\nGROUP BY a.NOTA_PENJUALAN_OLD_ID\r\n)  AS c on (a.nota_penjualan_id = c.nota_penjualan_id)\r\nleft join\r\n/* PELUNASAN NYA RETUR */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (y.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date(z.DOCUMENT_DATE)  <= :13) \r\nGROUP BY\r\nr.NOTA_PENJUALAN_OLD_ID        \r\n) AS rec_ret ON (a.NOTA_PENJUALAN_ID = rec_ret.NOTA_PENJUALAN_ID)\r\nleft join\r\n/* PELUNASAN RETUR DGN BKK */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR_RET\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (pd.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date(p.DOCUMENT_DATE)<= :13)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY n.NOTA_PENJUALAN_ID     \r\n) AS pay_ret ON (a.NOTA_PENJUALAN_ID = pay_ret.NOTA_PENJUALAN_ID)\r\nWHERE \r\n(\r\n ((a.NET_BYR1 = 0) and   (a.DOCUMENT_TYPE_ID < 5)) OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   >= 5) AND (a.NOTA_PENJUALAN_OLD_ID IS NULL)  \r\n)\r\n)\r\nAND (date(a.DOCUMENT_DATE)<=  :13 )\r\n&kondisi\r\n/* end penjualan */\r\n) as yy\r\nWHERE \r\n(\r\n(YY.AWAL  + YY.JUAL - Bayar_BKM   + bayar_BKK - RETUR_JUAL) <> 0\r\n) \r\nORDER BY PERSON_NAME, DOCUMENT_DATE\r\n', NULL, '', NULL),
      (1230, 1200, 0, 'SELECT \r\na.NOTA_PENJUALAN_ID,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\na.NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\nd.DOCUMENT_REFF_NO,\r\na.PERIOD_NAME,\r\na.TOTAL_AMOUNT,\r\na.TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\na.PPN_PERCEN,\r\na.PPN_AMOUNT,\r\na.PRINT_COUNT,\r\na.KARYAWAN_ID,\r\na.PPN_CODE,\r\na.JTEMPO,\r\nc.PERSON_NAME,\r\nSITE_NAME, \r\nv.DISPLAY_NAME,\r\nw.WAREHOUSE_NAME,\r\na.TOTAL_NET,\r\ncs.ADDRESS1,\r\nif( b.BASE_QTY=1,b.ENTERED_QTY /if(to_qty is null,1,to_qty),b.entered_qty) as ENTERED_QTY,\r\nif(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,to_qty),b.unit_price) AS UNIT_PRICE,\r\nb.BASE_QTY,\r\nb.DISCOUNT_PRICE,\r\nb.DISCOUNT_PRICE1,\r\nIFNULL(b.DISCOUNT_PERCEN,0)DISCOUNT_PERCEN,\r\nb.SUBTOTAL,\r\nb.NOTE,\r\nb.HPP,\r\nif(z.uom_code is null, b.entered_uom,z.uom_code) as ENTERED_UOM,\r\nb.ITEM_DESCRIPTION,\r\ni.ITEM_CODE,\r\nd.DOCUMENT_NO as SJ\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nleft join item_uom z on i.item_id=z.ITEM_ID and z.BASE_UOM_FLAG=\'y\'\r\nINNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nLEFT JOIN INVENTORY_OUT d on (d.INVENTORY_OUT_ID=a.INVENTORY_OUT_ID)\r\nWHERE a.DOCUMENT_TYPE_ID<5\r\n&KONDISI\r\nORDER BY\r\na.DOCUMENT_NO\r\n', NULL, '', NULL),
      (1231, 1021, 3, 'SELECT \r\n     a.DOCUMENT_NO,\r\n     a.DOCUMENT_REFF_NO,\r\n     a.DOCUMENT_DATE,\r\n     a.TOTAL_NET,\r\n     (SELECT SUM(SUBTOTAL) FROM NOTA_PENJUALAN_DETAIL WHERE NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID AND NOTA_PENJUALAN_PRE_ID=:NOTA_PENJUALAN_ID  ) as UM\r\n\r\n\r\nFROM \r\n     NOTA_PENJUALAN_DETAIL b\r\nLEFT JOIN NOTA_PENJUALAN a ON a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID\r\n\r\nWHERE\r\n   b.NOTA_PENJUALAN_PRE_ID=:NOTA_PENJUALAN_ID\r\n\r\n&KONDISI\r\n\r\nORDER BY a.DOCUMENT_DATE', NULL, NULL, NULL),
      (1232, 1202, 0, '/*EXCLUDE*/\r\nSELECT \r\na.NOTA_PENJUALAN_ID,\r\nconcat(b,c,d) as no,\r\na.DOCUMENT_NO,\r\nDATE(a.DOCUMENT_DATE)DOCUMENT_DATE,\r\nifnull(concat(e,f,g,h,i,j),\'000000000000000\')npwp_no,\r\nc.PERSON_NAME,\r\ncs.ADDRESS1,\r\nconcat(a.document_no,CONCAT(\' Rp. \',CAST(format(a.total_net,2) AS CHAR)))NOTE,\r\ni.ITEM_CODE,\r\nb.ITEM_DESCRIPTION,\r\nb.UNIT_PRICE,\r\nb.ENTERED_QTY,\r\n(b.SUBTOTAL+(b.DISCOUNT_PRICE*b.ENTERED_QTY)) as SUBTOTAL1,\r\n(b.DISCOUNT_PRICE*b.ENTERED_QTY)+b.DISCOUNT_PRICE1 as DISCOUNT_PRICE,\r\nround(((b.subtotal-b.DISCOUNT_PRICE1)* 0.1),2) as PPN,\r\nround((b.SUBTOTAL-b.DISCOUNT_PRICE1),2) as SUBTOTAL\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN (\r\nSELECT NOTA_PENJUALAN_ID,\r\nsubstring_index(a.DOCUMENT_REFF_NO,\'.\',-1) as d,\r\nsubstring_index(substring_index(a.DOCUMENT_REFF_NO,\'-\',-1),\'.\',1) as c,\r\nsubstring_index(substring_index(a.DOCUMENT_REFF_NO,\'-\',1),\'.\',-1) as b,\r\nsubstring_index(a.DOCUMENT_REFF_NO,\'.\',1) as a\r\nfrom nota_penjualan a\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nwhere true \r\n&kondisi) as no on a.NOTA_PENJUALAN_ID = no.NOTA_PENJUALAN_ID\r\nINNER JOIN (\r\nselect \r\nsubstring_index(NPWP_NO,\'.\',1) as E,\r\nsubstring_index(substring_index(NPWP_NO,\'.\',2),\'.\',-1) as F,\r\nsubstring_index(substring_index(NPWP_NO,\'.\',3),\'.\',-1) as G,\r\nsubstring_index(substring_index(NPWP_NO,\'-\',1),\'.\',-1) as H,\r\nsubstring_index(substring_index(NPWP_NO,\'-\',-1),\'.\',1) as I,\r\nsubstring_index(NPWP_NO,\'.\',-1) as J,\r\nPERSON_ID from person\r\n) as NPWP on a.PERSON_ID=npwp.PERSON_ID\r\nLEFT JOIN INVENTORY_OUT d on (d.INVENTORY_OUT_ID=a.INVENTORY_OUT_ID)\r\nWHERE a.DOCUMENT_TYPE_ID <> 5 \r\nAND IF(:14=1,TRUE,FALSE)\r\nAND i.JENIS_ID<>@UM\r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\n/*INCLUDE*/\r\nSELECT \r\na.NOTA_PENJUALAN_ID,\r\nconcat(b,c,d) as no,\r\na.DOCUMENT_NO,\r\nDATE(a.DOCUMENT_DATE)DOCUMENT_DATE,\r\nifnull(concat(e,f,g,h,i,j),\'000000000000000\')npwp_no,\r\nc.PERSON_NAME,\r\ncs.ADDRESS1,\r\nconcat(a.document_no,CONCAT(\' Rp. \',CAST(format(a.total_net,2) AS CHAR)))NOTE,\r\ni.ITEM_CODE,\r\nb.ITEM_DESCRIPTION,\r\nb.UNIT_PRICE,\r\nb.ENTERED_QTY,\r\nROUND(((b.SUBTOTAL+(b.DISCOUNT_PRICE*b.ENTERED_QTY))/1.1),2)as SUBTOTAL1,\r\n((b.DISCOUNT_PRICE/1.1)*ENTERED_QTY)+(b.DISCOUNT_PRICE1/1.1) as DISCOUNT_PRICE,\r\nROUND((((b.subtotal-b.DISCOUNT_PRICE1)/1.1)* 0.1),2) as PPN,\r\nROUND(((b.SUBTOTAL-b.DISCOUNT_PRICE1)/1.1),2) as SUBTOTAL\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN (\r\nSELECT NOTA_PENJUALAN_ID,\r\nsubstring_index(a.DOCUMENT_REFF_NO,\'.\',-1) as d,\r\nsubstring_index(substring_index(a.DOCUMENT_REFF_NO,\'-\',-1),\'.\',1) as c,\r\nsubstring_index(substring_index(a.DOCUMENT_REFF_NO,\'-\',1),\'.\',-1) as b,\r\nsubstring_index(a.DOCUMENT_REFF_NO,\'.\',1) as a\r\nfrom nota_penjualan a\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nwhere true \r\n&kondisi) as no on a.NOTA_PENJUALAN_ID = no.NOTA_PENJUALAN_ID\r\nINNER JOIN (\r\nselect \r\nsubstring_index(NPWP_NO,\'.\',1) as E,\r\nsubstring_index(substring_index(NPWP_NO,\'.\',2),\'.\',-1) as F,\r\nsubstring_index(substring_index(NPWP_NO,\'.\',3),\'.\',-1) as G,\r\nsubstring_index(substring_index(NPWP_NO,\'-\',1),\'.\',-1) as H,\r\nsubstring_index(substring_index(NPWP_NO,\'-\',-1),\'.\',1) as I,\r\nsubstring_index(NPWP_NO,\'.\',-1) as J,\r\nPERSON_ID from person\r\n) as NPWP on a.PERSON_ID=npwp.PERSON_ID\r\nLEFT JOIN INVENTORY_OUT d on (d.INVENTORY_OUT_ID=a.INVENTORY_OUT_ID)\r\nWHERE a.DOCUMENT_TYPE_ID <> 5 \r\nAND IF(:14=2,TRUE,FALSE)\r\nAND i.JENIS_ID<>@UM\r\n&KONDISI\r\nORDER by date(DOCUMENT_DATE), DOCUMENT_NO\r\n\r\n', NULL, '', NULL),
      (1233, 1203, 0, 'SELECT \r\na.NOTA_PENJUALAN_ID,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\na.NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\nd.DOCUMENT_REFF_NO,\r\na.PERIOD_NAME,\r\na.TOTAL_AMOUNT,\r\na.TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\na.PPN_PERCEN,\r\na.PPN_AMOUNT,\r\na.PRINT_COUNT,\r\na.KARYAWAN_ID,\r\na.PPN_CODE,\r\na.JTEMPO,\r\nc.PERSON_NAME,\r\nSITE_NAME, \r\nv.DISPLAY_NAME,\r\nw.WAREHOUSE_NAME,\r\na.TOTAL_NET,\r\ncs.ADDRESS1,\r\nif( b.BASE_QTY=1,b.ENTERED_QTY /if(to_qty is null,1,to_qty),b.entered_qty) as ENTERED_QTY,\r\nif(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,to_qty),b.unit_price) AS UNIT_PRICE,\r\nb.BASE_QTY,\r\nb.DISCOUNT_PRICE,\r\nb.DISCOUNT_PRICE1,\r\nIFNULL(b.DISCOUNT_PERCEN,0)DISCOUNT_PERCEN,\r\nb.SUBTOTAL,\r\nb.NOTE,\r\nb.HPP,\r\nif(z.uom_code is null, b.entered_uom,z.uom_code) as ENTERED_UOM,\r\nb.ITEM_DESCRIPTION,\r\ni.ITEM_CODE,\r\nd.DOCUMENT_NO as SJ\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nleft join item_uom z on i.item_id=z.ITEM_ID and z.BASE_UOM_FLAG=\'y\'\r\nINNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\nLEFT JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nLEFT JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nLEFT JOIN INVENTORY_OUT d on (d.INVENTORY_OUT_ID=a.INVENTORY_OUT_ID)\r\nWHERE a.DOCUMENT_TYPE_ID=5\r\n&KONDISI\r\nORDER BY\r\na.DOCUMENT_NO\r\n', NULL, '', NULL),
      (1234, 1204, 0, 'Select b.first_name, b.karyawan_id, a.document_date, a.document_no, s.document_no as so_no, c.PERSON_name, \r\nc.PERSON_CODE, \r\nsum(nd.subtotal -  nd.discount_price1 ) total_amount ,\r\nsum(nd.subtotal - nd.discount_price1) as   total_net  from\r\nnota_penjualan a\r\ninner join nota_penjualan_detail nd on (a.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join warehouse w on (a.warehouse_id = w.warehouse_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join karyawan b on(a.karyawan_id=b.karyawan_id)\r\ninner join PERSON c on (a.PERSON_id =c.PERSON_id)\r\nleft join so s on (a.so_id = s.so_id)\r\nwhere (a.status_id  <> @delete) \r\nand (a.invoice_type_id <>  @PREPAYMENT) \r\nand (i.JENIS_ID <>  @UM) \r\n&kondisi\r\nGROUP BY \r\na.document_date, a.document_no, s.document_no  , c.PERSON_name\r\norder by first_name', NULL, '', NULL),
      (1235, 1205, 0, 'Select j.document_date, j.document_no, a.JTEMPO,  a.document_date as tgl_terima, s.document_no as so_no, c.PERSON_name, sum(b.amount) TERIMA,\r\nc.PERSON_CODE, \r\nk.first_name,\r\nmax(j.total_net) total_amount ,\r\nmax(j.total_net) as   total_net from\r\nreceipt a \r\ninner join receipt_detail b on  (a.receipt_id = b.receipt_id)\r\ninner join nota_penjualan_aging g on (b.NOTA_PENJUALAN_AGING_ID =g.NOTA_PENJUALAN_AGING_ID)  \r\ninner join nota_penjualan j on  (g.NOTA_PENJUALAN_ID = j.NOTA_PENJUALAN_ID) \r\ninner join karyawan k on(j.karyawan_id=k.karyawan_id)\r\ninner join PERSON c on (a.PERSON_id = c.PERSON_id)\r\ninner join warehouse w on (j.warehouse_id = w.warehouse_id)\r\nleft join so s on (j.so_id = s.so_id)\r\nwhere \r\n(DATEDIFF(a.document_date ,j.jtempo)<=15)\r\nand (j.invoice_type_id <>  @PREPAYMENT) \r\n&kondisi\r\ngroup by \r\na.document_date, a.document_no, s.document_no  , PERSON_name\r\nUNION ALL\r\nSelect a.document_date, a.document_no, a.JTEMPO,   a.document_date as tgl_terima, s.document_no as so_no, c.PERSON_name, sum(nd.subtotal) *-1 TERIMA,\r\nc.PERSON_CODE, \r\nk.first_name,\r\nsum(nd.subtotal)*-1  total_amount ,\r\nsum(nd.subtotal)*-1  as   total_net \r\nfrom\r\nnota_penjualan a\r\ninner join karyawan k on(a.karyawan_id=k.karyawan_id)\r\ninner join nota_penjualan_detail nd on (a.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join PERSON c on (a.PERSON_id = c.PERSON_id)\r\ninner join warehouse w on (a.warehouse_id = w.warehouse_id)\r\nleft join so s on (a.so_id = s.so_id)\r\nwhere \r\n(DATEDIFF(a.document_date ,a.jtempo)<=15)\r\nand (a.invoice_type_id <>  @PREPAYMENT) \r\nand (i.JENIS_ID = @UM)\r\n&kondisi\r\ngroup by \r\na.document_date, a.document_no, s.document_no  , PERSON_name\r\norder by first_name\r\n\r\n\r\n', NULL, '', NULL),
      (1237, 1207, 0, 'Select a.document_date, a.document_no, a.JTEMPO,  terima.tgl_terima, s.document_no as so_no, c.PERSON_name,  terima.TERIMA,\r\nc.PERSON_CODE, \r\nsum(nd.subtotal) total_amount ,\r\nsum(nd.subtotal - nd.discount_price1) as   total_net ,\r\nb.first_name\r\nfrom\r\nnota_penjualan a\r\ninner join karyawan b on(a.karyawan_id=b.karyawan_id)\r\ninner join nota_penjualan_detail nd on (a.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join warehouse w on (a.warehouse_id = w.warehouse_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join PERSON c on (a.PERSON_id = c.PERSON_id)\r\ninner join nota_penjualan_aging g on (a.NOTA_PENJUALAN_ID = g.NOTA_PENJUALAN_ID) \r\nLEFT JOIN\r\n( SELECT NOTA_PENJUALAN_AGING_ID ,  max(DOCUMENT_DATE) AS tgl_terima, SUM(AMOUNT) AS TERIMA FROM \r\nreceipt r  \r\ninner join receipt_detail rd on (rd.receipt_id = r.receipt_id)\r\nWhere true\r\nGroup by NOTA_PENJUALAN_AGING_ID \r\n) terima on (g.NOTA_PENJUALAN_AGING_ID = terima.NOTA_PENJUALAN_AGING_ID)  and\r\n(DATEDIFF(terima.Tgl_terima ,a.jtempo)<=15)\r\nleft join so s on (a.so_id = s.so_id)\r\nwhere \r\n(a.status_id  <> @delete) \r\nand (a.invoice_type_id <>  @PREPAYMENT) \r\nand (i.JENIS_ID <>  @UM) \r\n&kondisi\r\nGROUP BY \r\na.document_date, a.document_no, s.document_no  , c.PERSON_name\r\norder by first_name,a.document_date, a.document_no', NULL, '', NULL),
      (1238, 1208, 0, 'Select  b.PERSON_code, b.PERSON_name, \r\n  ps.ADDRESS1, \r\nps.ADDRESS2, \r\nps.CITY ,\r\nk.FIRST_NAME\r\n  from\r\n PERSON b \r\n INNER JOIN KARYAWAN k ON b.KARYAWAN_ID=k.KARYAWAN_ID\r\n INNER JOIN PERSON_SITE ps on (b.PERSON_ID = ps.PERSON_ID)\r\n left join \r\n(SELECT PERSON_ID FROM \r\nnota_penjualan J \r\nWHERE TRUE\r\n&KONDISI\r\n) AS x ON ( b.PERSON_ID = x.PERSON_ID) \r\nwhere \r\n(B.active_flag = \'Y\' )\r\nAND \r\n(B.RAYON  LIKE  CONCAT(\'%\',:12,\'%\')) \r\nand (x.PERSON_id is NULL)\r\nORDER BY PERSON_NAME', NULL, '', NULL),
      (1240, 1210, 0, 'select\r\n  v.PERSON_name,\r\n  vs.address1,\r\n  v.PERSON_id,\r\n   vs.phone1,\r\n  a.document_date,\r\n  a.document_no,\r\n  if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty) as entered_qty,\r\n  if(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,d.to_qty),b.unit_price) as unit_price,\r\n  (b.item_description) as item_description,\r\n  (if(d.uom_code is null, b.entered_uom,d.uom_code)) as ENTERED_uom,\r\n  (b.subtotal) as subtotal,\r\n  max(ii.document_date) as SJ,\r\n  max(ii.document_no) as SJ_no,\r\n  sum(if( iid.BASE_QTY=1,iid.ENTERED_QTY /if( to_qty is null,1,d.to_qty),iid.entered_qty) ) as SJ_qty,\r\n  (max(if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty)) - sum(if( iid.BASE_QTY=1,iid.ENTERED_QTY /if( to_qty is null,1,d.to_qty),iid.entered_qty))) as sisa\r\nfrom\r\n  SO a\r\n  inner join inventory_out ii ON (a.SO_id = ii.SO_id)\r\n  inner join SO_detail b ON (a.SO_id = b.SO_id)\r\n  inner join inventory_out_detail iid ON (ii.inventory_out_id = iid.inventory_out_id and iid.item_id = b.item_id)\r\n  inner join PERSON v ON (a.PERSON_id = v.PERSON_id)\r\n  left join PERSON_site vs ON (a.PERSON_ID = vs.PERSON_ID)\r\n  inner join item i on (b.item_id = i.item_id)\r\n  left join item_uom d on i.item_id=d.ITEM_ID and d.BASE_UOM_FLAG=\'y\'\r\nWHERE ((a.STATUS_ID=@complete) or (a.STATUS_ID=@partial))\r\nand (ii.STATUS_ID <> @DELETE)\r\n&kondisi\r\ngroup by b.so_detail_id\r\norder by \r\nv.person_name,\r\na.document_date,\r\nii.document_date	', NULL, '', NULL),
      (1241, 1211, 0, 'SELECT \r\n	I.ITEM_ID,\r\n	I.ITEM_CODE,\r\n	I.ITEM_DESCRIPTION,\r\n	k.STOK,\r\n	B.SO_DETAIL_ID,\r\n	B.SO_ID,\r\n	B.ITEM_ID,\r\n	B.ENTERED_QTY * B.BASE_QTY/ ifnull(to_qty,1) AS ENTERED_QTY,\r\n	B.RECEIVED_ENTERED_QTY / ifnull(to_qty,1) as RECEIVED_ENTERED_QTY,\r\n	B.SUBTOTAL,\r\n	((B.ENTERED_QTY*B.BASE_QTY)-B.RECEIVED_ENTERED_QTY)/ ifnull(to_qty,1) AS SISA_QTY,\r\n	B.UNIT_PRICE,\r\n	B.DISCOUNT_PRICE,\r\n	B.DISCOUNT_PERCEN,\r\n	B.PRICE_AFTER_DISCOUNT,\r\n	B.NOTE,\r\n	B.ENTERED_UOM,\r\n	b.ITEM_DESCRIPTION,\r\n	p.PERSON_CODE,\r\n	p.PERSON_NAME,\r\n	a.DOCUMENT_NO,\r\n	a.DOCUMENT_DATE,\r\n	a.NEED_DATE\r\nFROM\r\n	SO_DETAIL B\r\n	INNER JOIN ITEM I ON (B.ITEM_ID =I.ITEM_ID)\r\n	inner join\r\n	(\r\n		select  k.ITEM_ID, sum(k.QTY_AWAL+k.QTY_MASUK-k.QTY_KELUAR/ ifnull(to_qty,1))  STOK\r\n		from  item_stok k\r\n		inner join item i ON (I.ITEM_ID = k.ITEM_ID)\r\n		left join item_uom ik on I.item_id = ik.item_id  and ik.BASE_UOM_FLAG = \'Y\' \r\n		GROUP BY k.ITEM_ID\r\n	) k  ON (b.ITEM_ID = k.ITEM_ID)\r\n	LEFT JOIN SO a ON (a.SO_ID = b.SO_ID)\r\n	LEFT JOIN PERSON p on (a.PERSON_ID=p.PERSON_ID)\r\n	left join item_uom kk on I.item_id = kk.item_id  and kk.BASE_UOM_FLAG = \'Y\' \r\nWHERE b.ENTERED_QTY > b.RECEIVED_ENTERED_QTY\r\n	and a.status_id IN (@new, @PARTIAL)\r\n	&KONDISI\r\nORDER BY i.item_code, a.DOCUMENT_DATE\r\n', NULL, '', NULL),
      (1243, 1213, 0, 'select\r\nio.DOCUMENT_NO,\r\nio.DOCUMENT_DATE,\r\np.PERSON_NAME,\r\nio.NOTE,\r\nio.inventory_out_id,\r\ni.ITEM_DESCRIPTION,\r\niod.ENTERED_QTY,\r\niod.UNIT_PRICE,\r\niod.SUBTOTAL\r\n\r\nfrom\r\ninventory_out io\r\njoin inventory_out_detail iod on io.INVENTORY_OUT_ID = iod.INVENTORY_OUT_ID\r\njoin person p on io.PERSON_ID = p.PERSON_ID\r\njoin item i on iod.ITEM_ID = i.ITEM_ID \r\njoin warehouse w on io.warehouse_id=w.warehouse_id\r\n\r\nWHERE TRUE\r\nand io.status_id not in(@complete,@delete)  \r\n&Kondisi\r\nORDER BY  io.DOCUMENT_NO, i.ITEM_DESCRIPTION', NULL, '', NULL),
      (1244, 1214, 0, 'SELECT \r\n  a.INVOICE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.TOTAL_NET,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  cs.SITE_NAME,\r\n  s.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME,\r\n  aa.DOCUMENT_DATE as TGL_LUNAS,\r\n  aa.DOCUMENT_NO as NO_LUNAS,\r\n  aa.DOCUMENT_REFF_NO as REFF_LUNAS,\r\n  b.NOTE as KETLUNAS,\r\n  k.COA_NAME,\r\n  aa.PERIOD_NAME as PER_LUNAS,\r\n  b.DISCOUNT,\r\n  b.AMOUNT\r\nFROM\r\n  INVOICE a\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n inner join INVOICE_DUE  cc on (a.INVOICE_id = cc.INVOICE_id)  \r\n inner join payment_detail b on (b.INVOICE_due_id = cc.INVOICE_due_id)\r\n inner join payment aa on (aa.payment_id = b.payment_id)\r\n inner join COA  k on (aa.BANK_ID = k.COA_ID) \r\n \r\nwhere a.STATUS_ID IN (@FULL_PAID, @PARTIAL_PAID)\r\n&Kondisi\r\nORDER BY   a.DOCUMENT_NO \r\n', NULL, '', NULL),
      (1245, 560, 0, 'SELECT \r\n  a.STATUS_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.NOTE,\r\n  a.TOTAL_AMOUNT,\r\n  a.PERSON_ID,\r\n  a.PERSON_SITE_ID,\r\n  a.PAYMENT_TERM_ID,\r\n  a.PERIOD_NAME,\r\n  a.PPN_CODE,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n(um.SUBTOTAL*-1) as UANG_MUKA,\r\n  x.PAID_AMOUNT, \r\n a.TOTAL_NET,\r\n  a.JTEMPO,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  cs.SITE_NAME,\r\n  l.DISPLAY_NAME,\r\n  a.INVOICE_ID,\r\n  w.WAREHOUSE_NAME,\r\n  \r\n   I.ITEM_DESCRIPTION,\r\n   I.ITEM_CODE,\r\n   b.DISCOUNT_PRICE,\r\n   b.DISCOUNT_PERCEN,\r\n   b.SUBTOTAL,\r\n   b.NOTE AS KET,\r\n   if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,bb.to_qty),b.entered_qty) AS ENTERED_QTY,\r\n   if(bb.uom_code is null, b.entered_uom,bb.uom_code) as entered_uom,\r\n   if(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,bb.to_qty),b.unit_price) AS UNIT_PRICE\r\nFROM\r\n  invoice a\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value l ON (a.STATUS_ID = l.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID=b.INVOICE_ID)\r\n  INNER JOIN ITEM i ON (b.ITEM_ID=i.ITEM_ID  )\r\nleft join item_uom bb on i.item_id=bb.ITEM_ID and bb.BASE_UOM_FLAG=\'y\'\r\n  LEFT JOIN(\r\n  SELECT \r\n  sum(a.SUBTOTAL) SUBTOTAL,\r\n  a.INVOICE_ID\r\n  FROM INVOICE_DETAIL a\r\n  INNER JOIN INVOICE b ON b.INVOICE_ID=a.INVOICE_PRE_ID\r\n  GROUP BY a.INVOICE_ID\r\n  )AS  UM on a.INVOICE_ID=um.INVOICE_ID\r\n  INNER JOIN INVOICE_DUE x on (a.INVOICE_ID = x.INVOICE_ID)\r\nwhere\r\nTrue\r\n AND b.INVOICE_PRE_ID IS NULL \r\nAND (PPN_AMOUNT > 0)\r\n&KONDISI\r\nORDER BY\r\n  a.DOCUMENT_NO\r\n', NULL, '', NULL),
      (1246, 1215, 0, 'SELECT \r\na.INVOICE_ID,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\na.NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\nd.DOCUMENT_REFF_NO,\r\na.PERIOD_NAME,\r\na.TOTAL_AMOUNT,\r\na.TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\na.PPN_PERCEN,\r\na.PPN_AMOUNT,\r\na.PPN_CODE,\r\na.JTEMPO,\r\nc.PERSON_NAME,\r\nSITE_NAME, \r\nv.DISPLAY_NAME,\r\nw.WAREHOUSE_NAME,\r\na.TOTAL_NET,\r\ncs.ADDRESS1,\r\nif( b.BASE_QTY=1,b.ENTERED_QTY /if(to_qty is null,1,to_qty),b.entered_qty) as ENTERED_QTY,\r\nif(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,to_qty),b.unit_price) AS UNIT_PRICE,\r\nb.BASE_QTY,\r\nb.DISCOUNT_PRICE,\r\nb.DISCOUNT_PRICE1,\r\nIFNULL(b.DISCOUNT_PERCEN,0)DISCOUNT_PERCEN,\r\nb.SUBTOTAL,\r\nb.NOTE,\r\nif(z.uom_code is null, b.entered_uom,z.uom_code) as ENTERED_UOM,\r\nb.ITEM_DESCRIPTION,\r\ni.ITEM_CODE,\r\nd.DOCUMENT_NO as SJ\r\nFROM\r\nINVOICE a\r\nINNER JOIN INVOICE_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nleft join item_uom z on i.item_id=z.ITEM_ID and z.BASE_UOM_FLAG=\'y\'\r\nINNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nLEFT JOIN INVENTORY_IN d on (d.INVENTORY_IN_ID=a.INVENTORY_IN_ID)\r\nWHERE a.DOCUMENT_TYPE_ID<5\r\n&KONDISI\r\nORDER BY\r\na.DOCUMENT_NO\r\n', NULL, '', NULL),
      (1247, 1216, 0, 'SELECT DISTINCT \r\n  b.INVOICE_DETAIL_ID,\r\n  b.INVOICE_ID,\r\n  b.ENTERED_QTY *  b.BASE_QTY AS QTY,\r\n  b.BASE_QTY,  \r\n  a.PPN_PERCEN,\r\n  (b.UNIT_PRICE /  b.BASE_QTY) AS HARGA,\r\n  (b.DISCOUNT_PRICE + b.discount_price1/ b.ENTERED_QTY) / b.BASE_QTY  AS DISKON,\r\n  b.DISCOUNT_PERCEN,\r\n  b.SUBTOTAL,\r\n  b.CREATED_DATE,\r\n  b.CREATED_BY,\r\n  b.LAST_UPDATE_DATE,\r\n  b.LAST_UPDATE_BY,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  i.ITEM_DESCRIPTION,\r\n  i.ITEM_CODE,\r\n  i.UOM_CODE,\r\n  i.ITEM_ID,\r\n  i.PART_NUMBER,\r\n  i.MIN_STOCK\r\nFROM\r\n  invoice a\r\n  INNER JOIN invoice_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN item i ON (b.item_id = i.item_id)\r\nWHERE \r\n  (b.ENTERED_QTY > 0 )  \r\n  &KONDISI\r\nORDER BY i.ITEM_DESCRIPTION, a.DOCUMENT_DATE', NULL, '', NULL),
      (1248, 1217, 0, 'select\r\ni.item_id, \r\ni.item_description,\r\ni.item_code,\r\ni.uom_code,\r\nn.DOCUMENT_NO,\r\nnd.BASE_QTY,\r\nb.TO_QTY,\r\ncs.PERSON_name,\r\ncs.person_code,\r\nif( nd.BASE_QTY=1,nd.ENTERED_QTY /if(to_qty is null,1,b.to_qty),nd.entered_qty) AS entered_qty,\r\nif(nd.BASE_QTY=1,nd.UNIT_PRICE * if(to_qty is null,1,b.to_qty),nd.unit_price) AS UNIT_PRICE, \r\n(nd.DISCOUNT_PRICE + DISCOUNT_PRICE1 / nd.ENTERED_QTY)/ nd.BASE_QTY AS DISCOUNT_PRICE , \r\nnd.SUBTOTAL - DISCOUNT_PRICE1 as SUBTOTAL,\r\nn.DOCUMENT_REFF_NO,\r\nn.DOCUMENT_DATE,\r\nn.DOCUMENT_NO,\r\nn.DOCUMENT_CLASS_CODE,\r\nn.JTEMPO,\r\nn.NOTE,\r\nc.display_name as merk,\r\ns.document_no as NO_SO,\r\nif(b.uom_code is null, nd.entered_uom,b.uom_code) as uom_code\r\nfrom\r\ninvoice_detail nd \r\ninner join item i on nd.item_id=i.item_id\r\ninner join invoice n on (nd.invoice_id=n.invoice_id)\r\ninner join PERSON cs  on(n.PERSON_id=cs.PERSON_id)\r\ninner join warehouse w on (n.warehouse_id = w.warehouse_id)\r\nleft join item_uom b on i.item_id=b.ITEM_ID and b.BASE_UOM_FLAG=\'y\'\r\nLEFT JOIN PO s on (n.PO_ID= s.PO_ID)\r\nleft join erp_lookup_value c on i.merek_id=c.erp_lookup_value_id\r\nwhere true\r\nand (n.status_id  <> @delete) \r\n&kondisi\r\norder by i.item_description, n.DOCUMENT_DATE\r\n', NULL, '', NULL),
      (1249, 610, 0, 'SELECT DISTINCT\r\n	@no :=  @no + 1 as Nmr,\r\n	A.DOCUMENT_NO,\r\n	A.DOCUMENT_DATE,\r\n	A.NEED_DATE,\r\n	A.PO_ID,\r\n	A.NEED_DATE,\r\n	C.PERSON_NAME,\r\n	E.DISPLAY_NAME,\r\n	a.document_no,\r\n	a.document_date, \r\n	a.need_date, \r\n	b.item_id, \r\n	b.item_description,\r\n	if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty) as Entered_QTY,\r\n	if( b.BASE_QTY=1,b.RECEIVED_Entered_QTY /if( to_qty is null,1,d.to_qty),b.RECEIVED_Entered_QTY) as RECEIVED_Entered_QTY,\r\n	c.PERSON_NAME,\r\n	((if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty))-(if( b.BASE_QTY=1,b.RECEIVED_Entered_QTY /if( to_qty is null,1,d.to_qty),b.RECEIVED_Entered_QTY))) as SISA_QTY,\r\n	(select sum(qty_awal+qty_masuk-qty_keluar) from item_stok s where s.item_id=i.item_id) as stok\r\nFROM\r\n   PO A\r\n   INNER JOIN PO_DETAIL B ON (A.PO_ID = B.PO_ID)\r\n  LEFT  JOIN ITEM I ON(B.ITEM_ID = I.ITEM_ID)\r\n left join item_uom d on i.item_id=d.ITEM_ID and d.BASE_UOM_FLAG=\'y\'\r\n   INNER JOIN PERSON C ON (A.PERSON_ID = C.PERSON_ID)\r\n   INNER JOIN ERP_LOOKUP_VALUE E ON (A.STATUS_ID = E.ERP_LOOKUP_VALUE_ID)\r\nWHERE\r\n(b.entered_QTY-b.RECEIVED_entered_QTY) > 0\r\nAND A.STATUS_ID not in ( @CLOSE,@DELETE)\r\n&KONDISI\r\nORDER BY\r\n  A.NEED_DATE, E.SEQ DESC, A.DOCUMENT_DATE, i.ITEM_DESCRIPTION', NULL, '', NULL),
      (1250, 1218, 0, 'SELECT\r\n	@no :=  @no + 1 as Nmr,\r\n	A.DOCUMENT_NO,\r\n	A.DOCUMENT_REFF_NO,\r\n	a.NOTE,\r\n	A.DOCUMENT_DATE,\r\n	A.NEED_DATE,\r\n	A.PO_ID,\r\n	A.NEED_DATE,\r\n	ad.ADDRESS_CODE,\r\n	C.PERSON_NAME,\r\n	PS.SITE_NAME,\r\n	E.DISPLAY_NAME,\r\n	b.item_id, \r\n	b.item_description,\r\n	a.TOTAL_NET,\r\n	b.UNIT_PRICE,\r\n	b.DISCOUNT_PRICE,\r\n	b.DISCOUNT_PERCEN,\r\n	b.SUBTOTAL,\r\n	b.ENTERED_UOM,\r\n	if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty) as Entered_QTY,\r\n	if( b.BASE_QTY=1,b.RECEIVED_Entered_QTY /if( to_qty is null,1,d.to_qty),b.RECEIVED_Entered_QTY) as RECEIVED_Entered_QTY,\r\n	((if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty))-(if( b.BASE_QTY=1,b.RECEIVED_Entered_QTY /if( to_qty is null,1,d.to_qty),b.RECEIVED_Entered_QTY))) as SISA_QTY,\r\n	(select sum(qty_awal+qty_masuk-qty_keluar) from item_stok s where s.item_id=i.item_id) as stok\r\nFROM\r\n   PO A\r\n   INNER JOIN PERSON C ON (A.PERSON_ID = C.PERSON_ID)\r\n   INNER JOIN PERSON_SITE ps ON a.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\n   INNER JOIN ADDRESS ad ON a.ADDRESS_ID=ad.ADDRESS_ID\r\n   INNER JOIN PO_DETAIL B ON (A.PO_ID = B.PO_ID)\r\n   INNER  JOIN ITEM I ON(B.ITEM_ID = I.ITEM_ID)\r\n   left join item_uom d on i.item_id=d.ITEM_ID and d.BASE_UOM_FLAG=\'y\'\r\n   INNER JOIN ERP_LOOKUP_VALUE E ON (A.STATUS_ID = E.ERP_LOOKUP_VALUE_ID)\r\nWHERE TRUE\r\n&KONDISI\r\nORDER BY\r\n  A.PO_ID', NULL, '', NULL),
      (1251, 1219, 0, 'select\r\n  v.PERSON_name,\r\n  vs.address1,\r\n  v.PERSON_id,\r\n   vs.phone1,\r\n  a.document_date,\r\n  a.document_no,\r\n  if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty) as entered_qty,\r\n  if(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,d.to_qty),b.unit_price) as unit_price,\r\n  (b.item_description) as item_description,\r\n  (if(d.uom_code is null, b.entered_uom,d.uom_code)) as ENTERED_uom,\r\n  (b.subtotal) as subtotal,\r\n  max(ii.document_date) as SJ,\r\n  max(ii.document_no) as SJ_no,\r\n  sum(if( iid.BASE_QTY=1,iid.ENTERED_QTY /if( to_qty is null,1,d.to_qty),iid.entered_qty) ) as SJ_qty,\r\n  (max(if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty)) - sum(if( iid.BASE_QTY=1,iid.ENTERED_QTY /if( to_qty is null,1,d.to_qty),iid.entered_qty))) as sisa\r\nfrom\r\n  PO a\r\n  inner join inventory_in ii ON (a.PO_id = ii.PO_id)\r\n  inner join PO_detail b ON (a.PO_id = b.PO_id)\r\n  inner join inventory_in_detail iid ON (ii.inventory_in_id = iid.inventory_in_id and iid.item_id = b.item_id)\r\n  inner join PERSON v ON (a.PERSON_id = v.PERSON_id)\r\n  left join PERSON_site vs ON (a.PERSON_ID = vs.PERSON_ID)\r\n  inner join item i on (b.item_id = i.item_id)\r\n  left join item_uom d on i.item_id=d.ITEM_ID and d.BASE_UOM_FLAG=\'y\'\r\nWHERE ((a.STATUS_ID=@complete) or (a.STATUS_ID=@partial))\r\nand (ii.STATUS_ID <> @DELETE)\r\n&kondisi\r\ngroup by b.po_detail_id\r\norder by \r\nv.person_name,\r\na.document_date,\r\nii.document_date	', NULL, '', NULL),
      (1252, 1220, 0, 'SELECT \r\na.invoice_ID,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\na.NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\na.PERIOD_NAME,\r\na.TOTAL_AMOUNT,\r\nABS(a.TOTAL_DISCOUNT)TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\na.PPN_PERCEN,\r\nABS(a.PPN_AMOUNT)PPN_AMOUNT,\r\na.PPN_CODE,\r\na.JTEMPO,\r\nc.PERSON_NAME,\r\nSITE_NAME, \r\nv.DISPLAY_NAME,\r\nw.WAREHOUSE_NAME,\r\na.TOTAL_NET,\r\ncs.ADDRESS1,\r\nif( b.BASE_QTY=1,b.ENTERED_QTY /if(to_qty is null,1,to_qty),b.entered_qty) as ENTERED_QTY,\r\nif(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,to_qty),b.unit_price) AS UNIT_PRICE,\r\nb.BASE_QTY,\r\nb.DISCOUNT_PRICE,\r\nb.DISCOUNT_PRICE1,\r\nIFNULL(b.DISCOUNT_PERCEN,0)DISCOUNT_PERCEN,\r\nb.SUBTOTAL,\r\nb.NOTE,\r\nif(z.uom_code is null, b.entered_uom,z.uom_code) as ENTERED_UOM,\r\nb.ITEM_DESCRIPTION,\r\ni.ITEM_CODE\r\nFROM\r\ninvoice a\r\nINNER JOIN invoice_detail b ON (a.invoice_ID = b.invoice_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nleft join item_uom z on i.item_id=z.ITEM_ID and z.BASE_UOM_FLAG=\'y\'\r\nINNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nWHERE a.DOCUMENT_TYPE_ID=5\r\n&KONDISI\r\nORDER BY\r\na.DOCUMENT_NO\r\n', NULL, '', NULL),
      (1254, 1221, 0, 'SELECT  yy.*, :12 as TGL FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_CODE,\r\nk.PERSON_ID,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS JUAL,\r\n0 AS BAYAR_BKM,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKK,\r\n0 RETUR_JUAL\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nINVOICE_DUE s\r\nINNER JOIN PAYMENT_DETAIL y on (s.INVOICE_DUE_id = y.INVOICE_DUE_id)\r\nINNER JOIN PAYMENT x on (x.PAYMENT_id = y.PAYMENT_id) and (date(x.DOCUMENT_DATE) <= :12 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE k.FLAG_SUPP = 1\r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_CODE,\r\nk.PERSON_ID,\r\n0 AS AWAL,\r\na.TOTAL_NET  AS JUAL,\r\nIFNULL(rec.bayar,0)    +    IFNULL(rec_ret.bayar_ret,0)   AS BAYAR_BKM,\r\nIFNULL(pay.bayar,0)  +   IFNULL(pay_ret.bayar_ret,0)  AS BAYAR_BKK,   \r\nIFNULL( c.retur,0) RETUR_JUAL\r\nFROM\r\nINVOICE a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nINNER JOIN INVOICE_DUE g ON (a.INVOICE_ID  = g.INVOICE_ID)  AND (INVOICE_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.INVOICE_DUE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE(date(z.DOCUMENT_DATE))   <= :12\r\nGROUP BY\r\ny.INVOICE_DUE_ID\r\n) AS rec ON (g.INVOICE_DUE_ID = rec.INVOICE_DUE_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.INVOICE_DUE_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nWHERE (date(p.DOCUMENT_DATE)<= :12)\r\nand (pd.INVOICE_DUE_ID IS NOT NULL)\r\nGROUP BY pd.INVOICE_DUE_ID\r\n) AS pay ON (g.INVOICE_DUE_ID = pay.INVOICE_DUE_ID)\r\nLEFT JOIN \r\n/* RETUR REFER KE NO PENJUALAN */ \r\n(SELECT a.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(a.TOTAL_NET) * -1 as RETUR\r\nFROM\r\nINVOICE a\r\nWHERE\r\n(DOCUMENT_TYPE_ID  >= 5 )\r\nAND  (a.INVOICE_OLD_ID IS NOT NULL )\r\nAND   (date(DOCUMENT_DATE) <= :12 )\r\nGROUP BY a.INVOICE_OLD_ID\r\n)  AS c on (a.INVOICE_id = c.INVOICE_id)\r\nleft join\r\n/* PELUNASAN NYA RETUR */ \r\n(SELECT r.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nINNER JOIN INVOICE_DUE n ON (y.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\nINNER JOIN INVOICE r ON (n.INVOICE_ID = r.INVOICE_ID)    \r\nWHERE (date(z.DOCUMENT_DATE)  <= :12) \r\nGROUP BY\r\nr.INVOICE_OLD_ID        \r\n) AS rec_ret ON (a.INVOICE_ID = rec_ret.INVOICE_ID)\r\nleft join\r\n/* PELUNASAN RETUR DGN BKK */ \r\n(SELECT r.INVOICE_OLD_ID AS INVOICE_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR_RET\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nINNER JOIN INVOICE_DUE n ON (pd.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\nINNER JOIN INVOICE r ON (n.INVOICE_ID = r.INVOICE_ID)    \r\nWHERE (date(p.DOCUMENT_DATE)<= :12)\r\nand (pd.INVOICE_DUE_ID IS NOT NULL)\r\nGROUP BY n.INVOICE_ID     \r\n) AS pay_ret ON (a.INVOICE_ID = pay_ret.INVOICE_ID)\r\nWHERE \r\n(\r\n (a.DOCUMENT_TYPE_ID < 5) OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   >= 5) AND (a.INVOICE_OLD_ID IS NULL)  \r\n)\r\n)\r\nAND (date(a.DOCUMENT_DATE)<=  :12 )\r\n&kondisi\r\n/* end penjualan */\r\n) as yy\r\nWHERE \r\n(\r\n(YY.AWAL  + YY.JUAL + Bayar_BKM   - bayar_BKK - RETUR_JUAL) <> 0\r\n) \r\nORDER BY PERSON_NAME, PERSON_CODE, DOCUMENT_DATE\r\n', NULL, '', NULL),
      (1255, 665, 0, 'SELECT * FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n   \'SALDO AWAL \' DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.DUE_DATE AS JTEMPO,\r\n   TIPE,\r\n   k.PERSON_NAME,\r\n   k.PERSON_CODE,\r\n   k.PERSON_ID,\r\n   a.TOTAL_AMOUNT AS AWAL,\r\n   0 AS JUAL,\r\n   0 AS BAYAR_BKM,\r\n   IFNULL(sa.AMOUNT,0) AS BAYAR_BKK,\r\n   0 RETUR_JUAL\r\nFROM \r\n  PERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\n LEFT JOIN \r\n (SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nINVOICE_DUE s\r\nINNER JOIN PAYMENT_DETAIL y on (s.INVOICE_DUE_id = y.INVOICE_DUE_id)\r\nINNER JOIN PAYMENT x on (x.PAYMENT_id = y.PAYMENT_id) and (date(x.DOCUMENT_DATE) <= :12 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE true\r\nand  k.FLAG_SUPP = 1\r\n&kondisi\r\n\r\n/* END SALDO AWAL */  \r\n\r\n\r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.JTEMPO,\r\n   TIPE, \r\n   k.PERSON_NAME,\r\n   k.PERSON_CODE,\r\n   k.PERSON_ID,\r\n   0 AS AWAL,\r\n   a.TOTAL_NET  AS JUAL,\r\n   IFNULL(rec.bayar,0)    +    IFNULL(rec_ret.bayar_ret,0)   AS BAYAR_BKM,\r\n   IFNULL(pay.bayar,0)  +   IFNULL(pay_ret.bayar_ret,0)  AS BAYAR_BKK,   \r\n   IFNULL( c.retur,0) RETUR_JUAL\r\n  FROM\r\n   invoice a\r\n   INNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\n   INNER JOIN INVOICE_DUE g ON (a.invoice_ID  = g.invoice_ID)  AND (INVOICE_FLAG = \'Y\')\r\n   LEFT JOIN \r\n   /* PELUNASAN NYA */ \r\n   (SELECT y.INVOICE_DUE_ID,\r\n   SUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\n    FROM\r\n      RECEIPT z        \r\n      INNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\n  WHERE(date(z.DOCUMENT_DATE))   <= :12\r\n  GROUP BY\r\n     y.INVOICE_DUE_ID\r\n     \r\n   ) AS rec ON (g.INVOICE_DUE_ID = rec.INVOICE_DUE_ID)\r\n   LEFT JOIN \r\n   /* PELUNASAN BKK */ \r\n   (SELECT pd.INVOICE_DUE_ID,\r\n      SUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\n       FROM\r\n         PAYMENT p\r\n         INNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\n     WHERE (date(p.DOCUMENT_DATE)<= :12)\r\n     and (pd.INVOICE_DUE_ID IS NOT NULL)\r\n     GROUP BY pd.INVOICE_DUE_ID\r\n     \r\n      ) AS pay ON (g.INVOICE_DUE_ID = pay.INVOICE_DUE_ID)\r\n    LEFT JOIN \r\n   /* RETUR REFER KE NO PENJUALAN */ \r\n    (SELECT a.invoice_OLD_ID AS invoice_ID,\r\n       SUM(a.TOTAL_NET) * -1 as RETUR\r\n        FROM\r\n          invoice a\r\n        WHERE\r\n        (DOCUMENT_TYPE_ID  >= 5 )\r\n    AND  (a.invoice_OLD_ID IS NOT NULL )\r\n    AND   (date(DOCUMENT_DATE) <= :12 )\r\n    GROUP BY a.invoice_OLD_ID\r\n       )  AS c on (a.invoice_id = c.invoice_id)\r\n left join\r\n/* PELUNASAN NYA RETUR */ \r\n      (SELECT r.invoice_OLD_ID AS invoice_ID,\r\n      SUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\n       FROM\r\n         RECEIPT z        \r\n         INNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\n         INNER JOIN INVOICE_DUE n ON (y.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\n         INNER JOIN invoice r ON (n.invoice_ID = r.invoice_ID)    \r\n     WHERE (date(z.DOCUMENT_DATE)  <= :12)  \r\n     GROUP BY\r\n        r.invoice_OLD_ID        \r\n      ) AS rec_ret ON (a.invoice_ID = rec_ret.invoice_ID)\r\n   left join\r\n\r\n  /* PELUNASAN RETUR DGN BKK */ \r\n   (SELECT r.invoice_OLD_ID AS invoice_ID,\r\n      SUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR_RET\r\n       FROM\r\n         PAYMENT p\r\n         INNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\n         INNER JOIN INVOICE_DUE n ON (pd.INVOICE_DUE_ID = n.INVOICE_DUE_ID  )\r\n         INNER JOIN invoice r ON (n.invoice_ID = r.invoice_ID)    \r\n     WHERE (date(p.DOCUMENT_DATE)<= :12)\r\n     and (pd.INVOICE_DUE_ID IS NOT NULL)\r\n     GROUP BY n.invoice_ID     \r\n   ) AS pay_ret ON (a.invoice_ID = pay_ret.invoice_ID)\r\n\r\n\r\nWHERE \r\n\r\n(\r\n  (a.DOCUMENT_TYPE_ID < 5) OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   >= 5) AND (a.invoice_OLD_ID IS NULL)  \r\n)\r\n)\r\nAND (date(a.DOCUMENT_DATE)<=  :12 )\r\n &kondisi\r\n/* end penjualan */\r\n\r\n) as yy\r\nWHERE \r\n(\r\n(YY.AWAL  + YY.JUAL + Bayar_BKM   - bayar_BKK - RETUR_JUAL) <> 0\r\n) \r\n\r\nORDER BY PERSON_NAME, PERSON_CODE, DOCUMENT_DATE\r\n', NULL, '', NULL),
      (1256, 1222, 0, 'select\r\ngab.item_description,\r\ngab.item_code,\r\ngab.PERSON_name,\r\ngab.PERSON_code,\r\ngab.PERSON_id,\r\nmax(gab.entered_uom) as Entered_uom,\r\nsum(gab.total_barang_bulan_lalu) as T_B_bulan_lalu,\r\nsum(gab.total_nilai_bulan_lalu) as T_N_bulan_lalu,\r\nsum(gab.total_barang_bulan_sekarang) as T_B_bulan_sekarang,\r\nsum(gab.total_nilai_bulan_sekarang) as T_N_bulan_sekarang\r\nfrom\r\n(\r\nselect\r\nnd.item_description,\r\nt.item_code,\r\ncs.PERSON_name,\r\ncs.PERSON_code,\r\ncs.PERSON_id,\r\nmax(nd.entered_uom) as Entered_uom,\r\nsum(nd.entered_qty) as total_barang_bulan_lalu,\r\nsum(nd.subtotal - nd.discount_price1)  as total_nilai_bulan_lalu,\r\n0 as total_barang_bulan_sekarang,\r\n0 as total_nilai_bulan_sekarang\r\nfrom\r\nPERSON cs\r\ninner join invoice n on(cs.PERSON_id=n.PERSON_id)\r\ninner join invoice_detail nd on (n.invoice_id=nd.invoice_id)\r\ninner join item t on (nd.item_id = t.item_id)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nwhere true \r\nand n.period_name<:PERIOD_NAME1\r\nand (n.status_id  <> @delete) \r\nand left(n.period_name,4)=left(:PERIOD_NAME1,4)\r\n&kondisi\r\ngroup by n.PERSON_id,nd.item_id\r\nunion all\r\nselect\r\nnd.item_description,\r\nt.item_code,\r\ncs.PERSON_name,\r\ncs.PERSON_code,\r\ncs.PERSON_id,\r\nmax(nd.entered_uom) as Entered_uom,\r\n0 as total_barang_bulan_lalu,\r\n0 as total_nilai_bulan_lalu,\r\nsum(nd.entered_qty) as total_barang_bulan_sekarang,\r\nsum(nd.subtotal - nd.discount_price1)  as total_nilai_bulan_sekarang\r\nfrom\r\nPERSON cs\r\ninner join invoice n on(cs.PERSON_id=n.PERSON_id)\r\ninner join invoice_detail nd on (n.invoice_id=nd.invoice_id)\r\ninner join item t on (nd.item_id = t.item_id)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nwhere\r\ntrue\r\nand n.period_name=:PERIOD_NAME1\r\nand (n.status_id  <> @delete) \r\n&kondisi\r\ngroup by n.PERSON_id,nd.item_id\r\n)\r\nas gab\r\nwhere true\r\ngroup by gab.PERSON_name,gab.item_description, gab.entered_uom\r\norder by gab.PERSON_name,gab.item_description', NULL, '', NULL),
