      (1258, 1224, 0, 'SELECT \r\n  b.RECEIPT_ID,\r\n  a.DOCUMENT_DATE, \r\n  a.DOCUMENT_NO, \r\n  a.NOTE, \r\n  a.TOTAL_AMOUNT, \r\n  a.CHECK_NO, \r\n  a.JTEMPO, \r\n  bank.COA_NAME BANK_NAME,\r\n  s.DISPLAY_NAME STATUS_DOC,\r\n  p.COA_CODE,\r\n  b.NOTE as KET_DET,\r\n  b.AMOUNT,\r\n  p.COA_NAME\r\nFROM\r\n  RECEIPT a\r\n  INNER JOIN receipt_detail b ON a.RECEIPT_ID=b.RECEIPT_ID\r\n  INNER JOIN COA p ON (b.COA_ID = p.COA_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA bank ON (a.BANK_ID = bank.COA_ID)\r\nWHERE  A.PERSON_ID IS NULL\r\n&Kondisi\r\nORDER BY\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_NO', NULL, '', NULL),
