      (1260, 1226, 0, 'SELECT a.asset_id,\r\n	a.asset_name,\r\n	a.asset_type_id,\r\n	a.nilai_asset,\r\n	a.entered_qty,\r\n	a.buying_date,\r\n	a.umur_asset,\r\n	a.rate/100 as rate,\r\n	IF(a.metode_depresiasi_id = @ND,0, IF(a.metode_depresiasi_id = @DD, 2, 1)) AS METODE_CODE,\r\n	RIGHT(a.metode_depresiasi_id,1) AS mtd,\r\n	m.display_name AS metode,\r\n	a.department_id,\r\n	e.coa_code,\r\n	e.coa_name,\r\n	a.susut_year + ifnull(z.amount_susut,0) as susut_year,\r\n	x.jan as jan, \r\n	x.feb as feb, \r\n	x.mar as mar, \r\n	x.apr as apr, \r\n	x.may as may, \r\n	x.jun as jun, \r\n	x.jul as jul, \r\n	x.aug as aug, \r\n	x.sep as sep, \r\n	x.okt as okt, \r\n	x.nov as nov, \r\n	x.des as des,\r\n	:PERIOD_NAME1 as TAHUN1,\r\n	ifnull(if(date_format(a.using_date,\'%Y%m\')>:PERIOD_NAME1,:PERIOD_NAME1,date_format(date_add(a.using_date, interval 1 month),\'%Y%m\')),:PERIOD_NAME1)as TAHUN\r\nFROM asset a\r\n	INNER JOIN coa e ON a.coa_id=e.coa_id\r\n	INNER JOIN erp_lookup_value m ON m.erp_lookup_value_id=a.metode_depresiasi_id\r\n	/*Tabel nilai penyusutan untuk ambil nilai dari transaksi jurnal penyusutan perbulan*/\r\n	left join (\r\n		select asset_id, period_name, sum(jan) as jan, sum(feb) as feb, sum(mar) as mar, sum(apr) as apr, sum(may) as may, sum(jun) as jun, sum(jul) as jul, sum(aug) as aug, sum(sep) as sep, sum(okt) as okt, sum(nov) as nov, sum(des) as des\r\n		from (\r\n		SELECT jd.ASSET_ID, j.PERIOD_NAME, sum(jd.AMOUNT_CR) as jan, 0 as feb, 0 as mar, 0 as apr, 0 as may, 0 as jun, 0 as jul, 0 as aug, 0 as sep, 0 as okt, 0 as nov, 0 as des\r\n		FROM journal j\r\n			INNER JOIN journal_detail jd ON(j.JOURNAL_ID = jd.JOURNAL_ID)\r\n		WHERE jd.AMOUNT_CR <> 0\r\n			and right(j.PERIOD_NAME, 2) = \'01\'\r\n			and asset_id is not null and left(j.period_name,4) = :YEAR1 AND j.period_name <= :PERIOD_NAME1\r\n		group by asset_id\r\n			\r\n		union\r\n\r\n		SELECT jd.ASSET_ID, j.PERIOD_NAME, 0 as jan, sum(jd.AMOUNT_CR) as feb, 0 as mar, 0 as apr, 0 as may, 0 as jun, 0 as jul, 0 as aug, 0 as 