ME,\r\n  check_no,\r\n  no_detail,\r\n  no_reff_detail,\r\n  (DEBET_DETAIL) as DEBET_DETAIL,\r\n  (KREDIT_DETAIL) as KREDIT_DETAIL\r\n  FROM \r\n( \r\n/* \r\nX1 = COA  UTK SALDO AWAL \r\nX2 = COA  UTK TRANSAKSI \r\nP = PERIODE\r\nT = TANGGAL\r\n\r\n*/\r\n\r\n/* BKM*/\r\nSELECT \r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_AMOUNT as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(ad.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no,\r\n  ifnull(n.document_no, ifnull(i.document_no,c.coa_code)) as no_detail,\r\n  ifnull(nn.document_reff_no, ifnull(ii.document_reff_no,c.coa_name)) as no_reff_detail,\r\n  if(ad.amount>=0,ad.amount,0) as DEBET_DETAIL,\r\n  if(ad.amount>=0,0,ad.amount) as KREDIT_DETAIL\r\nFROM\r\nRECEIPT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\ninner join receipt_detail ad on (a.receipt_id=ad.receipt_id)\r\nleft join nota_penjualan_aging n on (ad.nota_penjualan_aging_id=n.nota_penjualan_aging_id)\r\nleft join nota_penjualan nn on (n.nota_penjualan_id=nn.nota_penjualan_id)\r\nleft join invoice_due i on (ad.invoice_due_id=i.invoice_due_id)\r\nleft join invoice ii on (i.invoice_id=ii.invoice_id)\r\nleft join coa c on (ad.coa_id=c.coa_id)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n \r\n AND (a.PERIOD_NAME BETWEEN :PERIOD_NAME1 AND :PERIOD_NAME2)\r\n AND (DATE(a.DOCUMENT_DATE) BETWEEN :12 AND :22 )\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.NET_BYR1  as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no,\r\n  a.document_no as no_detail,\r\n  a.document_reff_no as no_reff_detail,\r\n  a.net_byr1 as DEBET_DETAIL,\r\n  0 as KREDIT_DETAIL\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR1 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\n and (a.DOCUMENT_TYPE_ID IN (3,4) )\r\nAND ( a.Status_ID<>@DELETE )\r\n \r\n AND (a.PERIOD_NAME BETWEEN :PERIOD_NAME1 AND :PERIOD_NAME2)\r\n AND (DATE(a.DOCUMENT_DATE) BETWEEN :12 AND :22 )\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT \r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_BYR2  as DEBET,\r\n  0 as KREDIT,\r\n a.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no,\r\n  a.document_no as no_detail,\r\n  a.document_reff_no as no_reff_detail,\r\n  a.total_byr2 as DEBET_DETAIL,\r\n  0 as KREDIT_DETAIL\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR2 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n a.DOCUMENT_TYPE_ID IN (3,4) \r\nAND  ( a.TOTAL_BYR2   > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_AMOUNT<>0 )\r\n \r\n AND (a.PERIOD_NAME BETWEEN :PERIOD_NAME1 AND :PERIOD_NAME2)\r\n AND (DATE(a.DOCUMENT_DATE) BETWEEN :12 AND :22 )\r\n\r\nUNION ALL\r\n/* BKK*/\r\nSELECT \r\n  5 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  0 as DEBET,\r\n  a.TOTAL_AMOUNT as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(ad.NOTE,\