      (1330, 1247, 0, 'SELECT\r\n0 AS URUT,\r\nCOA_ID, \r\nSUM(AWAL + DEBET - KREDIT) AS  SALDO_AWAL,\r\nCOA_CODE,\r\nCOA_NAME,\r\nNULL DOCUMENT_DATE,\r\nNULL NOTE,\r\n\'SALDO_AWAL\' ITEM_DESCRIPTION,\r\nNULL DOCUMENT_NO,\r\nNULL DOCUMENT_REFF_NO,\r\n0 AS DEBET,\r\n0 AS KREDIT \r\nFROM\r\n( \r\n\r\n/*\r\nTIPE --> digit menunjukan Jurnal BELI-JUAL-INV-BKK-BKM-GL\r\n\r\n AND (A.PERIOD_NAME =  :PERIOD_NAME1 )   = PERIODE\r\n AND (A.DOCUMENT_DATE < :11)  = TANGGAL\r\n AND (p.TGL_TOLAK < :11 )  = TANGGAL TOLAK\r\n\r\n &KONDISI = ACCOUNT\r\n*/\r\n\r\n/*saldo awal */\r\nselect\r\n  \'111111A\' as TIPE,\r\n  \'SALDO AWAL\' AS DOCUMENT_CLASS_CODE,\r\n  \' \' AS DOCUMENT_NO ,\r\nBALANCE_DATE AS  DOCUMENT_DATE,\r\n  \' \' AS DOCUMENT_REFF_NO ,\r\n  0  AS URUT,\r\n  1 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,\r\nC.ACCOUNT_ID ,\r\n \' \'  ITEM_DESCRIPTION ,\r\n  0 AS ID,\r\n  \' SALDO AWAL \' AS NOTE,\r\n   COA_SALDO AS AWAL,\r\n  0 AS DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  COA_BALANCE a\r\n  INNER join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\nWHERE TRUE \r\n  \r\n AND (A.PERIOD_NAME =  :PERIOD_NAME1 ) \r\n &KONDISI\r\nUNION ALL \r\n\r\n/*---- ADJUSTMENT */\r\n/* persediaan */\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_QTY_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,b.SUBTOTAL *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n  INNER JOIN ADJUST_QTY_DETAIL B on (a.ADJUST_QTY_ID = B.ADJUST_QTY_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\n AND (A.PERIOD_NAME =  :PERIOD_NAME1 )    AND (A.DOCUMENT_DATE < :11)   &KONDISI\r\n\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'0010