D B.INVOICE_DUE_ID IS NOT NULL) OR \r\n  (V.COA_PIUT_ID = C.COA_ID AND B.NOTA_PENJUALAN_AGING_ID IS NOT NULL) )\r\nWHERE (AMOUNT + B.DISCOUNT) <> 0 \r\nAND (B.COA_ID IS NULL ) \r\n AND  A.PERIOD_NAME >= :PERIOD_NAME1      &KONDISI\r\nAND a.DOCUMENT_DATE between :11 and :21\r\n\r\nUNION ALL\r\n/* POTONG HUTANG*/\r\n\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(DISCOUNT>=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN HUTANG\' AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(DISCOUNT<0,-1*DISCOUNT, 0)  AS DEBET,\r\n  IF(DISCOUNT>=0,DISCOUNT, 0)AS KREDIT\r\nFROM\r\nPAYMENT  A\r\nINNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n,\r\nV_ACCOUNT c\r\nWHERE \r\nDISCOUNT <> 0\r\nAND (((c.COA_ID = @POT_HUTANG) AND (B.INVOICE_DUE_ID IS NOT NULL )) OR\r\n ((c.COA_ID = @POT_PIUTANG) AND (B.NOTA_PENJUALAN_AGING_ID IS NOT NULL )))\r\nAND (B.COA_ID IS NULL)\r\n AND  A.PERIOD_NAME >= :PERIOD_NAME1      &KONDISI\r\nAND a.DOCUMENT_DATE between :11 and :21\r\n\r\nUNION ALL\r\n\r\n/*\r\n  BKK\r\nBANK\r\n*/\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 AS URUT,\r\n  70 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  0  AS DEBET,\r\n  A.TOTAL_AMOUNT KREDIT\r\nFROM\r\n  PAYMENT A\r\n  INNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE   \r\n(A.TOTAL_AMOUNT  <> 0) \r\n AND  A.PERIOD_NAME >= :PERIOD_NAME1      &KONDISI\r\nAND a.DOCUMENT_DATE between :11 and :21\r\n\r\n/* END OF BKK */\r\n\r\n\r\n\r\nUNION ALL\r\n/* JURNAL MEMO RIAL  */\r\nSELECT\r\n  \'000001J\' AS TIPE,\r\n\'JURNAL MEMO \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  80 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   a.NOTE as  ITEM_DESCRIPTION ,\r\n  a.JOURNAL_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  AMOUNT as DEBET,\r\nAMOUNT_CR as KREDIT\r\nFROM\r\n  JOURNAL a\r\n  INNER JOIN JOURNAL_detail b ON (a.JOURNAL_ID = b.JOURNAL_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n AND  A.PERIOD_NAME >= :PERIOD_NAME1      &KONDISI\r\nAND a.DOCUMENT_DATE between :11 and :21\r\n\r\n\r\nUNION ALL\r\n\r\n/* BG */ \r\n\r\n/*  BG  CAIR / TOLAK BANK TUJUAN */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 0, 1)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID_DEST = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in (@CLEAR, @TOLAK ))\r\n AND  A.PERIOD_NAME >= :PERIOD_NAME1      &KONDISI\r\nAND a.DOCUMENT_DATE between :11 and :21\r\n\r\nUNION ALL\r\n/* BG  CAIR / TOLAK BANK ASAL */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 1, 0)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID  = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in  (@CLEAR, @TOLAK ))\r\n AND  A.PERIOD_NAME >= :PERIOD_NAME1      &KONDISI\r\nAND a.DOCUMENT_DATE between :11 and :21\r\nUNION ALL\r\n\r\n/* BG  TOLAK BANK ASAL */\r\nSELECT\r\n   IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n AND  A.PERIOD_NAME >= :PERIOD_NAME1    AND (p.TGL_TOLAK between :11 and :21  )   &KONDISI\r\nUNION ALL\r\n/* BG   TOLAK BANK TUJUAN */\r\nSELECT\r\n    IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  2 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT  ,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID_DEST = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n AND  A.PERIOD_NAME >= :PERIOD_NAME1    AND (p.TGL_TOLAK between :11 and :21  )   &KONDISI\r\n)\r\n AS XX \r\nWHERE TRUE\r\nORDER BY COA_CODE, DOCUMENT_DATE, URUT\r\n', NULL, '', NULL),
      (1333, 1248, 9, 'Select count(P.PERSON_ID) as Frek,\r\nsum(b.subtotal - discount_price1) as Nilai,\r\nsum(b.subtotal - discount_price1 - (b.entered_qty*b.HPP))  as LABA,\r\nsum(b.entered_qty*b.unit_price)  as GROSS,\r\nsum(b.entered_qty* b.discount_price + discount_price1 )  as DISKON\r\nFrom Nota_penjualan a\r\ninner join Nota_Penjualan_Detail b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\ninner join person p on (a.person_id=p.person_id)\r\ninner join item i on (b.item_id = i.item_id)\r\nwhere TRUE\r\n&KONDISI\r\n', NULL, '', NULL),
      (1334, 1248, 0, 'Select P.PERSON_NAME,\r\nP.PERSON_CODE,\r\ncount(P.PERSON_ID) as Frek,\r\nsum(xx.nilai) as Nilai,\r\nsum(xx.laba)  as LABA,\r\nsum(xx.gross)  as GROSS,\r\nsum(xx.diskon)  as DISKON\r\nFrom Nota_penjualan a\r\ninner join \r\n(\r\nselect \r\na.nota_penjualan_id,\r\nsum(b.subtotal - discount_price1) as Nilai,\r\nsum(b.subtotal - discount_price1 - (b.entered_qty*b.HPP))  as LABA,\r\nsum(b.entered_qty*b.unit_price)  as GROSS,\r\nsum(b.entered_qty* b.discount_price + discount_price1 )  as DISKON\r\nFrom Nota_penjualan a\r\ninner join nota_Penjualan_Detail b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\ninner join item i on (b.item_id = i.item_id)\r\nwhere document_type_id<> 5\r\n&KONDISI\r\ngroup by nota_penjualan_id\r\n)as xx on a.nota_penjualan_id=xx.nota_penjualan_id\r\ninner join person p on (a.person_id=p.person_id)\r\ngroup by person_name\r\norder by Frek desc limit 10\r\n', NULL, '', NULL),
      (1335, 1248, 3, 'Select P.PERSON_NAME,\r\nP.PERSON_CODE,\r\ncount(P.PERSON_ID) as Frek,\r\nsum(xx.nilai) as Nilai,\r\nsum(xx.laba)  as LABA,\r\nsum(xx.gross)  as GROSS,\r\nsum(xx.diskon)  as DISKON\r\nFrom Nota_penjualan a\r\ninner join \r\n(\r\nselect \r\na.nota_penjualan_id,\r\nsum(b.subtotal - discount_price1) as Nilai,\r\nsum(b.subtotal - discount_price1 - (b.entered_qty*b.HPP))  as LABA,\r\nsum(b.entered_qty*b.unit_price)  as GROSS,\r\nsum(b.entered_qty* b.discount_price + discount_price1 )  as DISKON\r\nFrom Nota_penjualan a\r\ninner join nota_Penjualan_Detail b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\ninner join item i on (b.item_id = i.item_id)\r\nwhere document_type_id<> 5\r\n&KONDISI\r\ngroup by nota_penjualan_id\r\n)as xx on a.nota_penjualan_id=xx.nota_penjualan_id\r\ninner join person p on (a.person_id=p.person_id)\r\ngroup by person_name\r\norder by Nilai desc limit 10\r\n', NULL, '', NULL),
      (1336, 1248, 6, 'Select P.PERSON_NAME,\r\nP.PERSON_CODE,\r\ncount(P.PERSON_ID) as Frek,\r\nsum(xx.nilai) as Nilai,\r\nsum(xx.laba)  as LABA,\r\nsum(xx.gross)  as GROSS,\r\nsum(xx.diskon)  as DISKON\r\nFrom Nota_penjualan a\r\ninner join \r\n(\r\nselect \r\na.nota_penjualan_id,\r\nsum(b.subtotal - discount_price1) as Nilai,\r\nsum(b.subtotal - discount_price1 - (b.entered_qty*b.HPP))  as LABA,\r\nsum(b.entered_qty*b.unit_price)  as GROSS,\r\nsum(b.entered_qty* b.discount_price + discount_price1 )  as DISKON\r\nFrom Nota_penjualan a\r\ninner join nota_Penjualan_Detail b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\ninner join item i on (b.item_id = i.item_id)\r\nwhere true\r\nand (a.status_id  <> @delete) \r\nand (i.JENIS_ID <> @UM)\r\nand (a.DOCUMENT_TYPE_ID <> @PREPAYMENT)\r\n&KONDISI\r\ngroup by nota_penjualan_id\r\n)as xx on a.nota_penjualan_id=xx.nota_penjualan_id\r\ninner join person p on (a.person_id=p.person_id)\r\ngroup by person_name\r\norder by LABA desc limit 10', NULL, '', NULL),
      (1337, 1249, 9, 'SELECT  \r\na.ITEM_ID,\r\ni.ITEM_CODE,\r\ni.ITEM_DESCRIPTION,\r\ni.UOM_CODE, \r\nsum(entered_qty*base_qty) Qty,\r\nsum(subtotal - discount_price1) Nilai\r\nFROM \r\nnota_penjualan_detail a\r\nINNER JOIN Nota_penjualan b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\nINNER JOIN Item i on i.item_id=a.item_id\r\nWHERE i.jenis_id=@Goods\r\n&KONDISI\r\nGROUP BY item_id,i.item_code,item_description,i.uom_code\r\nORDER BY Qty asc limit 10\r\n', NULL, '', NULL),
      (1338, 1249, 0, 'SELECT  \r\na.ITEM_ID,\r\ni.ITEM_CODE,\r\ni.ITEM_DESCRIPTION,\r\ni.UOM_CODE, \r\nsum(subtotal - discount_price1) Nilai,\r\nsum(entered_qty*base_qty) Qty\r\nFROM\r\nnota_penjualan_detail a\r\nINNER JOIN Nota_penjualan b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\nINNER JOIN Item i on i.item_id=a.item_id\r\nWHERE i.jenis_id=@Goods\r\n&KONDISI\r\nGROUP BY item_id,i.item_code,item_description,i.uom_code\r\nORDER BY Nilai asc limit 10', NULL, '', NULL),
      (1339, 1249, 3, 'SELECT  \r\na.ITEM_ID,\r\ni.ITEM_CODE,\r\ni.ITEM_DESCRIPTION,\r\ni.UOM_CODE, \r\ncount(a.item_id) frek,\r\nsum(subtotal - discount_price1) Nilai\r\nFROM\r\nnota_penjualan_detail a\r\nINNER JOIN Nota_penjualan b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\nINNER JOIN Item i on i.item_id=a.item_id\r\nWHERE i.jenis_id=@Goods\r\n&KONDISI\r\nGROUP BY item_id,i.item_code,item_description,i.uom_code\r\nORDER BY Frek asc limit 10', NULL, '', NULL),
      (1340, 1249, 6, 'SELECT  \r\na.ITEM_ID,\r\ni.ITEM_CODE,\r\ni.ITEM_DESCRIPTION,\r\ni.UOM_CODE, \r\nsum(subtotal - discount_price1 - entered_qty* a.hpp)  laba,\r\nsum(subtotal - discount_price1) Nilai\r\nFROM \r\nnota_penjualan_detail a\r\nINNER JOIN Nota_penjualan b on (a.nota_penjualan_id=b.nota_penjualan_id)\r\nINNER JOIN Item i on i.item_id=a.item_id\r\nWHERE i.jenis_id=@Goods\r\n&KONDISI\r\nGROUP BY item_id,i.item_code,item_description,i.uom_code\r\nORDER BY laba asc limit 10', NULL, '', NULL),
      (1361, 1254, 0, ' SELECT\r\n   C.ACCOUNT_CODE,\r\n   C.ACCOUNT_NAME,\r\n   IFNULL(TOTAL_01,0) TOTAL_01,\r\n   IFNULL(TOTAL_02,0) TOTAL_02,\r\n   IFNULL(TOTAL_03,0) TOTAL_03,\r\n   IFNULL(TOTAL_04,0) TOTAL_04,\r\n   IFNULL(TOTAL_05,0) TOTAL_05,\r\n   IFNULL(TOTAL_06,0) TOTAL_06,\r\n   IFNULL(TOTAL_07,0) TOTAL_07,\r\n   IFNULL(TOTAL_08,0) TOTAL_08,\r\n   IFNULL(TOTAL_09,0) TOTAL_09,\r\n   IFNULL(TOTAL_10,0) TOTAL_10,\r\n   IFNULL(TOTAL_11,0) TOTAL_11,\r\n   IFNULL(TOTAL_12,0) TOTAL_12\r\n      \r\n\r\nFROM\r\n   ACCOUNT C\r\n   INNER JOIN COA O ON (C.ACCOUNT_ID = O.ACCOUNT_ID)\r\n   LEFT JOIN\r\n	   (\r\n	   SELECT COA_ID, \r\n	   sum(COA_DEBET-  COA_CREDIT) *-1 total_01\r\n	   FROM COA_BALANCE  \r\n	   WHERE PERIOD_NAME = CONCAT(LEFT(:PERIOD_NAME1,4) ,\'01\')\r\n	   GROUP BY COA_ID\r\n	   ) as x1 on (o.coa_id = x1.coa_id)       \r\n   \r\n   LEFT JOIN\r\n	   (\r\n	   SELECT COA_ID, \r\n	   sum(COA_DEBET-  COA_CREDIT) *-1 total_02\r\n	   FROM COA_BALANCE  \r\n	   WHERE PERIOD_NAME = CONCAT(LEFT(:PERIOD_NAME1,4) ,\'02\')\r\n	   GROUP BY COA_ID\r\n	   ) as x2 on (o.coa_id = x2.coa_id)       \r\n   LEFT JOIN\r\n   	   (\r\n   	   SELECT COA_ID, \r\n   	   sum(COA_DEBET-  COA_CREDIT) *-1 total_03\r\n   	   FROM COA_BALANCE  \r\n   	   WHERE PERIOD_NAME = CONCAT(LEFT(:PERIOD_NAME1,4) ,\'03\')\r\n   	   GROUP BY COA_ID\r\n   	   ) as x3 on (o.coa_id = x3.coa_id)       \r\n   LEFT JOIN\r\n   	   (\r\n   	   SELECT COA_ID, \r\n   	   sum(COA_DEBET-  COA_CREDIT) *-1 total_04\r\n   	   FROM COA_BALANCE  \r\n   	   WHERE PERIOD_NAME = CONCAT(LEFT(:PERIOD_NAME1,4) ,\'04\')\r\n   	   GROUP BY COA_ID\r\n   	   ) as x4 on (o.coa_id = x4.coa_id)       \r\n   LEFT JOIN\r\n   	   (\r\n   	   SELECT COA_ID, \r\n   	   sum(COA_DEBET-  COA_CREDIT) *-1 total_05\r\n   	   FROM COA_BALANCE  \r\n   	   WHERE PERIOD_NAME = CONCAT(LEFT(:PERIOD_NAME1,4) ,\'05\')\r\n   	   GROUP BY COA_ID\r\n   	   ) as x5 on (o.coa_id = x5.coa_id)       \r\n   LEFT JOIN\r\n   	   (\r\n   	   SELECT COA_ID,