      (1386, 1257, 0, 'SELECT ZZ.COA_ID,SUM(ZZ.SALDO) AS SALDO,SUM(ZZ.DEBET) AS DEBET,SUM(ZZ.KREDIT) AS KREDIT,ZZ.BANK_NAME FROM (\r\nSELECT \r\nxx.*,b.ACCOUNT_NAME AS BANK_NAME\r\nFROM(\r\nSELECT\r\nCOA_ID,\r\nSUM(SALDO + DEBET - KREDIT) AS SALDO,\r\n0 DEBET,\r\n0 KREDIT\r\n  FROM \r\n( /* \r\nX1 = COA  UTK SALDO AWAL \r\nX2 = COA  UTK TRANSAKSI \r\nP = PERIODE\r\nT = TANGGAL\r\nT2= TGL_TOLAK\r\nP2= PERIODE TOLAK\r\n\r\n*/\r\n/* SALDO AWAL*/\r\nSELECT \r\n  0 AS URUT, \r\n  \'SALDO AWAL\' as TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  a.COA_SALDO as SALDO,\r\n   0 AS DEBET,\r\n  0 as KREDIT,\r\n  null as PERSON_ID,\r\n  null as PERSON_CODE,\r\n  null as PERSON_NAME,\r\n  null as DOCUMENT_NO,\r\n  null as DOCUMENT_REFF_NO,\r\n  null as DOCUMENT_DATE,\r\n  \'SALDO AWAL\' as NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nCOA b\r\nINNER JOIN COA_BALANCE a on (a.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nWHERE c.ACCOUNT_TYPE_ID IN (@ACC_KASBANK,@ACC_GIRO)\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n\r\nUNION ALL\r\n/* BKM*/\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_AMOUNT as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nRECEIPT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.NET_BYR1  as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR1 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\nand (a.TOTAL_NET > 0) \r\nAND ( a.Status_ID<>@DELETE )\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_BYR2  as DEBET,\r\n  0 as KREDIT,\r\n a.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR2 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n  ( a.TOTAL_BYR2   > 0 )\r\nand (a.TOTAL_NET > 0) \r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_AMOUNT<>0 )\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* BKK*/\r\nSELECT \r\n  5 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  0 as DEBET,\r\n  a.TOTAL_AMOUNT as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nPAYMENT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* BKK DETAIL YG BANK */\r\nSELECT \r\n  2 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  d.AMOUNT  as DEBET,\r\n  0 as KREDIT,\r\n  0 PERSON_ID,\r\n  \' \'AS PERSON_CODE,\r\n  \'\' ASPERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(x.COA_NAME,\'\'),\' - \', IFNULL(d.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_DETAIL d on (a.PAYMENT_ID = d.PAYMENT_ID)\r\nINNER JOIN COA b on (d.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nINNER JOIN COA x on (a.BANK_ID=x.COA_ID)\r\nWHERE (c.ACCOUNT_TYPE_ID IN (@ACC_KASBANK ,@ACC_GIRO))\r\nand (a.Status_ID<>@DELETE )\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* BKM YG BANK */\r\nSELECT \r\n  6 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO,\r\n  0 as DEBET,\r\n   d.AMOUNT as KREDIT,\r\n  0 PERSON_ID,\r\n  \' \'AS PERSON_CODE,\r\n  \'\' ASPERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(x.COA_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nRECEIPT a\r\nINNER JOIN RECEIPT_DETAIL d on (a.RECEIPT_ID = d.RECEIPT_ID )\r\nINNER JOIN COA b on (d.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nINNER JOIN COA x on (a.BANK_ID=x.COA_ID)\r\nWHERE (c.ACCOUNT_TYPE_ID in (@ACC_KASBANK,@ACC_GIRO))\r\nand (a.Status_ID<>@DELETE )\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE TUJUAN */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',3 ,7) as URUT, \r\n  \'PENCAIRAN \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n INNER JOIN COA b on (a.BANK_ID_DEST =b.COA_ID)\r\nWHERE\r\n  a.STATUS_ID in ( @CLEAR, @TOLAK)\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE ASAL */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',7,3) as URUT, \r\n  \'PENCAIRAN \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0) ) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA b on (a.BANK_ID =b.COA_ID)\r\nWHERE\r\n  a.STATUS_ID in ( @CLEAR, @TOLAK)\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE TUJUAN yg tolak  */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',3 ,7) as URUT, \r\n  \'TOLAK \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.TGL_TOLAK as DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA b on (a.BANK_ID_DEST =b.COA_ID)\r\n  INNER JOIN PERIOD P on (EXTRACT(YEAR_MONTH FROM a.tgl_tolak)=P.PERIOD_NAME)\r\nWHERE\r\n  a.STATUS_ID in (  @TOLAK)\r\n AND (p.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.TGL_TOLAK < :12 )  \r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE ASAL YG TOLAK */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',7,3) as URUT, \r\n  \'TOLAK \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0) ) as DEBET,\r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.TGL_TOLAK as DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA b on (a.BANK_ID =b.COA_ID)\r\n  INNER JOIN PERIOD P on (EXTRACT(YEAR_MONTH FROM a.tgl_tolak)=P.PERIOD_NAME)\r\nWHERE\r\n  (a.STATUS_ID =  @TOLAK)\r\n AND (p.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.TGL_TOLAK < :12 )  \r\n\r\nUNION ALL\r\n/* JURNAL */\r\nSELECT \r\n  4 as URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n  AMOUNT as DEBET,\r\n  AMOUNT_CR as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\n  JOURNAL a   \r\n  INNER JOIN JOURNAL_DETAIL d ON (a.JOURNAL_ID = d.JOURNAL_ID)\r\n INNER JOIN COA b on (d.COA_ID =b.COA_ID)\r\nWHERE\r\n true \r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n) AS A \r\nWHERE TRUE\r\nGROUP BY COA_ID\r\nHAVING SALDO <> 0\r\n\r\nUNION ALL\r\n\r\nSELECT\r\nCOA_ID,\r\n0 SALDO,\r\nSUM(DEBET )  AS DEBET, \r\nSUM(KREDIT)  AS KREDIT\r\n  FROM \r\n( /* \r\nX1 = COA  UTK SALDO AWAL \r\nX2 = COA  UTK TRANSAKSI \r\nP = PERIODE\r\nT = TANGGAL\r\nT2= TGL_TOLAK\r\nP2= PERIODE TOLAK\r\n\r\n*/\r\n/* SALDO AWAL*/\r\nSELECT \r\n  0 AS URUT, \r\n  \'SALDO AWAL\' as TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  a.COA_SALDO as SALDO,\r\n   0 AS DEBET,\r\n  0 as KREDIT,\r\n  null as PERSON_ID,\r\n  null as PERSON_CODE,\r\n  null as PERSON_NAME,\r\n  null as DOCUMENT_NO,\r\n  null as DOCUMENT_REFF_NO,\r\n  null as DOCUMENT_DATE,\r\n  \'SALDO AWAL\' as NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nCOA b\r\nINNER JOIN COA_BALANCE a on (a.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nWHERE c.ACCOUNT_TYPE_ID IN (@ACC_KASBANK,@ACC_GIRO)\r\n AND FALSE\r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n\r\nUNION ALL\r\n/* BKM*/\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_AMOUNT as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nRECEIPT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n AND ( a.DOCUMENT_DATE  between :12 and :22 )\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.NET_BYR1  as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR1 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\nand (a.TOTAL_NET > 0) \r\nAND ( a.Status_ID<>@DELETE )\r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n AND ( a.DOCUMENT_DATE  between :12 and :22 )\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_BYR2  as DEBET,\r\n  0 as KREDIT,\r\n a.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR2 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n  ( a.TOTAL_BYR2   > 0 )\r\nand (a.TOTAL_NET > 0) \r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_AMOUNT<>0 )\r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n AND ( a.DOCUMENT_DATE  between :12 and :22 )\r\n\r\nUNION ALL\r\n/* BKK*/\r\nSELECT \r\n  5 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  0 as DEBET,\r\n  a.TOTAL_AMOUNT as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nPAYMENT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n \r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n AND ( a.DOCUMENT_DATE  between :12 and :22 )\r\n\r\nUNION ALL\r\n/* BKK DETAIL YG BANK */\r\nSELECT \r\n  2 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  d.AMOUNT  as DEBET,\r\n  0 as KREDIT,\r\n  0 PERSON_ID,\r\n  \' \'AS PERSON_CODE,\r\n  \'\' ASPERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(x.COA_NAME,\'\'),\' - \', IFNULL(d.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_DETAIL d on (a.PAYMENT_ID = d.PAYMENT_ID)\r\nINNER JOIN COA b on (d.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nINNER JOIN COA x on (a.BANK_ID=x.COA_ID)\r\nWHERE (c.ACCOUNT_TYPE_ID IN (@ACC_KASBANK ,@ACC_GIRO))\r\nand (a.Status_ID<>@DELETE )\r\n \r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n AND ( a.DOCUMENT_DATE  between :12 and :22 )\r\n\r\nUNION ALL\r\n/* BKM YG BANK */\r\nSELECT \r\n  6 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO,\r\n  0 as DEBET,\r\n   d.AMOUNT as KREDIT,\r\n  0 PERSON_ID,\r\n  \' \'AS PERSON_CODE,\r\n  \'\' ASPERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(x.COA_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nRECEIPT a\r\nINNER JOIN RECEIPT_DETAIL d on (a.RECEIPT_ID = d.RECEIPT_ID )\r\nINNER JOIN COA b on (d.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nINNER JOIN COA x on (a.BANK_ID=x.COA_ID)\r\nWHERE (c.ACCOUNT_TYPE_ID in (@ACC_KASBANK,@ACC_GIRO))\r\nand (a.Status_ID<>@DELETE )\r\n \r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n AND ( a.DOCUMENT_DATE  between :12 and :22 )\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE TUJUAN */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',3 ,7) as URUT, \r\n  \'PENCAIRAN \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n INNER JOIN COA b on (a.BANK_ID_DEST =b.COA_ID)\r\nWHERE\r\n  a.STATUS_ID in ( @CLEAR, @TOLAK)\r\n \r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n AND ( a.DOCUMENT_DATE  between :12 and :22 )\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE ASAL */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',7,3) as URUT, \r\n  \'PENCAIRAN \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0) ) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA b on (a.BANK_ID =b.COA_ID)\r\nWHERE\r\n  a.STATUS_ID in ( @CLEAR, @TOLAK)\r\n \r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n AND ( a.DOCUMENT_DATE  between :12 and :22 )\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE TUJUAN yg tolak  */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',3 ,7) as URUT, \r\n  \'TOLAK \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.TGL_TOLAK as DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA b on (a.BANK_ID_DEST =b.COA_ID)\r\n  INNER JOIN PERIOD P on (EXTRACT(YEAR_MONTH FROM a.tgl_tolak)=P.PERIOD_NAME)\r\nWHERE\r\n  a.STATUS_ID in (  @TOLAK)\r\n \r\n\r\n AND ( a.TGL_TOLAK between :12 and :22 )\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE ASAL YG TOLAK */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',7,3) as URUT, \r\n  \'TOLAK \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0) ) as DEBET,\r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.TGL_TOLAK as DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA b on (a.BANK_ID =b.COA_ID)\r\n  INNER JOIN PERIOD P on (EXTRACT(YEAR_MONTH FROM a.tgl_tolak)=P.PERIOD_NAME)\r\nWHERE\r\n  (a.STATUS_ID =  @TOLAK)\r\n \r\n\r\n AND ( a.TGL_TOLAK between :12 and :22 )\r\n\r\nUNION ALL\r\n/* JURNAL */\r\nSELECT \r\n  4 as URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n  AMOUNT as DEBET,\r\n  AMOUNT_CR as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\n  JOURNAL a   \r\n  INNER JOIN JOURNAL_DETAIL d ON (a.JOURNAL_ID = d.JOURNAL_ID)\r\n INNER JOIN COA b on (d.COA_ID =b.COA_ID)\r\nWHERE\r\n true \r\n \r\n AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\n AND ( a.DOCUMENT_DATE  between :12 and :22 )\r\n) AS A \r\nWHERE TRUE\r\nGROUP BY COA_ID\r\n)AS XX\r\nINNER JOIN COA a ON xx.COA_ID=a.COA_ID\r\nINNER JOIN ACCOUNT b ON a.ACCOUNT_ID=b.ACCOUNT_ID\r\nWHERE a.ACTIVE_FLAG = \'Y\' and (b.ACCOUNT_TYPE_ID in (@ACC_KASBANK,@ACC_GIRO))\r\n&KONDISI\r\n) AS ZZ\r\nGROUP BY ZZ.COA_ID', NULL, '', NULL),
      (1387, 1257, 3, 'SELECT  \r\nIF(STATUS_FLAG = \'M\',\'TOLAKAN BG MASUK\',\'TOLAKAN BG KELUAR \') AS BANK ,\r\nSUM(SALDO)SALDO,\r\nSUM(DEBET)DEBET,\r\nSUM(KREDIT)KREDIT\r\nFROM(\r\nSELECT\r\nSTATUS_FLAG,\r\nSUM(DEBET - KREDIT)  AS SALDO, \r\n0 DEBET,\r\n0 KREDIT\r\nFROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   STATUS_FLAG, \r\n   IF(STATUS_FLAG = \'M\',C.TOTAL_AMOUNT,0) AS DEBET,\r\n   IF(STATUS_FLAG <> \'M\',C.TOTAL_AMOUNT,0) AS KREDIT\r\n  FROM CHEQUE C\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\n\r\nAND (C.TGL_TOLAK  < :12)\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE TRUE\r\nAND (R.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKK */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   RD.AMOUNT DEBET,\r\n   0 AS KREDIT\r\nFROM \r\nPAYMENT R \r\nINNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE TRUE\r\nAND (R.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT  R.STATUS_FLAG,\r\n IF(R.STATUS_FLAG <> \'M\',R.TOTAL_AMOUNT,0) AS DEBET,\r\n  IF(R.STATUS_FLAG = \'M\',R.TOTAL_AMOUNT,0) AS KREDIT\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\nWHERE TRUE\r\nAND (R.CREATED_DATE < :12 )\r\n\r\n) \r\nAS XX\r\nWHERE TRUE\r\n&KONDISI\r\nGROUP BY  STATUS_FLAG\r\n\r\nUNION ALL\r\n\r\nSELECT\r\nSTATUS_FLAG,\r\n0 SALDO,\r\nSUM(DEBET )  AS DEBET, \r\nSUM(KREDIT)  AS KREDIT\r\nFROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   STATUS_FLAG, \r\n   IF(STATUS_FLAG = \'M\',C.TOTAL_AMOUNT,0) AS DEBET,\r\n   IF(STATUS_FLAG <> \'M\',C.TOTAL_AMOUNT,0) AS KREDIT\r\n  FROM CHEQUE C\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\n\r\nAND ( C.TGL_TOLAK  BETWEEN   :12  AND :22)\r\n\r\nUNION ALL