      (20004, 70, 0, 'SELECT \r\n  a.*, \r\n  w.WAREHOUSE_NAME,\r\n  v.DISPLAY_NAME,\r\n  sum(ad.entered_qty) as total_entered_qty\r\nFROM\r\n  adjust_qty a\r\n  inner join adjust_qty_detail ad on a.adjust_qty_id=ad.adjust_qty_id\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN erp_lookup_value v ON (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\n INNER JOIN ITEM i ON (ad.item_id = i.item_id)\r\nWHERE\r\nTRUE\r\n&KONDISI\r\ngroup by a.adjust_qty_id\r\nORDER BY\r\n  a.DOCUMENT_NO\r\n', NULL, '', NULL),
      (20005, 1184, 0, 'SELECT \r\n  i.ITEM_ID,\r\n  i.ITEM_DESCRIPTION, \r\n  i.ITEM_CODE \r\nFROM\r\n  ADJUST_QTY a\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN ADJUST_QTY_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n WHERE TRUE\r\n&Kondisi\r\nGROUP BY \r\ni.ITEM_CODE,\r\n  i.ITEM_ID,\r\n    i.ITEM_DESCRIPTION \r\n     \r\n', NULL, '', NULL),
      (20006, 95, 0, 'SELECT \r\n  a.TAG_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.TO_WH_ID,\r\n  a.PERIOD_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.CONFIRMED_FLAG,\r\n  a.PRINT_COUNT,\r\n  a.NOTE,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  w.WAREHOUSE_NAME,\r\n  w2.WAREHOUSE_NAME TUJUAN,\r\n  a.STATUS_ID,\r\n  s.DISPLAY_NAME AS STATUS_DOC\r\nFROM\r\n  tag a\r\n  INNER JOIN tag_detail b\r\n ON (a.TAG_ID =b.TAG_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN warehouse w2 ON (a.TO_WH_ID = w2.WAREHOUSE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n&Kondisi\r\nGROUP BY\r\n  a.TAG_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.TO_WH_ID,\r\n  a.PERIOD_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.CONFIRMED_FLAG,\r\n  a.PRINT_COUNT,\r\n  a.NOTE,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  w.WAREHOUSE_NAME,\r\n  w2.WAREHOUSE_NAME,\r\n  a.STATUS_ID,\r\n  s.DISPLAY_NAME \r\nORDER BY\r\n  a.DOCUMENT_NO\r\n', NULL, '', NULL),
