      (20008, 25, 3, 'SELECT \r\nxx.ITEM_ID,\r\nitem_code,\r\nitem_description,\r\nifnull(iu.uom_code,xx.uom_code)uom_code,\r\nwarehouse_id,\r\nSUM(STOK1)/ifnull(to_qty,1) STOK1,\r\nSUM(STOK2)/ifnull(to_qty,1) STOK2,\r\nSUM(STOK3)/ifnull(to_qty,1) STOK3,\r\nSUM(STOK4)/ifnull(to_qty,1) STOK4,\r\nSUM(STOK5)/ifnull(to_qty,1) STOK5,\r\nSUM(STOK6)/ifnull(to_qty,1) STOK6,\r\nSUM(STOK7)/ifnull(to_qty,1) STOK7,\r\nSUM(STOK8)/ifnull(to_qty,1) STOK8,\r\nSUM(STOK9)/ifnull(to_qty,1) STOK9,\r\nSUM(STOK10)/ifnull(to_qty,1) STOK10 ,\r\nHPP\r\nFROM(\r\nSELECT \r\ni.ITEM_ID,\r\ni.item_code,\r\ni.item_description,\r\ni.uom_code,\r\nxx.warehouse_id,\r\nIF(w.WAREHOUSE_ID=SPLIT_STR(yy.WAREHOUSE_ID,\',\',1),xx.MASUK - xx.KELUAR,0) as STOK1,\r\nIF(w.WAREHOUSE_ID=SPLIT_STR(yy.WAREHOUSE_ID,\',\',2),xx.MASUK - xx.KELUAR,0) as STOK2,\r\nIF(w.WAREHOUSE_ID=SPLIT_STR(yy.WAREHOUSE_ID,\',\',3),xx.MASUK - xx.KELUAR,0) as STOK3,\r\nIF(w.WAREHOUSE_ID=SPLIT_STR(yy.WAREHOUSE_ID,\',\',4),xx.MASUK - xx.KELUAR,0) as STOK4,\r\nIF(w.WAREHOUSE_ID=SPLIT_STR(yy.WAREHOUSE_ID,\',\',5),xx.MASUK - xx.KELUAR,0) as STOK5,\r\nIF(w.WAREHOUSE_ID=SPLIT_STR(yy.WAREHOUSE_ID,\',\',6),xx.MASUK - xx.KELUAR,0) as STOK6,\r\nIF(w.WAREHOUSE_ID=SPLIT_STR(yy.WAREHOUSE_ID,\',\',7),xx.MASUK - xx.KELUAR,0) as STOK7,\r\nIF(w.WAREHOUSE_ID=SPLIT_STR(yy.WAREHOUSE_ID,\',\',8),xx.MASUK - xx.KELUAR,0) as STOK8,\r\nIF(w.WAREHOUSE_ID=SPLIT_STR(yy.WAREHOUSE_ID,\',\',9),xx.MASUK - xx.KELUAR,0) as STOK9,\r\nIF(SPLIT_STR(yy.WAREHOUSE_ID,\',\',10)=\'\',0,IF(w.WAREHOUSE_ID>=SPLIT_STR(yy.WAREHOUSE_ID,\',\',10),xx.MASUK - xx.KELUAR,0)) as STOK10 ,\r\ni.HPP,\r\nxx.MASUK - xx.KELUAR as STOK\r\nFROM \r\nITEM i \r\nINNER JOIN(\r\nSELECT\r\n0 as URUT,\r\n0 as TIPE,\r\np.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\n\'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n\'\' AS DOCUMENT_NO,\r\n\'\' AS DOCUMENT_REFF_NO,\r\nSTART_DATE as DOCUMENT_DATE,\r\na.WAREHOUSE_ID ,\r\n\'\' AS NOTE,\r\nI.ITEM_ID,\r\na.QTY_AWAL AS MASUK,\r\n0 AS KELUAR,\r\na.HPP as HARGA,\r\n0 as DISKON,\r\na.QTY_AWAL * a.HPP   AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\n0 as ID\r\nFROM\r\nitem_balance a\r\nINNER JOIN PERIOD p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE True\r\nAND (A.PERIOD_NAME = :PERIOD_NAME1)\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVOICE         */\r\nSELECT\r\n1 as URUT,\r\n1 as TIPE,\r\nPERIOD_NAME,\r\n1  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE, \r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))   AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_IN_ID AS ID\r\nFROM\r\nINVENTORY_IN a\r\nINNER JOIN INVENTORY_IN_detail b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\nAND (DATE(a.DOCUMENT_DATE) <= :11)\r\nAND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n1 as URUT,\r\n2 as TIPE,\r\na.PERIOD_NAME,\r\n0  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY  AS HARGA  ,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))    AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_OUT_ID AS ID\r\nFROM\r\nINVENTORY_OUT a\r\nINNER JOIN INVENTORY_OUT_detail b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\nAND (DATE(a.DOCUMENT_DATE) <= :11)\r\nAND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\nUNION ALL\r\n/* TRANSAKSI Pembelia