      (20009, 65, 3, '\r\nselect\r\nx.ITEM_ID,\r\nx.document_date,\r\nx.document_no,\r\nif(x.masuk>= 0 , x.masuk,0 ) + if(x.keluar < 0, x.Keluar * -1,0) as Masuk,\r\nif(x.keluar>= 0 , x.keluar,0 ) + if(x.masuk < 0, x.masuk * -1,0) as Keluar\r\nfrom\r\n( SELECT\r\n0 as URUT,\r\n0 as TIPE,\r\np.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\n\'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n\'\' AS DOCUMENT_NO,\r\n\'\' AS DOCUMENT_REFF_NO,\r\nSTART_DATE as DOCUMENT_DATE,\r\na.WAREHOUSE_ID ,\r\n\'\' AS NOTE,\r\nI.ITEM_ID,\r\na.QTY_AWAL AS MASUK,\r\n0 AS KELUAR,\r\na.HPP as HARGA,\r\n0 as DISKON,\r\na.QTY_AWAL * a.HPP   AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\n0 as ID\r\nFROM\r\nitem_balance a\r\nINNER JOIN PERIOD p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE True\r\n AND FALSE\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVOICE         */\r\nSELECT\r\n1 as URUT,\r\n1 as TIPE,\r\nPERIOD_NAME,\r\n1  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE, \r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))   AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_IN_ID AS ID\r\nFROM\r\nINVENTORY_IN a\r\nINNER JOIN INVENTORY_IN_detail b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (b.ITEM_ID = :ITEM_ID) */ \r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n1 as URUT,\r\n2 as TIPE,\r\na.PERIOD_NAME,\r\n0  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY  AS HARGA  ,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))    AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_OUT_ID AS ID\r\nFROM\r\nINVENTORY_OUT a\r\nINNER JOIN INVENTORY_OUT_detail b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (b.ITEM_ID = :ITEM_ID) */ \r\nUNION ALL\r\n/* TRANSAKSI Pembelian yg ga pake BPB  */\r\n/* INVOICE          */\r\nSELECT\r\n1 as URUT,\r\n3 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1) )  AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVOICE_ID AS ID\r\nFROM\r\nINVOICE a\r\nINNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.INVENTORY_IN_DETAIL_ID is NULL\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (b.ITEM_ID = :ITEM_ID) */ \r\nUNION ALL\r\n/* TRANSAKSI Penjualan yg ga pake SJ  */\r\n/* NOTA_PENJUALAN           */\r\nSELECT\r\n1 as URUT,\r\n4 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY   as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1)  )  AS SUBTOTAL,\r\na.PERSON_ID,\r\na.NOTA_PENJUALAN_ID AS ID\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.INVENTORY_OUT_DETAIL_ID IS NULL\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (b.ITEM_ID = :ITEM_ID) */ \r\nUNION ALL\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n2 as URUT,\r\n5 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\nb.UNIT_PRICE /b.BASE_QTY as HARGA,\r\n0 as DISKON ,\r\nb.Subtotal AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.ADJUST_QTY_ID AS ID\r\nFROM\r\nadjust_qty a\r\nINNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.ENTERED_QTY  > 0\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (b.ITEM_ID = :ITEM_ID) */ \r\n\r\nUNION ALL\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n2 as URUT,\r\n5 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * -1 * b.BASE_QTY    AS KELUAR,\r\nb.UNIT_PRICE / b.BASE_QTY as HARGA,\r\n0 as DISKON ,\r\nb.Subtotal AS SUBTOTAL ,\r\n-1 as PERSON_ID,\r\na.ADJUST_QTY_ID AS ID\r\nFROM\r\nadjust_qty a\r\nINNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.ENTERED_QTY  < 0\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (b.ITEM_ID = :ITEM_ID) */ \r\n\r\nUNION ALL\r\n/* TRANSAKSI PAG, GUDANG ASAL  */\r\n/* TAG         */\r\nSELECT\r\n1 as URUT,\r\n6 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\nb.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nifnull(l.Hpp,0) as HARGA,\r\n0 AS DISKON, \r\n0 AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.TAG_ID AS ID\r\nFROM\r\ntag a\r\nINNER JOIN tag_detail b ON (a.tag_ID = b.tag_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nLEFT  JOIN Item_balance l on (b.item_id = l.item_id) and (a.period_name = l.period_name) and (a.warehouse_id = l.warehouse_id)\r\nWHERE TRUE\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (b.ITEM_ID = :ITEM_ID) */ \r\nUNION ALL\r\n/* TRANSAKSI PAG, GUDANG TUJUAN  */\r\n/* TAG         */\r\nSELECT\r\n1 as URUT,\r\n6 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.to_wh_ID as WAREHOUSE_ID,\r\nb.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\nifnull(l.Hpp,0) as HARGA,\r\n0 as DISKON ,\r\n0 AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.TAG_ID AS ID\r\nFROM\r\ntag a\r\nINNER JOIN tag_detail b ON (a.tag_ID = b.tag_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nLEFT  JOIN Item_balance l on (b.item_id = l.item_id) and (a.period_name = l.period_name) and (a.to_wh_id = l.warehouse_id)\r\nWHERE TRUE\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (b.ITEM_ID = :ITEM_ID) */ \r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  BAHAN BAKU       */\r\nSELECT\r\n1 as URUT,\r\n7 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.UNIT_PRICE /b.BASE_QTY   AS HARGA,\r\n0 as DISKON ,\r\nb.SUBTOTAL  AS SUBTOTAL, \r\n-1 as PERSON_ID,\r\na.BUILD_ID AS ID\r\nFROM\r\nbuild a\r\nINNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE  true\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (b.ITEM_ID = :ITEM_ID) */ \r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  ITEM YG DIBUAT       */\r\nSELECT\r\n1 as URUT,\r\n7 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\na.ENTERED_QTY *  a.BASE_QTY  AS MASUK,\r\n0 AS KELUAR,\r\na.TOTAL_AMOUNT / a.ENTERED_QTY / a.BASE_QTY  AS HARGA,\r\n0 as DISKON ,\r\na.TOTAL_AMOUNT AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.BUILD_ID AS ID\r\nFROM\r\nbuild a\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE true \r\n AND (a.PERIOD_NAME = :PERIOD_NAME1 ) \r\n/* AND (a.ITEM_ID = :ITEM_ID) */\r\n) AS X\r\ninner join item i on x.item_id=i.item_id\r\ninner join warehouse w on x.warehouse_id=w.warehouse_id\r\nWHERE TRUE\r\n&KONDISI\r\nORDER BY \r\n x.document_date\r\n', NULL, '', 0),
      (20010, 85, 0, 'SELECT \r\n     a.DOCUMENT_NO,\r\n     a.DOCUMENT_REFF_NO,\r\n     a.DOCUMENT_DATE,\r\n     p.PERSON_NAME,\r\n     s.SITE_NAME,\r\n     s.ADDRESS1,\r\n     so.DOCUMENT_NO as NO_SO,\r\n     w.WAREHOUSE_NAME,\r\n     a.NOTE,\r\n     a.INVENTORY_OUT_ID\r\nFROM \r\n   INVENTORY_OUT a\r\nLEFT JOIN SO on (so.SO_ID=a.SO_ID)\r\nLEFT JOIN PERSON p on (a.PERSON_ID=p.PERSON_ID)\r\nLEFT JOIN PERSON_SITE s on (p.PERSON_ID=s.PERSON_ID)\r\nLEFT JOIN WAREHOUSE w on (w.WAREHOUSE_ID=a.WAREHOUSE_ID)\r\nWHERE\r\n   (a.STATUS_ID<>@DELETE)\r\n&KONDISI\r\nORDER BY DOCUMENT_DATE,DOCUMENT_NO\r\n\r\n', NULL, '', NULL),
      (20011, 85, 3, 'SELECT\r\n    b.INVENTORY_OUT_ID,\r\n    i.ITEM_CODE,\r\n    b.ITEM_DESCRIPTION,\r\n    b.ENTERED_QTY,\r\n    b.ENTERED_UOM,\r\n    b.NOTE\r\nFROM \r\n  INVENTORY_OUT_DETAIL b\r\nLEFT JOIN ITEM I on (i.item_id=b.item_id)\r\nWHERE TRUE\r\n  /* b.INVENTORY_OUT_ID=:INVENTORY_OUT_ID*/\r\n&KONDISI\r\n', NULL, '', 0),
      (20012, 130, 3, 'select \r\n  gab.item_id,\r\n  gab.item_description,\r\n  sum(jum_bul) as jum_bul,\r\n 0 as angg,\r\n  sum(saldo) as saldo\r\n from\r\n(\r\nSelect \r\n  a.item_id,\r\n  a.item_description, \r\n  if(sum(x.entered_qty*x.base_qty) is null,0,sum(x.entered_qty*x.base_qty)) as jum_bul,\r\n  0 as angg,\r\n  0 as saldo\r\nfrom \r\n  item a\r\n  left join (select * from build b where true  AND (DATE(b.DOCUMENT_DATE) <= :11) and (b.Period_Name = :PERIOD_NAME1) AND (B.warehouse_id=:12)) as x on (a.item_id=x.item_id)\r\nwhere \r\n  true\r\n  /*a.item_id=:item_id*/\r\n  &kondisi \r\ngroup by \r\n  a.item_id, \r\n  a.item_description\r\n\r\n\r\nunion all\r\n\r\nSelect\r\n  a.item_id,\r\n  a.item_description,\r\n  0 as jum_bul,\r\n  0 as angg,\r\n  x.qty_awal as saldo\r\nfrom\r\n  item a\r\n  left join (select * from item_balance ib where true  and (ib.Period_Name = :PERIOD_NAME1)  AND (iB.warehouse_id=:12)) as x on (a.item_id=x.item_id)\r\nwhere true\r\n  /*a.item_id=:item_id*/\r\n  &kondisi\r\ngroup by\r\n  a.item_id,\r\n  a.item_description\r\n) as gab\r\ngroup by\r\n  gab.item_id\r\norder by\r\n  gab.item_description', NULL, '', 0),
      (20013, 1197, 0, 'SELECT \r\n  a.NOTA_PENJUALAN_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.TOTAL_NET,\r\n  a.NOTE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.PERIOD_NAME,\r\n  a.TOTAL_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.TOTAL_DISCOUNT_PERCEN,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.PRINT_COUNT,\r\n  a.KARYAWAN_ID,\r\n  a.PPN_CODE,\r\n  a.JTEMPO,\r\n  c.PERSON_NAME,\r\n  cs.SITE_NAME,\r\n  s.DISPLAY_NAME,\r\n  w.WAREHOUSE_NAME,\r\n  aa.DOCUMENT_DATE as TGL_LUNAS,\r\n  aa.DOCUMENT_NO as NO_LUNAS,\r\n  aa.DOCUMENT_REFF_NO as REFF_LUNAS,\r\n  b.NOTE as KETLUNAS,\r\n  k.COA_NAME,\r\n  aa.PERIOD_NAME as PER_LUNAS,\r\n  b.DISCOUNT,\r\n  b.AMOUNT\r\nFROM\r\n  nota_penjualan a\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n inner join nota_penjualan_aging  cc on (a.nota_penjualan_id = cc.nota_penjualan_id)\r\n  \r\n inner join receipt_detail b on (b.nota_penjualan_aging_id = cc.nota_penjualan_aging_id)\r\n inner join receipt aa on (aa.receipt_id = b.receipt_id)\r\n inner join COA  k on (aa.BANK_ID = k.COA_ID) \r\n \r\nwhere a.STATUS_ID IN (@FULL_PAID, @PARTIAL_PAID)\r\n&Kondisi\r\nORDER BY   a.DOCUMENT_NO \r\n', NULL, '  ', NULL),
      (20014, 605, 0, 'SELECT \r\n	I.ITEM_ID,\r\n	I.ITEM_CODE,\r\n	I.ITEM_DESCRIPTION,\r\n	k.STOK,\r\n	B.PO_DETAIL_ID,\r\n	B.PO_ID,\r\n	B.ITEM_ID,\r\n	B.ENTERED_QTY * B.BASE_QTY/ ifnull(to_qty,1) AS ENTERED_QTY,\r\n	B.RECEIVED_ENTERED_QTY / ifnull(to_qty,1) as RECEIVED_ENTERED_QTY,\r\n	B.SUBTOTAL,\r\n	((B.ENTERED_QTY*B.BASE_QTY)-B.RECEIVED_ENTERED_QTY)/ ifnull(to_qty,1) AS SISA_QTY,\r\n	B.UNIT_PRICE,\r\n	B.DISCOUNT_PRICE,\r\n	B.DISCOUNT_PERCEN,\r\n	B.PRICE_AFTER_DISCOUNT,\r\n	B.NOTE,\r\n	B.ENTERED_UOM,\r\n	b.ITEM_DESCRIPTION,\r\n	p.PERSON_CODE,\r\n	p.PERSON_NAME,\r\n	a.DOCUMENT_NO,\r\n	a.DOCUMENT_DATE,\r\n	a.NEED_DATE\r\nFROM\r\n	PO_DETAIL B\r\n	INNER JOIN ITEM I ON (B.ITEM_ID =I.ITEM_ID)\r\n	inner join\r\n	(\r\n		select  k.ITEM_ID, sum(k.QTY_AWAL+k.QTY_MASUK-k.QTY_KELUAR/ ifnull(to_qty,1))  STOK\r\n		from  item_stok k\r\n		inner join item i ON (I.ITEM_ID = k.ITEM_ID)\r\n		left join item_uom ik on I.item_id = ik.item_id  and ik.BASE_UOM_FLAG = \'Y\' \r\n		GROUP BY k.ITEM_ID\r\n	) k  ON (b.ITEM_ID = k.ITEM_ID)\r\n	LEFT JOIN PO a ON (a.PO_ID = b.PO_ID)\r\n	LEFT JOIN PERSON p on (a.PERSON_ID=p.PERSON_ID)\r\n	left join item_uom kk on I.item_id = kk.item_id  and kk.BASE_UOM_FLAG = \'Y\' \r\nWHERE b.ENTERED_QTY > b.RECEIVED_ENTERED_QTY\r\n	and a.status_id IN (@new, @PARTIAL)\r\n	&KONDISI\r\nORDER BY i.item_code, a.DOCUMENT_DATE\r\n', NULL, '', NULL),
      (20016, 1209, 0, 'SELECT \r\n  a.SO_ID,\r\n  a.STATUS_ID,\r\n  a.ADDRESS_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.NOTE,\r\n  a.TOTAL_AMOUNT,\r\n  a.PERSON_ID,\r\n  a.PERSON_SITE_ID,\r\n  a.PAYMENT_TERM_ID,\r\n  a.PERIOD_NAME,\r\n  a.PPN_CODE,\r\n  a.PPN_PERCEN,\r\n  a.PPN_AMOUNT,\r\n  a.TOTAL_DISCOUNT,\r\n  a.NEED_DATE,\r\n  a.PRINT_COUNT,\r\n  a.JTEMPO,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  c.PERSON_NAME,\r\n  cs.SITE_NAME,\r\n  a.DELIVER_ON,\r\n  l.DISPLAY_NAME,\r\n  a.TOTAL_NET,\r\n  b.SO_DETAIL_ID,\r\n  b.SO_ID,\r\n  b.SUBTOTAL,\r\n  b.ITEM_ID,\r\n  if( b.BASE_QTY=1,b.ENTERED_QTY /if(d.to_qty is null,1,d.to_qty),b.entered_qty) as ENTERED_QTY,\r\n  if( b.BASE_QTY=1,b.ENTERED_QTY /if(d.to_qty is null,1,d.to_qty),b.entered_qty) as RECEIVED_ENTERED_QTY,\r\n  b.BASE_QTY,\r\n  b.RECEIVED_BASE_QTY,\r\n  if(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,to_qty),b.unit_price) as UNIT_PRICE,\r\n  b.DISCOUNT_PRICE,\r\n  b.DISCOUNT_PERCEN,\r\n  b.PRICE_AFTER_DISCOUNT,\r\n  b.NOTE as NOTE_DET,\r\n  if(d.uom_code is null, b.entered_uom,d.uom_code) AS ENTERED_UOM,\r\n  b.ITEM_DESCRIPTION,\r\n  i.PART_NUMBER,\r\n  i.ITEM_CODE\r\nFROM\r\n  SO a\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\n  INNER JOIN erp_lookup_value l ON (a.STATUS_ID = l.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN SO_detail b ON (a.SO_ID = b.SO_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n  left join item_uom d on i.item_id=d.ITEM_ID and d.BASE_UOM_FLAG=\'y\'\r\nWHERE TRUE\r\n&KONDISI\r\nORDER BY a.SO_ID', NULL, '', NULL),
      (20017, 1192, 0, 'SELECT \r\n  v.DISPLAY_NAME AS JENIS,\r\n  v.ERP_LOOKUP_VALUE_ID\r\nFROM\r\n  erp_lookup_set vs\r\n  INNER JOIN erp_lookup_value v ON (vs.ERP_LOOKUP_SET_ID = v.ERP_LOOKUP_SET_ID)\r\nWHERE\r\n  vs.PROGRAM_CODE = \'JENIS\'  and (  v.active_flag = \'Y\' )\r\n&Kondisi\r\nORDER BY v.DISPLAY_NAME', NULL, '', NULL),
