               ELSE
                    SELECT    MAX(ERP_NO_ID), MAX(NO_TRANS_ID ) 
            INTO V_ID, v_seq FROM
                       erp_no a

                       WHERE 
                       (a.ERP_MENU_ID = VMENU_ID) and (a.PERIOD_NAME = vPERIOD_NAME);
