	(16, 519, 'BKM', '', '', 'FORM\\F_FIN_BKM', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\nreceipt.RECEIPT_ID,\r\nreceipt.DOCUMENT_NO,\r\nDATE_FORMAT(receipt.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\nreceipt.DOCUMENT_REFF_NO,\r\np.PERSON_NAME,\r\nreceipt.NOTE,\r\ncoa.COA_NAME BANK_NAME\r\nFROM ERP_NO_FORM,RECEIPT\r\nLEFT JOIN PERSON p ON receipt.PERSON_ID=p.PERSON_ID\r\nINNER JOIN COA on receipt.BANK_ID=coa.COA_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM RECEIPT_DETAIL\r\nWHERE RECEIPT_ID = :RECEIPT_ID )\r\nAND RECEIPT_ID = :RECEIPT_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* FROM\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 AS X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.RECEIPT_ID,\r\nCONCAT(DOCUMENT_NO,\' - \',ACCOUNT_NAME,IF(ACCOUNT_CODE IS NULL, \'\',CONCAT(\' - \',ACCOUNT_CODE)))AS URAI,\r\na.NOTE,\r\nFORMAT(a.AMOUNT,2) AMOUNT\r\nFROM(\r\nSELECT \r\n1 AS URUT,\r\na.RECEIPT_ID,\r\na.RECEIPT_DETAIL_ID,\r\nIFNULL(npa.DOCUMENT_NO,i.DOCUMENT_NO) DOCUMENT_NO,\r\nIF(p.FLAG_SUPP=0, v.ACCOUNT_NAME,va.ACCOUNT_NAME) AS ACCOUNT_NAME,\r\nIF(p.FLAG_SUPP=0, v.ACCOUNT_CODE,va.ACCOUNT_CODE) AS ACCOUNT_CODE,\r\na.NOTE,\r\na.AMOUNT\r\nFROM\r\nRECEIPT_DETAIL a\r\nINNER JOIN RECEIPT b ON a.RECEIPT_ID=b.RECEIPT_ID\r\nINNER JOIN PERSON p ON b.PERSON_ID=p.PERSON_ID\r\nLEFT JOIN V_ACCOUNT v ON (p.COA_PIUT_ID=v.COA_ID) AND p.FLAG_SUPP=0\r\nLEFT JOIN V_ACCOUNT va ON (p.COA_HUT_ID = va.COA_ID) AND p.FLAG_SUPP=1\r\nLEFT JOIN NOTA_PENJUALAN_AGING npa ON a.NOTA_PENJUALAN_AGING_ID=npa.NOTA_PENJUALAN_AGING_ID\r\nLEFT JOIN INVOICE_DUE i ON a.INVOICE_DUE_ID=i.INVOICE_DUE_ID\r\nWHERE a.RECEIPT_id=  :RECEIPT_ID AND a.COA_ID IS NULL\r\n\r\nUNION ALL\r\n\r\nSELECT \r\n2 AS URUT,\r\na.RECEIPT_ID,\r\na.RECEIPT_DETAIL_ID,\r\nc.COA_CODE AS DOCUMENT_NO,\r\nc.COA_NAME AS ACCOUNT_NAME,\r\nNULL AS ACCOUNT_CODE,\r\na.NOTE,\r\na.AMOUNT\r\nFROM\r\nRECEIPT_DETAIL a\r\nINNER JOIN V_ACCOUNT c ON a.COA_ID=c.COA_ID\r\nWHERE a.RECEIPT_id=  :RECEIPT_ID AND a.COA_ID IS NOT NULL\r\n)AS A\r\nORDER BY  a.URUT,a.RECEIPT_DETAIL_ID\r\n) AS YY\r\nWHERE\r\nTRUE\r\nAND X1 = :NO', '', ''),
	(17, 520, 'BKK', '', '', 'FORM\\F_FIN_BKK', '', '', 'SELECT \r\n@abc2:=0,\r\nNO,\r\npayment.PAYMENT_ID,\r\npayment.DOCUMENT_NO,\r\nDATE_FORMAT(payment.DOCUMENT_DATE,\'%d-%b-%Y\' ) as DOCUMENT_DATE ,\r\npayment.DOCUMENT_REFF_NO,\r\np.PERSON_NAME,\r\npayment.NOTE,\r\ncoa.COA_NAME BANK_NAME\r\nFROM ERP_NO_FORM,PAYMENT\r\nLEFT JOIN PERSON p ON payment.PERSON_ID=p.PERSON_ID\r\nINNER JOIN COA on payment.BANK_ID=coa.COA_ID\r\nWHERE\r\nNO <= (SELECT IF( count(*) > 0   && count(*) mod @Baris =0  , (COUNT(*))  DIV @Baris ,  (COUNT(*))  DIV @Baris +1 ) FROM PAYMENT_DETAIL\r\nWHERE PAYMENT_ID = :PAYMENT_ID )\r\nAND PAYMENT_ID = :PAYMENT_ID', 'SELECT  @abc2 :=0,@abc3 :=0,@abc4:=0, YY.* FROM\r\n(\r\nSELECT (((@abc2 := @abc2 + 1)-1) div @Baris )+1 AS X1 ,\r\n(((@ABC3 := @ABC3 + 1)-1 )MOD @Baris )+1 AS X2,\r\n(((@ABC4 := @ABC4 + 1)-1 ))+1 AS X3,\r\na.PAYMENT_ID,\r\nCONCAT(DOCUMENT_NO,\' - \',ACCOUNT_NAME,IF(ACCOUNT_CODE IS NULL, \'\',CONCAT(\' - \',ACCOUNT_CODE)))AS URAI,\r\na.NOTE,\r\nFORMAT(a.AMOUNT,2) AMOUNT\r\nFROM(\r\nSELECT \r\n1 AS URUT,\r\na.PAYMENT_ID,\r\na.PAYMENT_DETAIL_ID,\r\nIFNULL(npa.DOCUMENT_NO,i.DOCUMENT_NO) DOCUMENT_NO,\r\nIF(p.FLAG_SUPP=0, v.ACCOUNT_NAME,va.ACCOUNT_NAME) AS ACCOUNT_NAME,\r\nIF(p.FLAG_SUPP=0, v.ACCOUNT_CODE,va.ACCOUNT_CODE) AS ACCOUNT_CODE,\r\na.NOTE,\r\na.AMOUNT\r\nFROM\r\nPAYMENT_DETAIL a\r\nINNER JOIN PAYMENT b ON a.PAYMENT_ID=b.PAYMENT_ID\r\nINNER JOIN PERSON p ON b.PERSON_ID=p.PERSON_ID\r\nLEFT JOIN V_ACCOUNT v ON (p.COA_PIUT_ID=v.COA_ID) AND p.FLAG_SUPP=0\r\nLEFT JOIN V_ACCOUNT va ON (p.COA_HUT_ID = va.COA_ID) AND p.FLAG_SUPP=1\r\nLEFT JOIN NOTA_PENJUALAN_AGING npa ON a.NOTA_PENJUALAN_AGING_ID=npa.NOTA_PENJUALAN_AGING_ID\r\nLEFT JOIN INVOICE_DUE i ON a.INVOICE_DUE_ID=i.INVOICE_DUE_ID\r\nWHERE a.PAYMENT_id=  :PAYMENT_ID AND a.COA_ID IS NULL\r\n\r\nUNION ALL\r\n\r\nSELECT \r\n2 AS URUT,\r\na.PAYMENT_ID,\r\na.PAYMENT_DETAIL_ID,\r\nc.COA_CODE AS DOCUMENT_NO,\r\nc.COA_NAME AS ACCOUNT_NAME,\r\nNULL AS ACCOUNT_CODE,\r\na.NOTE,\r\na.AMOUNT\r\nFROM\