	(1, 'No Dokumen 2', 'No Dokumen(2 Par.)', 'SELECT a.DOCUMENT_NO as RETURN_FIELD, \r\na.DOCUMENT_NO as RETURN_VALUE, \r\na.DOCUMENT_DATE, \r\na.NOTE FROM PO\r\nWHERE document_type_id <> 5 \r\n&kondisi\r\norder by a.DOCUMENT_NO\r\nlimit &limit', 'DOCUMENT_NO', 'DOCUMENT_NO', 0, '2006-11-08 11:56:44', 1, '2007-08-25 13:48:02'),
